-- CASE 1: STEMI
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'IAM com Supradesnivelamento de ST (STEMI)', 'Paciente masculino de 58 anos apresenta dor toracica opressiva irradiando para o braco esquerdo ha 2 horas, acompanhada de sudorese, nauseas, ECG com elevacao do ST em V1-V4, troponina elevada e taquicardia.', 'clinica_medica', 'medio', '[{"id":"stemi-dados-1","textPt":"Dor toracica opressiva com irradiacao para o braco esquerdo","isCorrect":true,"explanationPt":"Apresentacao classica de angina ou IAM, com irradiacao aumentando a suspeita.","clinicalPearlPt":"A irradiacao para o braco esquerdo tem alta especificidade para eventos coronarianos."},{"id":"stemi-dados-2","textPt":"Elevacao do ST em V1-V4 no ECG","isCorrect":true,"explanationPt":"Indica isquemia aguda transmural, tipica de STEMI anterior.","clinicalPearlPt":"Elevacao >=2mm em V2-V3 em homens <40 anos confirma STEMI."},{"id":"stemi-dados-3","textPt":"Troponina elevada","isCorrect":true,"explanationPt":"Marcador de necrose miocardica, confirmando dano tecidual.","clinicalPearlPt":"Troponina I ou T >99o percentil define IAM."},{"id":"stemi-dados-4","textPt":"Febre de 38,5C","isCorrect":false,"explanationPt":"Sugere infeccao ou inflamacao, nao compativel com apresentacao coronariana aguda.","clinicalPearlPt":null},{"id":"stemi-dados-5","textPt":"Cefaleia","isCorrect":false,"explanationPt":"Sintoma inespecifico, nao relacionado a sindrome coronariana.","clinicalPearlPt":null},{"id":"stemi-dados-6","textPt":"Diarreia","isCorrect":false,"explanationPt":"Indica possivel gastroenterite, desviando do quadro cardiovascular.","clinicalPearlPt":null}]'::jsonb, '[{"id":"stemi-padrao-1","textPt":"Sindrome coronariana aguda com elevacao do ST","isCorrect":true,"explanationPt":"Padrao classico de oclusao coronaria aguda com isquemia transmural.","clinicalPearlPt":"STEMI requer reperfusao imediata para salvar miocardio."},{"id":"stemi-padrao-2","textPt":"Pericardite aguda","isCorrect":false,"explanationPt":"Apresenta elevacao difusa do ST, mas sem troponina elevada tipicamente.","clinicalPearlPt":null},{"id":"stemi-padrao-3","textPt":"Disseccao aortica","isCorrect":false,"explanationPt":"Dor lacerante, sem alteracoes especificas de ST segmentar.","clinicalPearlPt":null},{"id":"stemi-padrao-4","textPt":"Pneumonia","isCorrect":false,"explanationPt":"Nao causa elevacao focal do ST ou troponina elevada.","clinicalPearlPt":null}]'::jsonb, '[{"id":"stemi-hipotese-1","textPt":"STEMI anterior","isCorrect":true,"explanationPt":"Localizacao baseada em V1-V4, indicando oclusao da descendente anterior.","clinicalPearlPt":"Mortalidade alta sem reperfusao; ICP preferivel."},{"id":"stemi-hipotese-2","textPt":"IAM sem elevacao de ST (NSTEMI)","isCorrect":false,"explanationPt":"ECG normal ou depressao ST, nao elevacao.","clinicalPearlPt":null},{"id":"stemi-hipotese-3","textPt":"Pericardite","isCorrect":false,"explanationPt":"Elevacao concava difusa, aliviada por inclinacao anterior.","clinicalPearlPt":null},{"id":"stemi-hipotese-4","textPt":"Embolia pulmonar","isCorrect":false,"explanationPt":"Padrao S1Q3T3, sem elevacao ST focal.","clinicalPearlPt":null}]'::jsonb, '[{"id":"stemi-conduta-1","textPt":"ICP primaria em ate 90 min + dupla antiagregacao (AAS + ticagrelor) + heparina + morfina se necessario","isCorrect":true,"explanationPt":"Reperfusao urgente e o padrao ouro para STEMI.","clinicalPearlPt":"Meta de <90 min da chegada ao hospital para ICP reduz mortalidade."},{"id":"stemi-conduta-2","textPt":"Terapia conservadora com betabloqueadores","isCorrect":false,"explanationPt":"Inadequada para STEMI; requer reperfusao.","clinicalPearlPt":null},{"id":"stemi-conduta-3","textPt":"Trombolise se ICP indisponivel","isCorrect":false,"explanationPt":"Alternativa, mas ICP e preferivel se disponivel em 120 min.","clinicalPearlPt":null},{"id":"stemi-conduta-4","textPt":"Apenas analgesicos e repouso","isCorrect":false,"explanationPt":"Nao aborda a oclusao coronaria.","clinicalPearlPt":null}]'::jsonb, ARRAY['stemi-dados-1','stemi-dados-2','stemi-dados-3'], 'stemi-padrao-1', 'stemi-hipotese-1', 'stemi-conduta-1', '{"keyFindings":["Dor toracica opressiva com irradiacao","Elevacao do ST em V1-V4","Troponina elevada"],"systematicApproach":"Avaliar ABC, ECG imediato, troponina; se STEMI, ativar cateterismo.","commonMistakes":["Confundir com pericardite por elevacao ST","Ignorar irradiacao como ansiedade"],"clinicalCorrelation":"STEMI anterior afeta ventriculo esquerdo, risco de insuficiencia cardiaca."}'::jsonb, 0.500, 1.400, 0.250, true, true);

-- CASE 2: Cetoacidose Diabetica (CAD)
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Cetoacidose Diabetica (CAD)', 'Paciente feminina de 22 anos, diabete melito tipo 1, polidipsia/poliuria ha 2 dias, nauseas/vomitos, dor abdominal, respiracao de Kussmaul, halito cetonico, glicemia 480 mg/dL, pH 7,12, bicarbonato 8 mEq/L, cetonas ++++.', 'clinica_medica', 'dificil', '[{"id":"cad-dados-1","textPt":"Respiracao de Kussmaul e halito cetonico","isCorrect":true,"explanationPt":"Sinais classicos de acidose metabolica com cetose.","clinicalPearlPt":"Respiracao compensatoria profunda para acidose; halito indica corpos cetonicos."},{"id":"cad-dados-2","textPt":"Hiperglicemia extrema de 480 mg/dL","isCorrect":true,"explanationPt":"Glicemia >250 mg/dL com cetonas e acidose define CAD.","clinicalPearlPt":"Monitorar glicemia a cada 1-2h durante tratamento."},{"id":"cad-dados-3","textPt":"Acidose metabolica grave: pH 7,12 e bicarbonato 8 mEq/L","isCorrect":true,"explanationPt":"Gap anionico elevado confirma acidose por cetonas.","clinicalPearlPt":"pH <7,3 e bicarbonato <15 indicam gravidade."},{"id":"cad-dados-4","textPt":"Febre de 39C","isCorrect":false,"explanationPt":"Sugere infeccao, mas nao e achado primario de CAD isolada.","clinicalPearlPt":null},{"id":"cad-dados-5","textPt":"Oliguria e edema","isCorrect":false,"explanationPt":"Indica insuficiencia renal, nao tipico de CAD inicial.","clinicalPearlPt":null},{"id":"cad-dados-6","textPt":"Deficit neurologico focal","isCorrect":false,"explanationPt":"Sugere AVC ou outra emergencia neurologica, nao CAD.","clinicalPearlPt":null}]'::jsonb, '[{"id":"cad-padrao-1","textPt":"Emergencia metabolica com acidose de gap anionico em paciente com DM1","isCorrect":true,"explanationPt":"Combinacao de hiperglicemia, cetose e acidose define o padrao.","clinicalPearlPt":"Precipitacao comum por infeccao ou omissao de insulina."},{"id":"cad-padrao-2","textPt":"Hiperglicemia isolada sem acidose","isCorrect":false,"explanationPt":"Nao constitui CAD; faltam cetonas e acidose.","clinicalPearlPt":null},{"id":"cad-padrao-3","textPt":"Insuficiencia renal aguda","isCorrect":false,"explanationPt":"Elevacao de creatinina, mas sem cetose primaria.","clinicalPearlPt":null},{"id":"cad-padrao-4","textPt":"Infeccao gastrointestinal","isCorrect":false,"explanationPt":"Nauseas sem hiperglicemia ou acidose metabolica.","clinicalPearlPt":null}]'::jsonb, '[{"id":"cad-hipotese-1","textPt":"Cetoacidose diabetica (CAD)","isCorrect":true,"explanationPt":"Diagnostico baseado em glicemia >250, pH <7,3, cetonas positivas.","clinicalPearlPt":"Mortalidade <1% com tratamento precoce; monitorar potassio."},{"id":"cad-hipotese-2","textPt":"Acidose lactica","isCorrect":false,"explanationPt":"Gap anionico, mas sem cetonas; associada a metformina em DM2.","clinicalPearlPt":null},{"id":"cad-hipotese-3","textPt":"Intoxicacao por salicilatos","isCorrect":false,"explanationPt":"Acidose mista, mas sem hiperglicemia.","clinicalPearlPt":null},{"id":"cad-hipotese-4","textPt":"Sepse","isCorrect":false,"explanationPt":"Febre e leucocitose, mas sem cetose diabetica.","clinicalPearlPt":null}]'::jsonb, '[{"id":"cad-conduta-1","textPt":"Hidratacao IV (NaCl 0,9% 1L/h inicial) + insulina regular 0,1 U/kg/h IV + reposicao de potassio + monitorizacao a cada 1-2h","isCorrect":true,"explanationPt":"Corrige desidratacao, cetose e eletrolitos; insulina reduz glicemia 50-75 mg/dL/h.","clinicalPearlPt":"Evitar hipoglicemia; iniciar SG5% quando glicemia atingir 200-250 mg/dL."},{"id":"cad-conduta-2","textPt":"Bicarbonato IV imediato","isCorrect":false,"explanationPt":"Reservado para pH <6,9; risco de piora paradoxal.","clinicalPearlPt":null},{"id":"cad-conduta-3","textPt":"Insulina subcutanea apenas","isCorrect":false,"explanationPt":"IV e preferivel para controle rapido em CAD.","clinicalPearlPt":null},{"id":"cad-conduta-4","textPt":"Dieta e antidiabeticos orais","isCorrect":false,"explanationPt":"Inadequado para emergencia; requer IV.","clinicalPearlPt":null}]'::jsonb, ARRAY['cad-dados-1','cad-dados-2','cad-dados-3'], 'cad-padrao-1', 'cad-hipotese-1', 'cad-conduta-1', '{"keyFindings":["Respiracao de Kussmaul e halito cetonico","Hiperglicemia 480 mg/dL","Acidose grave pH 7,12/bicarbonato 8"],"systematicApproach":"Gasometria arterial, glicemia, cetonas; calcular gap anionico; tratar com fluidos e insulina.","commonMistakes":["Confundir com hiperglicemia hiperosmolar","Subestimar desidratacao (deficit 6-10L)"],"clinicalCorrelation":"CAD em DM1 por estresse ou nao adesao; risco de edema cerebral em jovens."}'::jsonb, 1.000, 1.500, 0.200, true, true);

-- CASE 3: PAC
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Pneumonia Adquirida na Comunidade (PAC)', 'Paciente masculino de 45 anos, tosse produtiva com escarro purulento ha 5 dias, febre 38,8C, dispneia, crepitacoes na base direita, leucocitos 14.000/mm3, PCR elevada, RX torax com consolidacao no lobo inferior direito.', 'clinica_medica', 'facil', '[{"id":"pac-dados-1","textPt":"Tosse produtiva com febre","isCorrect":true,"explanationPt":"Sinais de infeccao respiratoria aguda.","clinicalPearlPt":"Escarro purulento sugere bacteria como Streptococcus pneumoniae."},{"id":"pac-dados-2","textPt":"Crepitacoes focais na base direita","isCorrect":true,"explanationPt":"Indica consolidacao pulmonar localizada.","clinicalPearlPt":"Exame fisico confirma achados radiologicos."},{"id":"pac-dados-3","textPt":"Leucocitose e PCR elevada","isCorrect":true,"explanationPt":"Marcadores inflamatorios confirmam infeccao bacteriana.","clinicalPearlPt":"PCR >100 mg/L indica gravidade em PAC."},{"id":"pac-dados-4","textPt":"Sibilos bilaterais","isCorrect":false,"explanationPt":"Sugere asma ou DPOC, nao consolidacao focal.","clinicalPearlPt":null},{"id":"pac-dados-5","textPt":"Hemoptise","isCorrect":false,"explanationPt":"Indica possivel TB ou neoplasia, nao PAC tipica.","clinicalPearlPt":null},{"id":"pac-dados-6","textPt":"Dor toracica irradiando para costas","isCorrect":false,"explanationPt":"Sugere pleurite ou IAM, nao pneumonia primaria.","clinicalPearlPt":null}]'::jsonb, '[{"id":"pac-padrao-1","textPt":"Sindrome pulmonar infecciosa aguda com consolidacao","isCorrect":true,"explanationPt":"Combinacao de sintomas, exame e imagem define PAC.","clinicalPearlPt":"CURB-65 estratifica risco para decisao ambulatorial/hospitalar."},{"id":"pac-padrao-2","textPt":"Exacerbacao de DPOC","isCorrect":false,"explanationPt":"Aumento de dispneia sem febre ou consolidacao nova.","clinicalPearlPt":null},{"id":"pac-padrao-3","textPt":"Edema pulmonar","isCorrect":false,"explanationPt":"Infiltrados difusos bilaterais, sem febre.","clinicalPearlPt":null},{"id":"pac-padrao-4","textPt":"Tumoracao pulmonar","isCorrect":false,"explanationPt":"Nodulo unico sem sintomas infecciosos agudos.","clinicalPearlPt":null}]'::jsonb, '[{"id":"pac-hipotese-1","textPt":"PAC leve a moderada (CURB-65=1)","isCorrect":true,"explanationPt":"Pontuacao baixa (idade <65, sem confusao/uremia etc.) permite tratamento ambulatorial.","clinicalPearlPt":"Mortalidade <1% em baixos scores; amoxicilina como primeira linha."},{"id":"pac-hipotese-2","textPt":"Tuberculose pulmonar","isCorrect":false,"explanationPt":"Cronica, com hemoptise e perda de peso; RX cavitario.","clinicalPearlPt":null},{"id":"pac-hipotese-3","textPt":"Pneumonia aspirativa","isCorrect":false,"explanationPt":"Associada a disfagia ou aspiracao; lobo superior direito.","clinicalPearlPt":null},{"id":"pac-hipotese-4","textPt":"Embolia pulmonar","isCorrect":false,"explanationPt":"Dispneia subita sem febre ou escarro.","clinicalPearlPt":null}]'::jsonb, '[{"id":"pac-conduta-1","textPt":"Amoxicilina 500mg a cada 8h por 7 dias (ambulatorial, CURB-65 baixo) + tratamento sintomatico","isCorrect":true,"explanationPt":"Cobertura para pneumococo em PAC comunitaria leve.","clinicalPearlPt":"Duracao 5-7 dias suficiente; reavaliar em 48h se piora."},{"id":"pac-conduta-2","textPt":"Internacao com antibioticos IV","isCorrect":false,"explanationPt":"Reservado para CURB-65 >=2 ou comorbidades.","clinicalPearlPt":null},{"id":"pac-conduta-3","textPt":"Corticoides sistemicos","isCorrect":false,"explanationPt":"Nao rotina em PAC sem choque ou SDRA.","clinicalPearlPt":null},{"id":"pac-conduta-4","textPt":"Apenas analgesicos sem antibioticos","isCorrect":false,"explanationPt":"Inadequado; requer antimicrobiano para infeccao bacteriana.","clinicalPearlPt":null}]'::jsonb, ARRAY['pac-dados-1','pac-dados-2','pac-dados-3'], 'pac-padrao-1', 'pac-hipotese-1', 'pac-conduta-1', '{"keyFindings":["Tosse produtiva com febre","Crepitacoes na base direita","Leucocitose e PCR elevada"],"systematicApproach":"Avaliar CURB-65, RX torax; iniciar empirico e ajustar por cultura se grave.","commonMistakes":["Subestimar gravidade em jovens","Confundir com bronquite viral sem RX"],"clinicalCorrelation":"PAC por S. pneumoniae comum; vacinacao previne em idosos."}'::jsonb, -0.800, 1.200, 0.250, true, true);

