-- CASE 4: Colecistite Aguda
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Colecistite Aguda', 'Paciente feminina de 48 anos, obesa, com dor intensa em hipocondrio direito (HD) apos refeicao gordurosa, nauseas e vomitos, sinal de Murphy positivo, febre de 38,2C, leucocitose, ultrassonografia (US) mostrando espessamento da parede da vesicula biliar >4mm, liquido pericolecistico e calculos biliares.', 'cirurgia', 'medio', '[{"id":"colec-dados-1","textPt":"Dor em HD + sinal de Murphy positivo","isCorrect":true,"explanationPt":"Dor em hipocondrio direito irradiada e sinal de Murphy sao classicos da colecistite aguda.","clinicalPearlPt":"O sinal de Murphy tem alta especificidade para colecistite."},{"id":"colec-dados-2","textPt":"US com espessamento da parede + liquido pericolecistico + calculos","isCorrect":true,"explanationPt":"Achados ultrassonograficos confirmam inflamacao e litiase vesicular.","clinicalPearlPt":"Criterios de Tokyo incluem espessamento >4mm e liquido pericolecistico."},{"id":"colec-dados-3","textPt":"Febre + leucocitose","isCorrect":true,"explanationPt":"Sinais de infeccao sistemica suportam diagnostico inflamatorio.","clinicalPearlPt":"Leucocitose >10.000/mm3 e comum em casos agudos."},{"id":"colec-dados-4","textPt":"Ictericia/coluria","isCorrect":false,"explanationPt":"Sugere coledocolitiase ou obstrucao biliar, nao colecistite isolada.","clinicalPearlPt":null},{"id":"colec-dados-5","textPt":"Vesicula palpavel indolor","isCorrect":false,"explanationPt":"Caracteristica de carcinoma de vesicula (sinal de Courvoisier), nao de colecistite aguda.","clinicalPearlPt":null},{"id":"colec-dados-6","textPt":"Peritonite difusa","isCorrect":false,"explanationPt":"Indica complicacao como perfuracao, mas nao e apresentacao inicial tipica.","clinicalPearlPt":null}]'::jsonb, '[{"id":"colec-padrao-1","textPt":"Sindrome inflamatoria aguda da vesicula biliar (criterios de Tokyo)","isCorrect":true,"explanationPt":"Combinacao de sinais clinicos, laboratoriais e de imagem define o padrao.","clinicalPearlPt":"Criterios de Tokyo guiam o diagnostico e manejo."},{"id":"colec-padrao-2","textPt":"Obstrucao biliar extra-hepatica","isCorrect":false,"explanationPt":"Envolve ictericia e elevacao de bilirrubinas, ausentes aqui.","clinicalPearlPt":null},{"id":"colec-padrao-3","textPt":"Pancreatite aguda","isCorrect":false,"explanationPt":"Dor epigastrica irradiada para dorso e amilase elevada nao se aplicam.","clinicalPearlPt":null},{"id":"colec-padrao-4","textPt":"Ulcera peptica perfurada","isCorrect":false,"explanationPt":"Apresenta dor subita e peritonite, sem relacao com refeicao gordurosa.","clinicalPearlPt":null}]'::jsonb, '[{"id":"colec-hipotese-1","textPt":"Colecistite aguda litiasica","isCorrect":true,"explanationPt":"Diagnostico principal baseado em litiase e inflamacao vesicular.","clinicalPearlPt":"90% das colecistites sao litiasicas em adultos."},{"id":"colec-hipotese-2","textPt":"Coledocolitiase","isCorrect":false,"explanationPt":"Causa ictericia, nao predominante aqui.","clinicalPearlPt":null},{"id":"colec-hipotese-3","textPt":"Pancreatite biliar","isCorrect":false,"explanationPt":"Dor epigastrica e enzimas pancreaticas elevadas ausentes.","clinicalPearlPt":null},{"id":"colec-hipotese-4","textPt":"Apendicite aguda","isCorrect":false,"explanationPt":"Localiza-se em FID, nao em hipocondrio direito.","clinicalPearlPt":null}]'::jsonb, '[{"id":"colec-conduta-1","textPt":"Jejum + hidratacao IV + antibioticos IV (ceftriaxona + metronidazol) + colecistectomia laparoscopica precoce (em ate 72h)","isCorrect":true,"explanationPt":"Manejo padrao para colecistite aguda nao complicada.","clinicalPearlPt":"Cirurgia precoce reduz recorrencia e complicacoes."},{"id":"colec-conduta-2","textPt":"Analgesia oral e observacao","isCorrect":false,"explanationPt":"Inadequado para caso com sinais de infeccao.","clinicalPearlPt":null},{"id":"colec-conduta-3","textPt":"CPRE imediata","isCorrect":false,"explanationPt":"Reservada para coledocolitiase com ictericia.","clinicalPearlPt":null},{"id":"colec-conduta-4","textPt":"Antibioticos orais e alta","isCorrect":false,"explanationPt":"Risco de progressao sem suporte IV.","clinicalPearlPt":null}]'::jsonb, ARRAY['colec-dados-1','colec-dados-2','colec-dados-3'], 'colec-padrao-1', 'colec-hipotese-1', 'colec-conduta-1', '{"keyFindings":["Dor em HD + sinal de Murphy positivo","US com espessamento da parede + liquido pericolecistico + calculos","Febre + leucocitose"],"systematicApproach":"Avaliar historia clinica, exame fisico (sinal de Murphy), laboratoriais (leucocitose) e imagem (US) para confirmar criterios de Tokyo.","commonMistakes":["Confundir com obstrucao biliar por ausencia de ictericia","Ignorar US em favor de clinica isolada"],"clinicalCorrelation":"Colecistite aguda e comum em mulheres obesas, associada a litiase e risco de complicacoes como perfuracao."}'::jsonb, 0.300, 1.300, 0.250, true, true);

-- CASE 5: Obstrucao Intestinal
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Obstrucao Intestinal por Bridas', 'Paciente masculino de 65 anos com cirurgia abdominal previa (apendicectomia ha 20 anos), dor abdominal colica, distensao abdominal, vomitos (fecaloides), ruidos hidroaereos ausentes, sem eliminacao de flatos/fezes ha 48h. Raio-X: alcas intestinais delgadas dilatadas com niveis hidroaereos, ausencia de gas no colon.', 'cirurgia', 'dificil', '[{"id":"obst-dados-1","textPt":"Dor colica + distensao + vomitos (triade de Mondor)","isCorrect":true,"explanationPt":"Triade classica de obstrucao mecanica intestinal.","clinicalPearlPt":"Triade de Mondor e patognomonica para obstrucao de delgado."},{"id":"obst-dados-2","textPt":"Cirurgia abdominal previa (risco de aderencias)","isCorrect":true,"explanationPt":"Historico de laparotomia aumenta risco de bridas.","clinicalPearlPt":"Aderencias pos-cirurgicas causam 60-70% das obstrucoes."},{"id":"obst-dados-3","textPt":"Raio-X com niveis hidroaereos e ausencia de gas colonico","isCorrect":true,"explanationPt":"Padrao radiologico confirma obstrucao completa de delgado.","clinicalPearlPt":"Ausencia de gas no reto diferencia de ileo paralitico."},{"id":"obst-dados-4","textPt":"Diarreia sanguinolenta","isCorrect":false,"explanationPt":"Sugere isquemia ou infeccao, nao obstrucao mecanica.","clinicalPearlPt":null},{"id":"obst-dados-5","textPt":"Ascite","isCorrect":false,"explanationPt":"Indica malignidade ou cirrose, nao obstrucao aguda.","clinicalPearlPt":null},{"id":"obst-dados-6","textPt":"Massa palpavel em flanco esquerdo","isCorrect":false,"explanationPt":"Pode sugerir tumor, mas nao e achado primario aqui.","clinicalPearlPt":null}]'::jsonb, '[{"id":"obst-padrao-1","textPt":"Padrao de obstrucao mecanica de delgado (completa)","isCorrect":true,"explanationPt":"Baseado em clinica, historia e imagem radiologica.","clinicalPearlPt":"Raio-X mostra ponto de transicao em obstrucao mecanica."},{"id":"obst-padrao-2","textPt":"Ileo paralitico","isCorrect":false,"explanationPt":"Ruidos presentes e gas difuso no colon.","clinicalPearlPt":null},{"id":"obst-padrao-3","textPt":"Obstrucao colonica","isCorrect":false,"explanationPt":"Dilatacao colonica e fezes em colon proximal.","clinicalPearlPt":null},{"id":"obst-padrao-4","textPt":"Gastroenterite aguda","isCorrect":false,"explanationPt":"Diarreia e ruidos hiperativos, nao distensao.","clinicalPearlPt":null}]'::jsonb, '[{"id":"obst-hipotese-1","textPt":"Obstrucao intestinal por bridas/aderencias","isCorrect":true,"explanationPt":"Causa mais comum em pacientes com cirurgia previa.","clinicalPearlPt":"Bridas sao responsaveis por 75% das obstrucoes em adultos."},{"id":"obst-hipotese-2","textPt":"Hernia incarcerada","isCorrect":false,"explanationPt":"Requer exame para hernia, ausente na descricao.","clinicalPearlPt":null},{"id":"obst-hipotese-3","textPt":"Tumor colorretal","isCorrect":false,"explanationPt":"Mais comum em colon, com sangramento associado.","clinicalPearlPt":null},{"id":"obst-hipotese-4","textPt":"Volvo sigmoide","isCorrect":false,"explanationPt":"Dor aguda e distensao assimetrica em FIE.","clinicalPearlPt":null}]'::jsonb, '[{"id":"obst-conduta-1","textPt":"Sonda nasogastrica para descompressao + hidratacao IV + correcao eletrolitica + jejum + consulta cirurgica (laparotomia se sem melhora em 24-48h ou sinais de estrangulamento)","isCorrect":true,"explanationPt":"Manejo conservador inicial, com indicacao cirurgica se falha.","clinicalPearlPt":"Monitorar sinais de estrangulamento: febre, taquicardia, acidose."},{"id":"obst-conduta-2","textPt":"Laxantes e enemas","isCorrect":false,"explanationPt":"Contraindicado em obstrucao mecanica completa.","clinicalPearlPt":null},{"id":"obst-conduta-3","textPt":"Antibioticos profilaticos apenas","isCorrect":false,"explanationPt":"Nao rotina sem perfuracao ou isquemia.","clinicalPearlPt":null},{"id":"obst-conduta-4","textPt":"Observacao ambulatorial","isCorrect":false,"explanationPt":"Risco de complicacoes graves em idoso com obstrucao completa.","clinicalPearlPt":null}]'::jsonb, ARRAY['obst-dados-1','obst-dados-2','obst-dados-3'], 'obst-padrao-1', 'obst-hipotese-1', 'obst-conduta-1', '{"keyFindings":["Dor colica + distensao + vomitos (triade de Mondor)","Cirurgia abdominal previa (risco de aderencias)","Raio-X com niveis hidroaereos e ausencia de gas colonico"],"systematicApproach":"Historia (cirurgias previas), exame (distensao, ruidos ausentes), laboratoriais (eletrolitos) e imagem (raio-X simples) para diferenciar mecanica de funcional.","commonMistakes":["Confundir com ileo adinamico por ruidos ausentes","Ignorar risco de aderencias em paciente operado"],"clinicalCorrelation":"Obstrucao por aderencias e comum pos-laparotomia, podendo levar a isquemia se nao tratada precocemente."}'::jsonb, 0.800, 1.400, 0.200, true, true);

-- CASE 6: Trauma Abdominal Penetrante
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Trauma Abdominal Penetrante', 'Paciente masculino de 28 anos, ferimento por arma branca em quadrante superior esquerdo (QSE), instavel hemodinamicamente (PA 85/55, FC 125), abdome distendido e doloroso, sinais peritoneais, FAST positivo com liquido livre.', 'cirurgia', 'medio', '[{"id":"trauma-dados-1","textPt":"Ferimento penetrante em QSE com instabilidade hemodinamica","isCorrect":true,"explanationPt":"Trauma penetrante em zona de risco com choque indica sangramento intra-abdominal.","clinicalPearlPt":"Ferimentos em QSE podem lesar baco, estomago ou diafragma."},{"id":"trauma-dados-2","textPt":"Distensao abdominal + sinais peritoneais","isCorrect":true,"explanationPt":"Sugere irritacao peritoneal por hemoperitoneo ou perfuracao.","clinicalPearlPt":"Sinais peritoneais em trauma penetrante indicam laparotomia."},{"id":"trauma-dados-3","textPt":"FAST positivo (liquido abdominal livre)","isCorrect":true,"explanationPt":"Confirma hemoperitoneo em paciente instavel.","clinicalPearlPt":"FAST tem sensibilidade >80% para liquido livre em trauma."},{"id":"trauma-dados-4","textPt":"Sinais vitais estaveis","isCorrect":false,"explanationPt":"Permitiria avaliacao conservadora, mas ausente aqui.","clinicalPearlPt":null},{"id":"trauma-dados-5","textPt":"Enfisema subcutaneo","isCorrect":false,"explanationPt":"Sugere lesao toracica ou esofagica, nao abdominal isolada.","clinicalPearlPt":null},{"id":"trauma-dados-6","textPt":"Fratura pelvica","isCorrect":false,"explanationPt":"Causa sangramento retroperitoneal, mas nao peritoneal.","clinicalPearlPt":null}]'::jsonb, '[{"id":"trauma-padrao-1","textPt":"Choque hemorragico por trauma abdominal penetrante","isCorrect":true,"explanationPt":"Instabilidade + trauma penetrante em abdome define o padrao.","clinicalPearlPt":"Classificacao ATLS: classe III/IV de choque hemorragico."},{"id":"trauma-padrao-2","textPt":"Lesao toracica isolada","isCorrect":false,"explanationPt":"Requer exame toracico e sem sinais abdominais.","clinicalPearlPt":null},{"id":"trauma-padrao-3","textPt":"Trauma contuso abdominal","isCorrect":false,"explanationPt":"Mecanismo diferente, sem ferimento penetrante.","clinicalPearlPt":null},{"id":"trauma-padrao-4","textPt":"Intoxicacao aguda","isCorrect":false,"explanationPt":"Nao explica ferimento ou achados peritoneais.","clinicalPearlPt":null}]'::jsonb, '[{"id":"trauma-hipotese-1","textPt":"Lesao de viscera abdominal (provavel lesao esplenica/gastrica) por trauma penetrante","isCorrect":true,"explanationPt":"Localizacao em QSE sugere orgaos solidos ou ocos lesionados.","clinicalPearlPt":"Baco e orgao mais comumente lesado em trauma esquerdo."},{"id":"trauma-hipotese-2","textPt":"Lesao vascular de aorta","isCorrect":false,"explanationPt":"Causa choque rapido, mas sem ferimento abdominal tipico.","clinicalPearlPt":null},{"id":"trauma-hipotese-3","textPt":"Perfuracao diafragmatica isolada","isCorrect":false,"explanationPt":"Pode ocorrer, mas secundaria a lesao visceral principal.","clinicalPearlPt":null},{"id":"trauma-hipotese-4","textPt":"Lesao hepatica","isCorrect":false,"explanationPt":"Localizada em QSD, nao QSE.","clinicalPearlPt":null}]'::jsonb, '[{"id":"trauma-conduta-1","textPt":"ATLS ABC + protocolo de transfusao macica + laparotomia exploradora de emergencia (paciente instavel com sinais peritoneais)","isCorrect":true,"explanationPt":"Paciente instavel com peritonite requer cirurgia imediata.","clinicalPearlPt":"Transfusao 1:1:1 (hemacias:plasma:plaquetas) em hemorragia grave."},{"id":"trauma-conduta-2","textPt":"Observacao e TC abdominal","isCorrect":false,"explanationPt":"Contraindicado em paciente instavel hemodinamicamente.","clinicalPearlPt":null},{"id":"trauma-conduta-3","textPt":"Puncao peritoneal diagnostica","isCorrect":false,"explanationPt":"Obsoleta com FAST disponivel.","clinicalPearlPt":null},{"id":"trauma-conduta-4","textPt":"Angiografia seletiva","isCorrect":false,"explanationPt":"Para lesoes vasculares estaveis, nao aplicavel aqui.","clinicalPearlPt":null}]'::jsonb, ARRAY['trauma-dados-1','trauma-dados-2','trauma-dados-3'], 'trauma-padrao-1', 'trauma-hipotese-1', 'trauma-conduta-1', '{"keyFindings":["Ferimento penetrante em QSE com instabilidade hemodinamica","Distensao abdominal + sinais peritoneais","FAST positivo (liquido abdominal livre)"],"systematicApproach":"Seguir ATLS: ABCDE, estabilizar via aerea/circulacao, imagem rapida (FAST), decisao cirurgica baseada em estabilidade.","commonMistakes":["Atrasar cirurgia em instavel por TC","Subestimar profundidade de ferimento penetrante"],"clinicalCorrelation":"Trauma penetrante abdominal tem mortalidade alta se lesao vascular/visceral, exigindo abordagem agressiva."}'::jsonb, 0.400, 1.300, 0.250, true, true);

