-- CASE 7: Gravidez Ectopica Rota
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Gravidez Ectopica Rota', 'Mulher de 32 anos, 7 semanas de amenorreia, dor pelvica subita, sangramento vaginal, instavel hemodinamicamente (PA 85/55, FC 120), beta-hCG positivo, US transvaginal mostrando utero vazio + massa anexial + liquido livre no fundo de saco.', 'ginecologia_obstetricia', 'medio', '[{"id":"ect-dados-1","textPt":"Amenorreia de 7 semanas + dor pelvica subita + instabilidade hemodinamica","isCorrect":true,"explanationPt":"Achados classicos sugerem gravidez ectopica com ruptura, exigindo intervencao imediata.","clinicalPearlPt":"A instabilidade hemodinamica em gestante com dor abdominal e sinal de alarme para hemorragia interna."},{"id":"ect-dados-2","textPt":"Beta-hCG positivo com utero vazio na US","isCorrect":true,"explanationPt":"Indica gravidez extrauterina, confirmada pela ausencia de saco gestacional intrauterino.","clinicalPearlPt":"US transvaginal e o exame de escolha para diagnostico precoce de ectopia."},{"id":"ect-dados-3","textPt":"Liquido livre no Douglas (hemoperitoneo)","isCorrect":true,"explanationPt":"Sugere ruptura com sangramento intraperitoneal, correlacionado a instabilidade.","clinicalPearlPt":"O cul-de-sac e o local mais comum de acumulo de sangue em hemoperitoneo pelvico."},{"id":"ect-dados-4","textPt":"Sangramento vaginal","isCorrect":false,"explanationPt":"Achado inespecifico, comum em varias condicoes ginecologicas, nao diagnostico isolado.","clinicalPearlPt":null},{"id":"ect-dados-5","textPt":"Nauseas e vomitos","isCorrect":false,"explanationPt":"Sintomas inespecificos de gravidez normal, nao indicativos de complicacao aguda.","clinicalPearlPt":null},{"id":"ect-dados-6","textPt":"Febre baixa","isCorrect":false,"explanationPt":"Pode sugerir infeccao, mas nao e tipico de ectopia rota sem peritonite.","clinicalPearlPt":null}]'::jsonb, '[{"id":"ect-padrao-1","textPt":"Abdome agudo com instabilidade hemodinamica em mulher em idade reprodutiva","isCorrect":true,"explanationPt":"Padrao classico de emergencia ginecologica com risco de vida.","clinicalPearlPt":"Sempre suspeitar de ectopia em dor pelvica aguda com amenorreia."},{"id":"ect-padrao-2","textPt":"Ameaca de aborto","isCorrect":false,"explanationPt":"Nao explica instabilidade hemodinamica ou achados de massa anexial.","clinicalPearlPt":null},{"id":"ect-padrao-3","textPt":"Torcao ovariana","isCorrect":false,"explanationPt":"Pode causar dor subita, mas beta-hCG positivo aponta para etiologia gestacional.","clinicalPearlPt":null},{"id":"ect-padrao-4","textPt":"Doenca inflamatoria pelvica (DIP)","isCorrect":false,"explanationPt":"Geralmente apresenta dor cronica e febre, nao hemoperitoneo agudo.","clinicalPearlPt":null}]'::jsonb, '[{"id":"ect-hipotese-1","textPt":"Gravidez ectopica rota","isCorrect":true,"explanationPt":"Diagnostico compativel com todos os achados clinicos e ecograficos.","clinicalPearlPt":"A ectopia tubaria e a mais comum, representando 95% dos casos."},{"id":"ect-hipotese-2","textPt":"Aborto espontaneo incompleto","isCorrect":false,"explanationPt":"Utero vazio e massa anexial nao suportam essa hipotese.","clinicalPearlPt":null},{"id":"ect-hipotese-3","textPt":"Cisto ovariano hemorrágico roto","isCorrect":false,"explanationPt":"Beta-hCG positivo indica gravidez, nao cisto isolado.","clinicalPearlPt":null},{"id":"ect-hipotese-4","textPt":"Apendicite aguda","isCorrect":false,"explanationPt":"Localizacao pelvica e beta-hCG nao condizem com apendicite.","clinicalPearlPt":null}]'::jsonb, '[{"id":"ect-conduta-1","textPt":"Laparotomia ou laparoscopia de emergencia + estabilizacao hemodinamica + hemoderivados","isCorrect":true,"explanationPt":"Abordagem cirurgica imediata para hemostasia e remocao do produto.","clinicalPearlPt":"Em instabilidade, priorizar acesso venoso amplo e reposicao volemica agressiva."},{"id":"ect-conduta-2","textPt":"Metotrexato","isCorrect":false,"explanationPt":"Indicado apenas para ectopia nao rota e estavel hemodinamicamente.","clinicalPearlPt":null},{"id":"ect-conduta-3","textPt":"Conduta expectante com dosagem seriada de beta-hCG","isCorrect":false,"explanationPt":"Nao aplicavel em casos de ruptura com hemorragia ativa.","clinicalPearlPt":null},{"id":"ect-conduta-4","textPt":"Curetagem uterina","isCorrect":false,"explanationPt":"Nao indicada pois o utero esta vazio e nao ha produtos intrauterinos.","clinicalPearlPt":null}]'::jsonb, ARRAY['ect-dados-1','ect-dados-2','ect-dados-3'], 'ect-padrao-1', 'ect-hipotese-1', 'ect-conduta-1', '{"keyFindings":["Amenorreia + dor subita + instabilidade","Beta-hCG+ com utero vazio","Liquido livre no Douglas"],"systematicApproach":"Avaliar estabilidade vital, confirmar gravidez extrauterina por US, intervir cirurgicamente.","commonMistakes":["Confundir com aborto espontaneo","Atrasar intervencao em paciente instavel"],"clinicalCorrelation":"Emergencia obstetrica comum; mortalidade materna significativa se nao tratada."}'::jsonb, 0.600, 1.400, 0.250, true, true);

-- CASE 8: DMG
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Diabetes Mellitus Gestacional (DMG)', 'Mulher de 29 anos, G2P1, 26 semanas, TOTG 75g de rotina: jejum 95, 1h 190, 2h 162 (IADPSG: >=92, >=180, >=153). IMC 32, historia familiar de DM2. US fetal: macrossomia + polidramnio.', 'ginecologia_obstetricia', 'facil', '[{"id":"dmg-dados-1","textPt":"TOTG alterado (2 valores acima dos cutoffs IADPSG)","isCorrect":true,"explanationPt":"Criterios diagnosticos para DMG confirmados pelo teste oral de tolerancia a glicose.","clinicalPearlPt":"TOTG 75g e o padrao ouro para screening e diagnostico de DMG entre 24-28 semanas."},{"id":"dmg-dados-2","textPt":"Macrossomia + polidramnio na US fetal","isCorrect":true,"explanationPt":"Repercussoes fetais tipicas de hiperglicemia materna descontrolada.","clinicalPearlPt":"Polidramnio ocorre em 20-30% das gestacoes com DMG nao tratada."},{"id":"dmg-dados-3","textPt":"Obesidade + historia familiar de DM2","isCorrect":true,"explanationPt":"Fatores de risco que aumentam a suspeita e incidencia de DMG.","clinicalPearlPt":"Mulheres obesas tem risco 3-5 vezes maior de desenvolver DMG."},{"id":"dmg-dados-4","textPt":"Poliuria e polidipsia","isCorrect":false,"explanationPt":"Sintomas inespecificos, mais comuns em DM pre-existente grave, nao descritos no caso.","clinicalPearlPt":null},{"id":"dmg-dados-5","textPt":"Glicosuria","isCorrect":false,"explanationPt":"Achado sugestivo, mas nao diagnostico isolado em gestantes (limiar renal diminuido).","clinicalPearlPt":null},{"id":"dmg-dados-6","textPt":"HbA1c elevada","isCorrect":false,"explanationPt":"Nao recomendado para diagnostico de DMG devido a alteracoes fisiologicas na gravidez.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dmg-padrao-1","textPt":"Disturbio metabolico de glicose na gravidez com repercussoes fetais","isCorrect":true,"explanationPt":"Padrao caracteristico de DMG com impacto na mae e feto.","clinicalPearlPt":"DMG afeta 5-10% das gestacoes e aumenta risco de DM2 pos-parto em 50%."},{"id":"dmg-padrao-2","textPt":"DM2 pre-gestacional nao diagnosticada","isCorrect":false,"explanationPt":"Ausencia de diagnostico previo e glicemia de jejum normal antes da gestacao.","clinicalPearlPt":null},{"id":"dmg-padrao-3","textPt":"Hiperglicemia transitoria gestacional","isCorrect":false,"explanationPt":"Os valores atendem criterios diagnosticos plenos de DMG.","clinicalPearlPt":null},{"id":"dmg-padrao-4","textPt":"MODY","isCorrect":false,"explanationPt":"MODY e doenca genetica rara, sem relacao com gestacao como fator desencadeante.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dmg-hipotese-1","textPt":"Diabetes mellitus gestacional (DMG)","isCorrect":true,"explanationPt":"Diagnostico baseado nos criterios IADPSG do TOTG.","clinicalPearlPt":"Screening universal entre 24-28 semanas e recomendado pelo MS e FEBRASGO."},{"id":"dmg-hipotese-2","textPt":"Diabetes pre-gestacional","isCorrect":false,"explanationPt":"Ausencia de diagnostico previo e glicemia pre-natal normal.","clinicalPearlPt":null},{"id":"dmg-hipotese-3","textPt":"Intolerancia a glicose transitoria","isCorrect":false,"explanationPt":"Os valores atendem criterios diagnosticos plenos, nao transitoria.","clinicalPearlPt":null},{"id":"dmg-hipotese-4","textPt":"DM1 de inicio tardio","isCorrect":false,"explanationPt":"DM1 cursa com sintomas classicos e cetose, incompativel com caso assintomatico.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dmg-conduta-1","textPt":"Terapia nutricional + exercicio + automonitoramento glicemico + insulina se alvos nao atingidos em 2 semanas + vigilancia fetal","isCorrect":true,"explanationPt":"Abordagem escalonada iniciando com mudancas no estilo de vida, progredindo para farmacologica se necessario.","clinicalPearlPt":"Meta glicemica: jejum <95 mg/dL, 1h pos-prandial <140 mg/dL, 2h pos-prandial <120 mg/dL."},{"id":"dmg-conduta-2","textPt":"Insulina imediata sem dieta","isCorrect":false,"explanationPt":"Terapia nutricional e primeira linha; insulina reservada para falha.","clinicalPearlPt":null},{"id":"dmg-conduta-3","textPt":"Metformina como primeira linha","isCorrect":false,"explanationPt":"Metformina nao e primeira linha para DMG no Brasil; reservada para falha ou recusa de insulina.","clinicalPearlPt":null},{"id":"dmg-conduta-4","textPt":"Apenas dieta sem monitoramento","isCorrect":false,"explanationPt":"DMG requer monitoramento glicemico para ajuste terapeutico.","clinicalPearlPt":null}]'::jsonb, ARRAY['dmg-dados-1','dmg-dados-2','dmg-dados-3'], 'dmg-padrao-1', 'dmg-hipotese-1', 'dmg-conduta-1', '{"keyFindings":["TOTG com valores diagnosticos IADPSG","Repercussoes fetais na US (macrossomia, polidramnio)","Fatores de risco maternos (obesidade, HF DM2)"],"systematicApproach":"Realizar screening com TOTG entre 24-28 semanas, confirmar diagnostico, iniciar terapia nao farmacologica e monitorar glicemia.","commonMistakes":["Usar HbA1c para diagnostico de DMG","Ignorar vigilancia fetal"],"clinicalCorrelation":"DMG aumenta risco de pre-eclampsia, cesariana e macrossomia fetal."}'::jsonb, -0.500, 1.200, 0.250, true, true);

-- CASE 9: HPP
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Hemorragia Pos-Parto (HPP)', 'Mulher de 30 anos, G3P3, apos parto vaginal de bebe de 4,2kg, perda sanguinea >1000mL, utero atonico (flacido, acima do umbigo), PA 90/60, FC 115, placenta expelida completa, sem laceracoes, sem coagulopatia.', 'ginecologia_obstetricia', 'dificil', '[{"id":"hpp-dados-1","textPt":"Perda sanguinea >1000mL + instabilidade hemodinamica","isCorrect":true,"explanationPt":"Define HPP primaria grave, com risco de choque hipovolemico.","clinicalPearlPt":"HPP e a principal causa de mortalidade materna no mundo."},{"id":"hpp-dados-2","textPt":"Atonia uterina (utero flacido acima do umbigo)","isCorrect":true,"explanationPt":"Causa mais comum de HPP, representando 70-80% dos casos.","clinicalPearlPt":"Palpacao uterina e essencial no manejo imediato pos-parto."},{"id":"hpp-dados-3","textPt":"Fatores de risco (macrossomia + multiparidade)","isCorrect":true,"explanationPt":"Aumentam a probabilidade de atonia uterina pos-parto.","clinicalPearlPt":"Bebes >4kg duplicam o risco de HPP por atonia."},{"id":"hpp-dados-4","textPt":"Placenta retida","isCorrect":false,"explanationPt":"Placenta foi expelida completa, excluindo essa causa.","clinicalPearlPt":null},{"id":"hpp-dados-5","textPt":"Laceracoes cervicais","isCorrect":false,"explanationPt":"Ausencia de laceracoes documentada no exame fisico.","clinicalPearlPt":null},{"id":"hpp-dados-6","textPt":"Coagulopatia","isCorrect":false,"explanationPt":"Sem evidencia clinica ou laboratorial de disturbio de coagulacao.","clinicalPearlPt":null}]'::jsonb, '[{"id":"hpp-padrao-1","textPt":"HPP primaria por atonia uterina (Tonus - causa mais comum, classificacao 4T)","isCorrect":true,"explanationPt":"Padrao baseado na classificacao 4T (Tonus, Tecido, Trauma, Trombina).","clinicalPearlPt":"Atonia e responsavel por 75% das HPP imediatas."},{"id":"hpp-padrao-2","textPt":"HPP por trauma (laceracoes)","isCorrect":false,"explanationPt":"Sem laceracoes identificadas ao exame.","clinicalPearlPt":null},{"id":"hpp-padrao-3","textPt":"HPP por retencao de tecido","isCorrect":false,"explanationPt":"Placenta completa, excluindo retencao.","clinicalPearlPt":null},{"id":"hpp-padrao-4","textPt":"Coagulopatia de consumo (CIVD)","isCorrect":false,"explanationPt":"Sem evidencia de disturbio hemorrágico.","clinicalPearlPt":null}]'::jsonb, '[{"id":"hpp-hipotese-1","textPt":"HPP primaria por atonia uterina","isCorrect":true,"explanationPt":"Hipotese principal baseada em exame fisico e exclusao de outras causas pelo 4T.","clinicalPearlPt":"Classificacao 4T guia o diagnostico diferencial rapido na HPP."},{"id":"hpp-hipotese-2","textPt":"HPP por placenta acreta","isCorrect":false,"explanationPt":"Placenta completa, sem historia de cesarianas previas.","clinicalPearlPt":null},{"id":"hpp-hipotese-3","textPt":"Ruptura uterina","isCorrect":false,"explanationPt":"Parto vaginal sem trauma, utero intacto.","clinicalPearlPt":null},{"id":"hpp-hipotese-4","textPt":"Inversao uterina","isCorrect":false,"explanationPt":"Inversao uterina causa dor intensa e massa na vagina, nao descrito.","clinicalPearlPt":null}]'::jsonb, '[{"id":"hpp-conduta-1","textPt":"Massagem uterina + ocitocina 10-40U IV + misoprostol se refrataria + compressao bimanual + transfusao + considerar tamponamento com balao/cirurgia","isCorrect":true,"explanationPt":"Manejo escalonado pela classificacao 4T, iniciando com medidas conservadoras e progredindo se necessario.","clinicalPearlPt":"Ocitocina e primeira linha; misoprostol 600-1000mcg retal para atonia refrataria."},{"id":"hpp-conduta-2","textPt":"Histerectomia imediata","isCorrect":false,"explanationPt":"Reservada para falha completa de terapias conservadoras.","clinicalPearlPt":null},{"id":"hpp-conduta-3","textPt":"Exploracao manual apenas","isCorrect":false,"explanationPt":"Placenta ja foi expelida completa; nao aborda a atonia.","clinicalPearlPt":null},{"id":"hpp-conduta-4","textPt":"Acido tranexamico isolado","isCorrect":false,"explanationPt":"Adjuvante util, mas nao substitui uterotonicas e medidas mecanicas.","clinicalPearlPt":null}]'::jsonb, ARRAY['hpp-dados-1','hpp-dados-2','hpp-dados-3'], 'hpp-padrao-1', 'hpp-hipotese-1', 'hpp-conduta-1', '{"keyFindings":["Perda >1000mL + instabilidade hemodinamica","Atonia uterina confirmada (utero flacido)","Fatores de risco presentes (macrossomia, multiparidade)"],"systematicApproach":"Aplicar classificacao 4T: tratar Tonus com massagem e uterotonicas, monitorar vitais, preparar para escalonamento.","commonMistakes":["Subestimar perda sanguinea","Ignorar massagem uterina como primeira medida"],"clinicalCorrelation":"HPP por atonia e prevenivel com ocitocina profilatica no terceiro periodo do parto."}'::jsonb, 1.300, 1.600, 0.200, true, true);

