-- CASE 10: Meningite Bacteriana em Lactente
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Meningite Bacteriana em Lactente', 'Lactente de 8 meses apresenta irritabilidade, febre alta de 39,5C, vomitos, fontanela abaulada, rigidez nucal, erupcao petequial, taquicardia. Liquido cefalorraquidiano (LCR): turvo, >1000 celulas (predominantemente neutrofilos), glicose baixa, proteina elevada, Gram positivo para diplococos.', 'pediatria', 'dificil', '[{"id":"mening-dados-1","textPt":"Fontanela abaulada + rigidez nucal em lactente febril","isCorrect":true,"explanationPt":"Sinais classicos de hipertensao intracraniana e irritacao meningea em lactentes.","clinicalPearlPt":"Em lactentes febris com sinais meningeos, suspeite imediatamente de meningite bacteriana."},{"id":"mening-dados-2","textPt":"Erupcao petequial + instabilidade hemodinamica","isCorrect":true,"explanationPt":"Petequias sugerem meningococcemia associada, com taquicardia indicando choque.","clinicalPearlPt":"Rash petequial em contexto septico e altamente sugestivo de meningite meningococica."},{"id":"mening-dados-3","textPt":"LCR com pleocitose + hipoglicorraquia + proteina elevada","isCorrect":true,"explanationPt":"Achados tipicos de infeccao bacteriana no LCR: neutrofilia, glicose <40 mg/dL, proteina >100 mg/dL.","clinicalPearlPt":"Analise do LCR e essencial para diferenciar meningite bacteriana de viral."},{"id":"mening-dados-4","textPt":"Diarreia aquosa","isCorrect":false,"explanationPt":"Nao relacionado a meningite; sugere gastroenterite.","clinicalPearlPt":null},{"id":"mening-dados-5","textPt":"Fontanela normal","isCorrect":false,"explanationPt":"Fontanela abaulada e o achado presente, nao normal.","clinicalPearlPt":null},{"id":"mening-dados-6","textPt":"Secrecao auricular","isCorrect":false,"explanationPt":"Sugere otite media, mas nao explica sintomas meningeos centrais.","clinicalPearlPt":null}]'::jsonb, '[{"id":"mening-padrao-1","textPt":"Sindrome meningea aguda em lactente com sinais de sepse","isCorrect":true,"explanationPt":"Combinacao de febre, irritabilidade meningea e instabilidade hemodinamica define sepse meningea.","clinicalPearlPt":"Abordagem inicial em sepse pediatrica prioriza estabilizacao e antibioticos imediatos."},{"id":"mening-padrao-2","textPt":"Gastroenterite viral","isCorrect":false,"explanationPt":"Vomitos presentes, mas sem diarreia e com sinais neurologicos dominantes.","clinicalPearlPt":null},{"id":"mening-padrao-3","textPt":"Desidratacao por vomitos","isCorrect":false,"explanationPt":"Sem sinais de desidratacao significativa; foco e neurologico.","clinicalPearlPt":null},{"id":"mening-padrao-4","textPt":"Otite media aguda complicada","isCorrect":false,"explanationPt":"Ausencia de secrecao otologica; nao explica rash ou LCR alterado.","clinicalPearlPt":null}]'::jsonb, '[{"id":"mening-hipotese-1","textPt":"Meningite bacteriana aguda (provavel meningococica)","isCorrect":true,"explanationPt":"Compativel com achados clinicos e LCR bacteriano com diplococos Gram+.","clinicalPearlPt":"Meningococo e causa comum em lactentes; vacinacao previne doenca invasiva."},{"id":"mening-hipotese-2","textPt":"Meningite viral","isCorrect":false,"explanationPt":"LCR bacteriano (neutrofilos, Gram positivo) exclui etiologia viral.","clinicalPearlPt":null},{"id":"mening-hipotese-3","textPt":"Encefalite herpetica","isCorrect":false,"explanationPt":"Sem convulsoes ou deficits focais; LCR sugere bacteria.","clinicalPearlPt":null},{"id":"mening-hipotese-4","textPt":"Sepse sem foco meningeo","isCorrect":false,"explanationPt":"LCR alterado confirma foco meningeo.","clinicalPearlPt":null}]'::jsonb, '[{"id":"mening-conduta-1","textPt":"Antibioticos IV empiricos imediatos (ceftriaxona 100mg/kg/dia) + dexametasona antes ou com primeira dose + suporte hemodinamico + UTI","isCorrect":true,"explanationPt":"Terapia empirica cobre patogenos comuns; dexametasona reduz sequelas neurologicas.","clinicalPearlPt":"Inicie antibioticos em <1h de suspeita para reduzir mortalidade."},{"id":"mening-conduta-2","textPt":"Hidratacao oral e observacao","isCorrect":false,"explanationPt":"Inadequado para sepse; requer intervencao IV urgente.","clinicalPearlPt":null},{"id":"mening-conduta-3","textPt":"Antivirais IV (aciclovir)","isCorrect":false,"explanationPt":"Indicado para encefalite herpetica, mas LCR bacteriano requer antibioticos.","clinicalPearlPt":null},{"id":"mening-conduta-4","textPt":"Antibioticos orais ambulatoriais","isCorrect":false,"explanationPt":"Sepse meningea requer via IV e internacao em UTI.","clinicalPearlPt":null}]'::jsonb, ARRAY['mening-dados-1','mening-dados-2','mening-dados-3'], 'mening-padrao-1', 'mening-hipotese-1', 'mening-conduta-1', '{"keyFindings":["Fontanela abaulada + rigidez nucal em lactente febril","Erupcao petequial + instabilidade hemodinamica","LCR com pleocitose + hipoglicorraquia + proteina elevada"],"systematicApproach":"Avalie ABC, sinais vitais e meningeos; realize puncao lombar se estavel; inicie terapia empirica imediatamente.","commonMistakes":["Ignorar rash petequial","Atrasar antibioticos aguardando resultado de LCR","Confundir com gastroenterite por vomitos"],"clinicalCorrelation":"Meningite bacteriana tem alta mortalidade (10-20%) em lactentes; deteccao precoce salva vidas."}'::jsonb, 1.200, 1.600, 0.200, true, true);

-- CASE 11: Desidratacao por Diarreia
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Desidratacao por Diarreia Aguda', 'Crianca de 18 meses com diarreia aquosa 5-6x/dia ha 3 dias, vomitos, reducao na ingestao oral, choro sem lagrimas, olhos fundos, mucosas secas, turgor cutaneo diminuido (retracao lenta), taquicardia. Ainda alerta e capaz de beber.', 'pediatria', 'facil', '[{"id":"desid-dados-1","textPt":"Diarreia aquosa + vomitos ha 3 dias","isCorrect":true,"explanationPt":"Quadro classico de gastroenterite aguda infecciosa.","clinicalPearlPt":"Diarreia aguda e principal causa de desidratacao em criancas <5 anos."},{"id":"desid-dados-2","textPt":"Sinais de desidratacao (choro sem lagrimas, olhos fundos, mucosas secas)","isCorrect":true,"explanationPt":"Sinais moderados de desidratacao (5-10% perda de peso).","clinicalPearlPt":"Avalie desidratacao por sinais clinicos antes de obter peso."},{"id":"desid-dados-3","textPt":"Turgor cutaneo diminuido com retracao lenta","isCorrect":true,"explanationPt":"Teste de prega cutanea >2s indica desidratacao moderada.","clinicalPearlPt":"Reavaliacao frequente guia progressao da terapia."},{"id":"desid-dados-4","textPt":"Fezes com sangue","isCorrect":false,"explanationPt":"Sugere disenteria, mas ausente; diarreia e aquosa.","clinicalPearlPt":null},{"id":"desid-dados-5","textPt":"Febre alta 40C","isCorrect":false,"explanationPt":"Febre nao mencionada; foco principal e desidratacao.","clinicalPearlPt":null},{"id":"desid-dados-6","textPt":"Distensao abdominal com ruidos ausentes","isCorrect":false,"explanationPt":"Sugere obstrucao, nao compativel com gastroenterite.","clinicalPearlPt":null}]'::jsonb, '[{"id":"desid-padrao-1","textPt":"Gastroenterite aguda com desidratacao moderada (Plano B - OMS)","isCorrect":true,"explanationPt":"Criterios OMS: dois ou mais sinais de desidratacao moderada, paciente ainda alerta e bebendo.","clinicalPearlPt":"Plano B e seguro e eficaz em unidades de saude."},{"id":"desid-padrao-2","textPt":"Desidratacao grave (Plano C)","isCorrect":false,"explanationPt":"Paciente alerta e bebendo; nao letargico ou em choque.","clinicalPearlPt":null},{"id":"desid-padrao-3","textPt":"Infeccao respiratoria","isCorrect":false,"explanationPt":"Sem sintomas respiratorios; diarreia dominante.","clinicalPearlPt":null},{"id":"desid-padrao-4","textPt":"Intoxicacao alimentar","isCorrect":false,"explanationPt":"Quadro agudo de 3 dias com sinais de desidratacao aponta para gastroenterite viral.","clinicalPearlPt":null}]'::jsonb, '[{"id":"desid-hipotese-1","textPt":"Doenca diarreica aguda com desidratacao moderada","isCorrect":true,"explanationPt":"Causa principal: rotavirus ou bacteriana; desidratacao secundaria a perdas.","clinicalPearlPt":"Vacina contra rotavirus previne 70% dos casos graves."},{"id":"desid-hipotese-2","textPt":"Diarreia disenterica","isCorrect":false,"explanationPt":"Sem sangue ou muco nas fezes.","clinicalPearlPt":null},{"id":"desid-hipotese-3","textPt":"Sepse abdominal","isCorrect":false,"explanationPt":"Sem febre ou distensao; paciente estavel e alerta.","clinicalPearlPt":null},{"id":"desid-hipotese-4","textPt":"Invaginacao intestinal","isCorrect":false,"explanationPt":"Invaginacao cursa com dor colica paroxistica e fezes em geleia de framboesa, ausentes.","clinicalPearlPt":null}]'::jsonb, '[{"id":"desid-conduta-1","textPt":"Terapia com SRO (sais de reidratacao oral) - Plano B (75mL/kg em 4h na unidade de saude) + reavaliacao apos 4h + suplemento de zinco 20mg/dia por 10-14 dias + continuar amamentacao","isCorrect":true,"explanationPt":"SRO corrige desidratacao; zinco reduz duracao e gravidade da diarreia.","clinicalPearlPt":"Evite antidiarreicos em criancas; hidratacao oral e pilar do tratamento."},{"id":"desid-conduta-2","textPt":"Hidratacao IV imediata","isCorrect":false,"explanationPt":"Reservado para Plano C (grave); oral e preferivel em desidratacao moderada.","clinicalPearlPt":null},{"id":"desid-conduta-3","textPt":"Antibioticos empiricos","isCorrect":false,"explanationPt":"Nao indicados em diarreia aquosa nao disenterica.","clinicalPearlPt":null},{"id":"desid-conduta-4","textPt":"Jejum e antidiarreicos","isCorrect":false,"explanationPt":"Jejum piora desidratacao; antidiarreicos sao contraindicados em criancas.","clinicalPearlPt":null}]'::jsonb, ARRAY['desid-dados-1','desid-dados-2','desid-dados-3'], 'desid-padrao-1', 'desid-hipotese-1', 'desid-conduta-1', '{"keyFindings":["Diarreia aquosa + vomitos ha 3 dias","Sinais de desidratacao moderada","Turgor cutaneo diminuido"],"systematicApproach":"Classifique desidratacao por OMS (sem desidratacao/moderada/grave); inicie Plano B se moderada; reavalie apos 4h.","commonMistakes":["Subestimar sinais de desidratacao moderada","Iniciar IV desnecessariamente","Omitir suplementacao de zinco"],"clinicalCorrelation":"Diarreia causa 500 mil mortes/ano em <5 anos; SRO salva vidas."}'::jsonb, -1.000, 1.100, 0.250, true, true);

-- CASE 12: Asma Aguda em Crianca
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Asma Aguda em Crianca (Crise Moderada-Grave)', 'Menino de 6 anos, asmatico conhecido, infeccao respiratoria alta (IRA) ha 3 dias, agora com sibilos progressivos, dispneia, uso de musculatura acessoria, fala em frases, FR 40 irpm, FC 120 bpm, SpO2 91%, sibilos expiratorios bilaterais, fase expiratoria prolongada. Pico de fluxo 45% do previsto.', 'pediatria', 'medio', '[{"id":"asma-dados-1","textPt":"Sibilos progressivos com uso de musculatura acessoria em asmatico conhecido","isCorrect":true,"explanationPt":"Sinais de crise moderada-grave: retracoes e dispneia em paciente com diagnostico previo.","clinicalPearlPt":"Historia de asma guia avaliacao de gravidade; triggers virais sao os mais comuns em criancas."},{"id":"asma-dados-2","textPt":"Fala em frases + SpO2 91% + pico de fluxo 45%","isCorrect":true,"explanationPt":"Criterios GINA para crise moderada-grave: SpO2 <92%, PEF <50% do previsto.","clinicalPearlPt":"Monitore SpO2 continuamente; alvo >=94% durante crise."},{"id":"asma-dados-3","textPt":"Desencadeada por IRA viral (gatilho mais comum em criancas)","isCorrect":true,"explanationPt":"Virus respiratorios precipitam 80% das crises asmaticas em pediatria.","clinicalPearlPt":"Identificar e evitar gatilhos reduz frequencia de crises."},{"id":"asma-dados-4","textPt":"Estridor","isCorrect":false,"explanationPt":"Sugere obstrucao de via aerea superior (crupe/epiglotite), nao asma.","clinicalPearlPt":null},{"id":"asma-dados-5","textPt":"Sibilos unilaterais","isCorrect":false,"explanationPt":"Indica corpo estranho ou pneumonia; asma e bilateral e difusa.","clinicalPearlPt":null},{"id":"asma-dados-6","textPt":"Tosse produtiva com expectoracao purulenta","isCorrect":false,"explanationPt":"Sugere infeccao bacteriana; asma tem tosse predominantemente seca.","clinicalPearlPt":null}]'::jsonb, '[{"id":"asma-padrao-1","textPt":"Exacerbacao aguda de asma moderada a grave","isCorrect":true,"explanationPt":"Baseado em sintomas, sinais fisicos e PEF reduzido.","clinicalPearlPt":"Classificacao GINA orienta terapia escalonada em crises."},{"id":"asma-padrao-2","textPt":"Pneumonia bacteriana","isCorrect":false,"explanationPt":"Sem febre alta ou infiltrados; padrao e obstrutivo, nao consolidativo.","clinicalPearlPt":null},{"id":"asma-padrao-3","textPt":"Corpo estranho aspirado","isCorrect":false,"explanationPt":"Sibilos bilaterais e historia asmatica; corpo estranho e unilateral e subito.","clinicalPearlPt":null},{"id":"asma-padrao-4","textPt":"Crupe viral","isCorrect":false,"explanationPt":"Sem estridor ou tosse ladrante; dispneia e obstrutiva baixa.","clinicalPearlPt":null}]'::jsonb, '[{"id":"asma-hipotese-1","textPt":"Crise de asma moderada a grave","isCorrect":true,"explanationPt":"Compativel com triggers, sintomas e espirometria.","clinicalPearlPt":"Educacao asmatica e plano de acao reduzem recorrencias em 50%."},{"id":"asma-hipotese-2","textPt":"Bronquiolite","isCorrect":false,"explanationPt":"Tipica em <2 anos; paciente de 6 anos com asma conhecida.","clinicalPearlPt":null},{"id":"asma-hipotese-3","textPt":"Pneumotorax","isCorrect":false,"explanationPt":"Sem assimetria ou hipersonoridade; ausculta bilateral simetrica.","clinicalPearlPt":null},{"id":"asma-hipotese-4","textPt":"Insuficiencia cardiaca","isCorrect":false,"explanationPt":"Sem historia cardiaca; padrao respiratorio obstrutivo, nao congestivo.","clinicalPearlPt":null}]'::jsonb, '[{"id":"asma-conduta-1","textPt":"O2 para manter SpO2 >=94% + nebulizacao com salbutamol a cada 20min x3 na primeira hora + brometo de ipratropio + corticosteroide sistemico (prednisolona 1-2mg/kg)","isCorrect":true,"explanationPt":"Terapia inicial GINA: beta-agonista + anticolinergico + corticoide sistemico.","clinicalPearlPt":"Corticoides sistemicos precoces reduzem internacoes em 30%."},{"id":"asma-conduta-2","textPt":"Antibioticos","isCorrect":false,"explanationPt":"Nao indicados sem evidencia de infeccao bacteriana.","clinicalPearlPt":null},{"id":"asma-conduta-3","textPt":"Mucoliticos","isCorrect":false,"explanationPt":"Contraindicados em asma; podem piorar obstrucao.","clinicalPearlPt":null},{"id":"asma-conduta-4","textPt":"Sedacao e intubacao imediata","isCorrect":false,"explanationPt":"Reservado para insuficiencia respiratoria; paciente ainda responsivo a terapia.","clinicalPearlPt":null}]'::jsonb, ARRAY['asma-dados-1','asma-dados-2','asma-dados-3'], 'asma-padrao-1', 'asma-hipotese-1', 'asma-conduta-1', '{"keyFindings":["Sibilos progressivos com uso de musculatura acessoria","SpO2 91% + pico de fluxo 45% do previsto","Trigger viral (IRA ha 3 dias)"],"systematicApproach":"Avalie gravidade por GINA (PEF, SpO2, fala); inicie O2 e broncodilatadores; adicione corticoide sistemico precocemente.","commonMistakes":["Confundir com infeccao por tosse","Atrasar corticosteroide sistemico","Ignorar trigger viral"],"clinicalCorrelation":"Asma afeta 10% das criancas; crises graves podem progredir para insuficiencia respiratoria."}'::jsonb, 0.200, 1.300, 0.250, true, true);

