-- CASE 13: Dengue com Sinais de Alarme
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Dengue com Sinais de Alarme', 'Homem de 32 anos, febre ha 5 dias (agora em defervescencia), mialgia/artralgia intensa, dor retro-orbital, teste do torniquete positivo, agora com vomitos persistentes, dor abdominal (hepatomegalia dolorosa), letargia progressiva, plaquetas 68.000, hematocrito em ascensao (de 42% para 48%), petequias cutaneas.', 'saude_coletiva', 'medio', '[{"id":"dengue-dados-1","textPt":"Defervescencia + sinais de alarme (vomitos persistentes, dor abdominal, letargia)","isCorrect":true,"explanationPt":"Indica transicao para fase critica da dengue, com risco de complicacoes graves.","clinicalPearlPt":"Sinais de alarme na dengue sinalizam necessidade de hospitalizacao imediata."},{"id":"dengue-dados-2","textPt":"Trombocitopenia + hematocrito em ascensao (hemoconcentracao)","isCorrect":true,"explanationPt":"Plaquetas baixas e aumento do hematocrito sugerem extravasamento plasmatico.","clinicalPearlPt":"Monitorar hematocrito a cada 2-4 horas para detectar agravamento precoce."},{"id":"dengue-dados-3","textPt":"Teste do torniquete positivo + petequias (diatese hemorragica)","isCorrect":true,"explanationPt":"Sinais de fragilidade capilar e tendencia hemorragica confirmam gravidade.","clinicalPearlPt":"Teste do torniquete e indicador simples de permeabilidade vascular alterada."},{"id":"dengue-dados-4","textPt":"Tosse produtiva","isCorrect":false,"explanationPt":"Sintoma respiratorio nao presente no quadro de dengue tipica.","clinicalPearlPt":null},{"id":"dengue-dados-5","textPt":"Ictericia","isCorrect":false,"explanationPt":"Nao descrita no caso; sugeriria hepatopatia ou leptospirose.","clinicalPearlPt":null},{"id":"dengue-dados-6","textPt":"Rigidez de nuca","isCorrect":false,"explanationPt":"Sinal meningeo nao presente; sugeriria meningite.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dengue-padrao-1","textPt":"Febre hemorragica viral na fase critica com sinais de alarme","isCorrect":true,"explanationPt":"Padrao classico de dengue grave na fase de defervescencia.","clinicalPearlPt":"A fase critica (3o-7o dia) e quando ocorre o maior risco de choque."},{"id":"dengue-padrao-2","textPt":"Sepse bacteriana","isCorrect":false,"explanationPt":"Faltam sinais de infeccao bacteriana como leucocitose ou foco infeccioso.","clinicalPearlPt":null},{"id":"dengue-padrao-3","textPt":"Malaria com complicacoes","isCorrect":false,"explanationPt":"Ausencia de viagem para area endemica de malaria.","clinicalPearlPt":null},{"id":"dengue-padrao-4","textPt":"Leptospirose","isCorrect":false,"explanationPt":"Falta exposicao a agua contaminada e insuficiencia renal.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dengue-hipotese-1","textPt":"Dengue com sinais de alarme (Grupo C)","isCorrect":true,"explanationPt":"Classificacao OMS/MS para dengue com risco de progressao para choque.","clinicalPearlPt":"Grupo C requer hospitalizacao com hidratacao IV e monitoramento intensivo."},{"id":"dengue-hipotese-2","textPt":"Dengue sem sinais de alarme (Grupo B)","isCorrect":false,"explanationPt":"Presenca de vomitos persistentes e letargia exclui grupo B.","clinicalPearlPt":null},{"id":"dengue-hipotese-3","textPt":"Chikungunya","isCorrect":false,"explanationPt":"Ausencia de artrite incapacitante e rash maculopapular difuso tipicos.","clinicalPearlPt":null},{"id":"dengue-hipotese-4","textPt":"Leptospirose","isCorrect":false,"explanationPt":"Padrao febril e hemorragico mais tipico de arbovirose sem ictericia.","clinicalPearlPt":null}]'::jsonb, '[{"id":"dengue-conduta-1","textPt":"Hidratacao IV imediata (NaCl 0,9% 20mL/kg em bolus) + admissao hospitalar + monitorizacao proxima (sinais vitais + hematocrito a cada 2-4h) + monitoramento de plaquetas/sangramento + NOTIFICACAO compulsoria","isCorrect":true,"explanationPt":"Conduta padrao para grupo C: reposicao volemica agressiva e vigilancia.","clinicalPearlPt":"Reavalie apos bolus; se melhora, manter 25mL/kg em 6h; se piora, considerar Grupo D."},{"id":"dengue-conduta-2","textPt":"Tratamento ambulatorial com analgesicos e repouso","isCorrect":false,"explanationPt":"Inadequado devido aos sinais de alarme que demandam hospitalizacao.","clinicalPearlPt":null},{"id":"dengue-conduta-3","textPt":"Antibioticos empiricos","isCorrect":false,"explanationPt":"Nao indicados pois e infeccao viral.","clinicalPearlPt":null},{"id":"dengue-conduta-4","textPt":"Transfusao de plaquetas imediata","isCorrect":false,"explanationPt":"Reservada para sangramento ativo grave, nao profilatica.","clinicalPearlPt":null}]'::jsonb, ARRAY['dengue-dados-1','dengue-dados-2','dengue-dados-3'], 'dengue-padrao-1', 'dengue-hipotese-1', 'dengue-conduta-1', '{"keyFindings":["Defervescencia com sinais de alarme","Trombocitopenia e hemoconcentracao","Teste do torniquete positivo e petequias"],"systematicApproach":"Avaliar fase da doenca (critica), sinais de gravidade e laboratoriais para classificacao OMS/MS e conduta hospitalar.","commonMistakes":["Confundir fase critica com melhora clinica pela defervescencia","Subestimar hemoconcentracao","Nao notificar caso"],"clinicalCorrelation":"Dengue e endemia no Brasil; notificacao compulsoria para vigilancia epidemiologica."}'::jsonb, 0.400, 1.300, 0.250, true, true);

-- CASE 14: Sifilis Congenita
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Sifilis Congenita Precoce', 'Recem-nascido, 38 semanas, nascido de mae com VDRL positivo 1:64 no parto, sifilis nao tratada/inadequada na gestacao (penicilina benzatina apenas 1 dose em vez de 3). Exame neonatal: hepatoesplenomegalia, rash cutaneo (palmas/plantas), rinite serossanguinolenta, baixo peso ao nascer. VDRL sangue de cordao 1:32, Raio-X mostrando periostite.', 'saude_coletiva', 'dificil', '[{"id":"sifcon-dados-1","textPt":"Mae nao tratada/inadequada com VDRL 1:64","isCorrect":true,"explanationPt":"Transmissao vertical provavel devido a sorologia materna elevada e tratamento insuficiente.","clinicalPearlPt":"Rastreio e tratamento adequado da sifilis na gestacao previnem 98% dos casos congenitos."},{"id":"sifcon-dados-2","textPt":"Hepatoesplenomegalia neonatal + rash cutaneo (palmas/plantas) + rinite serossanguinolenta","isCorrect":true,"explanationPt":"Manifestacoes classicas de infeccao congenita precoce por Treponema pallidum.","clinicalPearlPt":"Rash nas palmas e plantas e altamente sugestivo de sifilis congenita."},{"id":"sifcon-dados-3","textPt":"Alteracoes osseas (periostite) no Raio-X","isCorrect":true,"explanationPt":"Lesoes esqueleticas confirmam infeccao disseminada em neonatos.","clinicalPearlPt":"Raio-X de ossos longos revela periostite e pseudoparalisia de Parrot em sifilis congenita."},{"id":"sifcon-dados-4","textPt":"Peso ao nascer normal","isCorrect":false,"explanationPt":"Caso descreve baixo peso ao nascer, nao normal.","clinicalPearlPt":null},{"id":"sifcon-dados-5","textPt":"Mae tratada com 3 doses completas","isCorrect":false,"explanationPt":"Caso descreve tratamento inadequado (apenas 1 dose).","clinicalPearlPt":null},{"id":"sifcon-dados-6","textPt":"VDRL negativo","isCorrect":false,"explanationPt":"VDRL e positivo tanto na mae (1:64) quanto no cordao (1:32).","clinicalPearlPt":null}]'::jsonb, '[{"id":"sifcon-padrao-1","textPt":"Infeccao neonatal com transmissao vertical de sifilis materna nao tratada","isCorrect":true,"explanationPt":"Padrao epidemiologico e clinico de sifilis congenita precoce.","clinicalPearlPt":"Sifilis congenita e doenca de notificacao compulsoria no Brasil."},{"id":"sifcon-padrao-2","textPt":"Infeccao bacteriana neonatal adquirida no parto","isCorrect":false,"explanationPt":"Sorologia especifica e quadro multissistemico apontam para sifilis, nao sepse neonatal tipica.","clinicalPearlPt":null},{"id":"sifcon-padrao-3","textPt":"Toxoplasmose congenita","isCorrect":false,"explanationPt":"Ausencia de coriorretinite ou calcificacoes intracranianas tipicas de toxo.","clinicalPearlPt":null},{"id":"sifcon-padrao-4","textPt":"RCIU idiopatico","isCorrect":false,"explanationPt":"Presenca de lesoes especificas (rash, periostite, rinite) exclui causa idiopatica.","clinicalPearlPt":null}]'::jsonb, '[{"id":"sifcon-hipotese-1","textPt":"Sifilis congenita precoce","isCorrect":true,"explanationPt":"Diagnostico baseado em clinica, sorologia e historia materna de tratamento inadequado.","clinicalPearlPt":"Sifilis congenita precoce manifesta-se nos primeiros 2 anos de vida."},{"id":"sifcon-hipotese-2","textPt":"Sifilis congenita tardia","isCorrect":false,"explanationPt":"Manifestacoes tardias ocorrem apos 2 anos (dentes de Hutchinson, tibia em sabre).","clinicalPearlPt":null},{"id":"sifcon-hipotese-3","textPt":"Citomegalovirose congenita","isCorrect":false,"explanationPt":"Falta de microcefalia ou hepatite citolitica tipicas de CMV.","clinicalPearlPt":null},{"id":"sifcon-hipotese-4","textPt":"Rubeola congenita","isCorrect":false,"explanationPt":"Rubeola congenita cursa com cardiopatia, catarata e surdez, ausentes no caso.","clinicalPearlPt":null}]'::jsonb, '[{"id":"sifcon-conduta-1","textPt":"Penicilina G cristalina 50.000 U/kg/dose IV a cada 12h (primeiros 7 dias) depois a cada 8h (total 10 dias) + investigacao completa (LCR, hemograma, funcao hepatica, Raio-X ossos longos) + NOTIFICACAO compulsoria + tratar mae e parceiro","isCorrect":true,"explanationPt":"Tratamento padrao para sifilis congenita sintomatica, com avaliacao de LCR para neurosifilis.","clinicalPearlPt":"Acompanhamento sorologico: VDRL mensal ate negativacao ou queda de 2 diluicoes."},{"id":"sifcon-conduta-2","textPt":"Observacao sem tratamento","isCorrect":false,"explanationPt":"Inadequado; requer terapia especifica imediata com penicilina IV.","clinicalPearlPt":null},{"id":"sifcon-conduta-3","textPt":"Azitromicina oral","isCorrect":false,"explanationPt":"Nao e primeira linha para neonatos; penicilina e o unico comprovado.","clinicalPearlPt":null},{"id":"sifcon-conduta-4","textPt":"Apenas notificacao sem investigacao","isCorrect":false,"explanationPt":"Investigacao completa (LCR, ossos, hemograma) e essencial para estadiamento e seguimento.","clinicalPearlPt":null}]'::jsonb, ARRAY['sifcon-dados-1','sifcon-dados-2','sifcon-dados-3'], 'sifcon-padrao-1', 'sifcon-hipotese-1', 'sifcon-conduta-1', '{"keyFindings":["Mae soropositiva nao tratada adequadamente","Sinais neonatais classicos (hepatoesplenomegalia, rash, rinite)","Lesoes osseas no Raio-X (periostite)"],"systematicApproach":"Avaliar historia gestacional, exame fisico neonatal e exames complementares para confirmar transmissao vertical e estadiar.","commonMistakes":["Assumir tratamento materno adequado sem verificar numero de doses","Ignorar VDRL positivo em cordao como falso-positivo","Nao solicitar Raio-X de ossos longos"],"clinicalCorrelation":"Sifilis congenita e prevenivel com rastreio pre-natal e tratamento completo; notificacao compulsoria para controle de ISTs."}'::jsonb, 1.500, 1.700, 0.200, true, true);

-- CASE 15: Surto de Sarampo
INSERT INTO fcr_cases (id, title_pt, clinical_presentation_pt, area, difficulty, dados_options, padrao_options, hipotese_options, conduta_options, correct_dados, correct_padrao, correct_hipotese, correct_conduta, structured_explanation, irt_difficulty, irt_discrimination, irt_guessing, is_public, is_ai_generated) VALUES (gen_random_uuid(), 'Surto de Sarampo - Vigilancia Epidemiologica', 'Crianca de 4 anos nao vacinada, febre alta ha 5 dias, tosse, coriza, conjuntivite (3 Cs), manchas de Koplik na mucosa jugal, seguido de exantema maculopapular iniciando na face/atras das orelhas e espalhando cefalocaudalmente. Contato com caso confirmado de sarampo na creche. 3 outras criancas na mesma creche desenvolvendo sintomas semelhantes.', 'saude_coletiva', 'facil', '[{"id":"sarampo-dados-1","textPt":"Prodrome classico (febre + 3 Cs: tosse/coriza/conjuntivite)","isCorrect":true,"explanationPt":"Sintomas iniciais tipicos do sarampo, precedendo o exantema.","clinicalPearlPt":"Os 3 Cs sao hallmark do prodrome do sarampo em criancas nao imunizadas."},{"id":"sarampo-dados-2","textPt":"Manchas de Koplik (patognomonicas) + exantema cefalocaudal","isCorrect":true,"explanationPt":"Manchas brancas na mucosa e rash progressivo confirmam diagnostico clinico.","clinicalPearlPt":"Manchas de Koplik aparecem 1-2 dias antes do rash e sao exclusivas do sarampo."},{"id":"sarampo-dados-3","textPt":"Vinculo epidemiologico (nao vacinada + contato com caso confirmado + aglomeracao)","isCorrect":true,"explanationPt":"Cluster na creche indica surto, facilitando confirmacao por vinculo.","clinicalPearlPt":"Sarampo e uma das doencas mais contagiosas (R0=12-18); vacinacao e chave para controle."},{"id":"sarampo-dados-4","textPt":"Exantema vesicular","isCorrect":false,"explanationPt":"Exantema vesicular sugere varicela, nao sarampo (maculopapular).","clinicalPearlPt":null},{"id":"sarampo-dados-5","textPt":"Artralgia/inchaco articular","isCorrect":false,"explanationPt":"Artralgia e mais tipica de rubeola em adultos, nao de sarampo.","clinicalPearlPt":null},{"id":"sarampo-dados-6","textPt":"Linfadenopatia cervical indolor","isCorrect":false,"explanationPt":"Linfadenopatia retroauricular e mais tipica de rubeola que de sarampo.","clinicalPearlPt":null}]'::jsonb, '[{"id":"sarampo-padrao-1","textPt":"Doenca exantematica altamente contagiosa com cluster epidemiologico (surto)","isCorrect":true,"explanationPt":"Padrao de transmissao respiratoria em ambiente fechado com nao vacinados.","clinicalPearlPt":"Surto = 2 ou mais casos confirmados com vinculo epidemiologico."},{"id":"sarampo-padrao-2","textPt":"Infeccao viral respiratoria isolada","isCorrect":false,"explanationPt":"Presenca de rash e Koplik exclui infeccao respiratoria simples.","clinicalPearlPt":null},{"id":"sarampo-padrao-3","textPt":"Reacao alergica a medicamento","isCorrect":false,"explanationPt":"Ausencia de exposicao medicamentosa e progressao cefalocaudal tipica de sarampo.","clinicalPearlPt":null},{"id":"sarampo-padrao-4","textPt":"Rubeola","isCorrect":false,"explanationPt":"Rubeola tem rash menos intenso, sem Koplik, e com linfadenopatia retroauricular.","clinicalPearlPt":null}]'::jsonb, '[{"id":"sarampo-hipotese-1","textPt":"Sarampo (caso confirmado por vinculo epidemiologico)","isCorrect":true,"explanationPt":"Diagnostico clinico-epidemiologico em contexto de surto.","clinicalPearlPt":"Confirmacao pode ser clinico-epidemiologica ou laboratorial (IgM anti-sarampo)."},{"id":"sarampo-hipotese-2","textPt":"Escarlatina","isCorrect":false,"explanationPt":"Escarlatina e bacteriana (Streptococcus), com rash aspero e lingua em framboesa.","clinicalPearlPt":null},{"id":"sarampo-hipotese-3","textPt":"Dengue com rash","isCorrect":false,"explanationPt":"Falta mialgia intensa e teste do torniquete; rash nao e cefalocaudal.","clinicalPearlPt":null},{"id":"sarampo-hipotese-4","textPt":"Roseola infantil","isCorrect":false,"explanationPt":"Roseola: rash surge apos defervescencia, nao com prodrome de 3 Cs.","clinicalPearlPt":null}]'::jsonb, '[{"id":"sarampo-conduta-1","textPt":"Notificacao imediata a vigilancia em saude (em ate 24h) + isolamento respiratorio + suplementacao de vitamina A (200.000 UI) + rastreamento de contatos + vacinacao pos-exposicao (ate 72h) ou imunoglobulina (ate 6 dias) para contatos suscetiveis + investigacao de surto","isCorrect":true,"explanationPt":"Medidas de controle de surto: notificacao compulsoria, isolamento e profilaxia de contatos.","clinicalPearlPt":"Vitamina A reduz mortalidade por sarampo em 50% em criancas desnutridas."},{"id":"sarampo-conduta-2","textPt":"Tratamento sintomatico ambulatorial sem notificacao","isCorrect":false,"explanationPt":"Notificacao e obrigatoria; risco de surto exige vigilancia epidemiologica.","clinicalPearlPt":null},{"id":"sarampo-conduta-3","textPt":"Antibioticos para infeccao secundaria","isCorrect":false,"explanationPt":"Antibioticos nao sao empiricos sem evidencia de complicacao bacteriana.","clinicalPearlPt":null},{"id":"sarampo-conduta-4","textPt":"Vacinacao imediata da crianca afetada","isCorrect":false,"explanationPt":"Ineficaz no caso sintomatico em curso; foco e em contatos suscetiveis.","clinicalPearlPt":null}]'::jsonb, ARRAY['sarampo-dados-1','sarampo-dados-2','sarampo-dados-3'], 'sarampo-padrao-1', 'sarampo-hipotese-1', 'sarampo-conduta-1', '{"keyFindings":["Prodrome com 3 Cs e febre alta","Manchas de Koplik e rash cefalocaudal","Vinculo epidemiologico com cluster na creche"],"systematicApproach":"Reconhecer padrao clinico e epidemiologico para notificacao rapida e controle de surto.","commonMistakes":["Confundir rash maculopapular com vesicular (varicela)","Nao notificar caso suspeito em ate 24h","Ignorar necessidade de bloqueio vacinal em contatos"],"clinicalCorrelation":"Sarampo e erradicavel pela vacinacao; surtos destacam falhas na cobertura vacinal e demandam vigilancia ativa."}'::jsonb, -0.300, 1.200, 0.250, true, true);
