-- ============================================================
-- FLASHCARDS: Saúde Coletiva — 200 cards
-- Deck: a0000001-0000-0000-0000-000000000005
-- ============================================================

INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Epidemiologia, Vigilância em saúde, SUS e políticas, Atenção primária, Saúde do trabalhador
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a diferença entre incidência e prevalência em epidemiologia?', 'A incidência mede o número de novos casos de uma doença em uma população durante um período específico, enquanto a prevalência representa o total de casos existentes (novos e antigos) em um momento dado. A incidência é útil para estudar etiologia, e a prevalência para planejamento de serviços de saúde. No Brasil, o SINAN utiliza essas medidas para doenças notificáveis.', 'saude_coletiva', 'Epidemiologia', 'Medidas epidemiológicas básicas', ARRAY['incidencia','prevalencia','epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é um estudo de coorte em epidemiologia?', 'Estudo de coorte é observacional prospectivo ou retrospectivo onde grupos expostos e não expostos a um fator de risco são seguidos para observar a ocorrência de desfechos. Vantagens: permite calcular risco relativo. Desvantagens: caro e demorado. Exemplo: estudo de Framingham para doenças cardiovasculares.', 'saude_coletiva', 'Epidemiologia', 'Tipos de estudos', ARRAY['coorte','observacional','risco_relativo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina odds ratio e como é calculado.', 'Odds ratio (OR) é uma medida de associação em estudos caso-controle, calculada como (odds de exposição nos casos) / (odds de exposição nos controles). OR >1 indica associação positiva. Não é risco relativo, mas aproxima-se em doenças raras. Usado em logística para modelagem.', 'saude_coletiva', 'Epidemiologia', 'Medidas de associação', ARRAY['odds_ratio','caso_controle','associacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da epidemiologia descritiva?', 'Epidemiologia descritiva descreve a distribuição de doenças por pessoa, lugar e tempo, usando variáveis demográficas. Não testa hipóteses causais, mas gera-as. Ferramentas: taxas, proporções. No Brasil, usada em boletins epidemiológicos do Ministério da Saúde.', 'saude_coletiva', 'Epidemiologia', 'Epidemiologia descritiva', ARRAY['distribuicao','pessoa_lugar_tempo','taxas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o conceito de causalidade em epidemiologia.', 'Causalidade envolve associação, temporalidade, força da associação, consistência, especificidade, plausibilidade biológica, dose-resposta, reversibilidade. Critérios de Hill auxiliam julgamento. Nem toda associação é causal; confusores devem ser controlados.', 'saude_coletiva', 'Epidemiologia', 'Causalidade', ARRAY['Hill','associacao','confusores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são indicadores epidemiológicos?', 'Indicadores são medidas quantitativas da saúde populacional, como mortalidade infantil, cobertura vacinal. Classificados em de saúde, morbidade, mortalidade. No SUS, monitorados pelo DATASUS para avaliação de políticas públicas.', 'saude_coletiva', 'Epidemiologia', 'Indicadores', ARRAY['mortalidade','morbidade','DATASUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina viés em estudos epidemiológicos.', 'Viés é erro sistemático que distorce a estimativa da associação verdadeira. Tipos: seleção (amostragem), informação (recall), confusão. Prevenção: randomização, cegamento, matching. Impacta validade interna dos estudos.', 'saude_coletiva', 'Epidemiologia', 'Viés e validade', ARRAY['vies','selecao','confusao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da curva epidêmica?', 'Curva epidêmica plota casos novos ao longo do tempo, ajudando a identificar padrões (pico, platô), surtos, endemias. Útil em vigilância para intervenções. No Brasil, usada em arboviroses como dengue.', 'saude_coletiva', 'Epidemiologia', 'Análise temporal', ARRAY['curva_epidemica','surtos','arboviroses']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a taxa de ataque em surtos?', 'Taxa de ataque é a proporção de casos entre os expostos em um surto. Calculada como (casos / expostos) x 100. Ajuda a identificar fonte de infecção. Exemplo: em surto alimentar, calcula-se por alimento consumido.', 'saude_coletiva', 'Epidemiologia', 'Investigação de surtos', ARRAY['taxa_de_ataque','surtos','expostos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o modelo de agente-hospedeiro-ambiente.', 'Modelo epidemiológico clássico: agente (causador), hospedeiro (susceptível), ambiente (facilitador). Interações determinam ocorrência de doenças. Aplicado em controle de vetores, imunização, saneamento no contexto brasileiro.', 'saude_coletiva', 'Epidemiologia', 'Modelos epidemiológicos', ARRAY['agente','hospedeiro','ambiente']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema de Informação de Agravos de Notificação (SINAN)?', 'SINAN é o sistema nacional para notificação compulsória de doenças e agravos. Registra dados epidemiológicos para vigilância. Mantido pelo Ministério da Saúde, integra dados de municípios para análise de tendências e intervenções.', 'saude_coletiva', 'Vigilância em saúde', 'Sistemas de vigilância', ARRAY['SINAN','notificacao','agravos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina vigilância epidemiológica ativa e passiva.', 'Vigilância ativa: busca proativa de casos por equipes de saúde. Passiva: notificação espontânea por profissionais. Ativa é mais completa mas custosa; passiva, mais ampla. No Brasil, combinadas em doenças como tuberculose.', 'saude_coletiva', 'Vigilância em saúde', 'Tipos de vigilância', ARRAY['ativa','passiva','epidemiologica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são as etapas da investigação de surto?', 'Etapas: confirmação do surto, descrição (quem, o quê, onde, quando), formulação de hipótese, testes (laboratorial, ambiental), controle (intervenções), comunicação. Baseado em manual do MS para resposta rápida.', 'saude_coletiva', 'Vigilância em saúde', 'Investigação de surtos', ARRAY['surto','hipotese','controle']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Lista Nacional de Notificação Compulsória?', 'Lista A (imediata) e B (até 24h ou semanal) de agravos como meningite, dengue, violência. Obrigatória para profissionais de saúde. Atualizada pela Portaria MS nº 204/2016, visa detecção precoce e controle.', 'saude_coletiva', 'Vigilância em saúde', 'Notificação compulsória', ARRAY['lista_A','lista_B','Portaria_204']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o papel da vigilância sanitária.', 'Vigilância sanitária monitora riscos à saúde pública em alimentos, medicamentos, serviços. Fiscaliza ANVISA e secretarias estaduais. Inclui inspeções, análises laboratoriais para prevenir contaminações e fraudes.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância sanitária', ARRAY['ANVISA','alimentos','medicamentos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema Nacional de Vigilância em Saúde (SNVS)?', 'SNVS integra vigilância epidemiológica, sanitária e ambiental. Coordena ações federais, estaduais, municipais via Ministério da Saúde. Facilita intercâmbio de dados para decisões em saúde pública.', 'saude_coletiva', 'Vigilância em saúde', 'Sistemas integrados', ARRAY['SNVS','epidemiologica','sanitaria']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina sentinela em vigilância.', 'Unidades sentinela são locais selecionados para monitoramento contínuo de agravos, como síndrome gripal em UPAs. Fornecem dados representativos com menor custo. Usado em influenza e COVID-19 no Brasil.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância sentinela', ARRAY['sentinela','sindrome_gripal','monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da vigilância ambiental?', 'Monitora fatores ambientais como qualidade da água, ar, solo para prevenir doenças. Integra SVS com agências como IBAMA. No Brasil, crucial para endemias como leptospirose em áreas urbanas.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância ambiental', ARRAY['agua','ar','leptospirose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são eventos de importância à saúde pública (EISP)?', 'EISP são situações incomuns ou graves como surtos, desastres, que demandam notificação imediata ao CIEVS. Portaria MS nº 2.041/2018 regula para resposta rápida nacional e internacional (OMS).', 'saude_coletiva', 'Vigilância em saúde', 'Eventos EISP', ARRAY['EISP','CIEVS','Portaria_2041']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o Centro de Informações Estratégicas em Vigilância em Saúde (CIEVS).', 'CIEVS são centros regionais para análise de riscos, investigação e articulação em vigilância. Recebem notificações de EISP e coordenam respostas. Implantados em capitais para agilidade em emergências.', 'saude_coletiva', 'Vigilância em saúde', 'CIEVS', ARRAY['CIEVS','riscos','emergencias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os princípios do SUS?', 'Universalidade (todos têm direito), equidade (atendimento conforme necessidade), integralidade (promoção, prevenção, tratamento, reabilitação), descentralização, participação social via conselhos. Base da Constituição 1988 e Lei 8.080/90.', 'saude_coletiva', 'SUS e políticas de saúde', 'Princípios do SUS', ARRAY['universalidade','equidade','integralidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Lei Orgânica da Saúde (Lei 8.080/90)?', 'Lei 8.080/90 cria o SUS, define organização, competências federativas, participação comunitária. Complementada pela 8.142/90 (conselhos). Fundamento legal para políticas públicas de saúde no Brasil.', 'saude_coletiva', 'SUS e políticas de saúde', 'Legislação SUS', ARRAY['Lei_8080','organizacao','conselhos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o financiamento do SUS.', 'Financiamento tripartite: União (mínimo 15% receita corrente líquida desde EC 86/2015), estados (12%), municípios (15%). Fontes: impostos, contribuições. Desafios: subfinanciamento crônico afeta expansão de serviços.', 'saude_coletiva', 'SUS e políticas de saúde', 'Financiamento', ARRAY['EC_86','tripartite','subfinanciamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são as Conferências de Saúde?', 'Conferências são instâncias deliberativas bienais (nacional, estadual, municipal) para debater políticas. Participação popular elege prioridades. Ex: 16ª CNS em 2019 discutiu desafios do SUS.', 'saude_coletiva', 'SUS e políticas de saúde', 'Participação social', ARRAY['conferencias','CNS','prioridades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina Conselho Nacional de Saúde (CNS).', 'CNS é instância máxima de controle social no SUS, paritário (governo/sociedade). Fiscaliza políticas, aprova diretrizes. Criado pela Lei 8.142/90, representa 50% não governamentais para democratização.', 'saude_coletiva', 'SUS e políticas de saúde', 'Controle social', ARRAY['CNS','paritario','fiscalizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o Pacto pela Saúde?', 'Pacto pela Saúde (2006) reorganiza SUS com bases: Pacto pela Vida (qualidade), Gestão (eficiência), Atenção à Saúde (acesso). Melhora coordenação federativa para equidade e integralidade.', 'saude_coletiva', 'SUS e políticas de saúde', 'Pactos e reformas', ARRAY['Pacto_pela_Saude','gestao','atencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Política Nacional de Atenção Básica (PNAB)?', 'PNAB (2017) define atenção primária como porta de entrada do SUS, com equipes multidisciplinares. Expande ESF, NASF. Objetivo: 80% resolutividade em APS para promoção e prevenção.', 'saude_coletiva', 'SUS e políticas de saúde', 'Políticas nacionais', ARRAY['PNAB','ESF','resolutividade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Emenda Constitucional 29/2000.', 'EC 29/2000 estabelece percentuais mínimos de aplicação em saúde: estados 12% e municípios 15% de receitas próprias. Para a União, a EC 29 vinculou o gasto ao valor do ano anterior corrigido pela variação nominal do PIB — o piso de 15% da receita corrente líquida para a União veio somente com a EC 86/2015 (consolidado pela EC 95/2016). Visa garantir recursos estáveis, mas enfrenta judicializações por descumprimento.', 'saude_coletiva', 'SUS e políticas de saúde', 'Emendas constitucionais', ARRAY['EC_29','percentuais','recursos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Programa Saúde da Família (PSF)?', 'PSF, agora ESF, é estratégia de APS com equipes (médico, enfermeiro, agentes comunitários) vinculadas a territórios. Cobertura >70% municípios. Foco em família, comunidade para redução de desigualdades.', 'saude_coletiva', 'SUS e políticas de saúde', 'Estratégias SUS', ARRAY['PSF','ESF','agentes_comunitarios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da Reforma Sanitária no SUS?', 'Reforma Sanitária (década 80) articulou 8ª Conferência e Constituição 88, criando SUS universal. Movimentos sociais e acadêmicos pressionaram por equidade. Legado: sistema público apesar de resistências neoliberais.', 'saude_coletiva', 'SUS e políticas de saúde', 'Reforma Sanitária', ARRAY['8a_Conferencia','Constituicao_88','equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Estratégia Saúde da Família (ESF)?', 'ESF é modelo de APS com equipes multiprofissionais atendendo famílias cadastradas. Aborda determinantes sociais, promove saúde. No Brasil, cobre ~76% da população (2024), reduzindo hospitalizações por condições sensíveis.', 'saude_coletiva', 'Atenção primária', 'Estratégia Saúde da Família', ARRAY['ESF','APS','cadastramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina atenção primária à saúde (APS).', 'APS é primeiro nível de contato, coordenando cuidados integrais, resolutivos e longitudinais. Baseada em princípios de equidade, participação. No SUS, via UBS e ESF, resolve 80-90% demandas comuns.', 'saude_coletiva', 'Atenção primária', 'Conceitos básicos', ARRAY['APS','UBS','integralidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os atributos da APS segundo Starfield?', 'Acessibilidade, continuidade, coordenação de cuidados, integralidade, orientação familiar/comunitária, gatekeeping. Essenciais para eficiência do sistema. Aplicados na PNAB para fortalecimento do SUS.', 'saude_coletiva', 'Atenção primária', 'Atributos APS', ARRAY['Starfield','acessibilidade','gatekeeping']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o Núcleo Ampliado de Saúde da Família (NASF).', 'NASF (2008) integrava profissionais (nutricionista, psicólogo, etc.) às equipes ESF para apoio matricial. Em 2020, renomeado NASF-AB; a partir de 2023/2024, transição para programa eMulti (Equipes Multiprofissionais). Mantém lógica de apoio matricial e amplia resolutividade em APS.', 'saude_coletiva', 'Atenção primária', 'NASF', ARRAY['NASF','matricial','multiprofissional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Atenção Básica no SUS?', 'Atenção Básica é ordenadora da rede, incluindo promoção, prevenção, diagnóstico, tratamento de alta frequência. Via UBS, ESF, programas como Hiperdia. Responsável por 80% ações em saúde pública brasileira.', 'saude_coletiva', 'Atenção primária', 'Atenção Básica', ARRAY['UBS','Hiperdia','prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos Agentes Comunitários de Saúde (ACS)?', 'ACS visitam domicílios, educam famílias, identificam riscos, mobilizam comunidade. Formação técnica, vínculo territorial. Essenciais na ESF para equidade, cobrindo áreas vulneráveis no SUS.', 'saude_coletiva', 'Atenção primária', 'ACS', ARRAY['ACS','visitas_domiciliares','comunidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Política Nacional de Promoção da Saúde (PNPS).', 'PNPS (2006) foca em estilos de vida saudáveis, redução de riscos (tabagismo, sedentarismo). Ações intersetoriais via Pacto pela Vida. Integra APS para empoderamento comunitário e prevenção de NCDs.', 'saude_coletiva', 'Atenção primária', 'Promoção da saúde', ARRAY['PNPS','estilos_de_vida','NCDs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são condições sensíveis à atenção primária?', 'Condições como hipertensão, diabetes, pneumonia que poderiam ser prevenidas/tratadas em APS, evitando hospitalizações. Indicador ICSAP usado no SUS para avaliar efetividade da atenção básica.', 'saude_coletiva', 'Atenção primária', 'Avaliação APS', ARRAY['ICSAP','hospitalizacoes','hipertensao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina abordagem familiar e comunitária na APS.', 'Abordagem familiar considera dinâmica familiar como unidade de cuidado; comunitária, contexto social e determinantes. Ferramentas: cadastramento, visitas. Promove integralidade e resolutividade no SUS.', 'saude_coletiva', 'Atenção primária', 'Abordagens', ARRAY['familiar','comunitaria','determinantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é vigilância em saúde do trabalhador (Visat)?', 'Visat monitora riscos ocupacionais, agravos relacionados ao trabalho. Integra SES, CEREST. Notificação via SINAN para doenças ocupacionais. Baseada na NR-4 e Lei 8.213/91 para prevenção e reabilitação.', 'saude_coletiva', 'Saúde do trabalhador', 'Vigilância Visat', ARRAY['Visat','riscos','SINAN']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as Normas Regulamentadoras (NRs) principais?', 'NRs são 38 normas do MTE para segurança e saúde no trabalho. Ex: NR-1 (disposições gerais + PGR), NR-7 (PCMSO), NR-15 (atividades insalubres). Nota: o antigo PPRA (NR-9) foi substituído pelo PGR (NR-1) desde jan/2022. Obrigatórias para empresas, fiscalizadas por auditores.', 'saude_coletiva', 'Saúde do trabalhador', 'Normas Regulamentadoras', ARRAY['NRs','MTE','PPRA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina Programa de Gerenciamento de Riscos Ocupacionais (PGR).', 'PGR substitui PPRA e PCMSO desde 2022 (NR-1). Identifica, avalia, controla riscos físicos, químicos, biológicos, ergonômicos, acidentes. Obrigatório para todas empresas, integra gestão SST.', 'saude_coletiva', 'Saúde do trabalhador', 'PGR e PCMSO', ARRAY['PGR','riscos','SST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são os Centros de Referência em Saúde do Trabalhador (CEREST)?', 'CEREST são unidades especializadas no SUS para diagnóstico, tratamento, reabilitação de agravos laborais. Oferecem Visat, formação. Regionalizados para articulação com APS e vigilância.', 'saude_coletiva', 'Saúde do trabalhador', 'CEREST', ARRAY['CEREST','agravos','reabilitacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Lista de Doenças Relacionadas ao Trabalho (LDT).', 'LDT (Portaria MS 1.999/06) classifica doenças ocupacionais para nexo causal em benefícios previdenciários. Inclui LER/DORT, asbestose, silicose. Auxilia notificação e indenizações.', 'saude_coletiva', 'Saúde do trabalhador', 'Doenças ocupacionais', ARRAY['LDT','LER_DORT','nexo_causal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da CIPA?', 'CIPA (Comissão Interna de Prevenção de Acidentes) representa trabalhadores e empregadores para identificar riscos, promover segurança. Obrigatória em empresas >20 funcionários (NR-5). Realiza SIPAT.', 'saude_coletiva', 'Saúde do trabalhador', 'CIPA e SESMT', ARRAY['CIPA','SIPAT','NR_5']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Programa de Controle Médico de Saúde Ocupacional (PCMSO)?', 'PCMSO (NR-7) monitora saúde trabalhadores via exames admissionais, periódicos, demissionais. Detecta precocemente agravos. Integra PGR, realizado por médicos do trabalho em empresas.', 'saude_coletiva', 'Saúde do trabalhador', 'PCMSO', ARRAY['PCMSO','exames','NR_7']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina insalubridade e periculosidade.', 'Insalubridade: exposição a agentes nocivos (ruído, químicos) com adicional de 10-40% salário (NR-15). Periculosidade: risco acentuado de vida (explosivos, eletricidade) com 30% adicional (NR-16). Fiscalizado MTE.', 'saude_coletiva', 'Saúde do trabalhador', 'Adicionais', ARRAY['insalubridade','periculosidade','NR_15']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Política Nacional de Saúde do Trabalhador (PNSST).', 'PNSST (1999, atualizada) integra saúde do trabalhador no SUS, com ênfase em prevenção, promoção, vigilância. Baseada em direitos humanos, participação. Enfrenta informalidade e precarização laboral.', 'saude_coletiva', 'Saúde do trabalhador', 'Políticas nacionais', ARRAY['PNSST','prevencao','informalidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é nexo causal em saúde do trabalho?', 'Nexo causal liga agravo à atividade laboral, presumido para LDT ou investigado por perito INSS. Essencial para aposentadoria especial, estabilidade gestante. Visat auxilia comprovação via notificações.', 'saude_coletiva', 'Saúde do trabalhador', 'Nexo causal', ARRAY['nexo_causal','INSS','LDT']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a média aritmética em bioestatística?', 'A média aritmética é calculada somando todos os valores de uma amostra e dividindo pelo número de observações, representando o valor central dos dados. É uma medida de tendência central apropriada para distribuições simétricas e intervalos de dados contínuos. No entanto, é sensível a valores atípicos, podendo distorcer os resultados em distribuições assimétricas.', 'saude_coletiva', 'Bioestatística', 'média', ARRAY['tendência central', 'estatística descritiva', 'dados contínuos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a definição de mediana em um conjunto de dados?', 'A mediana é o valor que divide o conjunto de dados ordenados ao meio, com 50% dos valores abaixo e 50% acima dela. É uma medida robusta de tendência central, ideal para dados assimétricos ou com outliers. Em bioestatística, é frequentemente usada em análises de renda ou tempos de sobrevivência.', 'saude_coletiva', 'Bioestatística', 'mediana', ARRAY['tendência central', 'dados ordenados', 'outliers']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que representa a moda em estatística?', 'A moda é o valor que ocorre com maior frequência em um conjunto de dados, podendo ser unimodal, bimodal ou multimodal. É útil para dados categóricos nominais, como tipos de doenças mais comuns em uma população. Diferente da média e mediana, não é afetada pela magnitude dos valores.', 'saude_coletiva', 'Bioestatística', 'moda', ARRAY['frequência', 'dados categóricos', 'distribuição']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como se calcula o desvio padrão?', 'O desvio padrão mede a dispersão dos dados em relação à média, calculado como a raiz quadrada da variância. Em bioestatística, indica a variabilidade em medidas como pressão arterial em uma população. Valores altos sugerem maior heterogeneidade nos dados.', 'saude_coletiva', 'Bioestatística', 'desvio padrão', ARRAY['dispersão', 'variância', 'medidas de variabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o intervalo de confiança em testes estatísticos?', 'O intervalo de confiança é uma faixa de valores que provavelmente contém o parâmetro populacional verdadeiro, com um nível de confiança como 95%. Em estudos epidemiológicos, estima a precisão de uma média amostral. Um intervalo estreito indica maior precisão na estimativa.', 'saude_coletiva', 'Bioestatística', 'intervalo de confiança', ARRAY['estimativa', 'nível de confiança', 'epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quando se usa o teste t de Student?', 'O teste t de Student compara médias de duas amostras independentes ou uma amostra com um valor conhecido, assumindo distribuição normal. É comum em bioestatística para avaliar diferenças em grupos de tratamento versus controle. Requer verificação de normalidade e homogeneidade de variâncias.', 'saude_coletiva', 'Bioestatística', 'teste t', ARRAY['comparação de médias', 'distribuição normal', 'amostras independentes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a aplicação do teste qui-quadrado?', 'O teste qui-quadrado avalia associações entre variáveis categóricas, como relação entre tabagismo e câncer de pulmão. É usado para testes de independência ou bondade de ajuste em tabelas de contingência. Assume amostras grandes e frequências esperadas mínimas de 5.', 'saude_coletiva', 'Bioestatística', 'qui-quadrado', ARRAY['variáveis categóricas', 'associação', 'tabelas de contingência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa o valor-p em uma análise estatística?', 'O valor-p é a probabilidade de obter resultados tão extremos quanto os observados, assumindo a nulidade verdadeira, indicando significância estatística. Um valor-p menor que 0,05 sugere rejeição da hipótese nula em convenção. Não mede o tamanho do efeito, apenas a compatibilidade com a nulidade.', 'saude_coletiva', 'Bioestatística', 'valor-p', ARRAY['significância', 'hipótese nula', 'probabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os tipos de amostragem em estudos epidemiológicos?', 'A amostragem aleatória simples garante igual chance a cada elemento, reduzindo viés, enquanto estratificada divide a população em subgrupos. Em bioestatística, a amostragem por conglomerados é útil para populações dispersas como municípios. O tamanho da amostra deve considerar margem de erro e confiança.', 'saude_coletiva', 'Bioestatística', 'amostragem', ARRAY['aleatória', 'estratificada', 'viés de seleção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é viés em pesquisa estatística?', 'Viés é um erro sistemático que distorce a estimativa do parâmetro populacional, como viés de seleção ao excluir grupos. Em estudos de saúde, o viés de informação ocorre na coleta de dados enviesada. Medidas preventivas incluem randomização e cegamento para minimizar impactos.', 'saude_coletiva', 'Bioestatística', 'viés', ARRAY['erro sistemático', 'seleção', 'informação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que foi a Reforma Psiquiátrica no Brasil?', 'A Reforma Psiquiátrica, iniciada na década de 1980, visou substituir manicômios por serviços comunitários, promovendo direitos humanos. Baseada na Lei 10.216/2001, enfatiza o tratamento em liberdade e reinserção social. Influenciada pelo movimento antimanicomial, integrou a saúde mental ao SUS.', 'saude_coletiva', 'Saúde mental coletiva', 'reforma psiquiátrica', ARRAY['antimanicomial', 'direitos humanos', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos CAPS na rede de saúde mental?', 'Os Centros de Atenção Psicossocial (CAPS) oferecem atendimento integral em saúde mental, com modalidades como CAPS I, II, III e AD para álcool e drogas. Funcionam como portas de entrada para casos graves, evitando hospitalizações desnecessárias. Integram a RAPS, promovendo reabilitação psicossocial.', 'saude_coletiva', 'Saúde mental coletiva', 'CAPS', ARRAY['atenção psicossocial', 'RAPS', 'reabilitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Rede de Atenção Psicossocial (RAPS)?', 'A RAPS é o conjunto de serviços e dispositivos para assistência em saúde mental no SUS, incluindo CAPS, leitos em hospitais gerais e serviços residenciais. Objetiva o cuidado integral e territorializado, com articulação entre atenção primária e especializada. Regulamentada pela Portaria GM/MS nº 3.088/2011.', 'saude_coletiva', 'Saúde mental coletiva', 'RAPS', ARRAY['rede de atenção', 'SUS', 'cuidado integral']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a saúde mental é integrada à atenção primária?', 'Na atenção primária, a saúde mental é abordada pela Estratégia Saúde da Família, com matriciamento para suporte a equipes. Identifica transtornos comuns como depressão e ansiedade precocemente. Promove acolhimento e referência para serviços especializados quando necessário.', 'saude_coletiva', 'Saúde mental coletiva', 'saúde mental na atenção primária', ARRAY['ESF', 'matriciamento', 'transtornos comuns']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os transtornos mentais comuns na população?', 'Transtornos comuns incluem depressão, ansiedade e estresse, afetando cerca de 20% da população adulta brasileira. São gerenciáveis na atenção básica com intervenções psicossociais e farmacológicas. Fatores de risco envolvem violência, pobreza e eventos estressantes.', 'saude_coletiva', 'Saúde mental coletiva', 'transtornos comuns', ARRAY['depressão', 'ansiedade', 'atenção básica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a abordagem ao uso de substâncias na saúde coletiva?', 'O uso de substâncias como álcool e drogas é tratado como questão de saúde pública, com ênfase em redução de danos e CAPS AD. Políticas incluem prevenção escolar e tratamento comunitário, evitando criminalização. Integra a RAPS para reabilitação integral.', 'saude_coletiva', 'Saúde mental coletiva', 'uso de substâncias', ARRAY['redução de danos', 'CAPS AD', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é matriciamento em saúde mental?', 'Matriciamento é o processo de apoio e supervisão de equipes de atenção primária por especialistas em saúde mental, fortalecendo a resolução local. Promove discussões de casos e capacitação contínua. Essencial para a integração entre níveis de atenção no SUS.', 'saude_coletiva', 'Saúde mental coletiva', 'matriciamento', ARRAY['apoio matricial', 'capacitação', 'integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como manejar crises em saúde mental coletiva?', 'O manejo de crises envolve acolhimento imediato em UPAs ou CAPS, priorizando não farmacologização excessiva e avaliação de risco suicida. Usa estratégias de desescalada e referência para internação breve se necessário. Enfatiza direitos do usuário e rede de apoio familiar.', 'saude_coletiva', 'Saúde mental coletiva', 'crise', ARRAY['acolhimento', 'risco suicida', 'desescalada']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa desinstitucionalização na psiquiatria?', 'Desinstitucionalização é o processo de fechamento de manicômios e transferência de cuidados para serviços comunitários, promovendo autonomia. No Brasil, avançou com a Reforma Psiquiátrica, reduzindo leitos psiquiátricos em 70%. Desafios incluem moradias assistidas e inclusão social.', 'saude_coletiva', 'Saúde mental coletiva', 'desinstitucionalização', ARRAY['reforma psiquiátrica', 'comunitário', 'autonomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os princípios da Lei 10.216/2001?', 'A Lei 10.216/2001 institui a proteção e os direitos das pessoas com transtornos mentais, garantindo tratamento humanizado e livre de constrangimentos. Proíbe práticas desumanas como eletrochoque sem consentimento e manicomialização. Integra a saúde mental ao SUS como direito universal.', 'saude_coletiva', 'Saúde mental coletiva', 'lei 10.216', ARRAY['direitos', 'tratamento humanizado', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os impactos da poluição do ar na saúde pública?', 'A poluição do ar, de fontes como veículos e indústrias, causa doenças respiratórias e cardiovasculares, com 7 milhões de mortes anuais globalmente. No Brasil, afeta populações urbanas, aumentando internações por asma e infarto. Medidas incluem monitoramento e padrões de qualidade pela ANVISA.', 'saude_coletiva', 'Saúde ambiental', 'poluição do ar', ARRAY['doenças respiratórias', 'monitoramento', 'ANVISA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como avaliar a qualidade da água para consumo humano?', 'A qualidade da água é avaliada por parâmetros físico-químicos, microbiológicos e sensoriais, conforme Portaria GM/MS nº 888/2021. Indicadores incluem coliformes totais e turbidez, visando prevenir diarreias e hepatites. Vigilância sanitária realiza análises periódicas em sistemas públicos.', 'saude_coletiva', 'Saúde ambiental', 'qualidade da água', ARRAY['parâmetros microbiológicos', 'vigilância sanitária', 'portaria 888']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância do saneamento básico no Brasil?', 'O saneamento básico, incluindo água, esgoto e lixo, reduz mortalidade por doenças infecciosas em até 90%. No SUS, é pilar para equidade em saúde, com metas do Plano Nacional de Saneamento. Cobertura incompleta afeta 35 milhões de brasileiros com água inadequada.', 'saude_coletiva', 'Saúde ambiental', 'saneamento', ARRAY['doenças infecciosas', 'equidade', 'plano nacional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como gerenciar resíduos sólidos em saúde coletiva?', 'A gestão de resíduos sólidos segue a PNRS, com coleta seletiva, reciclagem e tratamento para minimizar impactos ambientais. Em saúde, resíduos infectantes requerem incineração ou autoclave para prevenir infecções. Educação ambiental promove redução no fonte.', 'saude_coletiva', 'Saúde ambiental', 'resíduos sólidos', ARRAY['PNRS', 'reciclagem', 'resíduos infectantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os riscos dos agrotóxicos à saúde humana?', 'Agrotóxicos causam intoxicações agudas e crônicas, como câncer e distúrbios endócrinos, com 500 mil casos anuais no mundo. No Brasil, maior consumidor global, vigilância monitora resíduos em alimentos pela ANVISA. Políticas visam agricultura sustentável e redução de uso.', 'saude_coletiva', 'Saúde ambiental', 'agrotóxicos', ARRAY['intoxicação', 'ANVISA', 'sustentabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como as mudanças climáticas afetam a saúde pública?', 'Mudanças climáticas intensificam eventos extremos, vetores de doenças como dengue e desnutrição, afetando 250 mil mortes anuais até 2030. No Brasil, impacta populações vulneráveis em áreas costeiras e amazônicas. Estratégias incluem adaptação e mitigação via SUS.', 'saude_coletiva', 'Saúde ambiental', 'mudanças climáticas', ARRAY['vetores', 'adaptação', 'vulnerabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são zoonoses e exemplos no Brasil?', 'Zoonoses são doenças transmissíveis de animais a humanos, como raiva e leptospirose, com reservatórios silvestres ou domésticos. Vigilância epidemiológica monitora surtos em áreas urbanas periurbanas. Prevenção envolve vacinação animal e controle de roedores.', 'saude_coletiva', 'Saúde ambiental', 'zoonoses', ARRAY['transmissão', 'vigilância', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais estratégias de controle de vetores?', 'Controle de vetores como Aedes aegypti usa eliminação de criadouros, inseticidas e armadilhas para arboviroses. No Brasil, o Programa Nacional de Controle integra educação comunitária. Resistência inseticida requer monitoramento contínuo.', 'saude_coletiva', 'Saúde ambiental', 'vetores', ARRAY['Aedes', 'criadouros', 'resistência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a vigilância ambiental em saúde?', 'Vigilância ambiental monitora fatores ambientais como qualidade do ar e água para prevenir riscos à saúde. Integra ações intersetoriais com meio ambiente e saneamento. No SUS, apoia decisões em políticas públicas preventivas.', 'saude_coletiva', 'Saúde ambiental', 'vigilância ambiental', ARRAY['fatores de risco', 'intersetorial', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como responder a desastres ambientais na saúde?', 'Resposta a desastres envolve planos de contingência, evacuação e assistência médica imediata para lesões e infecções. No Brasil, o COBRADE coordena ações com SUS para enchentes e secas. Foco em populações vulneráveis e recuperação pós-desastre.', 'saude_coletiva', 'Saúde ambiental', 'desastres', ARRAY['contingência', 'COBRADE', 'recuperação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas estão no calendário vacinal infantil?', 'O calendário vacinal infantil inclui BCG, hepatite B, penta, VIP/VOP, rotavírus, pneumocócica e meningocócica, administradas em salas de vacinas do SUS. Protege contra 20 doenças graves nos primeiros anos de vida. Esquema é gratuito e universal para cobertura ampla.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'calendário vacinal', ARRAY['infantil', 'SUS', 'cobertura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a cadeia de frio no PNI?', 'A cadeia de frio mantém vacinas em temperaturas de 2-8°C desde produção até aplicação, usando geladeiras e termômetros. Essencial para preservar potência de vacinas termolábeis como varicela. Monitoramento diário previne perdas e falhas imunológicas.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'cadeia de frio', ARRAY['temperatura', 'armazenamento', 'monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como calcular a cobertura vacinal?', 'Cobertura vacinal é o percentual de doses aplicadas na população-alvo, visando 95% para imunidade coletiva. Calculada via SI-PNI com dados de salas de vacinas. Baixa cobertura aumenta surtos, como sarampo em 2019 no Brasil.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'cobertura vacinal', ARRAY['percentual', 'imunidade coletiva', 'SI-PNI']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o EAPV no programa de imunizações?', 'O Estudo de Avaliação da Cobertura Vacinal (EAPV) estima cobertura em domicílios por amostragem, corrigindo subnotificação. Realizado a cada 5 anos pelo IBGE para o PNI. Auxilia planejamento de campanhas e alocação de doses.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'EAPV', ARRAY['avaliação', 'amostragem', 'IBGE']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as características das campanhas de vacinação?', 'Campanhas de vacinação são periódicas para reforço ou surtos, como multivacinação para inadimplentes. Mobilizam equipes de saúde e mídia para alta adesão. No PNI, exemplos incluem influenza anual e HPV para adolescentes.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'campanhas', ARRAY['reforço', 'adesão', 'multivacinação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que causa hesitação vacinal e como combatê-la?', 'Hesitação vacinal surge de desinformação, desconfiança e mitos, reduzindo coberturas como no caso do sarampo. Combate via educação em saúde, comunicação clara e envolvimento comunitário. PNI promove confiança com evidências científicas.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'hesitação vacinal', ARRAY['desinformação', 'educação', 'confiança']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o funcionamento da sala de vacinas?', 'A sala de vacinas é unidade básica equipada para imunização, com registro no SI-PNI e manutenção da cadeia de frio. Realiza aplicação, orientação e notificação de eventos adversos. Deve seguir normas de biossegurança e acessibilidade.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'sala de vacinas', ARRAY['imunização', 'SI-PNI', 'biossegurança']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que compõe a rede de frio no PNI?', 'A rede de frio inclui armazéns regionais, municipais e salas de vacinas com equipamentos refrigerados e veículos para transporte. Garante distribuição equitativa de imunobiológicos. Treinamento de profissionais é obrigatório para manutenção.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'rede de frio', ARRAY['distribuição', 'equipamentos', 'treinamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do SI-PNI nos imunizações?', 'O SI-PNI é o sistema informatizado que registra doses, calcula coberturas e gera relatórios para o PNI. Facilita rastreabilidade de lotes e alertas de estoque. Integra dados nacionais para monitoramento epidemiológico.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'SI-PNI', ARRAY['registro', 'cobertura', 'rastreabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as metas do PNI para coberturas vacinais?', 'Metas do PNI visam 95% de cobertura para vacinas básicas, como poliomielite e sarampo, para erradicação. Monitoradas anualmente, ajustam estratégias em áreas de risco. Contribui para redução de incidência de doenças preveníveis.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'metas', ARRAY['95%', 'erradicação', 'monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema de Informações sobre Mortalidade (SIM)?', 'O SIM registra óbitos no Brasil via DATASUS, codificados pela CID-10 para análise de causas. Fornece indicadores como coeficiente de mortalidade para planejamento em saúde. Cobertura nacional desde 1975, com integração ao SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIM', ARRAY['óbitos', 'CID-10', 'DATASUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a função do SINASC?', 'O SINASC declara nascimentos vivos, coletando dados sobre peso, Apgar e assistência pré-natal para indicadores neonatais. Gerenciado pelo DATASUS, identifica riscos como prematuridade. Essencial para políticas de saúde materno-infantil no SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SINASC', ARRAY['nascimentos', 'indicadores neonatais', 'materno-infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que registra o SINAN?', 'O SINAN notifica agravos de notificação compulsória, como dengue e tuberculose, para vigilância epidemiológica. Permite análise temporal e espacial de surtos via DATASUS. Facilita resposta rápida e investigação de casos.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SINAN', ARRAY['notificação', 'agravos', 'vigilância']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona o SIH/SUS?', 'O SIH autoriza e registra internações hospitalares no SUS, gerando AIH para pagamento e análise de produção. Monitora leitos e custos, com dados no DATASUS. Contribui para equidade no acesso a procedimentos complexos.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIH', ARRAY['internações', 'AIH', 'equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o propósito do SIA?', 'O SIA registra procedimentos ambulatoriais no SUS, como consultas e exames, para controle de produção e financiamento. Integra dados do DATASUS para indicadores de acesso primário. Auxilia na alocação de recursos por região.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIA', ARRAY['procedimentos', 'ambulatorial', 'financiamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o e-SUS?', 'O e-SUS é o sistema eletrônico para atenção primária, registrando atendimentos na ESF com prontuário digital. Padroniza dados para integração nacional via DATASUS. Melhora qualidade e continuidade do cuidado em saúde básica.', 'saude_coletiva', 'Sistemas de informação em saúde', 'e-SUS', ARRAY['prontuário digital', 'ESF', 'integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais benefícios do prontuário eletrônico em saúde?', 'O prontuário eletrônico facilita acesso a histórico clínico, reduz erros e integra sistemas como e-SUS. No SUS, promove interoperabilidade entre níveis de atenção. Privacidade é garantida pela LGPD, com treinamento obrigatório.', 'saude_coletiva', 'Sistemas de informação em saúde', 'prontuário eletrônico', ARRAY['interoperabilidade', 'LGPD', 'continuidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são indicadores em sistemas de informação em saúde?', 'Indicadores são métricas como taxa de mortalidade infantil derivadas de SIM e SINASC para avaliação de desempenho. Calculados pelo DATASUS, orientam políticas e monitoramento. Devem ser sensíveis, específicos e atualizáveis.', 'saude_coletiva', 'Sistemas de informação em saúde', 'indicadores', ARRAY['métricas', 'avaliação', 'políticas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do DATASUS nos sistemas de saúde?', 'O DATASUS desenvolve e mantém sistemas como SIM e SINAN, processando dados para tabulação e análise. Fornece tabnet para consultas públicas e relatórios epidemiológicos. Essencial para evidências em planejamento do SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'DATASUS', ARRAY['processamento', 'tabnet', 'evidências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a tabulação de dados em saúde?', 'Tabulação organiza dados brutos em tabelas para análise descritiva, como frequências por idade em SINAN. Usada no DATASUS para gerar boletins e estudos. Facilita identificação de padrões e tendências epidemiológicas.', 'saude_coletiva', 'Sistemas de informação em saúde', 'tabulação', ARRAY['análise descritiva', 'padrões', 'epidemiológicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o financiamento do SUS no Brasil?', 'O Sistema Único de Saúde (SUS) é financiado por recursos da União, estados e municípios, com percentuais mínimos constitucionais: 15% da receita corrente líquida para municípios e 12% para estados. Esses recursos são usados para custear ações e serviços de saúde pública. A coordenação ocorre via Fundo Nacional de Saúde.', 'saude_coletiva', 'Gestão em saúde', 'Financiamento SUS', ARRAY['SUS','financiamento','gestão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os princípios do planejamento em saúde no SUS?', 'O planejamento em saúde no SUS segue princípios como descentralização, participação social e integralidade. Envolve a elaboração de planos de saúde tripartite (União, estados, municípios) com metas baseadas em necessidades populacionais. Ferramentas incluem o Plano Nacional de Saúde e pactuações regionais.', 'saude_coletiva', 'Gestão em saúde', 'Planejamento em saúde', ARRAY['SUS','planejamento','princípios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o PNASS?', 'O Plano Nacional de Ações e Serviços de Saúde (PNASS) é um instrumento de planejamento do SUS que define diretrizes, objetivos e metas para o período de quatro anos. Ele orienta a programação e alocação de recursos em saúde. É elaborado pelo Ministério da Saúde com participação de entes federativos.', 'saude_coletiva', 'Gestão em saúde', 'PNASS', ARRAY['SUS','PNASS','planejamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a acreditação em serviços de saúde?', 'A acreditação é um processo voluntário de avaliação externa da qualidade de serviços de saúde, realizado por organizações certificadoras. No SUS, promove a melhoria contínua e a segurança do paciente. Certificados como ONA (Organização Nacional de Acreditação) atestam conformidade com padrões nacionais.', 'saude_coletiva', 'Gestão em saúde', 'Acreditação', ARRAY['SUS','acreditação','qualidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a gestão de contratos em saúde pública?', 'A gestão de contratos em saúde pública inclui a elaboração, fiscalização e avaliação de convênios e contratos com prestadores de serviços. No SUS, segue normas da Lei 8.666/93 e princípios de transparência e eficiência. Visa garantir a prestação adequada de serviços à população.', 'saude_coletiva', 'Gestão em saúde', 'Gestão de contratos', ARRAY['SUS','contratos','gestão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do Fundo Nacional de Saúde no SUS?', 'O Fundo Nacional de Saúde (FNS) é o instrumento de programação e controle dos recursos federais para o SUS. Gerencia transferências constitucionais e repasses a estados e municípios. Assegura a equidade na distribuição de recursos com base em critérios populacionais e epidemiológicos.', 'saude_coletiva', 'Gestão em saúde', 'Financiamento SUS', ARRAY['SUS','FNS','recursos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como é estruturado o planejamento municipal de saúde?', 'O planejamento municipal de saúde é realizado via Plano Municipal de Saúde, alinhado ao PMS e PNASS. Inclui diagnóstico situacional, definição de metas e alocação de recursos. Envolve participação do Conselho de Saúde e segue diretrizes de regionalização.', 'saude_coletiva', 'Gestão em saúde', 'Planejamento em saúde', ARRAY['SUS','municipal','planejamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os objetivos principais do PNASS?', 'Os objetivos do PNASS incluem fortalecer a gestão do SUS, ampliar o acesso a serviços e promover a equidade em saúde. Define ações para prevenção, promoção e reabilitação. É revisado periodicamente para atender novas demandas sanitárias.', 'saude_coletiva', 'Gestão em saúde', 'PNASS', ARRAY['SUS','PNASS','objetivos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a certificação de qualidade em saúde?', 'A certificação de qualidade em saúde é um reconhecimento formal de que uma instituição atende padrões mínimos de segurança e eficiência. No contexto SUS, integra-se à acreditação e avalia processos assistenciais. Contribui para a humanização e resolutividade dos serviços.', 'saude_coletiva', 'Gestão em saúde', 'Acreditação', ARRAY['SUS','certificação','qualidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais normas regem os contratos no SUS?', 'Os contratos no SUS são regidos pela Lei de Licitações (8.666/93) e pela Lei Complementar 141/2012, que exige aplicação mínima de recursos em saúde. Incluem cláusulas de monitoramento e sanções por descumprimento. Promovem a accountability na gestão pública.', 'saude_coletiva', 'Gestão em saúde', 'Gestão de contratos', ARRAY['SUS','normas','contratos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Carta de Ottawa na promoção da saúde?', 'A Carta de Ottawa, de 1986, é um marco da promoção da saúde, definindo-a como processo de capacitação da comunidade para atuar em saúde. Propõe cinco áreas de ação: construir políticas saudáveis, criar ambientes favoráveis, fortalecer ação comunitária, desenvolver habilidades pessoais e reorientar serviços. Influencia políticas globais da OMS.', 'saude_coletiva', 'Promoção da saúde', 'Carta de Ottawa', ARRAY['promoção','Ottawa','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o PNPS no Brasil?', 'A Política Nacional de Promoção da Saúde (PNPS), instituída em 2006, visa ampliar a promoção da saúde no SUS. Integra ações intersetoriais para enfrentar determinantes sociais da saúde. Prioriza agendas como alimentação saudável, atividade física e redução de violência.', 'saude_coletiva', 'Promoção da saúde', 'PNPS', ARRAY['SUS','PNPS','promoção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são cidades saudáveis?', 'Cidades saudáveis são iniciativas baseadas na Carta de Ottawa para criar ambientes urbanos que promovam saúde. Envolvem planejamento urbanístico, mobilidade sustentável e participação comunitária. No Brasil, integram-se ao SUS via projetos locais de promoção.', 'saude_coletiva', 'Promoção da saúde', 'Cidades saudáveis', ARRAY['cidades','saudáveis','urbanismo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é ação intersetorial na saúde?', 'A ação intersetorial na promoção da saúde envolve articulação entre setores como educação, transporte e meio ambiente para enfrentar determinantes sociais. Promove políticas integradas e compartilhamento de responsabilidades. É princípio da PNPS e Carta de Ottawa.', 'saude_coletiva', 'Promoção da saúde', 'Ação intersetorial', ARRAY['intersetorial','promoção','determinantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa empoderamento em saúde?', 'O empoderamento em saúde é o processo de capacitação de indivíduos e comunidades para tomar decisões que afetem sua saúde. Baseia-se na educação e participação ativa. É uma estratégia chave da promoção da saúde segundo a OMS.', 'saude_coletiva', 'Promoção da saúde', 'Empoderamento', ARRAY['empoderamento','comunidade','capacitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as cinco ações da Carta de Ottawa?', 'As cinco ações são: construir políticas públicas saudáveis, criar ambientes suportivos, fortalecer comunidades, desenvolver habilidades pessoais e reorientar serviços de saúde. Elas guiam intervenções globais em promoção. Aplicam-se a contextos locais no SUS.', 'saude_coletiva', 'Promoção da saúde', 'Carta de Ottawa', ARRAY['Ottawa','ações','promoção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a PNPS se integra ao SUS?', 'A PNPS integra-se ao SUS por meio de pactuações em planos de saúde e financiamento de ações territoriais. Enfatiza a Atenção Básica como porta de entrada. Avalia impactos via indicadores de saúde populacional.', 'saude_coletiva', 'Promoção da saúde', 'PNPS', ARRAY['SUS','PNPS','integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais projetos exemplificam cidades saudáveis no Brasil?', 'Projetos como o Programa Cidades Saudáveis em municípios brasileiros promovem hortas comunitárias e ciclovias. Visam reduzir desigualdades urbanas em saúde. São apoiados pela OPAS e Ministério da Saúde.', 'saude_coletiva', 'Promoção da saúde', 'Cidades saudáveis', ARRAY['cidades','projetos','saudáveis']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que a ação intersetorial é essencial?', 'A ação intersetorial é essencial porque a saúde depende de fatores além do setor saúde, como saneamento e educação. Evita abordagens fragmentadas e potencializa resultados. Exemplos incluem políticas de segurança alimentar.', 'saude_coletiva', 'Promoção da saúde', 'Ação intersetorial', ARRAY['intersetorial','essencial','setores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover o empoderamento comunitário?', 'Promove-se via educação em saúde, conselhos participativos e projetos locais. Encoraja a liderança comunitária em diagnósticos e soluções. Resulta em maior adesão a práticas saudáveis.', 'saude_coletiva', 'Promoção da saúde', 'Empoderamento', ARRAY['empoderamento','comunidade','educação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é educação popular em saúde?', 'A educação popular em saúde baseia-se na pedagogia de Paulo Freire, enfatizando o diálogo e a conscientização crítica. Visa transformar realidades sociais por meio da participação ativa. Aplicada em ações comunitárias do SUS.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['educação','popular','Freire']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a metodologia de Paulo Freire na educação em saúde?', 'A metodologia de Paulo Freire usa o método dialógico, partindo da realidade do educando para problematizar e agir. Em saúde, foca em temas geradores como saneamento e nutrição. Promove autonomia e mudança social.', 'saude_coletiva', 'Educação em saúde', 'Paulo Freire', ARRAY['Freire','metodologia','dialógico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é alfabetização em saúde?', 'Alfabetização em saúde é a capacidade de obter, processar e compreender informações básicas de saúde para tomar decisões. Reduz desigualdades no acesso a serviços. No SUS, é promovida via educação continuada.', 'saude_coletiva', 'Educação em saúde', 'Alfabetização em saúde', ARRAY['alfabetização','saúde','decisões']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos agentes comunitários de saúde?', 'Os agentes comunitários de saúde (ACS) atuam na Atenção Básica, visitando famílias para educação e prevenção. São formados em saúde familiar e participam de vigilância. São essenciais para a integralidade do cuidado no SUS.', 'saude_coletiva', 'Educação em saúde', 'Agentes comunitários', ARRAY['ACS','comunitários','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a educação popular difere da tradicional?', 'A educação popular é horizontal e libertadora, contrastando com a tradicional bancária que deposita conhecimento. Enfatiza a co-construção de saberes. Em saúde, empodera comunidades para ações coletivas.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['popular','tradicional','empoderamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais etapas da metodologia Freire em saúde?', 'Etapas incluem codificação (representação da realidade), decodificação (discussão crítica) e ação transformadora. Aplicadas em rodas de conversa sobre saúde. Levam à conscientização e intervenção comunitária.', 'saude_coletiva', 'Educação em saúde', 'Paulo Freire', ARRAY['Freire','etapas','conscientização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que a alfabetização em saúde é importante?', 'É importante para melhorar a adesão a tratamentos e prevenção de doenças. Reduz barreiras culturais e linguísticas no SUS. Medida por escalas como a de Toledo e Fernandes.', 'saude_coletiva', 'Educação em saúde', 'Alfabetização em saúde', ARRAY['alfabetização','adesão','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais atribuições dos ACS na educação?', 'Os ACS educam sobre higiene, vacinação e direitos em saúde durante visitas domiciliares. Mobilizam comunidades para mutirões. Integram equipes multiprofissionais da Estratégia Saúde da Família.', 'saude_coletiva', 'Educação em saúde', 'Agentes comunitários', ARRAY['ACS','educação','visitas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como aplicar educação popular em campanhas?', 'Em campanhas, usa-se diálogo com a comunidade para identificar necessidades e co-criar mensagens. Exemplos incluem educação em DST/AIDS com grupos vulneráveis. Resulta em maior impacto e sustentabilidade.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['campanhas','diálogo','comunidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é análise custo-efetividade em saúde?', 'A análise custo-efetividade avalia se uma intervenção em saúde gera benefícios proporcionais ao custo, usando indicadores como custo por ano de vida ganho. Ajuda na alocação eficiente de recursos escassos. É ferramenta da economia da saúde.', 'saude_coletiva', 'Economia da saúde', 'Custo-efetividade', ARRAY['custo','efetividade','análise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são DALYs?', 'Os DALYs (Anos de Vida Ajustados por Incapacidade) medem o ônus de doença combinando mortalidade prematura e incapacidade. Usados pela OMS para priorizar intervenções. No Brasil, aplicados em avaliações do SUS.', 'saude_coletiva', 'Economia da saúde', 'DALY', ARRAY['DALY','ônus','doença']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é QALY?', 'O QALY (Ano de Vida Ajustado pela Qualidade) quantifica a qualidade e quantidade de vida gerada por uma intervenção. Varia de 0 (morte) a 1 (saúde perfeita). Útil em análises custo-utilidade para decisões em saúde.', 'saude_coletiva', 'Economia da saúde', 'QALY', ARRAY['QALY','qualidade','vida']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é avaliação de tecnologias em saúde?', 'A avaliação de tecnologias em saúde (ATS) examina eficácia, segurança e impacto econômico de medicamentos, procedimentos e equipamentos. No Brasil, coordenada pela CONITEC no SUS. Orienta incorporação ao sistema público.', 'saude_coletiva', 'Economia da saúde', 'ATS', ARRAY['ATS','tecnologias','CONITEC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é equidade no financiamento de saúde?', 'Equidade no financiamento de saúde aloca recursos priorizando populações vulneráveis e regiões com maiores necessidades. Contrasta com igualdade ao considerar desigualdades sociais. Princípio do SUS e ODS da ONU.', 'saude_coletiva', 'Economia da saúde', 'Equidade', ARRAY['equidade','financiamento','desigualdades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como calcular custo-efetividade?', 'Calcula-se dividindo o custo incremental pela efetividade incremental (ex: custo por DALY evitado). Compara opções para escolher a mais eficiente. Considera perspectiva societal ou pagadora.', 'saude_coletiva', 'Economia da saúde', 'Custo-efetividade', ARRAY['cálculo','incremental','eficiência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a diferença entre DALY e QALY?', 'DALY foca em perda de saúde (mortalidade + incapacidade), enquanto QALY em ganho de vida ajustada por qualidade. Ambos medem impacto, mas DALY é mais usado em ônus populacional. Aplicáveis em avaliações econômicas.', 'saude_coletiva', 'Economia da saúde', 'DALY', ARRAY['DALY','QALY','diferença']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o QALY é usado no SUS?', 'No SUS, QALY auxilia na priorização de tecnologias via CONITEC, avaliando custo por QALY ganho. Limiar de custo-utilidade define viabilidade. Promove alocação racional de recursos limitados.', 'saude_coletiva', 'Economia da saúde', 'QALY', ARRAY['SUS','QALY','priorização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais etapas da ATS no Brasil?', 'Etapas incluem protocolização, produção de evidências, consulta pública e decisão da CONITEC. Envolve stakeholders como sociedades médicas. Resulta em incorporação ou não ao SUS.', 'saude_coletiva', 'Economia da saúde', 'ATS', ARRAY['ATS','etapas','CONITEC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover equidade no financiamento?', 'Promove-se via fórmulas de repasse que ponderam vulnerabilidade social e epidemiológica. Inclui incentivos para regiões pobres. Monitorado pela Lei Complementar 141/2012.', 'saude_coletiva', 'Economia da saúde', 'Equidade', ARRAY['equidade','repasse','vulnerabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o modelo de Dahlgren-Whitehead?', 'O modelo de Dahlgren-Whitehead representa determinantes sociais da saúde em camadas: individual, comportamental, redes sociais, condições de vida e econômicas, macros políticas. Ilustra influências amplas na saúde. Usado em políticas de equidade.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Dahlgren-Whitehead', ARRAY['determinantes','modelo','equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a CNDSS?', 'A Comissão Nacional sobre Determinantes Sociais da Saúde (CNDSS) foi criada no Brasil em 2008 para propor ações intersetoriais contra desigualdades. Inspirada na OMS, produziu relatórios e recomendações. Influenciou a PNS e políticas sociais.', 'saude_coletiva', 'Determinantes sociais da saúde', 'CNDSS', ARRAY['CNDSS','determinantes','desigualdades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são desigualdades sociais em saúde?', 'Desigualdades sociais em saúde são diferenças evitáveis e injustas em indicadores de saúde entre grupos sociais. Causadas por pobreza, raça e gênero. No Brasil, evidenciadas por mortalidade infantil em regiões Norte/Nordeste.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Desigualdades sociais', ARRAY['desigualdades','saúde','injustas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais condições de vida afetam a saúde?', 'Condições de vida como moradia precária, saneamento inadequado e acesso a educação impactam diretamente a saúde. São determinantes sociais modificáveis por políticas públicas. Relacionam-se a morbimortalidade evitável.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Condições de vida', ARRAY['condições','vida','saneamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são determinantes sociais da saúde?', 'Determinantes sociais da saúde são fatores econômicos, sociais e ambientais que influenciam a saúde além dos biológicos. Incluem renda, educação e suporte social. Abordados pela OMS para reduzir desigualdades.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Determinantes sociais', ARRAY['determinantes','sociais','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o modelo Dahlgren-Whitehead é aplicado?', 'Aplicado em análises de políticas para identificar intervenções em camadas externas como emprego e ambiente. No SUS, orienta ações intersetoriais. Ajuda a prevenir doenças crônicas ligadas a desigualdades.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Dahlgren-Whitehead', ARRAY['modelo','aplicação','intersetorial']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais recomendações da CNDSS?', 'Recomendações incluem fortalecer intersetorialidade, investir em Atenção Básica e monitorar indicadores sociais. Enfatiza redução de desigualdades raciais e regionais. Base para a Estratégia Nacional de Saúde e Desenvolvimento.', 'saude_coletiva', 'Determinantes sociais da saúde', 'CNDSS', ARRAY['CNDSS','recomendações','intersetorialidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como medir desigualdades em saúde?', 'Medem-se por índices como razão de mortalidade entre grupos ou índice de Gini adaptado para saúde. Estudos epidemiológicos revelam padrões. Políticas visam reduzir gradientes sociais.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Desigualdades sociais', ARRAY['medir','índices','epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais intervenções melhoram condições de vida?', 'Intervenções incluem programas de saneamento, habitação digna e acesso a água potável. No Brasil, via PAC e SUS. Reduzem incidência de doenças infecciosas e melhoram qualidade de vida.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Condições de vida', ARRAY['intervenções','saneamento','habitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que os determinantes sociais são prioritários?', 'São prioritários porque explicam 80% das variações em saúde populacional, segundo a OMS. Exigem abordagens além do setor saúde. No Brasil, integram a agenda da CNDSS e PNS.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Determinantes sociais', ARRAY['prioritários','OMS','PNS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual é o principal desafio na saúde indígena no Brasil, segundo o SUS?', 'O principal desafio na saúde indígena é a garantia de acesso equitativo aos serviços de saúde, considerando a diversidade cultural e geográfica dos povos. O Subsistema de Atenção à Saúde Indígena (SasiSUS) foi criado para coordenar ações específicas, como atendimento diferenciado e respeito às práticas tradicionais. Epidemias como malária e tuberculose ainda afetam comunidades isoladas devido à falta de infraestrutura.', 'saude_coletiva', 'Saúde indígena', 'Acesso e políticas', ARRAY['SUS', 'SasiSUS', 'Epidemias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a Política Nacional de Atenção à Saúde dos Povos Indígenas?', 'A Política Nacional de Atenção à Saúde dos Povos Indígenas, instituída em 2002, visa promover a integralidade do cuidado, respeitando a autonomia cultural e os direitos ancestrais. Ela enfatiza a participação comunitária na gestão de saúde e a formação de profissionais sensíveis à etnicidade. Ações incluem vigilância epidemiológica e promoção de saúde em terras indígenas.', 'saude_coletiva', 'Saúde indígena', 'Políticas nacionais', ARRAY['Política Nacional', 'Autonomia cultural', 'Vigilância']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS atende emergências em áreas indígenas remotas?', 'O SUS atende emergências em áreas indígenas remotas por meio de equipes multidisciplinares, incluindo Casas de Saúde Indígena e unidades móveis. A articulação com a Força Aérea Brasileira facilita o transporte aeromédico em casos graves. Prioriza-se o atendimento culturalmente adequado para reduzir morbimortalidade por doenças infecciosas.', 'saude_coletiva', 'Saúde indígena', 'Atendimento emergencial', ARRAY['SUS', 'Casas de Saúde', 'Aeromédico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os principais agravos de saúde em povos indígenas?', 'Os principais agravos incluem doenças infecciosas como malária, tuberculose e diarreias, além de desnutrição e problemas respiratórios crônicos. Fatores como pobreza, saneamento precário e perda de territórios contribuem para alta vulnerabilidade. Programas de imunização e educação em saúde visam mitigar esses riscos.', 'saude_coletiva', 'Saúde indígena', 'Agravos epidemiológicos', ARRAY['Doenças infecciosas', 'Desnutrição', 'Imunização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos Distritos Sanitários Especiais Indígenas (DSEI)?', 'Os DSEI são responsáveis pela execução de ações de saúde em terras indígenas, coordenando equipes de campo e unidades fixas. Eles promovem a integração entre medicina convencional e tradicional, garantindo suprimentos e vigilância. Cobrem regiões específicas, como o DSEI Yanomami, para atender populações isoladas.', 'saude_coletiva', 'Saúde indígena', 'DSEI', ARRAY['DSEI', 'Equipes de campo', 'Medicina tradicional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a saúde mental é abordada em comunidades indígenas?', 'A saúde mental em comunidades indígenas considera o impacto do contato com a sociedade não indígena, como perda cultural e violência. Abordagens incluem terapia comunitária e valorização de saberes ancestrais para tratar transtornos como depressão e suicídio. O SUS integra psicólogos e agentes indígenas para intervenções culturalmente sensíveis.', 'saude_coletiva', 'Saúde indígena', 'Saúde mental', ARRAY['Saúde mental', 'Terapia comunitária', 'Agentes indígenas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Atenção Diferenciada à Saúde Indígena?', 'A Atenção Diferenciada à Saúde Indígena é um princípio do SUS que adapta serviços às necessidades específicas, como mobilidade e práticas culturais. Inclui formação de recursos humanos e articulação intersetorial para proteção territorial. Visa reduzir desigualdades em indicadores de saúde como mortalidade infantil.', 'saude_coletiva', 'Saúde indígena', 'Atenção diferenciada', ARRAY['SUS', 'Formação profissional', 'Mortalidade infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são prioritárias para povos indígenas?', 'Vacinas prioritárias incluem BCG, hepatite B, poliomielite e contra influenza, devido à alta suscetibilidade a surtos. Campanhas anuais no SUS visam cobertura vacinal acima de 95% em terras indígenas. A logística desafiadora exige armazenamento em cadeia de frio adaptada.', 'saude_coletiva', 'Saúde indígena', 'Imunização', ARRAY['Vacinas', 'Cobertura vacinal', 'Cadeia de frio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o tabagismo afeta a saúde indígena?', 'O tabagismo introduzido por contato cultural aumenta riscos de doenças respiratórias e cardiovasculares em indígenas. Programas de cessação usam abordagens comunitárias, integrando xamãs e educação preventiva. O SUS monitora prevalência para políticas antitabaco específicas.', 'saude_coletiva', 'Saúde indígena', 'Tabagismo', ARRAY['Tabagismo', 'Doenças respiratórias', 'Cessação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da participação indígena na gestão de saúde?', 'A participação indígena na gestão de saúde fortalece a democracia sanitária, com conselhos e conferências específicas. Permite priorizar demandas locais, como controle de endemias. A Lei 8.142/1990 garante representação em instâncias deliberativas.', 'saude_coletiva', 'Saúde indígena', 'Participação comunitária', ARRAY['Gestão participativa', 'Conselhos de saúde', 'Lei 8142']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o pré-natal de baixo risco na saúde da mulher?', 'O pré-natal de baixo risco na saúde da mulher envolve consultas regulares para monitorar gestação sem complicações, iniciando no primeiro trimestre. Inclui exames como ultrassom e testes laboratoriais para detectar anemias ou infecções precocemente. O SUS recomenda pelo menos seis consultas para reduzir mortalidade materna e fetal.', 'saude_coletiva', 'Saúde da mulher', 'Pré-natal', ARRAY['Gestação', 'SUS', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Atenção Básica para mulheres?', 'A Política Nacional de Atenção Básica para mulheres promove equidade de gênero, com ênfase em prevenção de câncer de mama e colo do útero. Inclui rastreamento por mamografia e Papanicolau em unidades de saúde da família. Visa integralidade do cuidado, abordando violência e saúde reprodutiva.', 'saude_coletiva', 'Saúde da mulher', 'Políticas nacionais', ARRAY['Equidade de gênero', 'Rastreamento', 'Saúde reprodutiva']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS aborda a violência contra a mulher?', 'O SUS aborda a violência contra a mulher por meio da Rede de Atenção às Vítimas de Violência, com serviços de acolhimento e notificação compulsória. A Lei Maria da Penha integra ações de saúde e justiça para proteção. Equipes multidisciplinares oferecem suporte psicológico e médico em UBS e hospitais.', 'saude_coletiva', 'Saúde da mulher', 'Violência de gênero', ARRAY['Lei Maria da Penha', 'Notificação', 'Acolhimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza a menopausa na atenção à saúde da mulher?', 'A menopausa na atenção à saúde da mulher é um período de transição hormonal que pode causar sintomas como fogachos e osteoporose. O SUS oferece acompanhamento com reposição hormonal seletiva e promoção de atividade física. Rastreio de riscos cardiovasculares é essencial para qualidade de vida.', 'saude_coletiva', 'Saúde da mulher', 'Menopausa', ARRAY['Hormônios', 'Osteoporose', 'Riscos cardiovasculares']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os direitos reprodutivos da mulher no SUS?', 'Os direitos reprodutivos da mulher no SUS incluem planejamento familiar, contracepção gratuita e interrupção legal da gravidez em casos de risco à vida. A Lei 9.263/1996 regula o procedimento de esterilização voluntária após 25 anos. Educação sexual integral visa empoderamento e prevenção de DSTs.', 'saude_coletiva', 'Saúde da mulher', 'Direitos reprodutivos', ARRAY['Planejamento familiar', 'Lei 9263', 'Educação sexual']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir o câncer de colo do útero no Brasil?', 'A prevenção do câncer de colo do útero no Brasil envolve vacinação contra HPV para meninas de 9 a 14 anos e rastreamento anual por citologia oncótica a partir dos 25 anos. O SUS distribui vacinas e realiza exames em UBS para detecção precoce. Fatores de risco como tabagismo e múltiplos parceiros sexuais são abordados em educação em saúde.', 'saude_coletiva', 'Saúde da mulher', 'Câncer de colo', ARRAY['HPV', 'Citologia', 'Rastreamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Rede Cegonha no SUS?', 'A Rede Cegonha no SUS é uma estratégia para qualificar o cuidado materno-infantil, desde a pré-natal até o puerpério, com humanização no parto. Inclui regulação de vagas e transporte sanitário para regiões remotas. Reduziu a mortalidade materna em 20% desde 2011 por meio de articulação intersetorial.', 'saude_coletiva', 'Saúde da mulher', 'Rede Cegonha', ARRAY['Cuidado materno', 'Humanização', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da anemia na gestação?', 'A anemia na gestação impacta o desenvolvimento fetal, aumentando riscos de parto prematuro e baixo peso ao nascer. O SUS suplementa ferro e ácido fólico em pré-natais para prevenção, monitorando hemoglobina em consultas. Mulheres com anemia grave recebem transfusões em hospitais de referência.', 'saude_coletiva', 'Saúde da mulher', 'Anemia gestacional', ARRAY['Suplementação', 'Ferro', 'Pré-natal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS promove a saúde bucal da mulher?', 'O SUS promove a saúde bucal da mulher por meio do Brasil Sorridente, com atenção integrada à gestação para prevenir cáries e gengivites hormonais. Consultas odontológicas gratuitas em UBS incluem profilaxia e restaurações. Educação sobre higiene oral reduz desigualdades em acesso a serviços.', 'saude_coletiva', 'Saúde da mulher', 'Saúde bucal', ARRAY['Brasil Sorridente', 'Profilaxia', 'Higiene oral']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve o cuidado no puerpério?', 'O cuidado no puerpério envolve monitoramento de sangramento, infecções e saúde mental pós-parto, com visitas domiciliares pela equipe da ESF. O SUS incentiva amamentação exclusiva até seis meses e vacinação do recém-nascido. Acompanhamento psicológico previne depressão pós-parto em mulheres vulneráveis.', 'saude_coletiva', 'Saúde da mulher', 'Puerpério', ARRAY['Amamentação', 'ESF', 'Depressão pós-parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Saúde do Idoso?', 'A Política Nacional de Saúde do Idoso, de 1998, garante atendimento integral no SUS, com ênfase em prevenção de quedas e doenças crônicas. Inclui a Caderneta de Saúde da Pessoa Idosa para rastreio de hipertensão e diabetes. Promove envelhecimento ativo por meio de grupos de atividade física em UBS.', 'saude_coletiva', 'Saúde do idoso', 'Políticas nacionais', ARRAY['Envelhecimento ativo', 'SUS', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir quedas em idosos?', 'A prevenção de quedas em idosos envolve avaliação de risco em consultas geriátricas, com adaptações ambientais como corrimãos e remoção de tapetes. O SUS oferece órteses e fisioterapia para melhorar equilíbrio e força muscular. Exercícios regulares reduzem em 25% o risco de fraturas por osteoporose.', 'saude_coletiva', 'Saúde do idoso', 'Prevenção de quedas', ARRAY['Avaliação geriátrica', 'Fisioterapia', 'Osteoporose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a atenção à saúde mental do idoso?', 'A atenção à saúde mental do idoso aborda demências como Alzheimer, com diagnóstico precoce e suporte familiar no SUS. Grupos de convivência em CRAS previnem isolamento social e depressão. Medicamentos como inibidores de colinesterase são fornecidos gratuitamente em farmácias de alto custo.', 'saude_coletiva', 'Saúde do idoso', 'Saúde mental', ARRAY['Alzheimer', 'CRAS', 'Demências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são recomendadas para idosos?', 'Vacinas recomendadas para idosos incluem influenza anualmente, pneumocócica e herpes zoster para prevenir infecções graves. O SUS realiza campanhas nacionais com cobertura em UBS para reduzir hospitalizações. Idosos com comorbidades recebem prioridade em doses reforçadas.', 'saude_coletiva', 'Saúde do idoso', 'Imunização', ARRAY['Influenza', 'Pneumocócica', 'Campanhas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS cuida da incontinência urinária em idosos?', 'O SUS cuida da incontinência urinária em idosos com avaliação uroginecológica e treinamento de assoalho pélvico em fisioterapia. Medicamentos anticolinérgicos são prescritos para casos de urgência. Acompanhamento multiprofissional previne complicações como infecções recorrentes.', 'saude_coletiva', 'Saúde do idoso', 'Incontinência', ARRAY['Fisioterapia', 'Uroginecologia', 'Infecções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza o envelhecimento populacional no Brasil?', 'O envelhecimento populacional no Brasil resulta em 14% de idosos acima de 60 anos, aumentando demanda por serviços crônicos no SUS. Políticas como o Estatuto do Idoso garantem prioridade em atendimentos. Projeções indicam 30% da população idosa até 2050, exigindo planejamento sanitário.', 'saude_coletiva', 'Saúde do idoso', 'Envelhecimento demográfico', ARRAY['Estatuto do Idoso', 'SUS', 'Projeções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da ESF na saúde do idoso?', 'A ESF na saúde do idoso realiza visitas domiciliares para monitorar adesão medicamentosa e sinais vitais. Promove educação em saúde para autocuidado em hipertensos e diabéticos. A estratégia reduziu em 15% as internações por descompensação crônica.', 'saude_coletiva', 'Saúde do idoso', 'Estratégia Saúde da Família', ARRAY['Visitas domiciliares', 'Autocuidado', 'Internações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como tratar a osteoporose em idosos?', 'O tratamento da osteoporose em idosos inclui bifosfonatos e cálcio/vitamina D prescritos no SUS, com densitometria óssea para diagnóstico. Exercícios de impacto controlado fortalecem ossos e previnem fraturas. Rastreio em mulheres pós-menopausa é anual a partir dos 65 anos.', 'saude_coletiva', 'Saúde do idoso', 'Osteoporose', ARRAY['Bifosfonatos', 'Densitometria', 'Exercícios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a malnutrição em idosos?', 'A malnutrição em idosos é comum por perda de apetite e problemas dentários, levando a sarcopenia e imunossupressão. O SUS oferece suplementação nutricional em UBS e avaliação antropométrica regular. Programas de alimentação coletiva em ILPIs combatem desnutrição em institucionalizados.', 'saude_coletiva', 'Saúde do idoso', 'Nutrição', ARRAY['Sarcopenia', 'Suplementação', 'ILPIs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da reabilitação geriátrica?', 'A reabilitação geriátrica restaura funcionalidade após AVC ou fraturas, com fisioterapia e terapia ocupacional no SUS. Equipes interdisciplinares em CAPS e hospitais melhoram independência e qualidade de vida. Reduz custos com internações prolongadas em 30%.', 'saude_coletiva', 'Saúde do idoso', 'Reabilitação', ARRAY['Fisioterapia', 'AVC', 'CAPS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Relação Nacional de Medicamentos Essenciais (Rename)?', 'A Rename lista medicamentos essenciais para o SUS, priorizando custo-efetividade e evidências científicas para doenças prevalentes. Atualizada periodicamente, garante acesso gratuito em farmácias de unidades básicas. Inclui analgésicos, antibióticos e antirretrovirais para integralidade do cuidado.', 'saude_coletiva', 'Políticas de medicamentos', 'Rename', ARRAY['SUS', 'Custo-efetividade', 'Acesso gratuito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a Assistência Terapêutica Integral (ATI)?', 'A ATI fornece medicamentos de alto custo para doenças crônicas como HIV e câncer via Componente Especializado da Assistência Farmacêutica. Protocolos clínicos definem critérios de dispensação em farmácias especializadas. O SUS monitora adesão para otimizar recursos e resultados terapêuticos.', 'saude_coletiva', 'Políticas de medicamentos', 'ATI', ARRAY['Medicamentos alto custo', 'HIV', 'Protocolos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Farmácia Popular?', 'A Farmácia Popular subsidia medicamentos para hipertensão, diabetes e asma em farmácias credenciadas, com descontos de até 90%. Amplia acesso em áreas sem UBS, reduzindo desassistência. O programa atende 20 milhões de brasileiros anualmente com genéricos.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmácia Popular', ARRAY['Subsídios', 'Genéricos', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a dispensação racional de medicamentos?', 'A dispensação racional de medicamentos envolve orientação farmacêutica para adesão e prevenção de interações no SUS. Farmacêuticos em UBS verificam prescrições e promovem uso adequado. Reduz resistência antimicrobiana e iatrogenia em populações vulneráveis.', 'saude_coletiva', 'Políticas de medicamentos', 'Dispensação', ARRAY['Orientação farmacêutica', 'Adesão', 'Resistência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS gerencia medicamentos biológicos?', 'O SUS gerencia medicamentos biológicos para imunossupressão em transplantes e autoimunes via judicialização ou protocolos. A Conitec avalia incorporação com evidências de eficácia e segurança. Armazenamento em cadeia de frio é crucial para biossimilares distribuídos.', 'saude_coletiva', 'Políticas de medicamentos', 'Biológicos', ARRAY['Conitec', 'Protocolos', 'Judicialização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a política de controle de preços de medicamentos?', 'A política de controle de preços de medicamentos, pela CMED, limita reajustes anuais para genéricos e de referência, garantindo acessibilidade. Multas por descumprimento protegem o consumidor. Integra a Assistência Farmacêutica do SUS para equidade.', 'saude_coletiva', 'Políticas de medicamentos', 'Controle de preços', ARRAY['CMED', 'Genéricos', 'Acessibilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Farmacovigilância no Brasil?', 'A Farmacovigilância no Brasil monitora reações adversas a medicamentos via Notivisa, reportando à Anvisa para alertas e recalls. Profissionais de saúde notificam em UBS para melhorar segurança. Contribui para atualizações na Rename e protocolos SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmacovigilância', ARRAY['Notivisa', 'Anvisa', 'Reações adversas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover o uso de genéricos?', 'A promoção de genéricos envolve intercambialidade comprovada pela Anvisa, com preços até 35% menores que referência. O SUS prioriza em licitações para economia de recursos. Campanhas educativas aumentam confiança e adesão em tratamentos crônicos.', 'saude_coletiva', 'Políticas de medicamentos', 'Genéricos', ARRAY['Anvisa', 'Intercambialidade', 'Economia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da judicialização de medicamentos?', 'A judicialização de medicamentos sobrecarrega orçamentos do SUS com demandas por fármacos off-label, sem evidências robustas. A Conitec media incorporações para racionalidade. Representa 1,5% do PIB em saúde, exigindo políticas de mediação.', 'saude_coletiva', 'Políticas de medicamentos', 'Judicialização', ARRAY['Conitec', 'Off-label', 'Orçamento SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que regula a seleção de medicamentos essenciais?', 'A seleção de medicamentos essenciais segue critérios da OMS, adaptados à epidemiologia brasileira pela Conitec. Prioriza necessidades de saúde pública como vacinas e antibióticos. Atualizações bienais incorporam inovações para efetividade no SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Seleção essencial', ARRAY['OMS', 'Conitec', 'Epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Agência Nacional de Vigilância Sanitária (Anvisa)?', 'A Anvisa regula serviços de saúde, inspecionando estabelecimentos para padrões de qualidade e biossegurança no SUS. Fiscaliza licenças de funcionamento de hospitais e clínicas. Colabora com regulação de dispositivos médicos e medicamentos para proteção ao usuário.', 'saude_coletiva', 'Regulação em saúde', 'Anvisa', ARRAY['Fiscalização', 'Biossegurança', 'Licenças']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Comissão Intergestores Tripartite (CIT)?', 'A CIT coordena políticas de saúde entre União, estados e municípios, aprovando pactuações de recursos e responsabilidades no SUS. Discute regulação de leitos e SAMU. Garante equidade na alocação de verbas federais para ações coletivas.', 'saude_coletiva', 'Regulação em saúde', 'CIT', ARRAY['Pactuação', 'SUS', 'Equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a regulação de leitos hospitalares?', 'A regulação de leitos hospitalares usa sistemas como o SISREG para alocação centralizada, priorizando urgências via UPAs e SAMU. Evita superlotação e otimiza uso de recursos no SUS. Estados coordenam com municípios para transferências seguras.', 'saude_coletiva', 'Regulação em saúde', 'Leitos', ARRAY['SISREG', 'SAMU', 'Urgências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Complexo Regulador em saúde?', 'O Complexo Regulador integra regulação assistencial, logística e judicial no SUS, com centrais de regulação para agendamentos especializados. Facilita acesso a exames e consultas em redes regionais. Melhora resolutividade em 40% das demandas ambulatoriais.', 'saude_coletiva', 'Regulação em saúde', 'Complexo Regulador', ARRAY['Assistencial', 'Agendamentos', 'Redes regionais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a função da Ouvidoria do SUS?', 'A Ouvidoria do SUS recebe denúncias e sugestões para melhoria de serviços, garantindo participação cidadã na gestão. Notifica irregularidades como filas excessivas para auditorias. Integra o controle social com conselhos de saúde locais.', 'saude_coletiva', 'Regulação em saúde', 'Ouvidoria', ARRAY['Participação cidadã', 'Denúncias', 'Controle social']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a Anvisa regula transplantes?', 'A Anvisa regula transplantes por meio de habilitação de serviços e controle de tecidos via CNT, assegurando doadores e receptores. Fiscaliza boas práticas em bancos de órgãos. Reduziu transmissão de doenças em 95% com protocolos rigorosos.', 'saude_coletiva', 'Regulação em saúde', 'Transplantes', ARRAY['CNT', 'Habilitação', 'Boas práticas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a regulação do SAMU?', 'A regulação do SAMU centraliza chamadas 192 para despacho de ambulâncias, priorizando códigos de Manchester para gravidade. Treina reguladores para triagem telefônica eficiente. Integra com UPAs para continuidade do cuidado pré-hospitalar.', 'saude_coletiva', 'Regulação em saúde', 'SAMU', ARRAY['Triagem', 'Códigos Manchester', 'Pré-hospitalar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da auditoria em saúde?', 'A auditoria em saúde verifica conformidade de procedimentos no SUS, controlando glosas e fraudes em faturamentos. Equipes do TCU e conselhos analisam contas hospitalares. Melhora transparência e alocação de recursos para prioridades epidemiológicas.', 'saude_coletiva', 'Regulação em saúde', 'Auditoria', ARRAY['TCU', 'Fraudes', 'Transparência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como regular planos de saúde privados?', 'A regulação de planos de saúde privados pela ANS define coberturas mínimas e prazos de atendimento, protegendo direitos do consumidor. Multa operadoras por negativas indevidas. Integra com SUS para portabilidade e continuidade assistencial.', 'saude_coletiva', 'Regulação em saúde', 'Planos privados', ARRAY['ANS', 'Coberturas', 'Portabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a pactuação interfederativa?', 'A pactuação interfederativa define responsabilidades entre entes federados no SUS, alocando recursos para ações como vigilância sanitária. Conferências tripartites aprovam termos anuais. Garante regionalização de serviços para equidade.', 'saude_coletiva', 'Regulação em saúde', 'Pactuação', ARRAY['Interfederativa', 'Regionalização', 'Vigilância']);


-- Round 4 expansion: 50 additional subspecialty cards
INSERT INTO flashcards (id, deck_id, front, back, area, topic, subspecialty, tags)
VALUES
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual é o principal desafio na saúde indígena no Brasil, segundo o SUS?', 'O principal desafio na saúde indígena é a garantia de acesso equitativo aos serviços de saúde, considerando a diversidade cultural e geográfica dos povos. O Subsistema de Atenção à Saúde Indígena (SasiSUS) foi criado para coordenar ações específicas, como atendimento diferenciado e respeito às práticas tradicionais. Epidemias como malária e tuberculose ainda afetam comunidades isoladas devido à falta de infraestrutura.', 'saude_coletiva', 'Saúde indígena', 'Acesso e políticas', ARRAY['SUS', 'SasiSUS', 'Epidemias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a Política Nacional de Atenção à Saúde dos Povos Indígenas?', 'A Política Nacional de Atenção à Saúde dos Povos Indígenas, instituída em 2002, visa promover a integralidade do cuidado, respeitando a autonomia cultural e os direitos ancestrais. Ela enfatiza a participação comunitária na gestão de saúde e a formação de profissionais sensíveis à etnicidade. Ações incluem vigilância epidemiológica e promoção de saúde em terras indígenas.', 'saude_coletiva', 'Saúde indígena', 'Políticas nacionais', ARRAY['Política Nacional', 'Autonomia cultural', 'Vigilância']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS atende emergências em áreas indígenas remotas?', 'O SUS atende emergências em áreas indígenas remotas por meio de equipes multidisciplinares, incluindo Casas de Saúde Indígena e unidades móveis. A articulação com a Força Aérea Brasileira facilita o transporte aeromédico em casos graves. Prioriza-se o atendimento culturalmente adequado para reduzir morbimortalidade por doenças infecciosas.', 'saude_coletiva', 'Saúde indígena', 'Atendimento emergencial', ARRAY['SUS', 'Casas de Saúde', 'Aeromédico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os principais agravos de saúde em povos indígenas?', 'Os principais agravos incluem doenças infecciosas como malária, tuberculose e diarreias, além de desnutrição e problemas respiratórios crônicos. Fatores como pobreza, saneamento precário e perda de territórios contribuem para alta vulnerabilidade. Programas de imunização e educação em saúde visam mitigar esses riscos.', 'saude_coletiva', 'Saúde indígena', 'Agravos epidemiológicos', ARRAY['Doenças infecciosas', 'Desnutrição', 'Imunização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos Distritos Sanitários Especiais Indígenas (DSEI)?', 'Os DSEI são responsáveis pela execução de ações de saúde em terras indígenas, coordenando equipes de campo e unidades fixas. Eles promovem a integração entre medicina convencional e tradicional, garantindo suprimentos e vigilância. Cobrem regiões específicas, como o DSEI Yanomami, para atender populações isoladas.', 'saude_coletiva', 'Saúde indígena', 'DSEI', ARRAY['DSEI', 'Equipes de campo', 'Medicina tradicional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a saúde mental é abordada em comunidades indígenas?', 'A saúde mental em comunidades indígenas considera o impacto do contato com a sociedade não indígena, como perda cultural e violência. Abordagens incluem terapia comunitária e valorização de saberes ancestrais para tratar transtornos como depressão e suicídio. O SUS integra psicólogos e agentes indígenas para intervenções culturalmente sensíveis.', 'saude_coletiva', 'Saúde indígena', 'Saúde mental', ARRAY['Saúde mental', 'Terapia comunitária', 'Agentes indígenas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Atenção Diferenciada à Saúde Indígena?', 'A Atenção Diferenciada à Saúde Indígena é um princípio do SUS que adapta serviços às necessidades específicas, como mobilidade e práticas culturais. Inclui formação de recursos humanos e articulação intersetorial para proteção territorial. Visa reduzir desigualdades em indicadores de saúde como mortalidade infantil.', 'saude_coletiva', 'Saúde indígena', 'Atenção diferenciada', ARRAY['SUS', 'Formação profissional', 'Mortalidade infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são prioritárias para povos indígenas?', 'Vacinas prioritárias incluem BCG, hepatite B, poliomielite e contra influenza, devido à alta suscetibilidade a surtos. Campanhas anuais no SUS visam cobertura vacinal acima de 95% em terras indígenas. A logística desafiadora exige armazenamento em cadeia de frio adaptada.', 'saude_coletiva', 'Saúde indígena', 'Imunização', ARRAY['Vacinas', 'Cobertura vacinal', 'Cadeia de frio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o tabagismo afeta a saúde indígena?', 'O tabagismo introduzido por contato cultural aumenta riscos de doenças respiratórias e cardiovasculares em indígenas. Programas de cessação usam abordagens comunitárias, integrando xamãs e educação preventiva. O SUS monitora prevalência para políticas antitabaco específicas.', 'saude_coletiva', 'Saúde indígena', 'Tabagismo', ARRAY['Tabagismo', 'Doenças respiratórias', 'Cessação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da participação indígena na gestão de saúde?', 'A participação indígena na gestão de saúde fortalece a democracia sanitária, com conselhos e conferências específicas. Permite priorizar demandas locais, como controle de endemias. A Lei 8.142/1990 garante representação em instâncias deliberativas.', 'saude_coletiva', 'Saúde indígena', 'Participação comunitária', ARRAY['Gestão participativa', 'Conselhos de saúde', 'Lei 8142']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o pré-natal de baixo risco na saúde da mulher?', 'O pré-natal de baixo risco na saúde da mulher envolve consultas regulares para monitorar gestação sem complicações, iniciando no primeiro trimestre. Inclui exames como ultrassom e testes laboratoriais para detectar anemias ou infecções precocemente. O SUS recomenda pelo menos seis consultas para reduzir mortalidade materna e fetal.', 'saude_coletiva', 'Saúde da mulher', 'Pré-natal', ARRAY['Gestação', 'SUS', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Atenção Básica para mulheres?', 'A Política Nacional de Atenção Básica para mulheres promove equidade de gênero, com ênfase em prevenção de câncer de mama e colo do útero. Inclui rastreamento por mamografia e Papanicolau em unidades de saúde da família. Visa integralidade do cuidado, abordando violência e saúde reprodutiva.', 'saude_coletiva', 'Saúde da mulher', 'Políticas nacionais', ARRAY['Equidade de gênero', 'Rastreamento', 'Saúde reprodutiva']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS aborda a violência contra a mulher?', 'O SUS aborda a violência contra a mulher por meio da Rede de Atenção às Vítimas de Violência, com serviços de acolhimento e notificação compulsória. A Lei Maria da Penha integra ações de saúde e justiça para proteção. Equipes multidisciplinares oferecem suporte psicológico e médico em UBS e hospitais.', 'saude_coletiva', 'Saúde da mulher', 'Violência de gênero', ARRAY['Lei Maria da Penha', 'Notificação', 'Acolhimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza a menopausa na atenção à saúde da mulher?', 'A menopausa na atenção à saúde da mulher é um período de transição hormonal que pode causar sintomas como fogachos e osteoporose. O SUS oferece acompanhamento com reposição hormonal seletiva e promoção de atividade física. Rastreio de riscos cardiovasculares é essencial para qualidade de vida.', 'saude_coletiva', 'Saúde da mulher', 'Menopausa', ARRAY['Hormônios', 'Osteoporose', 'Riscos cardiovasculares']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os direitos reprodutivos da mulher no SUS?', 'Os direitos reprodutivos da mulher no SUS incluem planejamento familiar, contracepção gratuita e interrupção legal da gravidez em casos de risco à vida. A Lei 9.263/1996 regula o procedimento de esterilização voluntária após 25 anos. Educação sexual integral visa empoderamento e prevenção de DSTs.', 'saude_coletiva', 'Saúde da mulher', 'Direitos reprodutivos', ARRAY['Planejamento familiar', 'Lei 9263', 'Educação sexual']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir o câncer de colo do útero no Brasil?', 'A prevenção do câncer de colo do útero no Brasil envolve vacinação contra HPV para meninas de 9 a 14 anos e rastreamento anual por citologia oncótica a partir dos 25 anos. O SUS distribui vacinas e realiza exames em UBS para detecção precoce. Fatores de risco como tabagismo e múltiplos parceiros sexuais são abordados em educação em saúde.', 'saude_coletiva', 'Saúde da mulher', 'Câncer de colo', ARRAY['HPV', 'Citologia', 'Rastreamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Rede Cegonha no SUS?', 'A Rede Cegonha no SUS é uma estratégia para qualificar o cuidado materno-infantil, desde a pré-natal até o puerpério, com humanização no parto. Inclui regulação de vagas e transporte sanitário para regiões remotas. Reduziu a mortalidade materna em 20% desde 2011 por meio de articulação intersetorial.', 'saude_coletiva', 'Saúde da mulher', 'Rede Cegonha', ARRAY['Cuidado materno', 'Humanização', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da anemia na gestação?', 'A anemia na gestação impacta o desenvolvimento fetal, aumentando riscos de parto prematuro e baixo peso ao nascer. O SUS suplementa ferro e ácido fólico em pré-natais para prevenção, monitorando hemoglobina em consultas. Mulheres com anemia grave recebem transfusões em hospitais de referência.', 'saude_coletiva', 'Saúde da mulher', 'Anemia gestacional', ARRAY['Suplementação', 'Ferro', 'Pré-natal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS promove a saúde bucal da mulher?', 'O SUS promove a saúde bucal da mulher por meio do Brasil Sorridente, com atenção integrada à gestação para prevenir cáries e gengivites hormonais. Consultas odontológicas gratuitas em UBS incluem profilaxia e restaurações. Educação sobre higiene oral reduz desigualdades em acesso a serviços.', 'saude_coletiva', 'Saúde da mulher', 'Saúde bucal', ARRAY['Brasil Sorridente', 'Profilaxia', 'Higiene oral']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve o cuidado no puerpério?', 'O cuidado no puerpério envolve monitoramento de sangramento, infecções e saúde mental pós-parto, com visitas domiciliares pela equipe da ESF. O SUS incentiva amamentação exclusiva até seis meses e vacinação do recém-nascido. Acompanhamento psicológico previne depressão pós-parto em mulheres vulneráveis.', 'saude_coletiva', 'Saúde da mulher', 'Puerpério', ARRAY['Amamentação', 'ESF', 'Depressão pós-parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Saúde do Idoso?', 'A Política Nacional de Saúde do Idoso, de 1998, garante atendimento integral no SUS, com ênfase em prevenção de quedas e doenças crônicas. Inclui a Caderneta de Saúde da Pessoa Idosa para rastreio de hipertensão e diabetes. Promove envelhecimento ativo por meio de grupos de atividade física em UBS.', 'saude_coletiva', 'Saúde do idoso', 'Políticas nacionais', ARRAY['Envelhecimento ativo', 'SUS', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir quedas em idosos?', 'A prevenção de quedas em idosos envolve avaliação de risco em consultas geriátricas, com adaptações ambientais como corrimãos e remoção de tapetes. O SUS oferece órteses e fisioterapia para melhorar equilíbrio e força muscular. Exercícios regulares reduzem em 25% o risco de fraturas por osteoporose.', 'saude_coletiva', 'Saúde do idoso', 'Prevenção de quedas', ARRAY['Avaliação geriátrica', 'Fisioterapia', 'Osteoporose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a atenção à saúde mental do idoso?', 'A atenção à saúde mental do idoso aborda demências como Alzheimer, com diagnóstico precoce e suporte familiar no SUS. Grupos de convivência em CRAS previnem isolamento social e depressão. Medicamentos como inibidores de colinesterase são fornecidos gratuitamente em farmácias de alto custo.', 'saude_coletiva', 'Saúde do idoso', 'Saúde mental', ARRAY['Alzheimer', 'CRAS', 'Demências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são recomendadas para idosos?', 'Vacinas recomendadas para idosos incluem influenza anualmente, pneumocócica e herpes zoster para prevenir infecções graves. O SUS realiza campanhas nacionais com cobertura em UBS para reduzir hospitalizações. Idosos com comorbidades recebem prioridade em doses reforçadas.', 'saude_coletiva', 'Saúde do idoso', 'Imunização', ARRAY['Influenza', 'Pneumocócica', 'Campanhas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS cuida da incontinência urinária em idosos?', 'O SUS cuida da incontinência urinária em idosos com avaliação uroginecológica e treinamento de assoalho pélvico em fisioterapia. Medicamentos anticolinérgicos são prescritos para casos de urgência. Acompanhamento multiprofissional previne complicações como infecções recorrentes.', 'saude_coletiva', 'Saúde do idoso', 'Incontinência', ARRAY['Fisioterapia', 'Uroginecologia', 'Infecções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza o envelhecimento populacional no Brasil?', 'O envelhecimento populacional no Brasil resulta em 14% de idosos acima de 60 anos, aumentando demanda por serviços crônicos no SUS. Políticas como o Estatuto do Idoso garantem prioridade em atendimentos. Projeções indicam 30% da população idosa até 2050, exigindo planejamento sanitário.', 'saude_coletiva', 'Saúde do idoso', 'Envelhecimento demográfico', ARRAY['Estatuto do Idoso', 'SUS', 'Projeções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da ESF na saúde do idoso?', 'A ESF na saúde do idoso realiza visitas domiciliares para monitorar adesão medicamentosa e sinais vitais. Promove educação em saúde para autocuidado em hipertensos e diabéticos. A estratégia reduziu em 15% as internações por descompensação crônica.', 'saude_coletiva', 'Saúde do idoso', 'Estratégia Saúde da Família', ARRAY['Visitas domiciliares', 'Autocuidado', 'Internações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como tratar a osteoporose em idosos?', 'O tratamento da osteoporose em idosos inclui bifosfonatos e cálcio/vitamina D prescritos no SUS, com densitometria óssea para diagnóstico. Exercícios de impacto controlado fortalecem ossos e previnem fraturas. Rastreio em mulheres pós-menopausa é anual a partir dos 65 anos.', 'saude_coletiva', 'Saúde do idoso', 'Osteoporose', ARRAY['Bifosfonatos', 'Densitometria', 'Exercícios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a malnutrição em idosos?', 'A malnutrição em idosos é comum por perda de apetite e problemas dentários, levando a sarcopenia e imunossupressão. O SUS oferece suplementação nutricional em UBS e avaliação antropométrica regular. Programas de alimentação coletiva em ILPIs combatem desnutrição em institucionalizados.', 'saude_coletiva', 'Saúde do idoso', 'Nutrição', ARRAY['Sarcopenia', 'Suplementação', 'ILPIs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da reabilitação geriátrica?', 'A reabilitação geriátrica restaura funcionalidade após AVC ou fraturas, com fisioterapia e terapia ocupacional no SUS. Equipes interdisciplinares em CAPS e hospitais melhoram independência e qualidade de vida. Reduz custos com internações prolongadas em 30%.', 'saude_coletiva', 'Saúde do idoso', 'Reabilitação', ARRAY['Fisioterapia', 'AVC', 'CAPS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Relação Nacional de Medicamentos Essenciais (Rename)?', 'A Rename lista medicamentos essenciais para o SUS, priorizando custo-efetividade e evidências científicas para doenças prevalentes. Atualizada periodicamente, garante acesso gratuito em farmácias de unidades básicas. Inclui analgésicos, antibióticos e antirretrovirais para integralidade do cuidado.', 'saude_coletiva', 'Políticas de medicamentos', 'Rename', ARRAY['SUS', 'Custo-efetividade', 'Acesso gratuito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a Assistência Terapêutica Integral (ATI)?', 'A ATI fornece medicamentos de alto custo para doenças crônicas como HIV e câncer via Componente Especializado da Assistência Farmacêutica. Protocolos clínicos definem critérios de dispensação em farmácias especializadas. O SUS monitora adesão para otimizar recursos e resultados terapêuticos.', 'saude_coletiva', 'Políticas de medicamentos', 'ATI', ARRAY['Medicamentos alto custo', 'HIV', 'Protocolos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Farmácia Popular?', 'A Farmácia Popular subsidia medicamentos para hipertensão, diabetes e asma em farmácias credenciadas, com descontos de até 90%. Amplia acesso em áreas sem UBS, reduzindo desassistência. O programa atende 20 milhões de brasileiros anualmente com genéricos.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmácia Popular', ARRAY['Subsídios', 'Genéricos', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a dispensação racional de medicamentos?', 'A dispensação racional de medicamentos envolve orientação farmacêutica para adesão e prevenção de interações no SUS. Farmacêuticos em UBS verificam prescrições e promovem uso adequado. Reduz resistência antimicrobiana e iatrogenia em populações vulneráveis.', 'saude_coletiva', 'Políticas de medicamentos', 'Dispensação', ARRAY['Orientação farmacêutica', 'Adesão', 'Resistência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS gerencia medicamentos biológicos?', 'O SUS gerencia medicamentos biológicos para imunossupressão em transplantes e autoimunes via judicialização ou protocolos. A Conitec avalia incorporação com evidências de eficácia e segurança. Armazenamento em cadeia de frio é crucial para biossimilares distribuídos.', 'saude_coletiva', 'Políticas de medicamentos', 'Biológicos', ARRAY['Conitec', 'Protocolos', 'Judicialização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a política de controle de preços de medicamentos?', 'A política de controle de preços de medicamentos, pela CMED, limita reajustes anuais para genéricos e de referência, garantindo acessibilidade. Multas por descumprimento protegem o consumidor. Integra a Assistência Farmacêutica do SUS para equidade.', 'saude_coletiva', 'Políticas de medicamentos', 'Controle de preços', ARRAY['CMED', 'Genéricos', 'Acessibilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Farmacovigilância no Brasil?', 'A Farmacovigilância no Brasil monitora reações adversas a medicamentos via Notivisa, reportando à Anvisa para alertas e recalls. Profissionais de saúde notificam em UBS para melhorar segurança. Contribui para atualizações na Rename e protocolos SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmacovigilância', ARRAY['Notivisa', 'Anvisa', 'Reações adversas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover o uso de genéricos?', 'A promoção de genéricos envolve intercambialidade comprovada pela Anvisa, com preços até 35% menores que referência. O SUS prioriza em licitações para economia de recursos. Campanhas educativas aumentam confiança e adesão em tratamentos crônicos.', 'saude_coletiva', 'Políticas de medicamentos', 'Genéricos', ARRAY['Anvisa', 'Intercambialidade', 'Economia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da judicialização de medicamentos?', 'A judicialização de medicamentos sobrecarrega orçamentos do SUS com demandas por fármacos off-label, sem evidências robustas. A Conitec media incorporações para racionalidade. Representa 1,5% do PIB em saúde, exigindo políticas de mediação.', 'saude_coletiva', 'Políticas de medicamentos', 'Judicialização', ARRAY['Conitec', 'Off-label', 'Orçamento SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que regula a seleção de medicamentos essenciais?', 'A seleção de medicamentos essenciais segue critérios da OMS, adaptados à epidemiologia brasileira pela Conitec. Prioriza necessidades de saúde pública como vacinas e antibióticos. Atualizações bienais incorporam inovações para efetividade no SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Seleção essencial', ARRAY['OMS', 'Conitec', 'Epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Agência Nacional de Vigilância Sanitária (Anvisa)?', 'A Anvisa regula serviços de saúde, inspecionando estabelecimentos para padrões de qualidade e biossegurança no SUS. Fiscaliza licenças de funcionamento de hospitais e clínicas. Colabora com regulação de dispositivos médicos e medicamentos para proteção ao usuário.', 'saude_coletiva', 'Regulação em saúde', 'Anvisa', ARRAY['Fiscalização', 'Biossegurança', 'Licenças']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Comissão Intergestores Tripartite (CIT)?', 'A CIT coordena políticas de saúde entre União, estados e municípios, aprovando pactuações de recursos e responsabilidades no SUS. Discute regulação de leitos e SAMU. Garante equidade na alocação de verbas federais para ações coletivas.', 'saude_coletiva', 'Regulação em saúde', 'CIT', ARRAY['Pactuação', 'SUS', 'Equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a regulação de leitos hospitalares?', 'A regulação de leitos hospitalares usa sistemas como o SISREG para alocação centralizada, priorizando urgências via UPAs e SAMU. Evita superlotação e otimiza uso de recursos no SUS. Estados coordenam com municípios para transferências seguras.', 'saude_coletiva', 'Regulação em saúde', 'Leitos', ARRAY['SISREG', 'SAMU', 'Urgências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Complexo Regulador em saúde?', 'O Complexo Regulador integra regulação assistencial, logística e judicial no SUS, com centrais de regulação para agendamentos especializados. Facilita acesso a exames e consultas em redes regionais. Melhora resolutividade em 40% das demandas ambulatoriais.', 'saude_coletiva', 'Regulação em saúde', 'Complexo Regulador', ARRAY['Assistencial', 'Agendamentos', 'Redes regionais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a função da Ouvidoria do SUS?', 'A Ouvidoria do SUS recebe denúncias e sugestões para melhoria de serviços, garantindo participação cidadã na gestão. Notifica irregularidades como filas excessivas para auditorias. Integra o controle social com conselhos de saúde locais.', 'saude_coletiva', 'Regulação em saúde', 'Ouvidoria', ARRAY['Participação cidadã', 'Denúncias', 'Controle social']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a Anvisa regula transplantes?', 'A Anvisa regula transplantes por meio de habilitação de serviços e controle de tecidos via CNT, assegurando doadores e receptores. Fiscaliza boas práticas em bancos de órgãos. Reduziu transmissão de doenças em 95% com protocolos rigorosos.', 'saude_coletiva', 'Regulação em saúde', 'Transplantes', ARRAY['CNT', 'Habilitação', 'Boas práticas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a regulação do SAMU?', 'A regulação do SAMU centraliza chamadas 192 para despacho de ambulâncias, priorizando códigos de Manchester para gravidade. Treina reguladores para triagem telefônica eficiente. Integra com UPAs para continuidade do cuidado pré-hospitalar.', 'saude_coletiva', 'Regulação em saúde', 'SAMU', ARRAY['Triagem', 'Códigos Manchester', 'Pré-hospitalar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da auditoria em saúde?', 'A auditoria em saúde verifica conformidade de procedimentos no SUS, controlando glosas e fraudes em faturamentos. Equipes do TCU e conselhos analisam contas hospitalares. Melhora transparência e alocação de recursos para prioridades epidemiológicas.', 'saude_coletiva', 'Regulação em saúde', 'Auditoria', ARRAY['TCU', 'Fraudes', 'Transparência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como regular planos de saúde privados?', 'A regulação de planos de saúde privados pela ANS define coberturas mínimas e prazos de atendimento, protegendo direitos do consumidor. Multa operadoras por negativas indevidas. Integra com SUS para portabilidade e continuidade assistencial.', 'saude_coletiva', 'Regulação em saúde', 'Planos privados', ARRAY['ANS', 'Coberturas', 'Portabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a pactuação interfederativa?', 'A pactuação interfederativa define responsabilidades entre entes federados no SUS, alocando recursos para ações como vigilância sanitária. Conferências tripartites aprovam termos anuais. Garante regionalização de serviços para equidade.', 'saude_coletiva', 'Regulação em saúde', 'Pactuação', ARRAY['Interfederativa', 'Regionalização', 'Vigilância']);
