INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Cardiologia, Endocrinologia, Pneumologia, Nefrologia, Gastroenterologia
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é o tratamento inicial recomendado para um paciente com hipertensão arterial sistêmica estágio 1, sem comorbidades?', 'O tratamento inicial para hipertensão estágio 1 (PAS 140-159 mmHg ou PAD 90-99 mmHg) sem comorbidades é modificação do estilo de vida: dieta DASH, redução de sal, exercício físico regular, perda de peso e cessação do tabagismo. Se não controlado em 3-6 meses, iniciar IECA ou BRA como monoterapia.', 'clinica_medica', 'Cardiologia', 'Hipertensão', ARRAY['hipertensao','tratamento','estilo_vida']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Em um paciente com infarto agudo do miocárdio (IAM) com supradesnivelamento do ST, qual é a terapia de reperfusão preferencial?', 'A terapia de reperfusão preferencial é a angioplastia primária percutânea (ICP) se realizada em até 90 minutos do primeiro contato médico. Caso não disponível, usar fibrinolítico como alteplase em até 12 horas do início dos sintomas, seguido de ICP de resgate se falha.', 'clinica_medica', 'Cardiologia', 'Infarto Agudo do Miocárdio', ARRAY['IAM','reperfusao','angioplastia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são os principais fatores de risco para doença arterial coronariana?', 'Os principais fatores de risco incluem hipertensão arterial, dislipidemia, diabetes mellitus, tabagismo, obesidade, sedentarismo, idade avançada, história familiar e sexo masculino. A dislipidemia é caracterizada por LDL elevado e HDL baixo.', 'clinica_medica', 'Cardiologia', 'Doença Arterial Coronariana', ARRAY['DAC','fatores_risco','dislipidemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar insuficiência cardíaca com fração de ejeção preservada?', 'O diagnóstico de IC com FE preservada (ICFEp) requer sintomas de IC, evidência de elevação de peptídeo natriurético (BNP >35 pg/mL), FEVE ≥50% e evidência de diastólica por ecocardiograma (E/e'' >14 ou massa IV elevada). Excluir outras causas.', 'clinica_medica', 'Cardiologia', 'Insuficiência Cardíaca', ARRAY['IC','FE_preservada','ecocardiograma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o manejo inicial de arritmia ventricular sustentada em paciente estável?', 'Em paciente hemodinamicamente estável com taquicardia de QRS largo: se TV monomórfica confirmada, amiodarona 150 mg IV em 10 min (primeira linha). Adenosina NÃO é indicada para TV verdadeira — usar APENAS se forte suspeita de TSV com condução aberrante. Se instável, cardioversão elétrica sincronizada imediata (100-200 J). Avaliar causa subjacente como isquemia.', 'clinica_medica', 'Cardiologia', 'Arritmias', ARRAY['arritmia','ventricular','amiodarona']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a síndrome metabólica e seus critérios diagnósticos?', 'A síndrome metabólica é um conjunto de fatores que aumentam risco cardiovascular: obesidade abdominal (cintura >102cm homens/>88cm mulheres), triglicerídeos ≥150 mg/dL, HDL <40mg/dL homens/<50mg/dL mulheres, PA ≥130/85 mmHg, glicemia de jejum ≥100 mg/dL. Diagnóstico com ≥3 critérios.', 'clinica_medica', 'Cardiologia', 'Síndrome Metabólica', ARRAY['sindrome_metabolica','criterios','risco_CV']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual a conduta para endocardite infecciosa com indicação cirúrgica?', 'Indicações cirúrgicas incluem insuficiência cardíaca refratária, infecção persistente apesar de antibióticos, embolia sistêmica recorrente, abscesso pericárdico ou prótese valvular infectada. Preferir cirurgia precoce em IAM com IC.', 'clinica_medica', 'Cardiologia', 'Endocardite', ARRAY['endocardite','cirurgia','antibioticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como avaliar risco de eventos cardiovasculares em paciente com DAC?', 'Usar escore de risco como SCORE ou Framingham para prever eventos em 10 anos. Fatores incluem idade, sexo, tabagismo, PA, colesterol total/HDL. Em DAC estabelecida, estratificar com teste ergométrico ou cintilografia miocárdica.', 'clinica_medica', 'Cardiologia', 'Avaliação de Risco', ARRAY['risco_CV','SCORE','Framingham']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais vacinas são recomendadas para pacientes com valvopatia?', 'Pacientes com valvopatia devem receber vacina contra influenza anualmente, pneumocócica (PCV13 + PPSV23), hepatite B e, se imunossuprimidos, outras como varicela. Evitar vacinas vivas em imunodeprimidos.', 'clinica_medica', 'Cardiologia', 'Valvopatia', ARRAY['valvopatia','vacinas','prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a pericardite aguda e seu tratamento?', 'Pericardite aguda apresenta dor torácica pleurítica, atrito pericárdico e elevação difusa de ST no ECG. Tratamento: AINEs (ibuprofeno 600mg 3x/dia) ou colchicina por 3 meses para reduzir recorrência. Corticoides se contraindicação.', 'clinica_medica', 'Cardiologia', 'Pericardite', ARRAY['pericardite','dor_toracica','AINEs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é o diagnóstico diferencial de hipoatividade tireoidiana em adultos?', 'O diagnóstico diferencial inclui hipotireoidismo primário (Hashimoto), secundário (hipopituitarismo), iatrogênico (pós-tireoidectomia ou radioiodo), amiodarona-induzido e resistência hormonal. Dosar TSH, T4 livre e anticorpos anti-TPO.', 'clinica_medica', 'Endocrinologia', 'Hipotireoidismo', ARRAY['hipotireoidismo','TSH','Hashimoto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como manejar diabetes mellitus tipo 2 recém-diagnosticado?', 'Iniciar com metformina 500mg 2x/dia, associada a educação diabética, dieta hipocalórica e exercício. Monitorar HbA1c a cada 3 meses, meta <7%. Se não controlado, adicionar inibidor de DPP-4 ou sulfonilureia.', 'clinica_medica', 'Endocrinologia', 'Diabetes Tipo 2', ARRAY['DM2','metformina','HbA1c']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais os sintomas clássicos da crise de feocromocitoma?', 'Sintomas incluem cefaleia intensa, sudorese profusa, palpitações e hipertensão paroxística. Triade clássica: cefaleia, sudorese e taquicardia. Dosar catecolaminas urinárias ou plasmáticas e imagem abdominal para tumor adrenal.', 'clinica_medica', 'Endocrinologia', 'Feocromocitoma', ARRAY['feocromocitoma','crise','catecolaminas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a síndrome de Cushing e como confirmar?', 'Síndrome de Cushing é hiperadrenocorticismo endógeno, com sintomas como ganho de peso central, estrias roxas, hirsutismo e hipertensão. Confirmação: teste de supressão com dexametasona 1mg (cortisol matinal >1,8 mcg/dL) e dosagem de ACTH.', 'clinica_medica', 'Endocrinologia', 'Síndrome de Cushing', ARRAY['Cushing','dexametasona','ACTH']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o tratamento para hipoglicemia em paciente diabético usando insulina?', 'Tratamento inicial: 15g de carboidrato rápido (suco ou glicose oral) se consciente. Recheckar glicemia em 15 min; repetir se <70 mg/dL. Se grave (inconsciente), glucagon IM ou IV glicose 50%. Prevenir com ajuste de doses.', 'clinica_medica', 'Endocrinologia', 'Hipoglicemia', ARRAY['hipoglicemia','insulina','glucagon']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar osteoporose em mulher pós-menopausa?', 'Diagnosticar por T-score ≤ -2,5 na DEXA de coluna lombar ou fêmur proximal. Avaliar fatores de risco (idade >65, FRX prévia, tabagismo). Tratar com bifosfonatos (alendronato 70mg/semana) + cálcio/vit D.', 'clinica_medica', 'Endocrinologia', 'Osteoporose', ARRAY['osteoporose','DEXA','bifosfonatos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais as indicações para terapia de reposição de testosterona?', 'Indicações incluem hipogonadismo confirmado (testosterona total <300 ng/dL + sintomas como fadiga, baixa libido). Contraindicações: câncer de próstata, apneia obstrutiva grave. Monitorar PSA e hematócrito.', 'clinica_medica', 'Endocrinologia', 'Hipogonadismo', ARRAY['testosterona','hipogonadismo','PSA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a tireotoxicose e seu manejo inicial?', 'Tireotoxicose: sintomas como perda de peso, taquicardia, tremor, exoftalmia em Graves. Manejo: metimazol 10-30mg/dia (primeira linha) ou propiltiouracil 100mg 3x/dia (preferido no 1º trimestre de gestação), betabloqueadores para sintomas adrenérgicos. Avaliar causa (TSH, T4 livre).', 'clinica_medica', 'Endocrinologia', 'Tireotoxicose', ARRAY['tireotoxicose','Graves','propiltiouracil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como avaliar disfunção erétil em contexto endócrino?', 'Avaliar testosterona total/livre, prolactina, TSH e glicemia. Disfunção endócrina comum em hipogonadismo, hiperprolactinemia ou DM. Tratar causa subjacente; se idiopática, inibidores PDE5 como sildenafil.', 'clinica_medica', 'Endocrinologia', 'Disfunção Erétil', ARRAY['DE','testosterona','PDE5']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o critério para diagnóstico de diabetes gestacional?', 'Diagnóstico por curva glicêmica com 75g OGTT: glicemia de jejum ≥92 mg/dL, 1h ≥180 mg/dL ou 2h ≥153 mg/dL (critérios IADPSG). Manejo: dieta, exercício; insulina se glicemia >95 jejum ou >120 pós-prandial.', 'clinica_medica', 'Endocrinologia', 'Diabetes Gestacional', ARRAY['DM_gestacional','OGTT','insulina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é a abordagem inicial para paciente com dispneia e sibilos em exacerbação de DPOC?', 'Abordagem inicial: broncodilatadores inalatórios (salbutamol + ipratrópio), corticoides sistêmicos (prednisona 40mg/dia por 5 dias), oxigenoterapia para SatO2 88-92%. Antibióticos se infecção (escarro purulento).', 'clinica_medica', 'Pneumologia', 'DPOC', ARRAY['DPOC','exacerbacao','broncodilatadores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar asma em adulto com história de atopia?', 'Diagnóstico baseado em história de episódios reversíveis de dispneia/sibilos, espirometria com VEF1/CVF <70% e reversibilidade ≥12% E ≥200 mL em valor absoluto pós-broncodilatador (ambos critérios necessários — GINA 2025). Teste de provocação com metacolina se dúvida.', 'clinica_medica', 'Pneumologia', 'Asma', ARRAY['asma','espirometria','reversibilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a síndrome do desconforto respiratório agudo (SDRA) e critérios?', 'SDRA é lesão pulmonar aguda com hipoxemia (PaO2/FiO2 ≤300), infiltrados bilaterais em RX tórax, sem sobrecarga cardíaca. Graus: leve (200-300), moderado (100-200), grave (<100). Suporte: ventilação protetora.', 'clinica_medica', 'Pneumologia', 'SDRA', ARRAY['SDRA','hipoxemia','ventilacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o tratamento para tuberculose pulmonar ativa?', 'RNTI: rifampicina, isoniazida, pirazinamida e etambutol por 2 meses, seguido de rifampicina + isoniazida por 4 meses. Monitorar adesão, efeitos hepáticos e resistência (MDR-TB). Vacina BCG para prevenção.', 'clinica_medica', 'Pneumologia', 'Tuberculose', ARRAY['TB','RNTI','rifampicina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como manejar derrame pleural parapneumônico?', 'Drenagem torácica se grande (>1cm ultrassom) ou sintomático. Analisar líquido: pH <7.2, glicose <60 mg/dL ou LDH >1000 UI/L indica necessidade de drenagem. Antibióticos para infecção subjacente.', 'clinica_medica', 'Pneumologia', 'Derrame Pleural', ARRAY['derrame','parapneumonico','drenagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais os fatores de risco para carcinoma de pulmão de células não pequenas?', 'Fatores: tabagismo (80-85% dos casos), exposição a radônio/asbesto, história familiar, DPOC prévia. Estadiamento por TNM; tratamento cirúrgico se precoce, quimio/radio se avançado.', 'clinica_medica', 'Pneumologia', 'Câncer de Pulmão', ARRAY['CP','tabagismo','estadiamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é apneia obstrutiva do sono e como tratar?', 'AOS: pausas respiratórias >10s durante sono devido a obstrução, com sonolência diurna. Diagnóstico por polissonografia (IAH >5). Tratamento: CPAP nasal, perda de peso, evitar álcool. Cirurgia se anatômica.', 'clinica_medica', 'Pneumologia', 'Apneia do Sono', ARRAY['AOS','CPAP','polissonografia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual a conduta para embolia pulmonar de alto risco?', 'Embolia pulmonar de alto risco (hipotensão, choque): trombólise (alteplase 100mg IV em 2h) se sem contraindicações. Suporte hemodinâmico, anticoagulação com HBPM. Filtro de veia cava se recorrência.', 'clinica_medica', 'Pneumologia', 'Embolia Pulmonar', ARRAY['EP','trombolise','anticoagulacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como classificar gravidade da pneumonia adquirida na comunidade?', 'Classificação CURB-65: confusão, ureia >7 mmol/L, frequência respiratória ≥30/min, PA <90/60 mmHg, idade ≥65. Pontuação ≥3 indica internação em UTI. Tratamento empírico: amoxicilina + azitromicina.', 'clinica_medica', 'Pneumologia', 'Pneumonia', ARRAY['PAC','CURB65','antibioticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é o manejo de lesão renal aguda pré-renal?', 'Lesão renal aguda pré-renal (hipoperfusão): restaurar volume com cristaloides (soro 20mL/kg bolus), corrigir causas (hipotensão, desidratação). Monitorar creatinina e diurese. Evitar nefrotóxicos como AINEs.', 'clinica_medica', 'Nefrologia', 'Lesão Renal Aguda', ARRAY['LRA','pre_renal','hidratacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar síndrome nefrótica em adulto?', 'Síndrome nefrótica: proteinúria >3,5g/24h, hipoalbuminemia <3g/dL, edema e hiperlipidemia. Causas comuns: glomerulonefrite membranosa, diabetes. Biópsia renal se necessário. Tratamento: IECA, diuréticos.', 'clinica_medica', 'Nefrologia', 'Síndrome Nefrótica', ARRAY['nefrotica','proteinuria','IECA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a doença renal crônica e seus estágios?', 'DRC: taxa de filtração glomerular <60 mL/min/1,73m² por >3 meses. Estágios: 1 (GF >90 com dano), 2 (60-89), 3a (45-59), 3b (30-44), 4 (15-29), 5 (<15 ou diálise). Controle PA <130/80.', 'clinica_medica', 'Nefrologia', 'Doença Renal Crônica', ARRAY['DRC','estagios','TFG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o tratamento para pielonefrite aguda não complicada?', 'Pielonefrite aguda: ciprofloxacino 500mg 2x/dia por 7 dias ou ceftriaxona IV se grave. Hospitalizar se vômitos, desidratação ou comorbidades. Cultura de urina para sensibilidade.', 'clinica_medica', 'Nefrologia', 'Pielonefrite', ARRAY['pielonefrite','ciprofloxacino','cultura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como manejar hipercalemia em paciente com insuficiência renal?', 'Hipercalemia (>5,5 mEq/L): estabilizar membrana com cálcio gluconato IV, deslocar K intracelular com insulina + glicose ou bicarbonato, remover K com resinas (kayexalato) ou diálise se grave.', 'clinica_medica', 'Nefrologia', 'Hipercalemia', ARRAY['hipercalemia','calcio','dialise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais as indicações para diálise em DRC estágio 5?', 'Indicações: uremia sintomática, hipercalemia refratária, acidose metabólica grave, sobrecarga volêmica com edema pulmonar, pericardite urêmica. Modalidades: hemodiálise ou peritoneal.', 'clinica_medica', 'Nefrologia', 'Diálise', ARRAY['dialise','indicacoes','uremia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a glomerulonefrite pós-estreptocócica?', 'Glomerulonefrite pós-estreptocócica: hematúria, proteinúria, edema e hipertensão 1-2 semanas pós-faringite/amigdalite. Hipocomplementemia C3. Tratamento suporte; resolve em 90% casos.', 'clinica_medica', 'Nefrologia', 'Glomerulonefrite', ARRAY['GN','pos_estrepto','C3']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como prevenir infecção do trato urinário recorrente em mulheres?', 'Prevenção: hidratação abundante, micção pós-coito, evitar espermicidas. Profilaxia com nitrofurantoína 50mg/noite se >3 ITU/ano. Cranberry ou D-manose como adjuvantes.', 'clinica_medica', 'Nefrologia', 'ITU', ARRAY['ITU','prevencao','profilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o critério para transplante renal?', 'Critérios: DRC estágio 5, sem contraindicações ativas (infecção, câncer). Avaliação pré: compatibilidade ABO/HLA, rastreio de infecções. Pós-transplante: imunossupressores como tacrolimo + micofenolato.', 'clinica_medica', 'Nefrologia', 'Transplante Renal', ARRAY['transplante','imunossupressao','HLA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é o tratamento de primeira linha para doença de refluxo gastroesofágico?', 'IBPs como omeprazol 20mg/dia por 4-8 semanas para DRGE erosiva. Mudanças no estilo de vida: elevar cabeceira, evitar refeições noturnas, perda de peso. Endoscopia se alarme (disfagia, anemia).', 'clinica_medica', 'Gastroenterologia', 'DRGE', ARRAY['DRGE','IBP','omeprazol']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar úlcera péptica sangrante?', 'Classificação de Forrest: Ia = jato arterial ativo, Ib = sangramento em babação, IIa = vaso visível não sangrante, IIb = coágulo aderente, IIc = mancha pigmentada plana, III = base limpa. Forrest Ia-IIb indicam tratamento endoscópico.', 'clinica_medica', 'Gastroenterologia', 'Úlcera Péptica', ARRAY['ulcera','sangramento','endoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a doença celíaca e como confirmar?', 'Doença celíaca: intolerância ao glúten com atrofia vilositária. Sintomas: diarreia, perda de peso, anemia. Confirmação: anticorpos anti-transglutaminase IgA + biópsia duodenal. Tratamento: dieta sem glúten vitalícia.', 'clinica_medica', 'Gastroenterologia', 'Doença Celíaca', ARRAY['celiaca','gluten','biopsia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o manejo de hepatite B crônica HBeAg positiva?', 'Manejo: entecavir ou tenofovir 300mg/dia se ALT >2x ULN e DNA HBV >20.000 UI/mL. Monitorar função hepática, HBV DNA e HBeAg a cada 6 meses. Vacinação para contatos.', 'clinica_medica', 'Gastroenterologia', 'Hepatite B', ARRAY['HBV','entecavir','DNA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como tratar constipação crônica funcional?', 'Tratamento: fibras (25-30g/dia), laxantes osmóticos (lactulose 15-30mL/dia), atividade física. Se refratária, lubiprostone ou linaclotide. Avaliar causas orgânicas com colonoscopia se >50 anos.', 'clinica_medica', 'Gastroenterologia', 'Constipação', ARRAY['constipacao','fibras','laxantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais os sinais de alarme em dispepsia que indicam endoscopia?', 'Sinais de alarme: perda de peso involuntária, disfagia, anemia ferropriva, sangramento GI, vômitos persistentes, idade >55 com sintomas novos. Suspeita de úlcera, câncer ou esôfago de Barrett.', 'clinica_medica', 'Gastroenterologia', 'Dispepsia', ARRAY['dispepsia','alarmes','endoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a colangite aguda e tratamento?', 'Colangite aguda: febre, icterícia, dor em hipocôndrio direito (tríade de Charcot). Tratamento: antibióticos IV (ceftriaxona + metronidazol), descompressão biliar por CPRE urgente se gravidade (Tokyo guidelines).', 'clinica_medica', 'Gastroenterologia', 'Colangite', ARRAY['colangite','CPRE','antibioticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como diagnosticar doença inflamatória intestinal (DII)?', 'DII (Crohn ou retocolite): diarreia crônica, dor abdominal, perda de peso. Diagnóstico: colonoscopia com biópsia (Crohn: ilhotes, RCU: contínuo), exames de imagem (TC enterografia). Tratar com mesalazina ou imunossupressores.', 'clinica_medica', 'Gastroenterologia', 'DII', ARRAY['DII','colonoscopia','mesalazina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o tratamento para pancreatite aguda leve?', 'Pancreatite aguda leve: jejum inicial, hidratação IV (soro 250-500mL/h), analgésicos (opioides como morfina ou tramadol; petidina não é mais recomendada). Reintroduzir dieta oral precoce se melhora. Investigar causa (litíase, etilismo) com USG abdominal.', 'clinica_medica', 'Gastroenterologia', 'Pancreatite', ARRAY['pancreatite','hidratacao','USG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a síndrome do intestino irritável e manejo?', 'SII: dor abdominal recorrente com alteração do hábito intestinal, sem dano orgânico. Critérios Roma IV. Manejo: dieta FODMAP baixa, probióticos, antiespasmódicos (mebeverina), TCAs para dor refratária.', 'clinica_medica', 'Gastroenterologia', 'SII', ARRAY['SII','Roma','FODMAP']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual é a principal causa da anemia ferropriva?', 'A anemia ferropriva é causada principalmente pela deficiência de ferro devido a perdas sanguíneas crônicas, como sangramentos menstruais abundantes ou hemorragias gastrointestinais. Os sintomas incluem fadiga, palidez e dispneia. O diagnóstico é confirmado por hemograma com microcitose e hipocromia, e o tratamento envolve suplementação de ferro oral.', 'clinica_medica', 'Hematologia', 'anemia ferropriva', ARRAY['ferro', 'sangue', 'deficiencia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são os achados laboratoriais na anemia megaloblástica?', 'A anemia megaloblástica resulta de deficiência de vitamina B12 ou folato, levando a macrocitose com MCV elevado acima de 100 fL. Há hipersegmentação de neutrófilos e anemia macrocítica normocrômica no hemograma. O tratamento corrige a deficiência subjacente, com melhora rápida dos sintomas.', 'clinica_medica', 'Hematologia', 'anemia megaloblástica', ARRAY['vitamina', 'b12', 'folato']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a policitemia vera?', 'A policitemia vera é uma neoplasia mieloproliferativa com proliferação excessiva de eritrócitos, levando a hematócrito elevado e risco de trombose. Sintomas incluem cefaleia, prurido pós-banho e esplenomegalia. O diagnóstico usa critérios da OMS, incluindo mutação JAK2, e o tratamento inclui flebotomia e hidroxiureia.', 'clinica_medica', 'Hematologia', 'policitemia vera', ARRAY['neoplasia', 'eritrocitos', 'trombose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são os tipos principais de leucemia?', 'As leucemias são neoplasias hematológicas divididas em agudas (linfoblástica e mieloide) e crônicas (linfocítica e mieloide). Elas causam infiltração medular com anemia, infecções e sangramentos. O diagnóstico é por mielograma e imunofenotipagem, com tratamento quimioterápico intensivo.', 'clinica_medica', 'Hematologia', 'leucemia', ARRAY['neoplasia', 'medula', 'quimioterapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como se diferencia o linfoma de Hodgkin do não-Hodgkin?', 'O linfoma de Hodgkin apresenta células de Reed-Sternberg e tem prognóstico melhor, enquanto o não-Hodgkin é mais heterogêneo com subtipos variados. Ambos causam linfadenopatia e sintomas B como febre e sudorese noturna. O tratamento envolve quimioterapia e radioterapia, guiado por estadiamento.', 'clinica_medica', 'Hematologia', 'linfomas', ARRAY['linfoma', 'reed-sternberg', 'quimioterapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é a PTI e seu tratamento?', 'A púrpura trombocitopênica idiopática (PTI) é uma trombocitopenia autoimune com plaquetas baixas, causando petéquias e sangramentos. O diagnóstico exclui outras causas, e o tratamento inicial é com corticosteroides. Em casos refratários, usa-se imunoglobulina ou esplenectomia.', 'clinica_medica', 'Hematologia', 'PTI', ARRAY['trombocitopenia', 'autoimune', 'corticoides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são as principais coagulopatias?', 'As coagulopatias incluem hemofilia (deficiência de fatores VIII ou IX) e doença de von Willebrand, levando a sangramentos mucosos e articulares. O diagnóstico usa testes de coagulação como TTPa prolongado. O tratamento é com reposição de fatores e DDAVP em alguns casos.', 'clinica_medica', 'Hematologia', 'coagulopatias', ARRAY['coagulacao', 'hemofilia', 'fatores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Características da hemofilia A?', 'A hemofilia A é uma coagulopatia hereditária ligada ao X por deficiência do fator VIII, afetando principalmente homens. Manifesta-se com hemartroses e hematomas espontâneos. O tratamento profilático usa concentrados de fator VIII recombinante.', 'clinica_medica', 'Hematologia', 'hemofilia', ARRAY['fator viii', 'hereditaria', 'profilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é trombofilia e como diagnosticar?', 'A trombofilia é uma predisposição a tromboses venosas, causada por mutações como fator V Leiden ou deficiência de proteína C/S. Fatores de risco incluem uso de anticoncepcionais e imobilidade. O diagnóstico é por testes genéticos e coagulograma após episódio trombótico.', 'clinica_medica', 'Hematologia', 'trombofilia', ARRAY['trombose', 'genetica', 'anticoagulantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas e tratamento do mieloma múltiplo?', 'O mieloma múltiplo é uma neoplasia plasmocelular com dor óssea, anemia e insuficiência renal por hipercalcemia e proteinúria de Bence-Jones. O diagnóstico usa eletroforese de proteínas e biópsia de medula. O tratamento inclui bortezomibe, lenalidomida e transplante autólogo.', 'clinica_medica', 'Hematologia', 'mieloma', ARRAY['plasmocitos', 'ossos', 'quimioterapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios diagnósticos para artrite reumatoide?', 'A artrite reumatoide é uma doença autoimune com artrite simétrica de pequenas articulações, usando critérios ACR/EULAR com pontuação por anticorpos RF e anti-CCP. Sintomas incluem rigidez matinal e nódulos reumatoides. O tratamento inicia com DMARDs como metotrexato e biológicos.', 'clinica_medica', 'Reumatologia', 'artrite reumatoide', ARRAY['autoimune', 'articulacoes', 'dmards']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manifestações do lúpus eritematoso sistêmico?', 'O lúpus eritematoso sistêmico (LES) é autoimune com rash malar, artrite e nefrite, diagnosticado por critérios SLICC com ANA positivo. Pode afetar múltiplos órgãos como coração e pulmões. O tratamento usa hidroxicloroquina e imunossupressores como prednisona.', 'clinica_medica', 'Reumatologia', 'lúpus', ARRAY['autoimune', 'rash', 'imunossupressores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Características da espondilite anquilosante?', 'A espondilite anquilosante é uma espondiloartrite com dor lombar inflamatória e rigidez, associada a HLA-B27. Radiografias mostram sacroileíte e bambu espinhal. O tratamento inclui AINEs e biológicos anti-TNF.', 'clinica_medica', 'Reumatologia', 'espondilite anquilosante', ARRAY['espondiloartrite', 'hla-b27', 'anti-tnf']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Causa e tratamento da gota?', 'A gota é causada por hiperuricemia com depósitos de cristais de urato em articulações, levando a artrite aguda no podagra. Fatores incluem dieta rica em purinas e álcool. O tratamento agudo usa colchicina e AINEs, profilático com alopurinol.', 'clinica_medica', 'Reumatologia', 'gota', ARRAY['urico', 'cristais', 'alopurinol']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diagnóstico diferencial da fibromialgia?', 'A fibromialgia é uma síndrome de dor crônica generalizada com pontos tender e fadiga, sem achados inflamatórios. Diagnóstico por critérios ACR com exclusão de outras doenças. O tratamento é multidisciplinar com exercício, amitriptilina e cognitivo-comportamental.', 'clinica_medica', 'Reumatologia', 'fibromialgia', ARRAY['dor cronica', 'pontos tender', 'exercicio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Formas da esclerodermia?', 'A esclerodermia é uma doença do tecido conjuntivo com endurecimento cutâneo, dividida em localizada e sistêmica (esclerose sistêmica). A sistêmica tem fenômeno de Raynaud e fibrose pulmonar. O tratamento é sintomático com imunossupressores para casos graves.', 'clinica_medica', 'Reumatologia', 'esclerodermia', ARRAY['fibrose', 'raynaud', 'imunossupressores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Classificação das vasculites?', 'As vasculites são inflamações de vasos por tamanho: pequenas (púrpura de Henoch-Schönlein), médias (poliangeíte) e grandes (arterite de Takayasu). Sintomas incluem febre e rash. O tratamento varia com esteroides e ciclofosfamida.', 'clinica_medica', 'Reumatologia', 'vasculites', ARRAY['inflamacao', 'vasos', 'esteroides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas da polimialgia reumática?', 'A polimialgia reumática causa dor e rigidez em ombros e quadris em idosos, associada a arterite de células gigantes. Elevação de VHS e PCR. O tratamento é com baixas doses de prednisona com resposta rápida.', 'clinica_medica', 'Reumatologia', 'polimialgia reumática', ARRAY['idosos', 'rigidez', 'prednisona']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Complicações da síndrome antifosfolípide?', 'A síndrome antifosfolípide é autoimune com tromboses recorrentes e abortos, por anticorpos anticardiolipina e anticoagulante lúpico. Diagnóstico por critérios de Sydney. O tratamento usa varfarina e heparina em gestantes.', 'clinica_medica', 'Reumatologia', 'síndrome antifosfolípide', ARRAY['trombose', 'anticorpos', 'anticoagulantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Fatores de risco para osteoporose?', 'A osteoporose é perda óssea com risco de fraturas, mais comum em mulheres pós-menopausa por estrogênio baixo e idade. Diagnóstico por densitometria óssea com T-score <-2,5. O tratamento inclui bifosfonatos, cálcio e vitamina D.', 'clinica_medica', 'Reumatologia', 'osteoporose', ARRAY['ossos', 'fraturas', 'bifosfonatos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tipos de AVC e manejo inicial?', 'O AVC isquêmico é por oclusão vascular e hemorrágico por ruptura, com sintomas como hemiparesia e afasia. Diagnóstico por TC de crânio. O manejo inicial inclui trombólise com alteplase em até 4,5 horas para isquêmico.', 'clinica_medica', 'Neurologia', 'AVC', ARRAY['isquemico', 'hemorragico', 'trombolise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Classificação da epilepsia?', 'A epilepsia é caracterizada por crises recorrentes, classificadas como parciais (focais) ou generalizadas (tônico-clônicas). EEG ajuda no diagnóstico. O tratamento inicia com carbamazepina ou valproato, ajustado por tipo de crise.', 'clinica_medica', 'Neurologia', 'epilepsia', ARRAY['crises', 'eeg', 'anticonvulsivantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas da doença de Parkinson?', 'A doença de Parkinson é neurodegenerativa com tremor de repouso, bradicinesia e rigidez, por perda de neurônios dopaminérgicos na substância negra. Diagnóstico clínico. O tratamento usa levodopa/carbidopa para alívio sintomático.', 'clinica_medica', 'Neurologia', 'Parkinson', ARRAY['tremor', 'dopamina', 'levodopa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios para esclerose múltipla?', 'A esclerose múltipla é desmielinizante com disseminação no espaço e tempo, diagnosticada por critérios McDonald com RM e liquor. Sintomas incluem neurite óptica e fraqueza. O tratamento usa interferons e ocrelizumabe.', 'clinica_medica', 'Neurologia', 'esclerose múltipla', ARRAY['desmielinizante', 'rm', 'imunomoduladores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tipos de cefaleia primária?', 'As cefaleias primárias incluem enxaqueca com aura, cefaleia tensional e em salvas, sem causa subjacente. Enxaqueca tem fotofobia e náuseas. O tratamento agudo usa triptanos, profilático com betabloqueadores.', 'clinica_medica', 'Neurologia', 'cefaleia', ARRAY['enxaqueca', 'tensional', 'triptanos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Causas de neuropatia periférica?', 'A neuropatia periférica pode ser diabética, alcoólica ou por deficiências vitamínicas, com parestesias e perda sensitiva distal. Diagnóstico por EMG e biópsia nervosa. O tratamento é etiológico, com gabapentina para dor.', 'clinica_medica', 'Neurologia', 'neuropatia periférica', ARRAY['nervos', 'diabetes', 'gabapentina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sinais de meningite bacteriana?', 'A meningite bacteriana apresenta febre, rigidez nucal e fotofobia, causada por pneumococo ou meningococo. Punção lombar mostra pleocitose neutrofílica. O tratamento empírico é ceftriaxona e vancomicina.', 'clinica_medica', 'Neurologia', 'meningite', ARRAY['bacteriana', 'punção', 'antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estágios da doença de Alzheimer?', 'A doença de Alzheimer é demência progressiva com perda de memória, afasia e apraxia, por placas amiloides e emaranhados neurofibrilares. Diagnóstico por critérios NIA-AA e RM. O tratamento sintomático usa donepezila.', 'clinica_medica', 'Neurologia', 'Alzheimer', ARRAY['demencia', 'memoria', 'donepezila']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diagnóstico de miastenia gravis?', 'A miastenia gravis é autoimune com fraqueza muscular flutuante, piorando com esforço, por anticorpos anti-AChR. Teste de edrofônio e EMG confirmam. O tratamento usa piridostigmina e timectomia.', 'clinica_medica', 'Neurologia', 'miastenia gravis', ARRAY['autoimune', 'fraqueza', 'piridostigmina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Fases da síndrome de Guillain-Barré?', 'A síndrome de Guillain-Barré é polirradiculoneuropatia pós-infecciosa com fraqueza ascendente e arreflexia. Líquido cefalorraquidiano mostra proteinorraquia dissociada. O tratamento é imunoglobulina IV ou plasmaférese.', 'clinica_medica', 'Neurologia', 'Guillain-Barré', ARRAY['neuropatia', 'fraqueza', 'imunoglobulina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estágios do HIV e tratamento?', 'O HIV progride de infecção aguda para crônica assintomática e AIDS com CD4 <200, causando infecções oportunistas. Diagnóstico por teste rápido (TR1) e ELISA 4ª geração (Ag/Ab) com confirmação por segundo teste rápido ou ELISA de fabricante diferente. Western blot foi removido do protocolo brasileiro desde 2014. O tratamento é HAART com antirretrovirais como tenofovir.', 'clinica_medica', 'Infectologia', 'HIV/AIDS', ARRAY['retrovirus', 'cd4', 'haart']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diagnóstico de tuberculose pulmonar?', 'A tuberculose é causada por Mycobacterium tuberculosis, com tosse crônica, hemoptise e febre noturna. Diagnóstico por baciloscopia, cultura e teste de Mantoux. O tratamento é RIPE por 6 meses.', 'clinica_medica', 'Infectologia', 'tuberculose', ARRAY['micobateria', 'pulmonar', 'ripe']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Transmissão das hepatites virais?', 'As hepatites B e C são transmitidas por sangue e sexualmente, causando hepatite aguda ou crônica com cirrose. Sorologia detecta HBsAg para B e anti-HCV para C. O tratamento para C usa sofosbuvir.', 'clinica_medica', 'Infectologia', 'hepatites virais', ARRAY['viral', 'figado', 'antivirais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas e prevenção da dengue?', 'A dengue é arbovirose por Aedes aegypti com febre, dor retro-orbital e mialgias, podendo evoluir para choque. Diagnóstico sorológico. A prevenção é eliminação de criadouros e repelentes.', 'clinica_medica', 'Infectologia', 'dengue', ARRAY['arbovirus', 'febre', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Ciclo de vida da malária?', 'A malária é causada por Plasmodium, transmitida por Anopheles, com ciclos hepático e eritrocítico causando febre cíclica. Diagnóstico por gota espessa. O tratamento usa artemisinina combinada.', 'clinica_medica', 'Infectologia', 'malária', ARRAY['plasmodium', 'febre', 'artemisinina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Apresentação clínica da leptospirose?', 'A leptospirose é zoonose por Leptospira com febre, icterícia e insuficiência renal na forma de Weil. Diagnóstico sorológico. O tratamento é penicilina ou doxiciclina.', 'clinica_medica', 'Infectologia', 'leptospirose', ARRAY['zoonose', 'ictericia', 'antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estágios da sífilis?', 'A sífilis é causada por Treponema pallidum, com chancro primário, rash secundário e goma terciária. Diagnóstico por VDRL e FTA-ABS. O tratamento é penicilina G benzatina.', 'clinica_medica', 'Infectologia', 'sífilis', ARRAY['treponema', 'estagios', 'penicilina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios de Duke para endocardite?', 'A endocardite infecciosa é infecção valvar com febre, sopro e embolias, diagnosticada por critérios de Duke com hemoculturas positivas. Ecocardiograma mostra vegetações. O tratamento é antibióticos IV por 4-6 semanas.', 'clinica_medica', 'Infectologia', 'endocardite', ARRAY['valvar', 'hemoculturas', 'antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Definição e manejo da sepse?', 'A sepse é disfunção orgânica por resposta desregulada a infecção, com qSOFA ≥2. Identificar foco infeccioso e culturas. O tratamento é antibióticos precoces e suporte hemodinâmico.', 'clinica_medica', 'Infectologia', 'sepse', ARRAY['infecção', 'orgânica', 'antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Infecções oportunistas em AIDS?', 'Em AIDS, infecções oportunistas como Pneumocystis jirovecii pneumonia e toxoplasmose ocorrem com CD4 baixo. Profilaxia com trimetoprima-sulfametoxazol. Diagnóstico por biópsia e sorologia.', 'clinica_medica', 'Infectologia', 'infecções oportunistas', ARRAY['aids', 'cd4', 'profilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Características da psoríase?', 'A psoríase é doença inflamatória crônica com placas eritematoescamosas em cotovelos e joelhos, associada a artrite. Histologia mostra hiperqueratose. O tratamento tópico usa corticoides e fototerapia UVB.', 'clinica_medica', 'Dermatologia', 'psoríase', ARRAY['inflamatoria', 'placas', 'fototerapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Fatores desencadeantes da dermatite atópica?', 'A dermatite atópica é eczema pruriginoso em flexuras, associado a atopia como asma. Barreiras cutâneas alteradas por filagrina. O tratamento é emolientes, corticoides tópicos e tacrolimo.', 'clinica_medica', 'Dermatologia', 'dermatite atópica', ARRAY['eczema', 'atopia', 'emolientes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sinais de melanoma maligno?', 'O melanoma é neoplasia melanocítica com assimetria, bordas irregulares, cor variada e diâmetro >6mm (ABCDE). Risco por exposição solar e nevos. O tratamento é excisão cirúrgica com margens.', 'clinica_medica', 'Dermatologia', 'melanoma', ARRAY['neoplasia', 'abcde', 'excisão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tratamento de micoses superficiais?', 'As micoses superficiais como tinea pedis são causadas por dermatófitos, com descamação e prurido. Diagnóstico por KOH. O tratamento usa antifúngicos tópicos como clotrimazol.', 'clinica_medica', 'Dermatologia', 'micoses superficiais', ARRAY['dermatofitos', 'kOH', 'antifúngicos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Classificação da hanseníase?', 'A hanseníase é por Mycobacterium leprae, classificada em paucibacilar e multibacilar por classificação operacional da OMS (paucibacilar ≤5 lesões e multibacilar >5 lesões) ou espectro clínico de Ridley-Jopling. Hipopigmentadas máculas com anestesia. O tratamento é poliquimioterapia com rifampicina.', 'clinica_medica', 'Dermatologia', 'hanseníase', ARRAY['micobateria', 'espectro', 'pqt']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tipos de pênfigo?', 'O pênfigo é autoimune bolhoso com pênfigo vulgar (mucosas) e foliáceo (cutâneo), por anticorpos desmossômicos. Biópsia mostra acantólise. O tratamento é prednisona e rituximabe.', 'clinica_medica', 'Dermatologia', 'pênfigo', ARRAY['autoimune', 'bolhas', 'corticoides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Causas de urticária aguda?', 'A urticária é reação alérgica com pápulas pruriginosas efêmeras, desencadeada por alimentos, drogas ou infecções. Histamina mediada. O tratamento usa anti-histamínicos H1 como loratadina.', 'clinica_medica', 'Dermatologia', 'urticária', ARRAY['alergia', 'prurido', 'antihistaminicos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Graus de acne vulgar?', 'A acne é obstrução folicular com comedões, pápulas e nódulos, por Propionibacterium acnes. Graus de leve a grave. O tratamento tópico usa peróxido de benzoíla, sistêmico isotretinoína em grave.', 'clinica_medica', 'Dermatologia', 'acne', ARRAY['follicular', 'comedoes', 'isotretinoina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Mecanismos de farmacodermias?', 'As farmacodermias são reações cutâneas a drogas, como exantema morbiliforme ou síndrome de Stevens-Johnson. Tipo IV de hipersensibilidade. O tratamento é suspender o fármaco e corticoides em graves.', 'clinica_medica', 'Dermatologia', 'farmacodermias', ARRAY['reacoes', 'drogas', 'suspensao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manifestações cutâneas do lúpus?', 'O lúpus cutâneo inclui discoide com cicatrizes atróficas e subagudo com anéis psoriasiformes, associado a LES. Fotossensibilidade comum. O tratamento usa protetor solar e hidroxicloroquina.', 'clinica_medica', 'Dermatologia', 'lúpus cutâneo', ARRAY['autoimune', 'fotossensivel', 'hidroxicloroquina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é envelhecimento saudável na geriatria?', 'O envelhecimento saudável envolve manutenção da funcionalidade física e cognitiva, com ênfase em atividade física regular, dieta equilibrada e controle de comorbidades. Na geriatria, promove-se a prevenção de fragilidade e sarcopenia por meio de exercícios e nutrição adequada. Isso reduz o risco de quedas e hospitalizações em idosos.', 'clinica_medica', 'Geriatria', 'Envelhecimento', ARRAY['geriatria','envelhecimento','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o risco de polifarmácia em idosos?', 'A polifarmácia, uso de cinco ou mais medicamentos, aumenta o risco de interações medicamentosas, reações adversas e adesão ruim em idosos. Na geriatria, recomenda-se revisão periódica dos medicamentos para deprescribir itens desnecessários. Isso previne eventos como quedas e delirium induzidos por fármacos.', 'clinica_medica', 'Geriatria', 'Polifarmácia', ARRAY['geriatria','polifarmácia','medicamentos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como prevenir quedas em idosos?', 'A prevenção de quedas em idosos inclui avaliação de risco com ferramentas como o Timed Up and Go, correção de fatores ambientais e uso de dispositivos de auxílio. Medicamentos que causam tontura devem ser ajustados, e exercícios de equilíbrio são recomendados. Isso reduz morbimortalidade associada a fraturas.', 'clinica_medica', 'Geriatria', 'Quedas', ARRAY['geriatria','quedas','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza a demência em idosos?', 'A demência é um síndrome com declínio cognitivo progressivo afetando memória, orientação e função diária, comum em Alzheimer e vascular. Na geriatria, diagnóstico envolve história clínica, testes como MMSE e exclusão de causas reversíveis. Tratamento foca em manejo sintomático e suporte familiar.', 'clinica_medica', 'Geriatria', 'Demência', ARRAY['geriatria','demência','cognitivo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual o conceito de fragilidade geriátrica?', 'A fragilidade é um estado de vulnerabilidade aumentada a estressores, caracterizado por perda de reserva fisiológica em múltiplos sistemas. Critérios de Fried incluem perda de peso involuntária, exaustão e lentidão. Intervenções precoces com exercício e nutrição podem reverter ou retardar o processo.', 'clinica_medica', 'Geriatria', 'Fragilidade', ARRAY['geriatria','fragilidade','idosos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como identificar delirium em idosos?', 'O delirium é um distúrbio agudo de atenção e cognição, frequentemente causado por infecções, medicamentos ou desidratação em idosos. Sinais incluem flutuação do nível de consciência e alucinações. Diagnóstico usa CAM, e tratamento envolve correção da causa subjacente e ambiente calmo.', 'clinica_medica', 'Geriatria', 'Delirium', ARRAY['geriatria','delirium','agudo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é sarcopenia e seu manejo?', 'A sarcopenia é a perda progressiva de massa e força muscular relacionada à idade, aumentando risco de quedas e dependência. Diagnóstico por critérios EWGSOP inclui medida de força de preensão. Manejo envolve treinamento resistido, proteína adequada e vitamina D.', 'clinica_medica', 'Geriatria', 'Sarcopenia', ARRAY['geriatria','sarcopenia','muscular']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual avaliação inicial em idoso com queixa de memória?', 'A avaliação inicial inclui história detalhada, exame físico e testes cognitivos como Mini-Mental. Excluir causas reversíveis como hipotiroidismo ou B12 deficiente. Na geriatria, encaminhar para neuroimagem se suspeita de demência.', 'clinica_medica', 'Geriatria', 'Envelhecimento', ARRAY['geriatria','memória','avaliação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Riscos da polimedicação em geriatria?', 'Riscos incluem interações, toxicidade e não adesão, levando a hospitalizações. Ferramentas como Beers Criteria guiam prescrição segura. Revisão anual é essencial para otimizar terapia.', 'clinica_medica', 'Geriatria', 'Polifarmácia', ARRAY['geriatria','riscos','medicamentos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Protocolo para idoso após queda?', 'Protocolo inclui avaliação de lesões, ABCDE, história de medicamentos e comorbidades. Exames como RX e hemograma se indicado. Prevenção futura com fisioterapia e remoção de riscos ambientais.', 'clinica_medica', 'Geriatria', 'Quedas', ARRAY['geriatria','protocolo','queda']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Qual protocolo para dor torácica em emergências?', 'O protocolo para dor torácica inicia com avaliação ABC, ECG em 10 minutos e história de fatores de risco. Administrar aspirina se suspeita de SCA, e monitorar para infarto. Diferencial inclui dissecção aórtica e embolia pulmonar.', 'clinica_medica', 'Emergências clínicas', 'Dor torácica', ARRAY['emergências','dor torácica','protocolo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é o código para AVC em emergências?', 'O código AVC ativa equipe multidisciplinar para trombólise em até 4,5 horas de início dos sintomas. Inclui TC de crânio imediata e avaliação NIHSS. Meta é door-to-needle em 60 minutos para elegíveis.', 'clinica_medica', 'Emergências clínicas', 'Código AVC', ARRAY['emergências','AVC','trombólise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo inicial de anafilaxia?', 'Manejo inicial de anafilaxia é intramuscular de epinefrina 0,3-0,5 mg em adultos, seguido de oxigênio e fluidos IV. Monitorar para reação bifásica e evitar anti-histamínicos como único tratamento. Transferir para observação.', 'clinica_medica', 'Emergências clínicas', 'Anafilaxia', ARRAY['emergências','anafilaxia','epinefrina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tratamento de status epilepticus?', 'O status epilepticus requer benzodiazepínicos IV como lorazepam 0,1 mg/kg inicialmente, seguido de fenitoína se persiste. Intubação se necessário para proteção das vias aéreas. Investigar causa subjacente como hipoglicemia.', 'clinica_medica', 'Emergências clínicas', 'Status epilepticus', ARRAY['emergências','epilepsia','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Emergência hipertensiva: quando intervir?', 'Emergência hipertensiva é PA >180/120 com lesão em órgão-alvo como encefalopatia. Reduzir PA gradualmente com nitroprussiato ou labetalol IV. Meta é 25% redução em horas, evitando hipotensão.', 'clinica_medica', 'Emergências clínicas', 'Hipertensiva', ARRAY['emergências','hipertensão','urgência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sinais de dissecção aórtica em dor torácica?', 'Sinais incluem dor súbita e irradiada para costas, assimetria de pulsos e hipertensão. ECG normaliza diferencial de IAM. TC angiografia confirma diagnóstico.', 'clinica_medica', 'Emergências clínicas', 'Dor torácica', ARRAY['emergências','dissecção','aórtica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios para trombólise em AVC isquêmico?', 'Critérios incluem início <4,5h, sem contraindicações como sangramento recente ou cirurgia. NIHSS ≥4 e TC sem hemorragia. Benefício máximo em janelas iniciais.', 'clinica_medica', 'Emergências clínicas', 'Código AVC', ARRAY['emergências','trombólise','AVC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Segunda dose de epinefrina em anafilaxia?', 'Segunda dose se sintomas persistem após 5-15 min da primeira. Manter via aérea pérvia e corticoides IV para prevenção de recorrência. Educar paciente sobre auto-injetor.', 'clinica_medica', 'Emergências clínicas', 'Anafilaxia', ARRAY['emergências','segunda dose','reação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Refractário status epilepticus: próximo passo?', 'Após benzodiazepínicos e fenitoína, usar fenobarbital ou propofol. EEG contínuo monitora atividade. Controle de temperatura e glicemia é essencial.', 'clinica_medica', 'Emergências clínicas', 'Status epilepticus', ARRAY['emergências','refratário','epilepsia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo de urgência hipertensiva sem lesão?', 'Urgência hipertensiva sem lesão em órgão-alvo permite redução oral gradual com IECA ou betabloqueadores. Monitorar em 24-48h. Evitar redução rápida para prevenir isquemia.', 'clinica_medica', 'Emergências clínicas', 'Hipertensiva', ARRAY['emergências','urgência','PA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que são bundles para sepse?', 'Bundles para sepse incluem medida de lactato em 1h, culturas antes de antibióticos e fluidos 30ml/kg em choque. Surviving Sepsis Campaign guia o manejo precoce. Meta é ressuscitação em 3h.', 'clinica_medica', 'Medicina intensiva', 'Sepse', ARRAY['intensiva','sepse','bundles']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Indicações para ventilação mecânica?', 'Indicações incluem falência respiratória aguda com PaO2/FiO2 <300, hipercapnia ou falha de desmame. Modos como volume-controlado ajustam tidal volume 6ml/kg. Monitorar PEEP para evitar barotrauma.', 'clinica_medica', 'Medicina intensiva', 'Ventilação', ARRAY['intensiva','ventilação','mecânica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tipos de choque e tratamento inicial?', 'Tipos incluem hipovolêmico (fluidos), cardiogênico (inotrópicos), distributivo (sepse, vasopressores) e obstrutivo (desobstruir). Avaliar com eco e lactato. Ressuscitação guiada por metas hemodinâmicas.', 'clinica_medica', 'Medicina intensiva', 'Choque', ARRAY['intensiva','choque','tipos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Fármacos vasoativos em UTI?', 'Norepinefrina é primeira linha em choque séptico para manter PAM >65mmHg. Vasopressina adiciona em refratários, dobutamina para suporte inotrópico. Titular com monitoração invasiva.', 'clinica_medica', 'Medicina intensiva', 'Vasoativos', ARRAY['intensiva','vasoativos','fármacos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Definição e manejo de ARDS?', 'ARDS é lesão pulmonar aguda com PaO2/FiO2 ≤300, opacidades bilaterais e sem cardiogênese. Manejo com ventilação protetora (tidal 6ml/kg, Pplat <30), PEEP otimizado e prone se grave. ECMO em refratários.', 'clinica_medica', 'Medicina intensiva', 'ARDS', ARRAY['intensiva','ARDS','pulmonar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios de sepse grave?', 'Sepse (Sepsis-3, 2016) é infecção com disfunção orgânica (SOFA ≥2). O termo ''sepse grave'' foi abolido. Iniciar antibióticos empíricos em 1h e controle do foco. Fluidos e vasopressores mantêm perfusão.', 'clinica_medica', 'Medicina intensiva', 'Sepse', ARRAY['intensiva','grave','critérios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Desmame de ventilação mecânica?', 'Desmame inicia com SBT (spontaneous breathing trial) por 30-120 min se paciente estável. Critérios incluem RSBI <105 e resolução da causa. Falha leva a reinstituição.', 'clinica_medica', 'Medicina intensiva', 'Ventilação', ARRAY['intensiva','desmame','SBT']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Choque distributivo: características?', 'Características incluem vasodilatação, hiperdinâmica e resposta inicial a fluidos. Causas como sepse demandam antibióticos e noradrenalina. Eco avalia função cardíaca.', 'clinica_medica', 'Medicina intensiva', 'Choque', ARRAY['intensiva','distributivo','sepse']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Escolha de vasopressor em cardiogênico?', 'Em choque cardiogênico, dobutamina ou milrinona para inotropia, com norepinefrina se hipotensão persiste. Evitar sobrecarga volêmica. Balão intra-aórtico se refratário.', 'clinica_medica', 'Medicina intensiva', 'Vasoativos', ARRAY['intensiva','cardiogênico','inotrópicos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Berlin criteria para ARDS?', 'Critérios incluem timing <1 semana, hipoxemia, opacidades não explicadas por efusões e origem não cardíaca. Gravidade: leve 200-300, moderada 100-200, grave <100 PaO2/FiO2.', 'clinica_medica', 'Medicina intensiva', 'ARDS', ARRAY['intensiva','Berlin','critérios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios diagnósticos de depressão?', 'Depressão maior requer 5 ou mais sintomas por 2 semanas, incluindo humor deprimido ou anedonia. DSM-5 inclui fadiga, culpa e ideação suicida. Avaliar com PHQ-9 em clínica.', 'clinica_medica', 'Psiquiatria', 'Depressão', ARRAY['psiquiatria','depressão','DSM']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo inicial de ansiedade aguda?', 'Manejo inclui benzodiazepínicos de ação curta como lorazepam para alívio imediato, e terapia cognitivo-comportamental a longo prazo. Identificar gatilhos e comorbidades. SSRIs para transtorno generalizado.', 'clinica_medica', 'Psiquiatria', 'Ansiedade', ARRAY['psiquiatria','ansiedade','manejo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Fases do transtorno bipolar?', 'Fases incluem mania (euforia, insônia, grandiosidade), hipomania e depressão alternadas. Diagnóstico por história de episódios. Estabilizadores como lítio previnem recidivas.', 'clinica_medica', 'Psiquiatria', 'Bipolar', ARRAY['psiquiatria','bipolar','fases']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas positivos da esquizofrenia?', 'Sintomas positivos incluem delírios, alucinações e pensamento desorganizado. Antipsicóticos atípicos como risperidona tratam esses. Reabilitação psicossocial é essencial.', 'clinica_medica', 'Psiquiatria', 'Esquizofrenia', ARRAY['psiquiatria','esquizofrenia','positivos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Farmacologia em transtornos psiquiátricos?', 'SSRIs como sertralina para depressão e ansiedade; lítio para bipolar; antipsicóticos para esquizofrenia. Monitorar efeitos colaterais como síndrome serotoninérgica. Ajustar doses gradualmente.', 'clinica_medica', 'Psiquiatria', 'Psicofarmacologia', ARRAY['psiquiatria','farmacologia','SSRIs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Avaliação de risco suicida?', 'Avaliação usa escalas como SAD PERSONS, indagando plano, intento e meios. Internar se alto risco. Intervenção inclui remoção de acesso a letal e terapia dialética.', 'clinica_medica', 'Psiquiatria', 'Suicídio', ARRAY['psiquiatria','suicídio','risco']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diferencial de depressão em clínica médica?', 'Diferencial inclui hipotireoidismo, anemia e câncer. Avaliar com TSH, hemograma e história. Tratar causa subjacente antes de antidepressivos.', 'clinica_medica', 'Psiquiatria', 'Depressão', ARRAY['psiquiatria','diferencial','médica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Terapias para transtorno de ansiedade?', 'Terapias incluem TCC para exposição gradual e mindfulness. Medicamentos como buspirona para longo prazo. Combinar para melhores outcomes.', 'clinica_medica', 'Psiquiatria', 'Ansiedade', ARRAY['psiquiatria','terapias','TCC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manía aguda: tratamento?', 'Tratamento com antipsicóticos como haloperidol e benzodiazepínicos para agitação. Lítio ou valproato para manutenção. Hospitalizar se risco.', 'clinica_medica', 'Psiquiatria', 'Bipolar', ARRAY['psiquiatria','manía','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas negativos na esquizofrenia?', 'Sintomas negativos incluem apatia, anedonia e isolamento social. Clozapina para refratários. Suporte social melhora qualidade de vida.', 'clinica_medica', 'Psiquiatria', 'Esquizofrenia', ARRAY['psiquiatria','negativos','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Marcadores tumorais: PSA para próstata?', 'PSA elevado sugere câncer de próstata, mas não específico (BPH ou prostatite). Níveis >4 ng/ml indicam biópsia. Monitorar pós-tratamento para recorrência.', 'clinica_medica', 'Oncologia clínica', 'Marcadores', ARRAY['oncologia','PSA','próstata']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estadiamento TNM: o que significa?', 'TNM descreve tamanho do tumor (T), linfonodos (N) e metástases (M). Guia prognóstico e terapia, como quimio em estágio IV. AJCC atualiza critérios por tipo.', 'clinica_medica', 'Oncologia clínica', 'Estadiamento', ARRAY['oncologia','TNM','estágio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Síndromes paraneoplásicas comuns?', 'Síndromes incluem hipercalcemia por PTHrP em pulmão escamoso e SIADH por SCLC. Tratamento da causa oncológica resolve a maioria. Sintomas guiam suspeita.', 'clinica_medica', 'Oncologia clínica', 'Paraneoplásicas', ARRAY['oncologia','síndromes','paraneoplásicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo da dor oncológica?', 'Manejo segue escada da OMS: não opioides, opioides fracos, fortes como morfina. Adjuvantes para neuropática. Avaliar com escala numérica e rotacionar se tolerância.', 'clinica_medica', 'Oncologia clínica', 'Dor', ARRAY['oncologia','dor','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Cuidados paliativos: princípios?', 'Princípios incluem controle de sintomas, suporte psicológico e qualidade de vida. Equipe multidisciplinar aborda dor, náusea e ansiedade. Discutir metas no final da vida.', 'clinica_medica', 'Oncologia clínica', 'Paliativos', ARRAY['oncologia','paliativos','cuidados']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'CEA como marcador em colorretal?', 'CEA elevado em câncer colorretal monitora resposta e recorrência pós-cirurgia. Níveis normais <5 ng/ml. Não para screening devido a falsos positivos.', 'clinica_medica', 'Oncologia clínica', 'Marcadores', ARRAY['oncologia','CEA','colorretal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estágios de mama pelo TNM?', 'Estágio I: T1N0M0 pequeno e localizado; IV: metástases distantes. Mamografia e sentinel node guiam. Terapia adjuvante baseada em HR e HER2.', 'clinica_medica', 'Oncologia clínica', 'Estadiamento', ARRAY['oncologia','mama','TNM']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Síndrome de Cushing paraneoplásica?', 'Causada por ACTH ectópico em SCLC, com hipertensão e hipocalemia. Dexametasona supressão testa. Quimio trata o tumor primário.', 'clinica_medica', 'Oncologia clínica', 'Paraneoplásicas', ARRAY['oncologia','Cushing','ACTH']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Opioides para dor oncológica crônica?', 'Morfina ou oxicodona titulada para alívio, com laxantes para constipação. PCA para dor irruptiva. Rotação evita dependência percebida.', 'clinica_medica', 'Oncologia clínica', 'Dor', ARRAY['oncologia','opioides','crônica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'DNR em cuidados paliativos?', 'DNR (não ressuscitar) discute preferências do paciente para evitar intervenções fúteis. Suporte hospice foca em conforto. Família participa das decisões.', 'clinica_medica', 'Oncologia clínica', 'Paliativos', ARRAY['oncologia','DNR','hospice']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é LER/DORT na medicina do trabalho?', 'LER/DORT refere-se a Lesões por Esforços Repetitivos e Distúrbios Osteomusculares Relacionados ao Trabalho. Principais fatores de risco incluem posturas inadequadas, força excessiva e falta de pausas. Prevenção envolve ergonomia e ginástica laboral.', 'clinica_medica', 'Medicina do trabalho', 'LER/DORT', ARRAY['ergonomia', 'lesoes', 'prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são as principais intoxicações ocupacionais?', 'Intoxicações ocupacionais ocorrem por exposição a solventes, metais pesados e pesticidas. Sintomas variam de irritação cutânea a danos neurológicos graves. Prevenção inclui uso de EPIs e monitoramento via PCMSO.', 'clinica_medica', 'Medicina do trabalho', 'intoxicações ocupacionais', ARRAY['quimicos', 'exposicao', 'epis']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que caracteriza dermatoses ocupacionais?', 'Dermatoses ocupacionais são lesões cutâneas causadas por irritantes ou alérgenos no trabalho. Exemplos incluem dermatite de contato e acne ocupacional. Diagnóstico por história ocupacional; tratamento com corticoides e remoção da exposição.', 'clinica_medica', 'Medicina do trabalho', 'dermatoses ocupacionais', ARRAY['pele', 'alergia', 'diagnostico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é PAIR na medicina do trabalho?', 'PAIR é a Perda Auditiva Induzida por Ruído, condição irreversível causada por exposição prolongada a níveis sonoros elevados. Caracteriza-se por perda neurossensorial bilateral simétrica. Prevenção com protetores auriculares e controle de ruído.', 'clinica_medica', 'Medicina do trabalho', 'PAIR', ARRAY['auditiva', 'ruido', 'prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais são as pneumoconioses mais comuns?', 'Pneumoconioses são doenças pulmonares por inalação de poeiras minerais: silicose, asbestose e pneumoconiose do carvão. Causam fibrose pulmonar progressiva. Diagnóstico por radiografia e história ocupacional.', 'clinica_medica', 'Medicina do trabalho', 'pneumoconioses', ARRAY['pulmonar', 'poeira', 'fibrose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais aspectos da legislação trabalhista protegem a saúde?', 'A CLT e NRs como NR-7 (PCMSO) e NR-9 (PPRA) estabelecem obrigações para saúde ocupacional. Incluem exames admissionais, periódicos e demissionais. Direitos incluem afastamento por CAT e benefícios previdenciários.', 'clinica_medica', 'Medicina do trabalho', 'legislação trabalhista', ARRAY['clt', 'normas', 'direitos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é PCMSO e sua importância?', 'PCMSO é o Programa de Controle Médico de Saúde Ocupacional, obrigatório para empresas. Inclui exames admissionais, periódicos e de mudança de função. Ajuda na prevenção de doenças ocupacionais e conformidade legal.', 'clinica_medica', 'Medicina do trabalho', 'PCMSO', ARRAY['programa', 'exames', 'ocupacional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como classificar acidentes de trabalho?', 'Acidentes de trabalho são típicos (durante o serviço) ou atípicos (fora, mas relacionados). Tipos incluem agudos (fraturas) e crônicos (doenças). Comunicação via CAT para fins previdenciários.', 'clinica_medica', 'Medicina do trabalho', 'acidentes de trabalho', ARRAY['cat', 'classificacao', 'prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que envolve ergonomia no trabalho?', 'Ergonomia estuda adaptação do trabalho ao homem, prevenindo lesões por design de postos adequados. Princípios incluem postura neutra, alcance acessível e pausas. Reduz LER/DORT e aumenta produtividade.', 'clinica_medica', 'Medicina do trabalho', 'ergonomia', ARRAY['posto', 'postura', 'design']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais os impactos da saúde mental no trabalho?', 'Saúde mental no trabalho envolve estresse, burnout e depressão por sobrecarga e assédio. Prevenção por programas de suporte psicológico e equilíbrio trabalho-vida. Intervenção precoce é essencial.', 'clinica_medica', 'Medicina do trabalho', 'saúde mental no trabalho', ARRAY['estresse', 'burnout', 'suporte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais diretrizes para rastreamento de câncer em adultos?', 'Rastreamento inclui mamografia para mulheres >40 anos, Rastreamento de câncer de próstata: SBU recomenda PSA + toque retal para homens >50 anos (ou >45 se fatores de risco). INCA/MS NÃO recomendam rastreamento populacional — decisão compartilhada, e colonoscopia a partir de 50 anos. Baseia-se em risco individual. Objetivo é detecção precoce para melhor prognóstico.', 'clinica_medica', 'Medicina preventiva', 'rastreamento de câncer', ARRAY['deteccao', 'mamografia', 'psa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais vacinas são recomendadas para adultos?', 'Vacinação em adultos inclui hepatite B, HPV até 45 anos, influenza anual e pneumocócica para idosos. Calendário PNI orienta doses de tétano-difteria e herpes zoster. Importante para grupos de risco.', 'clinica_medica', 'Medicina preventiva', 'vacinação adultos', ARRAY['calendario', 'pni', 'imunizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como prevenir eventos cardiovasculares?', 'Prevenção cardiovascular envolve controle de hipertensão, dislipidemia e tabagismo via estilo de vida e estatinas. Avaliação de risco com escore Framingham. Meta é reduzir mortalidade por infarto e AVC.', 'clinica_medica', 'Medicina preventiva', 'prevenção cardiovascular', ARRAY['risco', 'estatinas', 'estilo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que compõe um check-up preventivo?', 'Check-up inclui anamnese, exame físico, hemograma, glicemia e lipidograma, adaptado à idade e sexo. Para mulheres, Papanicolaou; para homens, PSA. Frequência anual ou bienal.', 'clinica_medica', 'Medicina preventiva', 'check-up', ARRAY['exames', 'anual', 'adaptado']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é profilaxia pré e pós-exposição?', 'PrEP para HIV em profissionais de risco; PEP com antirretrovirais após exposição. Para hepatites, imunoglobulina e vacina. Início imediato para eficácia.', 'clinica_medica', 'Medicina preventiva', 'profilaxia pré/pós-exposição', ARRAY['hiv', 'pep', 'vacina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como prevenir quedas em idosos?', 'Prevenção inclui avaliação de equilíbrio, correção de visão e calçados adequados. Exercícios de fortalecimento e remoção de obstáculos ambientais. Suplementação de vitamina D se deficiente.', 'clinica_medica', 'Medicina preventiva', 'prevenção de quedas idosos', ARRAY['equilibrio', 'exercicios', 'vitaminad']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Estratégias para cessação do tabagismo?', 'Cessação usa aconselhamento, reposição nicotínica e bupropiona/vareniclina. Abordagem stepwise com suporte psicológico. Redução de risco cardiovascular em meses.', 'clinica_medica', 'Medicina preventiva', 'cessação tabagismo', ARRAY['nicotina', 'bupropiona', 'aconselhamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Recomendações para atividade física?', 'Recomenda-se 150 min/sem de aeróbica moderada mais fortalecimento 2x/semana. Para sedentários, iniciar gradual. Reduz risco de obesidade, diabetes e depressão.', 'clinica_medica', 'Medicina preventiva', 'atividade física', ARRAY['aerobica', 'fortalecimento', 'semanal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Como prevenir diabetes tipo 2?', 'Prevenção envolve dieta balanceada, exercício e controle de peso, com metformina em alto risco. Rastreio por glicemia de jejum em obesos. Meta HbA1c <5,7%.', 'clinica_medica', 'Medicina preventiva', 'prevenção diabetes', ARRAY['dieta', 'exercicio', 'metformina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Abordagens para saúde mental preventiva?', 'Inclui promoção de sono, mindfulness e suporte social para reduzir ansiedade. Rastreio de depressão com PHQ-9 em consultas. Intervenção precoce evita agravamento.', 'clinica_medica', 'Medicina preventiva', 'saúde mental preventiva', ARRAY['mindfulness', 'rastreio', 'suporte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quais sintomas de intoxicação por organofosforados?', 'Causa colinérgico excessivo: salivação, lacrimejamento, diarreia, miose (SLUD). Tratamento com atropina e pralidoxima. Exposição comum em agricultura.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação por organofosforados', ARRAY['colinergico', 'atropina', 'agricultura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Tratamento de intoxicação por benzodiazepínicos?', 'Causa sedação e depressão respiratória; flumazenil é antídoto, mas com risco de convulsões. Suporte com ventilação se necessário. Monitorar abstinência em crônicos.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação por benzodiazepínicos', ARRAY['sedacao', 'flumazenil', 'suporte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo de overdose de opioides?', 'Tríade: coma, miose, depressão respiratória. Naloxona IV ou IM reverte rapidamente. Repetir doses se recidiva; suporte ventilatório essencial.', 'clinica_medica', 'Toxicologia clínica', 'overdose opioides', ARRAY['naloxona', 'coma', 'respiratorio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Protocolo para intoxicação por paracetamol?', 'Causa hepatotoxicidade; N-acetilcisteína é antídoto se níveis >150 mcg/mL em 4h. Avaliar com nomograma de Rumack-Matthew. Monitorar ALT/AST.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação por paracetamol', ARRAY['hepatotoxicidade', 'nac', 'nomograma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Primeiros socorros em envenenamento por cobras?', 'Envenenamento ofídico causa dor local, edema e coagulopatia; soro antiofídico específico. Imobilizar membro, evitar torniquete. Monitorar hematológicos.', 'clinica_medica', 'Toxicologia clínica', 'envenenamento por cobras', ARRAY['soro', 'edema', 'coagulopatia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sintomas de intoxicação alcoólica aguda?', 'Causa euforia, ataxia e hipotermia; suporte com hidratação e glicose. Monitorar hipoglicemia e aspiração. Abstinência pode ocorrer após.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação alcoólica aguda', ARRAY['etanol', 'hidratacao', 'hipoglicemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Características da síndrome serotoninérgica?', 'Excesso de serotonina: hipertermia, rigidez, tremores e confusão. Causada por ISRS + IMAOs. Tratamento com ciproheptadina e benzodiazepínicos.', 'clinica_medica', 'Toxicologia clínica', 'síndrome serotoninérgica', ARRAY['hipertermia', 'ciproheptadina', 'isrs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sinais de intoxicação digitálica?', 'Causa náuseas, arritmias (bradicardia, bloqueios) e hipercalemia. Digoxina imunofab é antídoto para casos graves. Monitorar ECG e potássio.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação digitálica', ARRAY['arritmias', 'digoxina', 'ecg']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'O que é saturnismo e seu tratamento?', 'Intoxicação por chumbo com anemia, dor abdominal e neuropatia. Quelantes como EDTA ou succímero removem metal. Prevenção em indústrias com chumbo.', 'clinica_medica', 'Toxicologia clínica', 'saturnismo', ARRAY['chumbo', 'quelantes', 'anemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Manejo de intoxicação por monóxido de carbono?', 'CO causa cefaleia, confusão e hipóxia; oxigênio hiperbárico acelera eliminação. Carboxihemoglobina >25% indica gravidade. Sintomas mimetizam gripe.', 'clinica_medica', 'Toxicologia clínica', 'intoxicação por monóxido de carbono', ARRAY['oxigenio', 'hiperbarico', 'hipoxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Critérios para tratamento de hepatite B crônica?', 'Trata-se com tenofovir ou entecavir se ALT elevada e DNA-VHB >20.000 UI/mL. Monitorar fibrose hepática e cirrose. Vacinação previne infecção.', 'clinica_medica', 'Hepatologia', 'hepatite B crônica', ARRAY['tenofovir', 'dna', 'vacina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diagnóstico e tratamento da hepatite C?', 'Diagnostica-se por anti-VHC e RNA-VHC; tratada com sofosbuvir + daclatasvir por 12 semanas, cura >95%. Rastreio em grupos de risco. Complicações incluem cirrose.', 'clinica_medica', 'Hepatologia', 'hepatite C', ARRAY['rna', 'sofosbuvir', 'cura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Complicações da cirrose hepática?', 'Causa hipertensão portal, ascite e encefalopatia; Child-Pugh ou MELD estratificam gravidade. Tratamento com diuréticos e beta-bloqueadores. Transplante em casos avançados.', 'clinica_medica', 'Hepatologia', 'cirrose hepática', ARRAY['ascite', 'child', 'transplante']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Graus e tratamento da encefalopatia hepática?', 'Graus I-IV: confusão a coma por amônia elevada. Tratamento com lactulose e rifaximina. Identificar gatilhos como infecção ou sangramento.', 'clinica_medica', 'Hepatologia', 'encefalopatia hepática', ARRAY['amonia', 'lactulose', 'graus']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Rastreio e tratamento de hepatocarcinoma?', 'Rastreia-se por USG e AFP a cada 6 meses em cirróticos. Tratamento por ressecção, TACE ou transplante. Prognóstico depende de estádio Barcelona.', 'clinica_medica', 'Hepatologia', 'hepatocarcinoma', ARRAY['usg', 'afp', 'tace']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Causas e manejo da esteatose hepática?', 'NAFLD por obesidade e diabetes; progride a NASH com inflamação. Manejo com perda de peso, exercício e controle metabólico. Biópsia se suspeita de fibrose.', 'clinica_medica', 'Hepatologia', 'esteatose hepática', ARRAY['nash', 'obesidade', 'perda']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Características da hepatite autoimune?', 'Afeta mulheres jovens, com ANA positivo e hipergammaglobulinemia. Tratamento com prednisona + azatioprina. Monitorar resposta e recidiva.', 'clinica_medica', 'Hepatologia', 'hepatite autoimune', ARRAY['ana', 'prednisona', 'mulheres']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Diagnóstico da doença de Wilson?', 'Acúmulo de cobre hepático; diagnóstico por ceruloplasmina baixa, cobre urinário elevado e anel de Kayser-Fleischer. Tratamento com penicilamina ou trientina.', 'clinica_medica', 'Hepatologia', 'doença de Wilson', ARRAY['cobre', 'ceruloplasmina', 'penicilamina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Sinais e tratamento da hemocromatose?', 'Sobrecarga de ferro hereditária; fadiga, artrite e bronzeamento. Diagnóstico por ferritina alta e mutação HFE. Tratamento por flebotomia regular.', 'clinica_medica', 'Hepatologia', 'hemocromatose', ARRAY['ferro', 'ferritina', 'flebotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Indicações para transplante hepático?', 'Indica-se em falência hepática aguda, cirrose descompensada ou hepatocarcinoma dentro de Milão. MELD prioriza. Pós-operatório com imunossupressores.', 'clinica_medica', 'Hepatologia', 'transplante hepático', ARRAY['meld', 'milao', 'imunossupressao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Indicações e técnica da paracentese?', 'Para ascite sintomática ou diagnóstica; agulha 18G no quadrante inferior sob USG. Analisar líquido para gradiente albumina. Complicações raras.', 'clinica_medica', 'Procedimentos clínicos', 'paracentese', ARRAY['ascite', 'usg', 'albumina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quando realizar toracocentese?', 'Para derrame pleural diagnóstico ou terapêutico; local entre 7-9 espaço intercostal pós-axilar. Analisar pH, proteínas e LDH. Evitar em coagulopatia.', 'clinica_medica', 'Procedimentos clínicos', 'toracocentese', ARRAY['derrame', 'ph', 'ldh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Contraindicações à punção lombar?', 'Para LCR em meningite ou SAH; contraindicações incluem hipertensão intracraniana e coagulopatia. Técnica em L4-L5 com agulha 22G. Complicação: cefaleia.', 'clinica_medica', 'Procedimentos clínicos', 'punção lombar', ARRAY['lcr', 'meningite', 'cefaleia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Técnica de acesso venoso central?', 'Via veia jugular ou subclávia; usar técnica de Seldinger com fio-guia. Confirmar por RX tórax. Complicações: pneumotórax e infecção.', 'clinica_medica', 'Procedimentos clínicos', 'acesso venoso central', ARRAY['seldinger', 'rx', 'pneumotorax']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Passos para intubação orotraqueal?', 'Posição sniff, laringoscopia e tubo 7-8mm. Confirmar por capnografia. Sequência rápida com sedação e bloqueador. Complicações incluem trauma laríngeo.', 'clinica_medica', 'Procedimentos clínicos', 'intubação orotraqueal', ARRAY['laringoscopia', 'capnografia', 'via_aerea']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Indicações para sonda nasogástrica?', 'Para descompressão gástrica em íleo ou nutrição; inserir via narina com lubrificação. Confirmar por RX ou pH aspirado. Cuidados com aspiração.', 'clinica_medica', 'Procedimentos clínicos', 'passagem de sonda nasogástrica', ARRAY['descompressao', 'rx', 'ph']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Técnica de drenagem de tórax?', 'Para pneumotórax ou hemotórax; tubo 28Fr no 5º espaço intercostal. Conectar a selo d''água. Monitorar saída e infecção.', 'clinica_medica', 'Procedimentos clínicos', 'drenagem de tórax', ARRAY['pneumotorax', 'tubo', 'selo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Procedimento de artrocentese?', 'Para artrite ou hemartrose; assepsia, agulha 18-20G no joelho suprapatelar. Analisar líquido sinovial. Aliviar pressão e diagnosticar.', 'clinica_medica', 'Procedimentos clínicos', 'artrocentese', ARRAY['joelho', 'sinovial', 'artrite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Indicações para biópsia de medula óssea?', 'Para leucemia ou anemia aplástica; punção posterior ilíaca com Jamshidi. Analisar morfologia e citogenética. Complicações: sangramento raro.', 'clinica_medica', 'Procedimentos clínicos', 'biópsia de medula óssea', ARRAY['jamshidi', 'leucemia', 'morfologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000001', 'Quando realizar cardioversão elétrica?', 'Para FA ou flutter atrial eletiva; sedação, pás 100-200J bifásico. Anticoagulação prévia se >48h. Monitorar pós-procedimento.', 'clinica_medica', 'Procedimentos clínicos', 'cardioversão elétrica', ARRAY['fa', 'joules', 'anticoagulacao']);


-- ============================================================
-- FLASHCARDS: Cirurgia — 200 cards
-- Deck: a0000001-0000-0000-0000-000000000002
-- ============================================================

INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Trauma, Abdome agudo, Cirurgia vascular, Urologia, Ortopedia
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente de 25 anos com trauma torácico fechado após colisão veicular apresenta dispneia e hipotensão. Qual a conduta inicial?', 'A conduta inicial em trauma torácico fechado com dispneia e hipotensão é estabilização ABCDE, com intubação orotraqueal se necessário, drenagem pleural se pneumotórax, e avaliação com RX tórax e ecografia FAST. Transferir para centro de trauma se instável. Isso segue protocolo ATLS para prevenir choque hemorrágico.', 'cirurgia', 'Trauma', 'Trauma torácico', ARRAY['ATLS','pneumotorax','estabilizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Em trauma craniano grave, qual o valor de GCS que indica intubação protetora?', 'GCS ≤8 indica intubação endotraqueal para proteção das vias aéreas em trauma craniano grave. Manter PaO2 >60mmHg e PaCO2 35-45mmHg para evitar hipertensão intracraniana secundária à hipóxia ou hipercapnia. Monitorar PIC se disponível.', 'cirurgia', 'Trauma', 'Trauma craniano', ARRAY['GCS','intubacao','PIC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com ferimento penetrante abdominal. Quando indicar laparotomia exploradora?', 'Indicar laparotomia exploradora em ferimento penetrante abdominal com evidência de peritonite, evisceração, hemorragia ou instabilidade hemodinâmica. Se estável, pode-se optar por laparoscopia diagnóstica. Seguir protocolo de trauma para avaliação FAST positiva.', 'cirurgia', 'Trauma', 'Trauma abdominal', ARRAY['laparotomia','ferimento_penetrante','FAST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o manejo inicial de fratura exposta de tíbia em vítima de trauma?', 'Manejo inicial: imobilização, antibioticoterapia profilática (cefazolina; adicionar aminoglicosídeo em Gustilo III), tetanização se necessário, e desbridamento cirúrgico o mais precoce possível (janela de 6h não é mais considerada rígida — priorizar qualidade do desbridamento). Classificar por Gustilo-Anderson para guiar tratamento.', 'cirurgia', 'Trauma', 'Fraturas', ARRAY['fratura_exposta','Gustilo','desbridamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Em queimaduras, qual a fórmula para estimar ressuscitação volêmica?', 'Fórmula de Parkland: 4ml/kg/%ESC de Ringer lactato nas primeiras 24h, metade nas primeiras 8h pós-queimadura. Ajustar com base em diurese (0,5-1ml/kg/h) e sinais vitais. Monitorar para evitar sobrecarga ou hipovolemia.', 'cirurgia', 'Trauma', 'Queimaduras', ARRAY['Parkland','ressuscitacao','ESC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente politraumatizado com suspeita de lesão esplênica. Qual exame complementar?', 'Ecografia FAST é o exame inicial para detectar hemoperitônio em lesão esplênica. Se positivo e instável, esplenectomia ou embolização. TC abdome se estável para graduar lesão por escala AAST.', 'cirurgia', 'Trauma', 'Lesões viscerais', ARRAY['FAST','esplenectomia','AAST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o sinal clássico de tamponamento cardíaco em trauma?', 'Tríade de Beck: hipotensão, distensão venosa jugular e sons cardíacos abafados. Confirmar com ecocardiograma ou pericardiocentese. Em trauma penetrante, cirurgia torácica urgente.', 'cirurgia', 'Trauma', 'Trauma cardíaco', ARRAY['Beck','tamponamento','pericardiocentese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Manejo de choque hemorrágico classe III em trauma?', 'Choque classe III (perda 30-40%): ressuscitação com controle de danos (DCR): hemocomponentes precoces na proporção 1:1:1 (CH:PFC:plaquetas), ácido tranexâmico em <3h, hipotensão permissiva (PAS 80-90 mmHg) até hemostasia cirúrgica. Cristaloides em volumes limitados (evitar coagulopatia dilucional). Vasopressores se refratário.', 'cirurgia', 'Trauma', 'Choque hemorrágico', ARRAY['choque_classe','hemocomponentes','dano_controlado']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicação de fasciotomia em trauma de membros?', 'Síndrome compartimental com pressão >30mmHg ou delta P <30mmHg indica fasciotomia urgente para prevenir rabdomiólise e perda do membro. Comum em fraturas associadas a isquemia.', 'cirurgia', 'Trauma', 'Síndrome compartimental', ARRAY['fasciotomia','pressao_compartimental','rabdomiolise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com trauma cervical. Quando suspeitar de lesão medular?', 'Suspeitar em trauma cervical com déficit neurológico, dor radicular ou instabilidade radiológica (NEXUS ou Canadian C-spine). Imobilizar com colar cervical e realizar TC se alto risco.', 'cirurgia', 'Trauma', 'Trauma raquimedular', ARRAY['NEXUS','imobilizacao','TC_cervical']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente de 40 anos com dor abdominal súbita intensa, irradia para ombro. Suspeita de?', 'Suspeita de perfuração visceral, como úlcera gástrica perfurada. Sinais: abdome em tábua, defesa, rebote positivo. Exame: RX abdome em pé para pneumoperitônio livre.', 'cirurgia', 'Abdome agudo', 'Perfuração visceral', ARRAY['perfuracao','pneumoperitonio','ulcera']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Em apendicite aguda, qual o escore de Alvarado para alta probabilidade?', 'Escore de Alvarado ≥7 indica alta probabilidade de apendicite: sintomas (anorexia, náusea), sinais (dor McBurney, rebote), leucocitose >10.000. Indica apendicectomia ou TC confirmatória.', 'cirurgia', 'Abdome agudo', 'Apendicite', ARRAY['Alvarado','McBurney','apendicectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Dor abdominal cólica com vômitos biliosos em idoso. Diagnóstico diferencial?', 'Diagnóstico diferencial: obstrução intestinal por aderências ou volvo. Exame: RX abdome com níveis hidroaéreos. Se suspeita de estrangulamento, laparotomia exploradora urgente.', 'cirurgia', 'Abdome agudo', 'Obstrução intestinal', ARRAY['colica_biliar','niveis_hidroaereos','laparotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente grávida com dor abdominal no QID. Quando suspeitar de gravidez ectópica?', 'Suspeitar de gravidez ectópica em dor QID, amenorreia e beta-HCG positivo sem visualização intrauterina na USG. Sinais de ruptura: hipotensão, dor intensa. Tratamento: salpingectomia laparoscópica.', 'cirurgia', 'Abdome agudo', 'Gravidez ectópica', ARRAY['ectopica','beta_HCG','salpingectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Colecistite aguda: sinais de Murphy positivo. Conduta?', 'Sinais de Murphy positivo indica colecistite. USG confirma litíase e espessamento vesicular. Conduta: antibióticos IV (cefalosporina + metronidazol), colecistectomia laparoscópica em 72h se estável.', 'cirurgia', 'Abdome agudo', 'Colecistite', ARRAY['Murphy','USG','colecistectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Pancreatite aguda: qual critério de Ranson para gravidade?', 'Critério de Ranson na admissão: idade >55, leucócitos >16.000, glicemia >200, LDH >350, AST >250. ≥3 pontos indica gravidade (mortalidade: 3-4 pontos ~15%, 5-6 pontos ~40%, ≥7 ~100%). Prefer APACHE II ou BISAP para prognóstico atual. TC com contraste se complicada.', 'cirurgia', 'Abdome agudo', 'Pancreatite', ARRAY['Ranson','gravidade','TC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Dor abdominal difusa com febre em alcoólatra. Suspeita?', 'Suspeita de pancreatite alcoólica. Amilase/lipase elevadas >3x normal. Classificar por Atlanta: leve ou grave. Suporte: NPO, hidratação, analgésicos. Necrotizante pode requerer necrosectomia.', 'cirurgia', 'Abdome agudo', 'Pancreatite alcoólica', ARRAY['amilase','Atlanta','necrosectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com icterícia obstrutiva e dor cólica. Exame inicial?', 'Exame inicial: USG abdominal para dilatação biliar e litíase. Se colangite (tríade de Charcot: febre, icterícia, dor), CPRE urgente para desobstrução. Antibióticos empíricos.', 'cirurgia', 'Abdome agudo', 'Icterícia obstrutiva', ARRAY['Charcot','USG','CPRE']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Diverticulite aguda: classificação de Hinchey. Quando cirurgia?', 'Classificação Hinchey: I (abscesso pericólico/mesentérico confinado), II (abscesso pélvico — extensão para pelve), III (peritonite purulenta generalizada), IV (peritonite fecal). Cirurgia em III/IV: Hartmann ou ressecção com anastomose primária + lavagem. Antibióticos ± drenagem percutânea em I/II.', 'cirurgia', 'Abdome agudo', 'Diverticulite', ARRAY['Hinchey','Hartmann','peritonite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Abdome agudo em criança: suspeita de intussuscepção. Como diagnosticar?', 'Intussuscepção: USG mostra alvo duplo. Sinais: dor cólica intermitente, massa palpável, geleia de groselha. Redução por enema opaco ou cirurgia se falha ou perfuração.', 'cirurgia', 'Abdome agudo', 'Intussuscepção', ARRAY['alvo_duplo','enema','reducao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com claudicação intermitente em MMII. Suspeita de aterosclerose?', 'Suspeita de doença arterial periférica aterosclerótica. Índice tornozelo-braquial (ITB) <0,9 confirma. Tratamento: cessação tabágica, exercício supervisionado, antiagregantes. Revascularização se crítica.', 'cirurgia', 'Cirurgia vascular', 'Doença arterial periférica', ARRAY['claudicacao','ITB','revascularizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Aneurisma aórtico abdominal (AAA) >5,5cm. Indicação cirúrgica?', 'Indicação para reparo endovascular (EVAR) ou aberto em AAA assintomático >5,5cm em homens, >5cm em mulheres, devido risco de ruptura (1-5%/ano). Monitorar com USG anual se <5cm.', 'cirurgia', 'Cirurgia vascular', 'Aneurisma aórtico', ARRAY['AAA','EVAR','ruptura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Trombose venosa profunda (TVP) em perna. Escala Wells para probabilidade?', 'Escala Wells: ≥2 pontos indica probabilidade alta (imobilidade, câncer, TVP prévia). Confirmar com USG Doppler. Tratamento: anticoagulação (HBPM ou DOAC) por 3-6 meses.', 'cirurgia', 'Cirurgia vascular', 'TVP', ARRAY['Wells','Doppler','anticoagulacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com isquemia aguda de membro. Escala Rutherford. Conduta?', 'Classificação Rutherford para isquemia aguda: I (viável — sem déficit sensitivo/motor), IIa (marginalmente ameaçado — déficit sensitivo leve), IIb (imediatamente ameaçado — déficit motor, dor de repouso), III (irreversível — anestesia, paralisia, sem Doppler). Conduta: heparina IV imediata; IIa: trombólise ou embolectomia; IIb: embolectomia urgente; III: amputação. Angiografia intraop.', 'cirurgia', 'Cirurgia vascular', 'Isquemia aguda', ARRAY['Rutherford','trombolise','embolectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Varizes de MMII CEAP C3. Quando indicar escleroterapia?', 'CEAP C3 (edema): escleroterapia ou ablação endovenosa para veias safenas incompetentes. Sintomáticas: cansaço, prurido. Evitar em TVP prévia. Monitorar recorrência.', 'cirurgia', 'Cirurgia vascular', 'Varizes', ARRAY['CEAP','escleroterapia','ablacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Dissecção aórtica tipo B. Tratamento médico inicial?', 'Tipo B (descendente): controle pressórico com beta-bloqueadores (labetalol) para FC <60 e PAS <120. Seguimento com TC. Cirurgia se complicada (malperfusão, ruptura).', 'cirurgia', 'Cirurgia vascular', 'Dissecção aórtica', ARRAY['tipo_B','beta_bloqueadores','TEVAR']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Síndrome do roubo subclávio. Sinais e tratamento?', 'Sinais: claudicação braquial, atrofia muscular. Causado por estenose subclávia proximal. Tratamento: angioplastia com stent ou bypass. Confirmar com arteriografia.', 'cirurgia', 'Cirurgia vascular', 'Síndrome do roubo', ARRAY['roubo_subclavio','angioplastia','bypass']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Úlcera de estase venosa. Classificação e manejo?', 'Classificação CEAP C6. Manejo: compressão elástica (30-40mmHg), desbridamento, enxertos se necessário. Tratar insuficiência venosa subjacente com ablação.', 'cirurgia', 'Cirurgia vascular', 'Úlcera venosa', ARRAY['CEAP_C6','compressao','desbridamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fístula arteriovenosa para hemodiálise. Quando monitorar?', 'Monitorar se débito <600ml/min ou recanalização. Angio TC se suspeita de estenose. Revisão cirúrgica ou angioplastia para manter patência.', 'cirurgia', 'Cirurgia vascular', 'Fístula AV', ARRAY['hemodialise','estenose','angioplastia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com hematúria macroscópica pós-trauma. Suspeita de?', 'Suspeita de lesão urotelial (uretra, bexiga). Realizar uretrocistografia retrógrada. Se extravasamento, cateter vesical e urologia. Grau de lesão por AAST.', 'cirurgia', 'Urologia', 'Trauma urogenital', ARRAY['hematuria','uretrocistografia','AAST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Cálculo ureteral 7mm. Indicação de tratamento endourológico?', 'Cálculo 7mm: risco de obstrução, indicar ureteroscopia com litotripsia laser se sintomático (cólica renal). Alfa-bloqueadores para expulsão se <5mm. USG ou TC não contrastada.', 'cirurgia', 'Urologia', 'Cálculos renais', ARRAY['ureteroscopia','litotripsia','colica_renal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Hiperplasia prostática benigna (HPB) com IPSS >20. Tratamento?', 'IPSS >20: moderado-grave. Tratamento: alfa-bloqueadores (tamsulosina) ou inibidores 5-ARI (finasterida). TURP se retenção urinária ou complicações. Fluxometria para avaliar.', 'cirurgia', 'Urologia', 'HPB', ARRAY['IPSS','TURP','tamsulosina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Câncer de próstata PSA >20. Estadiamento?', 'PSA >20: alto risco. Estadiamento: biópsia guiada por USG, RM multiparamétrica, TC/PET para metástases. Escala Gleason para graduação. Tratamento: prostatectomia ou radioterapia.', 'cirurgia', 'Urologia', 'Câncer de próstata', ARRAY['PSA','Gleason','prostatectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fimose patológica em adulto. Indicação cirúrgica?', 'Fimose patológica: infecções recorrentes, parafimose. Indicação: postectomia ou prépucectomia. Higiene inadequada leva a balanite. Avaliar para diabetes.', 'cirurgia', 'Urologia', 'Fimose', ARRAY['postectomia','balanite','parafimose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Incontinência urinária de esforço em mulher pós-parto. Tipo?', 'Tipo: incontinência de esforço por hiperlaxidade uretral. Tratamento: exercícios de Kegel, pessário ou sling mid-uretral. Urodinâmica para confirmar.', 'cirurgia', 'Urologia', 'Incontinência', ARRAY['esforco','Kegel','sling']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Cisto renal simples Bosniak I. Manejo?', 'Bosniak I: cisto simples benigno. Manejo: seguimento USG anual se >3cm, mas geralmente incidental. Não requer intervenção. Diferenciar de complexos (II-IV) com TC/RM.', 'cirurgia', 'Urologia', 'Cistos renais', ARRAY['Bosniak','USG','incidental']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Próstata endurecida em PSA 4-10. Próximo passo?', 'Suspeita de adenocarcinoma. Próximo: biópsia transretal guiada por USG. Escala de risco D''Amico para planejamento. RM para lesões suspeitas.', 'cirurgia', 'Urologia', 'Diagnóstico de próstata', ARRAY['biopsia','DAmico','RM']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Litíase coraliforme renal. Tratamento preferencial?', 'Litíase coraliforme: nefrolitotripsia percutânea (PCNL) para >2cm. ESWL se <2cm. Prevenir recorrência com hidratação e dieta baixa em oxalato.', 'cirurgia', 'Urologia', 'Litíase coraliforme', ARRAY['PCNL','ESWL','oxalato']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Paciente com fratura de fêmur proximal. Classificação Garden para femoral?', 'Classificação Garden para fratura de colo femoral: I (impacto valgo), II (não deslocado), III (deslocado parcial), IV (deslocado total). Tratamento: artroplastia em idosos, fixação em jovens.', 'cirurgia', 'Ortopedia', 'Fraturas proximais fêmur', ARRAY['Garden','artroplastia','fixacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Escoliose idiopática. Quando indicar braçadeira?', 'Cobb >20-25° em adolescente. Braçadeira ortótica para curvas 25-40° em crescimento. Cirurgia (fusão posterior) se >45-50° ou progressão. RX para monitoramento.', 'cirurgia', 'Ortopedia', 'Escoliose', ARRAY['Cobb','bracadeira','fusao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fratura de rádio distal Colles. Tratamento conservador?', 'Colles: fratura extra-articular dorsal. Tratamento: redução fechada e imobilização gessada 4-6 semanas se deslocamento <2mm. Cirurgia (placas) se instável ou cominutiva.', 'cirurgia', 'Ortopedia', 'Fraturas rádio', ARRAY['Colles','gesso','placas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Lesão do manguito rotador. Teste positivo de Jobe. IRM?', 'Teste de Jobe positivo: supraspinatus. IRM confirma ruptura parcial/completa. Tratamento: fisio para parcial; artroscopia para completa sintomática em jovens.', 'cirurgia', 'Ortopedia', 'Ombro', ARRAY['Jobe','IRM','artroscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Osteoartrite de joelho Kellgren III. Opções cirúrgicas?', 'Kellgren III: moderada com osteófitos. Opções: osteotomia corretiva em jovens ou artroplastia total de joelho (ATJ) em idosos >60° deformidade. Injeções intra-articulares prévias.', 'cirurgia', 'Ortopedia', 'Osteoartrite', ARRAY['Kellgren','ATJ','osteotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fratura de patela deslocada. Indicação de cirurgia?', 'Deslocamento >3mm ou >2mm com incongruência articular indica fixação com fios de tensão ou parafusos. Imobilização pós-op. RX para avaliar.', 'cirurgia', 'Ortopedia', 'Fraturas patela', ARRAY['deslocamento','fios_tensao','RX']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tendinite de Aquiles crônica. Tratamento?', 'Tratamento: repouso, AINEs, fisio excêntrica. Cirurgia (desbridamento) se falha e ruptura iminente. USG ou IRM para confirmar tendinopatia.', 'cirurgia', 'Ortopedia', 'Tendinite Aquiles', ARRAY['excentrica','desbridamento','IRM']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Luxação glenoumeral anterior recorrente. Teste de Apprehension?', 'Teste positivo indica instabilidade. Cirurgia: Bankart artroscópico para reparo labrum. Reforço com Latarjet se ósseo. Fisio pós-op.', 'cirurgia', 'Ortopedia', 'Luxação ombro', ARRAY['Apprehension','Bankart','Latarjet']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Pseudartrose de clavícula. Quando operar?', 'Pseudartrose sintomática: redução aberta e fixação com placa ou enxerto ósseo. Comum em fraturas médias não consolidadas. RX confirma não-união.', 'cirurgia', 'Ortopedia', 'Pseudartrose', ARRAY['clavicula','placa','enxerto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Hallux valgus moderado. Correção cirúrgica?', 'Correção: osteotomia Scarf ou Chevron para metatarsal + liberação capsular. Indicação se dor persistente e AOFAS <70. Órteses prévias.', 'cirurgia', 'Ortopedia', 'Hallux valgus', ARRAY['Scarf','AOFAS','osteotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual é a definição e classificação do pneumotórax?', 'O pneumotórax é a presença de ar no espaço pleural, causando colapso pulmonar parcial ou total. Classifica-se em espontâneo primário (sem doença pulmonar subjacente), secundário (associado a doenças como DPOC) e traumático (por lesão torácica). O tratamento varia de observação em casos pequenos a drenagem torácica em casos sintomáticos.', 'cirurgia', 'Cirurgia torácica', 'pneumotórax', ARRAY['torácica', 'emergência', 'drenagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como se apresenta o hemotórax e qual o manejo inicial?', 'O hemotórax é o acúmulo de sangue no espaço pleural, frequentemente decorrente de trauma torácico. Apresenta-se com dor torácica, dispneia e instabilidade hemodinâmica em casos graves. O manejo inicial inclui estabilização do paciente, drenagem torácica com tubo e, se necessário, toracotomia para controle de sangramento.', 'cirurgia', 'Cirurgia torácica', 'hemotórax', ARRAY['torácica', 'trauma', 'sangramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que caracteriza o derrame pleural e suas causas principais?', 'O derrame pleural é o acúmulo de líquido no espaço pleural, podendo ser transudato (causas como insuficiência cardíaca) ou exsudato (infecções, neoplasias). Sintomas incluem dispneia e dor pleurítica. O diagnóstico é feito por toracocentese e análise do líquido, com tratamento etiológico e drenagem se sintomático.', 'cirurgia', 'Cirurgia torácica', 'derrame pleural', ARRAY['torácica', 'diagnóstico', 'toracocentese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o tratamento do empiema pleural?', 'O empiema é a infecção purulenta do espaço pleural, evoluindo em fases: exsudativa, fibrino-purulenta e organizada. O tratamento envolve antibióticos intravenosos e drenagem, podendo requerer decorticação cirúrgica em casos avançados. A toracocentese inicial confirma o diagnóstico e guia a terapia antimicrobiana.', 'cirurgia', 'Cirurgia torácica', 'empiema', ARRAY['torácica', 'infecção', 'cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como diagnosticar e tratar a mediastinite?', 'A mediastinite é a infecção do mediastino, frequentemente pós-esofágica ou odontogênica, com sintomas como febre, dor retrosternal e crepitação. Diagnóstico por TC de tórax e culturas. Tratamento inclui antibióticos de amplo espectro, drenagem cirúrgica e suporte vital.', 'cirurgia', 'Cirurgia torácica', 'mediastinite', ARRAY['torácica', 'infecção', 'drenagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais os princípios do manejo do trauma torácico?', 'O trauma torácico pode ser penetrante ou contuso, afetando pulmão, coração ou grandes vasos. Avaliação inicial segue ATLS: via aérea, respiração, circulação. Manejo inclui drenagem de pneumo/hemotórax, pericardiocentese e toracotomia em instabilidade.', 'cirurgia', 'Cirurgia torácica', 'trauma torácico', ARRAY['torácica', 'emergência', 'ATLS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para drenagem torácica no tórax?', 'A drenagem torácica é indicada para pneumotórax hipertensivo, hemotórax maciço ou derrame sintomático. Realiza-se no 4º-5º espaço intercostal, linha médio-axilar, com tubo conectado a selo d''água. Complicações incluem infecção e lesão pulmonar.', 'cirurgia', 'Cirurgia torácica', 'drenagem torácica', ARRAY['torácica', 'procedimento', 'complicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quando realizar toracotomia em emergências torácicas?', 'A toracotomia é indicada em hemotórax com drenagem >1500mL inicial ou >200mL/h por 2-4h, ou instabilidade hemodinâmica persistente. Pode ser anterolateral ou postero-lateral. O objetivo é controle de sangramento e reparo de lesões.', 'cirurgia', 'Cirurgia torácica', 'toracotomia', ARRAY['torácica', 'cirurgia', 'emergência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é VATS e suas aplicações em cirurgia torácica?', 'VATS (Video-Assisted Thoracoscopic Surgery) é uma técnica minimamente invasiva para biópsias, drenagem de empiema e lobectomias. Vantagens incluem menor dor pós-operatória e recuperação rápida. Contraindicada em coagulopatias ou aderências extensas.', 'cirurgia', 'Cirurgia torácica', 'VATS', ARRAY['torácica', 'minimamente invasiva', 'diagnóstico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Estadiamento e tratamento do câncer de pulmão?', 'O câncer de pulmão é estadiado pelo TNM, com não pequenas células (adenocarcinoma, escamoso) e pequenas células. Tratamento inclui cirurgia (lobectomia) para estádios iniciais, quimioterapia/radioterapia para avançados. Fatores prognósticos incluem tamanho tumoral e metástases.', 'cirurgia', 'Cirurgia torácica', 'câncer de pulmão', ARRAY['torácica', 'oncologia', 'estadiamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações e técnica da esofagectomia?', 'A esofagectomia é indicada para câncer esofágico ou acalasia grave. Técnicas incluem transhiatal ou transtorácica, com reconstrução por estômago ou cólon. Complicações comuns são fístula anastomótica e refluxo.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'esofagectomia', ARRAY['digestivo', 'câncer', 'reconstrução']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tipos de gastrectomia e indicações?', 'A gastrectomia subtotal é para úlceras ou câncer gástrico distal, enquanto total para proximal ou difuso. Inclui linfadenectomia D2 em oncologia. Pós-operatório requer suplementação de B12 e ferro devido à malabsorção.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'gastrectomia', ARRAY['digestivo', 'oncologia', 'complicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Procedimento de colectomia e suas variantes?', 'A colectomia remove parte do cólon, como hemicolectomia direita para câncer ou diverticulite. Abordagens aberta ou laparoscópica. Risco de anastomose leak e íleo pós-operatório.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'colectomia', ARRAY['digestivo', 'câncer', 'laparoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para hepatectomia?', 'A hepatectomia é indicada para tumores hepáticos primários (HCC) ou metástases colorretais. Segmentectomia ou lobectomia guiada por anatomia hepática. Pré-operatório avalia função hepática com Child-Pugh.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'hepatectomia', ARRAY['digestivo', 'fígado', 'oncologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tipos de pancreatectomia e complicações?', 'A pancreatectomia cefálica (Whipple) é para câncer periampular, enquanto distal para corpo/cauda. Complicações incluem fístula pancreática, infecção e diabetes. Nutrição pós-op é crucial.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'pancreatectomia', ARRAY['digestivo', 'pâncreas', 'fístula']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações cirúrgicas para bypass gástrico?', 'O bypass gástrico é para obesidade mórbida (IMC >40) ou com comorbidades. Cria Roux-en-Y para restrição e malabsorção. Complicações incluem dumping syndrome e deficiência nutricional.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'bypass gástrico', ARRAY['digestivo', 'obesidade', 'bariátrica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tratamento cirúrgico da DRGE?', 'A DRGE cirúrgica usa fundoplicatura de Nissen para refluxo refratário ou hérnia hiatal. Reforça o esfíncter esofágico inferior. Eficaz em 90% dos casos, com risco de disfagia transitória.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'DRGE cirúrgico', ARRAY['digestivo', 'refluxo', 'fundoplicatura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Manejo cirúrgico da diverticulite aguda?', 'A diverticulite complicada (abscesso, perfuração) requer ressecção com colostomia em Hartmann. Não complicada é conservadora com antibióticos. Profilaxia com colectomia eletiva em recorrências.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'diverticulite', ARRAY['digestivo', 'cólon', 'complicada']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é megacólon e seu tratamento?', 'O megacólon chagásico é dilatação colônica por denervação parassimpática. Tratamento cirúrgico é ressecção com ileorretoanastomose. Conservador para assintomáticos.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'megacólon', ARRAY['digestivo', 'chagas', 'ressecção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tratamento cirúrgico da acalasia?', 'A miotomia de Heller por laparoscopia é o padrão para acalasia, com fundoplicatura associada. Melhora a deglutição em 90%. Dilatação endoscópica é alternativa inicial.', 'cirurgia', 'Cirurgia do aparelho digestivo', 'acalasia', ARRAY['digestivo', 'esôfago', 'miotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Classificação e tratamento da hérnia inguinal?', 'A hérnia inguinal é direta (medial) ou indireta (lateral), por fraqueza da parede abdominal. Tratamento é herniorrafia com prótese (Lichtenstein). Eletiva em assintomáticas, urgente em encarceradas.', 'cirurgia', 'Hérnias', 'inguinal', ARRAY['hérnia', 'inguinal', 'Lichtenstein']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Diferenças entre hérnia inguinal e femoral?', 'A hérnia femoral sai abaixo do ligamento inguinal, mais comum em mulheres, com risco de estrangulamento. Inguinal acima, mais em homens. Ambas requerem reparo cirúrgico, femoral com abordagem inguinal ou pré-peritoneal.', 'cirurgia', 'Hérnias', 'femoral', ARRAY['hérnia', 'femoral', 'estrangulamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Manejo da hérnia incisional?', 'A hérnia incisional ocorre em cicatrizes cirúrgicas, tratada por reparo com malha sintética. Fatores de risco incluem infecção pós-op e obesidade. Técnica de Rives-Stoppa para grandes defeitos.', 'cirurgia', 'Hérnias', 'incisional', ARRAY['hérnia', 'incisional', 'malha']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Características da hérnia umbilical em adultos?', 'A hérnia umbilical em adultos é por aumento da pressão intra-abdominal, como obesidade ou ascite. Reparo com sutura ou malha, especialmente se >2cm. Risco de encarceramento baixo, mas possível.', 'cirurgia', 'Hérnias', 'umbilical', ARRAY['hérnia', 'umbilical', 'adulto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é hérnia epigástrica e seu tratamento?', 'A hérnia epigástrica é protusão de gordura pré-peritoneal na linha alba superior ao umbigo. Tratamento cirúrgico ambulatorial com sutura ou malha. Assintomáticas podem ser observadas.', 'cirurgia', 'Hérnias', 'epigástrica', ARRAY['hérnia', 'epigástrica', 'linha alba']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Hérnia de Spiegel: localização e manejo?', 'A hérnia de Spiegel ocorre na linha semilunar lateral ao reto. Rara, tratada por reparo aberto com malha devido à tensão. Diagnóstico por exame físico ou USG.', 'cirurgia', 'Hérnias', 'Spiegel', ARRAY['hérnia', 'Spiegel', 'rara']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tratamento da hérnia hiatal?', 'A hérnia hiatal tipo I (deslizante) é assintomática ou com DRGE; tipo II-IV requer cirurgia. Fundoplicatura laparoscópica corrige. Complicações incluem disfagia.', 'cirurgia', 'Hérnias', 'hérnia hiatal', ARRAY['hérnia', 'hiatal', 'fundoplicatura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Técnica de Lichtenstein para hérnia inguinal?', 'A técnica de Lichtenstein é tensão-livre, com malha poliéster sobre o canal inguinal. Fixada com suturas não absorvíveis. Baixa recorrência (<2%) e recuperação rápida.', 'cirurgia', 'Hérnias', 'técnica de Lichtenstein', ARRAY['hérnia', 'Lichtenstein', 'tensão-livre']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Uso de telas em reparo de hérnias?', 'Telas sintéticas (polipropileno) reforçam a parede abdominal, reduzindo recorrência em 50%. Colocadas pré-peritoneal ou onlay. Contraindicadas em infecção ativa.', 'cirurgia', 'Hérnias', 'telas', ARRAY['hérnia', 'telas', 'sintética']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Manejo de hérnias complicadas (encarcerada/estrangulada)?', 'Hérnia encarcerada requer redução manual ou cirurgia; estrangulada é emergência com isquemia. Ressecção intestinal se necrose. Antibióticos e hidratação pré-op.', 'cirurgia', 'Hérnias', 'hérnias complicadas', ARRAY['hérnia', 'emergência', 'estrangulamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Avaliação pré-operatória em cirurgia geral?', 'A avaliação pré-op inclui história, exame físico, ECG, hemograma e testes funcionais (espirometria em tabagistas). Avalia risco cardiovascular com escore de Lee. Otimiza comorbidades como diabetes.', 'cirurgia', 'Pré e pós-operatório', 'avaliação pré-op', ARRAY['pré-op', 'risco', 'otimização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Orientações sobre jejum pré-operatório?', 'Jejum de sólidos por 8h e líquidos claros por 2h pré-anestesia reduz aspiração. Exceções para diabéticos com insulina. Diretrizes ASA atualizadas permitem mais flexibilidade.', 'cirurgia', 'Pré e pós-operatório', 'jejum', ARRAY['pré-op', 'anestesia', 'ASA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Profilaxia de TVP em pacientes cirúrgicos?', 'Profilaxia de TVP usa heparina de baixo peso molecular ou compressão pneumática intermitente. Indicada em cirurgias abdominais >1h ou imobilidade. Duração 7-10 dias pós-op.', 'cirurgia', 'Pré e pós-operatório', 'profilaxia TVP', ARRAY['pós-op', 'TVP', 'heparina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Antibioticoprofilaxia em cirurgias?', 'Antibioticoprofilaxia é administrada 30-60min antes da incisão, como cefazolina para limpas-contaminadas. Dose única ou repetida em procedimentos longos. Reduz infecção do sítio cirúrgico em 50%.', 'cirurgia', 'Pré e pós-operatório', 'antibioticoprofilaxia', ARRAY['pré-op', 'infecção', 'cefazolina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Complicações pós-operatórias comuns?', 'Complicações incluem infecção, hemorragia, trombose e pneumonia. Monitorar sinais vitais e drenagens. Prevenção com higiene e mobilização precoce.', 'cirurgia', 'Pré e pós-operatório', 'complicações pós-op', ARRAY['pós-op', 'infecção', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é deiscência de ferida cirúrgica?', 'Deiscência é a abertura da incisão, superficial ou profunda (evisceração). Fatores: infecção, desnutrição, tosse. Tratamento conservador ou reaproximação cirúrgica.', 'cirurgia', 'Pré e pós-operatório', 'deiscência', ARRAY['pós-op', 'ferida', 'evisceração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Manejo de fístulas pós-operatórias?', 'Fístulas são vazamentos anômalos, como enterocutâneas, tratadas com nutrição parenteral, sepse controle e drenagem. Cirurgia tardia se persiste >4 semanas. Taxa de fechamento espontâneo 70%.', 'cirurgia', 'Pré e pós-operatório', 'fístulas', ARRAY['pós-op', 'fístula', 'nutrição']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para nutrição enteral pós-op?', 'Nutrição enteral inicia precoce (<48h) em cirurgias gastrointestinais para manter integridade mucosa. Sondas nasogástricas ou jejunais. Contraindicada em íleo ou obstrução.', 'cirurgia', 'Pré e pós-operatório', 'nutrição enteral', ARRAY['pós-op', 'enteral', 'precoce']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Controle da dor pós-operatória?', 'Dor pós-op controlada com multimodal: opioides, AINEs, bloqueios regionais. Escala VAS guia. Evita complicações como trombose por imobilidade.', 'cirurgia', 'Pré e pós-operatório', 'dor pós-op', ARRAY['pós-op', 'dor', 'multimodal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Critérios para alta hospitalar pós-cirurgia?', 'Alta quando tolera dieta, mobiliza sem ajuda, ferida limpa e analgésicos orais. Sem febre ou drenagens excessivas. Orientação sobre sinais de alarme.', 'cirurgia', 'Pré e pós-operatório', 'alta hospitalar', ARRAY['pós-op', 'alta', 'critérios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fases da anestesia geral?', 'Anestesia geral induz inconsciência, analgesia e relaxamento muscular com IV (propofol) ou inalatória (sevoflurano). Manutenção com ventilação controlada. Recuperação em PACU monitora.', 'cirurgia', 'Anestesiologia', 'anestesia geral', ARRAY['anestesia', 'geral', 'indução']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações e complicações da raquianestesia?', 'Raquianestesia é para cirurgias abdominais/pélvicas inferiores, injetando anestésico local subaracnoide. Bloco sensitivo-motor rápido. Complicações: hipotensão, cefaleia pós-punção.', 'cirurgia', 'Anestesiologia', 'raquianestesia', ARRAY['anestesia', 'regional', 'raqui']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Diferenças entre raquianestesia e peridural?', 'Peridural é contínua via cateter para pós-op, enquanto raqui é única. Peridural em espaço epidural para cesáreas ou torácicas. Ambas regionais, mas peridural mais versátil.', 'cirurgia', 'Anestesiologia', 'peridural', ARRAY['anestesia', 'epidural', 'cateter']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Técnica de bloqueio de nervo periférico?', 'Bloqueio de nervo usa USG para guiar anestésico local em plexos (braquial). Para membros, reduz opioides. Complicações: hematoma, infecção rara.', 'cirurgia', 'Anestesiologia', 'bloqueio de nervo', ARRAY['anestesia', 'periférico', 'USG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Avaliação de intubação difícil?', 'Intubação difícil prevista por Mallampati >3, tireomentoniana <6cm ou mobilidade cervical limitada. Algoritmo ASA usa laringoscópio, vídeo-laringoscópio. Backup com cricotireoidotomia.', 'cirurgia', 'Anestesiologia', 'intubação difícil', ARRAY['anestesia', 'via aérea', 'Mallampati']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Gerenciamento da via aérea em anestesia?', 'Via aérea mantida por máscara, laringoscópio ou dispositivos supraglóticos (LMA). Em obstrução, manobra de Sellick. Monitora SpO2 e EtCO2.', 'cirurgia', 'Anestesiologia', 'via aérea', ARRAY['anestesia', 'via aérea', 'gerenciamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Níveis de sedação em procedimentos?', 'Sedação mínima (ansiolítica), moderada (consciente) ou profunda (quase anestesia). Usa midazolam ou propofol. Monitora respiração e oxigenação.', 'cirurgia', 'Anestesiologia', 'sedação', ARRAY['anestesia', 'sedação', 'níveis']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é choque anestésico e tratamento?', 'Choque anestésico é hipotensão por vasodilatamento ou hipovolemia induzida. Tratamento: fluidos, vasopressores (efedrina) e otimização ventilatória. Prevenção com hidratação pré-op.', 'cirurgia', 'Anestesiologia', 'choque anestésico', ARRAY['anestesia', 'choque', 'hipotensão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Sinais e manejo da hipertermia maligna?', 'Hipertermia maligna é reação genética a anestésicos voláteis, com taquicardia, rigidez e hipertermia. Tratamento: dantrolene IV, resfriamento e hipercânes. Suspeita em MH susceptible.', 'cirurgia', 'Anestesiologia', 'hipertermia maligna', ARRAY['anestesia', 'hipertermia', 'dantrolene']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Monitorização padrão em anestesia?', 'Monitorização inclui ECG, PA não invasiva, SpO2, capnografia e temperatura. ASA padrão para segurança. Alarmes alertam hipoxemia ou arritmias.', 'cirurgia', 'Anestesiologia', 'monitorização', ARRAY['anestesia', 'monitorização', 'ASA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais são os tipos de mastectomia em cirurgia oncológica?', 'A mastectomia simples remove a glândula mamária sem os linfonodos; a mastectomia radical inclui a mama, linfonodos axilares e músculo peitoral; a mastectomia modificada radical preserva o músculo peitoral. Essas técnicas visam remover o tecido tumoral com margens adequadas.', 'cirurgia', 'Cirurgia oncológica', 'Tipos de mastectomia', ARRAY['oncologia','mama','mastectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para colectomia em cirurgia oncológica?', 'A colectomia é indicada para câncer colorretal, remoção de segmentos do cólon afetados. Tipos incluem hemicolectomia direita ou esquerda, dependendo da localização do tumor.', 'cirurgia', 'Cirurgia oncológica', 'Colectomia', ARRAY['oncologia','cólon','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é gastrectomia no tratamento de câncer gástrico?', 'A gastrectomia é a remoção parcial ou total do estômago para câncer gástrico. A subtotal remove o terço distal, enquanto a total remove todo o órgão com reconstrução.', 'cirurgia', 'Cirurgia oncológica', 'Gastrectomia', ARRAY['oncologia','estômago','gastrectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é biópsia de linfonodo sentinela?', 'É a identificação e remoção do primeiro linfonodo que drena o tumor, para avaliar metástases. Usada em câncer de mama e melanoma.', 'cirurgia', 'Cirurgia oncológica', 'Linfonodo sentinela', ARRAY['oncologia','biópsia','linfonodo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que são margens cirúrgicas em oncologia?', 'São as bordas de tecido normal ao redor do tumor removido, para garantir ressecção completa. Margens positivas indicam resíduo tumoral.', 'cirurgia', 'Cirurgia oncológica', 'Margens cirúrgicas', ARRAY['oncologia','cirurgia','margens']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para gastrectomia total?', 'Indicada em tumores proximais ou difusos do estômago. Requer linfadenectomia e reconstrução com Y de Roux.', 'cirurgia', 'Cirurgia oncológica', 'Gastrectomia', ARRAY['câncer','gástrico','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Vantagens do linfonodo sentinela em oncologia?', 'Reduz a necessidade de dissecção axilar completa, diminuindo linfedema. Corante ou radiofármaco localiza o nó.', 'cirurgia', 'Cirurgia oncológica', 'Linfonodo sentinela', ARRAY['câncer','mama','sentinela']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais as complicações da colectomia?', 'Complicações incluem infecção, anastomose falha e íleo paralítico. A abordagem laparoscópica reduz o tempo de recuperação.', 'cirurgia', 'Cirurgia oncológica', 'Colectomia', ARRAY['câncer','intestino','complicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como avaliar margens cirúrgicas?', 'Avaliadas por exame patológico intraoperatório ou pós-operatório. Margens livres confirmam remoção adequada do tumor.', 'cirurgia', 'Cirurgia oncológica', 'Margens cirúrgicas', ARRAY['câncer','patologia','avaliação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é mastectomia em contexto oncológico?', 'A mastectomia é a remoção cirúrgica da mama afetada por câncer. Pode ser total, parcial ou radical, dependendo da extensão da doença e do estadiamento.', 'cirurgia', 'Cirurgia oncológica', 'Mastectomia', ARRAY['cirurgia','câncer','mama']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais as vantagens da laparoscopia em cirurgia minimamente invasiva?', 'Reduz dor pós-operatória, tempo de internação e cicatrizes. Melhora recuperação e cosmética.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Vantagens da laparoscopia', ARRAY['minimamente invasiva','laparoscopia','vantagens']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como é o posicionamento de trocateres na laparoscopia?', 'Trocateres colocados em pontos estratégicos: um umbilical para óptica, outros para instrumentos. Posicionamento varia com o procedimento.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Posicionamento de trocateres', ARRAY['laparoscopia','trocater','técnica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é pneumoperitônio e suas complicações?', 'Insuflação de CO2 na cavidade abdominal para visualização. Complicações incluem hipercapnia, embolia gasosa e lesão orgânica.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Pneumoperitônio', ARRAY['laparoscopia','pneumoperitônio','complicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é cirurgia robótica minimamente invasiva?', 'Uso de robôs para precisão em procedimentos laparoscópicos. Sistemas como Da Vinci oferecem visão 3D e movimentos articulados.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Cirurgia robótica', ARRAY['robótica','minimamente invasiva','tecnologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Complicações no posicionamento de trocateres?', 'Lesão vascular ou intestinal durante inserção. Uso de técnica de Hasson reduz riscos.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Trocateres', ARRAY['complicações','cirurgia','laparoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Monitoramento durante pneumoperitônio?', 'Monitorar pressão intra-abdominal (12-15 mmHg) e capnografia para CO2. Desinsuflar se houver instabilidade hemodinâmica.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Pneumoperitônio', ARRAY['cirurgia','monitoramento','CO2']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Vantagens da cirurgia robótica?', 'Melhor destreza, redução de tremores e ergonomia para o cirurgião. Útil em prostatectomia e cirurgias cardíacas.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Robótica', ARRAY['cirurgia','robô','vantagens']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Limitações da cirurgia robótica?', 'Custo elevado, tempo de setup longo e curva de aprendizado. Não substitui completamente a cirurgia aberta em casos complexos.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Cirurgia robótica', ARRAY['limitações','robótica','custo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é laparoscopia e suas indicações?', 'Técnica cirúrgica com pequenas incisões e câmera. Indicada para colecistectomia, apendicectomia e procedimentos ginecológicos.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Laparoscopia', ARRAY['cirurgia','endoscopia','invasiva']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para cirurgia robótica?', 'Indicada em urologia, ginecologia e torácica para precisão. Reduz sangramento e tempo de recuperação.', 'cirurgia', 'Cirurgia minimamente invasiva', 'Robótica', ARRAY['indicações','cirurgia','robô']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais as indicações de cirurgia bariátrica baseadas no IMC?', 'Indicada para IMC > 40 kg/m² ou > 35 com comorbidades como diabetes. Avaliação multidisciplinar é essencial.', 'cirurgia', 'Cirurgia bariátrica', 'Indicações por IMC', ARRAY['bariátrica','IMC','indicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Diferenças entre bypass gástrico e gastrectomia em manga?', 'Bypass altera anatomia intestinal, promovendo malabsorção; manga reduz tamanho gástrico sem desvio. Bypass tem maior resolução de diabetes.', 'cirurgia', 'Cirurgia bariátrica', 'Bypass vs manga', ARRAY['bariátrica','bypass','manga']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é síndrome de dumping pós-bariátrica?', 'Ocorrência de sintomas como náusea e diarreia após ingestão de alimentos hiperosmolares. Mais comum após bypass gástrico devido ao trânsito rápido.', 'cirurgia', 'Cirurgia bariátrica', 'Síndrome de dumping', ARRAY['bariátrica','dumping','complicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais deficiências nutricionais após bariátrica?', 'Deficiências de ferro, vitamina B12, cálcio e vitamina D. Suplementação vitalícia é recomendada.', 'cirurgia', 'Cirurgia bariátrica', 'Deficiências nutricionais', ARRAY['nutrição','bariátrica','deficiências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é cirurgia metabólica?', 'Procedimentos bariátricos para controle de diabetes tipo 2 em pacientes não obesos. Bypass gástrico melhora sensibilidade à insulina.', 'cirurgia', 'Cirurgia bariátrica', 'Cirurgia metabólica', ARRAY['metabólica','diabetes','bariátrica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Vantagens da gastrectomia em manga?', 'Procedimento mais simples, sem malabsorção, menor risco de deficiências. Perda de peso de 50-70% do excesso.', 'cirurgia', 'Cirurgia bariátrica', 'Gastrectomia em manga', ARRAY['cirurgia','manga','vantagens']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tratamento da síndrome de dumping?', 'Dieta fracionada, evitar açúcares simples e refeições pequenas. Sintomas precoces duram 30 min, tardios envolvem hipoglicemia.', 'cirurgia', 'Cirurgia bariátrica', 'Dumping', ARRAY['tratamento','síndrome','pós-operatório']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Monitoramento nutricional pós-bariátrica?', 'Exames laboratoriais regulares para vitaminas e minerais. Ajustes dietéticos e suplementos previnem complicações.', 'cirurgia', 'Cirurgia bariátrica', 'Nutrição', ARRAY['monitoramento','pós-operatório','vitaminas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é cirurgia bariátrica e critérios?', 'Procedimentos para perda de peso em obesidade mórbida. Critérios incluem falha em tratamentos conservadores e IMC elevado.', 'cirurgia', 'Cirurgia bariátrica', 'Bariátrica', ARRAY['obesidade','cirurgia','critérios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para cirurgia metabólica?', 'Pacientes com IMC 30-35 e diabetes refratário. Foco em remissão glicêmica sem perda de peso excessiva.', 'cirurgia', 'Cirurgia bariátrica', 'Metabólica', ARRAY['indicações','cirurgia','diabetes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais os tipos de rejeição em transplantes?', 'Hiperaguda (imediata por anticorpos), aguda (dias a semanas por células T) e crônica (meses a anos por fibrose). Imunossupressão previne.', 'cirurgia', 'Transplantes', 'Tipos de rejeição', ARRAY['transplante','rejeição','imunologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Protocolos de imunossupressão em transplantes?', 'Indução com anticorpos e manutenção com corticoides, inibidores de calcineurina (ciclosporina) e antimetabólitos (micofenolato). Ajustes por função do órgão.', 'cirurgia', 'Transplantes', 'Imunossupressão', ARRAY['transplante','protocolos','imunossupressores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Critérios de morte encefálica para transplantes?', 'Coma irreversível, ausência de reflexos troncoencefálicos e apneia. Confirmado por dois exames clínicos em intervalos.', 'cirurgia', 'Transplantes', 'Morte encefálica', ARRAY['transplante','cérebro','critérios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é alocação de órgãos em transplantes?', 'Sistema baseado em compatibilidade ABO, tempo de espera e urgência. No Brasil, regulado pela CNCD.', 'cirurgia', 'Transplantes', 'Alocação de órgãos', ARRAY['transplante','alocação','sistema']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é rejeição hiperaguda?', 'Rejeição imediata pós-transplante por incompatibilidade ABO ou pré-formados anticorpos. Causa trombose vascular.', 'cirurgia', 'Transplantes', 'Rejeição', ARRAY['transplante','hiperaguda','anticorpos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Complicações da imunossupressão?', 'Infecções oportunistas, nefrotoxicidade e maior risco de câncer. Monitoramento de níveis séricos é crucial.', 'cirurgia', 'Transplantes', 'Protocolos', ARRAY['complicações','imunossupressão','infecções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Exames complementares para morte encefálica?', 'EEG plano, angiografia cerebral ou Doppler transcraniano mostrando ausência de fluxo. Essencial para doadores.', 'cirurgia', 'Transplantes', 'Encefálica', ARRAY['morte','exames','doador']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Critérios para doador de órgãos?', 'Idade < 70 anos, ausência de infecções transmissíveis e função orgânica preservada. Avaliação hemodinâmica é chave.', 'cirurgia', 'Transplantes', 'Alocação', ARRAY['doador','critérios','avaliação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Fatores na alocação de órgãos?', 'Gravidade da doença, tamanho do órgão e distância geográfica. Prioriza centros regionais para otimizar viabilidade.', 'cirurgia', 'Transplantes', 'Órgãos', ARRAY['alocação','fatores','prioridade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Quais as contraindicações para doação?', 'Doenças infecciosas como HIV, hepatite ou câncer metastático. Avaliação rigorosa previne transmissão.', 'cirurgia', 'Transplantes', 'Doadores', ARRAY['contraindicações','transplante','doação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é tireoidectomia em cirurgia de cabeça e pescoço?', 'Remoção parcial ou total da glândula tireoide para nódulos ou câncer. Preserva paratireoides e nervo laríngeo recorrente.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Tireoidectomia', ARRAY['cabeça','pescoço','tireoide']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é parotidectomia?', 'Remoção da glândula parótida para tumores benignos ou malignos. Superficial preserva nervo facial; total remove com ele se invadido.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Parotidectomia', ARRAY['parótida','cirurgia','tumor']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Indicações para traqueostomia?', 'Obstrução de vias aéreas superiores, ventilação prolongada ou trauma. Procedimento cirúrgico para acesso traqueal.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Traqueostomia', ARRAY['traqueia','cirurgia','indicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é câncer laríngeo e tratamento?', 'Tumor maligno da laringe, tratado por laringectomia parcial ou total com radioterapia. Estadiamento TNM guia a abordagem.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Câncer laríngeo', ARRAY['laring','câncer','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é dissecção cervical em oncologia?', 'Remoção de linfonodos cervicais para metástases de câncer de cabeça e pescoço. Níveis I-VI dependendo do primário.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Dissecção cervical', ARRAY['cervical','linfonodos','oncologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Complicações da tireoidectomia?', 'Hipoparatireoidismo transitório, lesão do nervo laríngeo e hematoma. Monitorar cálcio sérico pós-operatório.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Tireoidectomia', ARRAY['complicações','cirurgia','tireoide']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Preservação do nervo facial na parotidectomia?', 'Dissecção cuidadosa identifica ramos do VII par. Risco de paralisia facial pós-operatória.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Parótida', ARRAY['nervo','facial','preservação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Cuidados pós-traqueostomia?', 'Higiene do estoma, aspiração de secreções e monitoramento de infecções. Decanulação quando vias aéreas restauradas.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Traqueostomia', ARRAY['pós-operatório','cuidados','estoma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Tipos de dissecção de pescoço?', 'Radical (remove linfonodos, músculo, nervo), modificada (preserva estruturas) e seletiva (níveis específicos). Baseada em N+.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Dissecção', ARRAY['pescoço','tipos','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Complicações do câncer laríngeo?', 'Disfonia, disfagia e obstrução respiratória. Voz esofágica pós-laringectomia total.', 'cirurgia', 'Cirurgia de cabeça e pescoço', 'Laringeal', ARRAY['oncologia','complicações','laringe']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual é a principal indicação para retalhos cutâneos em cirurgia plástica reparadora?', 'Os retalhos cutâneos são indicados principalmente para cobrir defeitos teciduais onde há perda de pele e tecido subcutâneo, garantindo vascularização adequada e minimizando infecções. Eles são essenciais em reconstruções após traumas ou ressecções oncológicas, promovendo cicatrização sem contração excessiva. A escolha do retalho depende da localização, tamanho e necessidade de sensibilidade.', 'cirurgia', 'Cirurgia plástica reparadora', 'Reconstrução tecidual', ARRAY['retalhos', 'reconstrução', 'vascularização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é um enxerto de pele de espessura total e suas vantagens?', 'O enxerto de pele de espessura total inclui epiderme e derme completa, sendo ideal para áreas de alta funcionalidade como mãos e face. Suas vantagens incluem melhor qualidade cosmética, menor contração e maior durabilidade comparado a enxertos parciais. No entanto, requer leito receptor vascularizado e tem maior risco de falha por necrose.', 'cirurgia', 'Cirurgia plástica reparadora', 'Enxertos cutâneos', ARRAY['enxerto', 'pele', 'espessura total']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como é feita a reconstrução mamária pós-mastectomia?', 'A reconstrução mamária pode ser imediata ou tardia, utilizando implantes siliconados ou retalhos autólogos como o DIEP. O retalho DIEP preserva o músculo reto abdominal, reduzindo morbidade doadora. A escolha depende do perfil da paciente, com foco em simetria e satisfação psicológica.', 'cirurgia', 'Cirurgia plástica reparadora', 'Reconstrução mamária', ARRAY['mamária', 'reconstrução', 'DIEP']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o papel da microcirurgia em reparos vasculares?', 'A microcirurgia permite anastomoses precisas de vasos e nervos, essencial em retalhos livres para reconstruções complexas. Ela melhora a sobrevida dos tecidos transferidos ao restaurar fluxo sanguíneo microvascular. Complicações incluem trombose, exigindo anticoagulação e monitoração pós-operatória.', 'cirurgia', 'Cirurgia plástica reparadora', 'Microcirurgia', ARRAY['microcirurgia', 'anastomose', 'retalhos livres']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva o tratamento de cicatrizes hipertróficas.', 'Cicatrizes hipertróficas são tratadas com injeções de corticosteroides para reduzir inflamação e espessura, associadas a silicone tópico. Em casos graves, silício sheets ou laser terapia são usados para melhorar textura. A prevenção inicia com tensão mínima na sutura e cuidados pós-operatórios.', 'cirurgia', 'Cirurgia plástica reparadora', 'Cicatrizes', ARRAY['hipertróficas', 'corticosteroides', 'silicone']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que são expansores teciduais e sua aplicação?', 'Expansores teciduais são dispositivos infláveis usados para estender pele em reconstruções, como em queimaduras ou defeitos congênitos. Eles promovem crescimento gradual de tecido, permitindo cobertura de áreas maiores. O processo envolve inflação seriada, com risco de infecção ou extrusão.', 'cirurgia', 'Cirurgia plástica reparadora', 'Expansores', ARRAY['expansores', 'teciduais', 'reconstrução']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a importância da reconstrução labial em traumas?', 'A reconstrução labial restaura função oral e estética após traumas ou ressecções, usando retalhos locais como o de Abbe. Ela preserva sensibilidade e mobilidade, essencial para alimentação e fala. Complicações incluem deiscência, exigindo suturas cuidadosas.', 'cirurgia', 'Cirurgia plástica reparadora', 'Reconstrução facial', ARRAY['labial', 'trauma', 'retalhos locais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Explique o uso de enxertos ósseos em cirurgias reparadoras.', 'Enxertos ósseos autólogos fornecem suporte estrutural e incorporação biológica em defeitos ósseos pós-trauma ou infecção. Eles promovem osteogênese, osteoindução e osteocondução. Alternativas alógenas são usadas quando há limitação doadora, mas com maior risco de rejeição.', 'cirurgia', 'Cirurgia plástica reparadora', 'Enxertos ósseos', ARRAY['ósseos', 'autólogos', 'osteogênese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar malformações congênitas faciais?', 'Malformações como fenda labial são corrigidas cirurgicamente em etapas, iniciando aos 3-6 meses com rotação-advancement. Isso restaura anatomia e função, com lipossução secundária para refinamento. A abordagem multidisciplinar inclui ortodontia e fonoaudiologia.', 'cirurgia', 'Cirurgia plástica reparadora', 'Malformações', ARRAY['congênitas', 'fenda labial', 'multidisciplinar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é a técnica de Z-plastia em reparos?', 'A Z-plastia alonga e reorienta cicatrizes, quebrando linhas de tensão para melhorar funcionalidade e estética. Ela envolve transposição de retalhos triangulares, com ângulos de 60 graus ideais. É útil em contraturas pós-queimadura ou trauma.', 'cirurgia', 'Cirurgia plástica reparadora', 'Z-plastia', ARRAY['Z-plastia', 'cicatrizes', 'contraturas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a abordagem cirúrgica para apendicite aguda em crianças?', 'A apendicectomia laparoscópica é o padrão em crianças, minimizando trauma e permitindo recuperação rápida. Em casos perfurados, dreno e antibióticos IV são essenciais para controlar peritonite. A idade pediátrica exige ajuste de doses e monitoração de desidratação.', 'cirurgia', 'Cirurgia pediátrica', 'Apendicite', ARRAY['apendicectomia', 'laparoscópica', 'peritonite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar hérnia inguinal em lactentes?', 'A herniorrafia inguinal é indicada precocemente em lactentes para prevenir encarceramento, usando abordagem aberta ou laparoscópica. O saco herniário é ligado alto, preservando o cordão espermático. Complicações incluem isquemia testicular, mais comum em meninos prematuros.', 'cirurgia', 'Cirurgia pediátrica', 'Hérnia inguinal', ARRAY['herniorrafia', 'lactentes', 'encarceramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva o manejo de atresia esofágica.', 'A atresia esofágica com fístula traqueoesofágica é corrigida por anastomose primária esofágica após estabilização respiratória. Fístula é ligada e esôfago distal mobilizado. Complicações incluem fístula anastomótica e refluxo, requerendo dilatações pós-operatórias.', 'cirurgia', 'Cirurgia pediátrica', 'Atresia esofágica', ARRAY['atresia', 'anastomose', 'fístula']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o tratamento para piloromiotomia em estenose hipertrófica?', 'A piloromiotomia de Ramstedt é o tratamento de escolha para estenose pilórica hipertrófica, dividindo fibras musculares sem mucosotomia. Realizada por laparoscopia ou aberta, alivia obstrução gástrica em lactentes. Pós-operatório inclui hidratação e alimentação gradual.', 'cirurgia', 'Cirurgia pediátrica', 'Estenose pilórica', ARRAY['piloromiotomia', 'hipertrófica', 'Ramstedt']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como é feita a correção de cardiopatias congênitas em neonatos?', 'Correções como coartação aórtica envolvem ressecção e anastomose ou patch em neonatos via toracotomia. Estabilização hemodinâmica pré-operatória é crucial com prostaglandinas. Monitoração pós-operatória foca em hipertensão e disfunção renal.', 'cirurgia', 'Cirurgia pediátrica', 'Cardiopatias', ARRAY['congênitas', 'coartação', 'neonatos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Explique o manejo de malformação de intestino em recém-nascidos.', 'Malrotação intestinal é corrigida por Ladd procedure, que corrige a posição e remove banda de Ladd para prevenir volvo. Realizada em urgência se houver obstrução ou isquemia. Pós-operatório inclui nutrição parenteral inicial.', 'cirurgia', 'Cirurgia pediátrica', 'Malformações intestinais', ARRAY['malrotação', 'Ladd', 'volvo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a cirurgia para gastrosquise em neonatos?', 'A gastrosquise é tratada com redução intestinal e fechamento primário ou silástico se necessário, protegendo alças expostas. Pós-operatório envolve suporte nutricional e monitoração de motilidade. Complicações incluem infecção e adesões.', 'cirurgia', 'Cirurgia pediátrica', 'Gastrosquise', ARRAY['gastrosquise', 'redução', 'silástico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva a abordagem para cisto de escolha em crianças.', 'Cisto de escolha é excisado cirurgicamente para prevenir infecção ou malignidade, com remoção do trato fistuloso. Abordagem via cervical com drenagem se infectado. Recorrência é rara com excisão completa.', 'cirurgia', 'Cirurgia pediátrica', 'Cisto de escolha', ARRAY['cisto', 'excisão', 'fistuloso']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar trauma esplênico em pediatria?', 'Trauma esplênico grau I-III é manejado conservadoramente com monitoração e transfusão se necessário, preservando função imunológica. Grau IV-V requer esplenectomia ou reparo angiográfico. Vacinação pós-esplenectomia contra pneumococo é essencial.', 'cirurgia', 'Cirurgia pediátrica', 'Trauma esplênico', ARRAY['trauma', 'conservador', 'esplenectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é a cirurgia de Kasai para atresia biliar?', 'A portoenterostomia de Kasai restaura fluxo biliar em atresia extra-hepática, anastomizando alças jejunais ao hilo hepático. Indicada antes dos 60 dias para melhor prognóstico. Complicações incluem colangite recorrente e necessidade de transplante hepático.', 'cirurgia', 'Cirurgia pediátrica', 'Atresia biliar', ARRAY['Kasai', 'portoenterostomia', 'colangite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a classificação de queimaduras por profundidade?', 'Queimaduras são classificadas como 1º grau (eritema superficial), 2º grau superficial (bolhas) ou profundo (trombose vascular), e 3º grau (necrose completa). A avaliação guia o tratamento, com 2º grau profundo podendo requerer enxerto. Superfície corporal queimada usa regra dos 9s, ajustada em crianças.', 'cirurgia', 'Queimaduras', 'Classificação', ARRAY['profundidade', 'graus', 'regra dos 9s']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como calcular a superfície corporal queimada em adultos?', 'A regra dos 9s atribui 9% por segmento: cabeça 9%, cada braço 9%, tronco anterior/posterior 18% cada, pernas 18% cada, genitais 1%. Para áreas irregulares, usa-se Lund-Browder ajustado por idade. Isso determina fluidoterapia com fórmula de Parkland: 4ml/kg/% queimada.', 'cirurgia', 'Queimaduras', 'Superfície queimada', ARRAY['regra dos 9s', 'Parkland', 'Lund-Browder']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o manejo inicial de queimadura por inalação?', 'Queimadura por inalação requer intubação precoce se houver edema laríngeo ou CO elevado, com oxigenoterapia e broncoscopia para avaliação. Fluidos são ajustados por diurese, e profilaxia de infecção com antibióticos. Monitoração em UTI é essencial para falência respiratória.', 'cirurgia', 'Queimaduras', 'Inalação', ARRAY['inalação', 'intubação', 'broncoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva a excisão tangencial em queimaduras.', 'Excisão tangencial remove tecido necrótico camada por camada até sangramento viável, ideal para 2º grau profundo. Realizada precocemente para reduzir infecção e perda proteica. Cobertura com enxerto de pele mesh é subsequente.', 'cirurgia', 'Queimaduras', 'Excisão', ARRAY['tangencial', 'necrótico', 'enxerto mesh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é a fórmula de Parkland para ressuscitação?', 'A fórmula de Parkland calcula Ringer lactato: 4ml x peso(kg) x %SBQ, metade nas primeiras 8h pós-queimadura, resto em 16h. Ajusta-se por diurese alvo de 0,5-1ml/kg/h. Evita sobrecarga em idosos ou cardíacos.', 'cirurgia', 'Queimaduras', 'Ressuscitação', ARRAY['Parkland', 'Ringer', 'diurese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar infecção em queimados?', 'Infecções em queimados são tratadas com desbridamento cirúrgico e antibióticos baseados em cultura, como vancomicina para MRSA. Prevenção inclui barreiras estéreis e nutrição hiperproteica. Monitoração de sepse com lactato e procalcitonina.', 'cirurgia', 'Queimaduras', 'Infecção', ARRAY['infecção', 'desbridamento', 'sepse']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o papel da nutrição em pacientes queimados?', 'Nutrição enteral precoce fornece calorias elevadas (25-40kcal/kg) e proteínas (1,5-2g/kg) para combater catabolismo hipermetabólico. Fórmulas especiais incluem arginina e ômega-3. Monitora-se pré-albumina para ajuste.', 'cirurgia', 'Queimaduras', 'Nutrição', ARRAY['hipermetabólico', 'enteral', 'arginina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva complicações tardias de queimaduras.', 'Complicações tardias incluem contraturas cicatriciais, requerendo fisioterapia e cirurgia plástica, e hipertrofia tecidual. Risco de melanoma em áreas cicatrizadas exige vigilância. Suporte psicológico aborda TEPT pós-queimadura.', 'cirurgia', 'Queimaduras', 'Complicações', ARRAY['contraturas', 'cicatriciais', 'TEPT']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que são queimaduras elétricas e manejo?', 'Queimaduras elétricas causam necrose profunda por corrente, com entrada/saída pontos; manejo inclui fasciotomia para síndrome compartimental. Exploração cirúrgica precoce detecta dano vascular. Monitora-se arritmias cardíacas.', 'cirurgia', 'Queimaduras', 'Elétricas', ARRAY['elétricas', 'fasciotomia', 'arritmias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a abordagem para queimadura química?', 'Queimaduras químicas requerem irrigação imediata com água por 20-30min para neutralizar agente, seguida de desbridamento. Ácidos causam coagulação, bases liquefação tecidual. Avaliação endoscópica se ingestão.', 'cirurgia', 'Queimaduras', 'Químicas', ARRAY['químicas', 'irrigação', 'desbridamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é cicatrização por segunda intenção?', 'Cicatrização por segunda intenção ocorre em feridas abertas, preenchendo com granulação de base a superfície, mais lenta que primária. Indicada para infecções ou feridas irregulares, com risco maior de cicatrizes hipertróficas. Cuidados incluem limpeza diária e curativos úmidos.', 'cirurgia', 'Feridas e cicatrização', 'Segunda intenção', ARRAY['segunda intenção', 'granulação', 'curativos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva os fatores que atrasam a cicatrização.', 'Fatores como desnutrição proteica, diabetes e tabagismo atrasam cicatrização por hipóxia e imunossupressão. Infecção bacteriana inibe fases inflamatória e proliferativa. Idosos têm redução de colágeno e vascularização.', 'cirurgia', 'Feridas e cicatrização', 'Fatores atraso', ARRAY['desnutrição', 'diabetes', 'infecção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o papel do colágeno na cicatrização?', 'Colágeno é sintetizado por fibroblastos na fase proliferativa, fornecendo tensile strength à ferida. Tipo I predomina em cicatrizes maduras, tipo III inicial. Deficiência em escorbuto atrasa deposição.', 'cirurgia', 'Feridas e cicatrização', 'Colágeno', ARRAY['colágeno', 'fibroblastos', 'proliferativa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como classificar feridas cirúrgicas?', 'Feridas são classificadas como limpas (eletivas), limpas-contaminadas (trato GI), contaminadas (inflamadas) e sujas (pus). Isso prediz risco infeccioso: 1-5% em limpas vs 20-40% em sujas. Profilaxia antibiótica ajusta-se à classe.', 'cirurgia', 'Feridas e cicatrização', 'Classificação', ARRAY['limpas', 'contaminadas', 'profilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é dehiscência de ferida?', 'Dehiscência é abertura de sutura cirúrgica, causada por infecção, isquemia ou tosse excessiva. Manejo inclui reabertura, desbridamento e ressutura ou intenção secundária. Prevenção com suturas de tensão e nutrição adequada.', 'cirurgia', 'Feridas e cicatrização', 'Dehiscência', ARRAY['dehiscência', 'infecção', 'desbridamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Explique a fase inflamatória da cicatrização.', 'A fase inflamatória inicia com hemostasia por plaquetas, seguida de neutrófilos e macrófagos removendo debris. Libera citocinas para angiogênese e fibroblastos. Dura 3-5 dias, essencial para controle infeccioso.', 'cirurgia', 'Feridas e cicatrização', 'Inflamatória', ARRAY['inflamatória', 'neutrófilos', 'citocinas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o tratamento de úlceras por pressão?', 'Úlceras por pressão são tratadas com alívio de pressão, desbridamento e curativos de hidrogel para estágio II-IV. Nutrição rica em zinco e proteínas acelera reparo. Cirurgia plástica para estágios graves com retalhos.', 'cirurgia', 'Feridas e cicatrização', 'Úlceras pressão', ARRAY['pressão', 'desbridamento', 'hidrogel']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva o uso de curativos avançados.', 'Curativos avançados como hidrocoloides mantêm umidade, promovendo autólise em feridas crônicas. Prata iônica combate infecção em úlceras diabéticas. Escolha baseia-se em exsudato e profundidade.', 'cirurgia', 'Feridas e cicatrização', 'Curativos', ARRAY['avançados', 'hidrocoloides', 'prata iônica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é cicatrização por terceira intenção?', 'Cicatrização por terceira intenção usa packing em feridas infectadas, permitindo granulação antes de fechar. Reduz tensão e infecção comparado a primária. Comum em perfurações abdominais.', 'cirurgia', 'Feridas e cicatrização', 'Terceira intenção', ARRAY['terceira intenção', 'packing', 'granulação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a importância da vitamina C na cicatrização?', 'Vitamina C é cofator para hidroxilação de prolina no colágeno, essencial para força tecidual. Deficiência causa escorbuto com feridas frágeis. Suplementação em desnutridos acelera reparo.', 'cirurgia', 'Feridas e cicatrização', 'Vitamina C', ARRAY['vitamina C', 'colágeno', 'escorbuto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como manejar apendicite perfurada como urgência?', 'Apendicite perfurada requer apendicectomia imediata com lavagem peritoneal e dreno, associada a antibióticos de amplo espectro. Pós-operatório monitora abscesso subfrênico por TC. Conservador só em estável sem peritonite.', 'cirurgia', 'Urgências cirúrgicas', 'Apendicite', ARRAY['perfurada', 'lavagem', 'antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva o tratamento de colecistite aguda.', 'Colecistite aguda é tratada com colecistectomia laparoscópica precoce em 72h, após hidratação e antibióticos. Em grave, colecistostomia percutânea descomprime. Complicações incluem gangrena e perfuração.', 'cirurgia', 'Urgências cirúrgicas', 'Colecistite', ARRAY['aguda', 'laparoscópica', 'colecistostomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual o manejo de diverticulite complicada?', 'Diverticulite complicada com abscesso requer drenagem percutânea e antibióticos IV; perfuração indica Hartmann procedure. Pós-operatório foca em nutrição e prevenção de recorrência com fibra. Classificação Hinchey guia abordagem.', 'cirurgia', 'Urgências cirúrgicas', 'Diverticulite', ARRAY['complicada', 'drenagem', 'Hartmann']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar obstrução intestinal?', 'Obstrução intestinal requer descompressão com sonda nasogástrica e hidratação; cirurgia se estrangulamento ou isquemia por laparotomia. Adesões são causa comum pós-operatória. TC confirma transição e níveis hidroaéreos.', 'cirurgia', 'Urgências cirúrgicas', 'Obstrução', ARRAY['intestinal', 'descompressão', 'estrangulamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Explique a triagem em trauma abdominal.', 'Triagem em trauma usa ATLS: ABCDE, com FAST para hemoperitônio e DPL se instável. Laparotomia exploradora em choque hemorrágico. Escala de trauma abdominal prediz lesões sólidas.', 'cirurgia', 'Urgências cirúrgicas', 'Trauma abdominal', ARRAY['ATLS', 'FAST', 'laparotomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é síndrome compartimental e tratamento?', 'Síndrome compartimental é aumento pressórico em compartimento, causando isquemia; tratamento é fasciotomia emergente em 6h. Monitora-se pressão >30mmHg. Comum em fraturas e queimaduras.', 'cirurgia', 'Urgências cirúrgicas', 'Compartimental', ARRAY['compartimental', 'fasciotomia', 'isquemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Descreva o manejo de hemorragia GI alta.', 'Hemorragia GI alta requer estabilização com fluidos e transfusão, endoscopia para hemostasia (clip ou injeção). Se falha, angiografia ou cirurgia. Úlcera de Duodeno é causa comum.', 'cirurgia', 'Urgências cirúrgicas', 'Hemorragia GI', ARRAY['GI alta', 'endoscopia', 'hemostasia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Qual a abordagem para pancreatite aguda grave?', 'Pancreatite aguda grave é manejada com suporte intensivo, nutrição enteral e drenagem de coleções se infectadas. Necrosectomia de minimamente invasiva em persistente. Escala Ranson prognostica mortalidade.', 'cirurgia', 'Urgências cirúrgicas', 'Pancreatite', ARRAY['aguda', 'enteral', 'necrosectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'Como tratar torção testicular?', 'Torção testicular é urgência urológica, com detorsão manual ou cirúrgica em 6h para salvar testículo; orquiectomia se necrose. Doppler confirma ausência fluxo. Profilaxia com orquidopexia contralateral.', 'cirurgia', 'Urgências cirúrgicas', 'Torção testicular', ARRAY['torção', 'detorsão', 'Doppler']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000002', 'O que é laparotomia exploradora em peritonite?', 'Laparotomia exploradora em peritonite difusa envolve lavagem extensa, controle fonte (ex: perfuração) e drenagem. Antibióticos pós-operatórios cobrem anaeróbios. Fechamento temporário com vac se instável.', 'cirurgia', 'Urgências cirúrgicas', 'Peritonite', ARRAY['laparotomia', 'lavagem', 'antibióticos']);


-- ============================================================
-- FLASHCARDS: Ginecologia e Obstetrícia — 200 cards
-- Deck: a0000001-0000-0000-0000-000000000003
-- ============================================================

INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Pré-natal, Parto, Ginecologia, Oncologia ginecológica, Planejamento familiar
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual é o exame inicial recomendado para triagem de anemia no pré-natal?', 'A hemoglobina e o hematócrito são exames iniciais para triagem de anemia no pré-natal. Valores normais: Hb > 11 g/dL no 1º e 3º trimestres, > 10,5 g/dL no 2º. Anemia ferropriva é comum, tratar com suplementação de ferro 60 mg/dia.', 'ginecologia_obstetricia', 'Pré-natal', 'Triagem de anemia', ARRAY['anemia','pre_natal','suplementacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando realizar a primeira consulta pré-natal ideal?', 'A primeira consulta pré-natal deve ser realizada o mais precocemente possível, idealmente até 12 semanas de gestação. Inclui história clínica, exame físico e exames laboratoriais básicos para avaliar riscos.', 'ginecologia_obstetricia', 'Pré-natal', 'Consulta inicial', ARRAY['consulta','pre_natal','gestacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais vacinas são recomendadas durante o pré-natal?', 'Vacinas: dTpa (20-36 semanas, ideal 27-36 sem — MS/FEBRASGO), influenza (qualquer trimestre), hepatite B se não vacinada. Evitar vacinas vivas como rubéola. Atualizar cartão vacinal da gestante para proteção materna e fetal.', 'ginecologia_obstetricia', 'Pré-natal', 'Vacinação', ARRAY['vacinas','pre_natal','imunizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como avaliar o risco de pré-eclâmpsia no pré-natal?', 'Fatores de risco: nuliparidade, idade >40, hipertensão prévia, diabetes, obesidade. Triagem com proteinúria, PA >140/90 mmHg após 20 semanas. Profilaxia com AAS 100-150 mg/dia se alto risco.', 'ginecologia_obstetricia', 'Pré-natal', 'Pré-eclâmpsia', ARRAY['pre_eclampsia','risco','profilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o ultrassom de rotina no pré-natal?', 'Ultrassonografias: 1º trimestre (11-14 sem) para datagem e risco cromossômico; 2º (20-24 sem) para anatomia fetal; 3º (30-34 sem) para crescimento. Monitorar Doppler se necessário.', 'ginecologia_obstetricia', 'Pré-natal', 'Ultrassonografia', ARRAY['ultrassom','fetal','rotina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar diabetes gestacional?', 'Triagem com TTOG 75g entre 24-28 semanas. Se alterado, dieta, atividade física; se HbA1c >6,5% ou glicemia de jejum >95 mg/dL, insulina. Monitorar glicemia capilar 4x/dia.', 'ginecologia_obstetricia', 'Pré-natal', 'Diabetes gestacional', ARRAY['diabetes','gestacional','manejo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais exames sorológicos no pré-natal?', 'Sorologias: HIV, sífilis (VDRL), hepatite B (HBsAg), toxoplasmose (IgM/IgG), rubéola (IgG). Repetir no 3º trimestre se alto risco. Profilaxia conforme resultados positivos.', 'ginecologia_obstetricia', 'Pré-natal', 'Sorologias', ARRAY['sorologia','infeccoes','pre_natal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o ganho de peso recomendado na gestação?', 'Ganho de peso: baixo peso (12,5-18 kg), normal (11,5-16 kg), sobrepeso (7-11,5 kg), obesidade (5-9 kg). Orientar dieta balanceada e atividade física moderada.', 'ginecologia_obstetricia', 'Pré-natal', 'Nutrição', ARRAY['peso','gestacao','dieta']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como detectar malformações congênitas?', 'Rastreio: ultrassom morfológico no 2º trimestre, translucência nucal no 1º, cariótipo se alto risco. Testes não invasivos (NIPT) para aneuploidias. Aconselhamento genético se alterado.', 'ginecologia_obstetricia', 'Pré-natal', 'Malformações', ARRAY['malformacoes','rastreio','genetica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o manejo de ITU no pré-natal?', 'Infecção urinária: assintomática tratar com nitrofurantoína ou ampicilina; sintomática com cefalosporina. Urocultura mensal no 1º trimestre. Hidratação e higiene perineal preventivas.', 'ginecologia_obstetricia', 'Pré-natal', 'Infecções urinárias', ARRAY['ITU','pre_natal','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sinais de trabalho de parto verdadeiro?', 'Contrações regulares, progressivas, >30 seg, intervalos <10 min. Dilatação cervical >3 cm, expulsão de tampão mucoso. Diferenciar de falso parto por ausência de alterações cervicais.', 'ginecologia_obstetricia', 'Parto', 'Trabalho de parto', ARRAY['parto','contracoes','sinais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como monitorar o bem-estar fetal durante o parto?', 'Monitorização cardiotocográfica (CTG): frequência cardíaca fetal, variabilidade, acelerações, desacelerações. pH fetal se oligoidrâmnio ou suspeita de sofrimento. Parto vaginal com analgesia se estável.', 'ginecologia_obstetricia', 'Parto', 'Monitorização fetal', ARRAY['CTG','fetal','bem_estar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a conduta em apresentação pélvica?', 'Apresentação pélvica: versão cefálica externa (VCE) a partir de 36 semanas se sem CI. Cesárea se falha ou CI (nulípara, macrossomia). Parto vaginal possível em multípara selecionada.', 'ginecologia_obstetricia', 'Parto', 'Apresentação pélvica', ARRAY['pelvica','versao','cesarea']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é a curva de Bishop e sua importância?', 'Curva de Bishop avalia maturação cervical: escore 0-13 baseado em dilatação, apagamento, consistência, posição, estação. Escore ≥6 favorável para indução. Maturação com misoprostol se <6.', 'ginecologia_obstetricia', 'Parto', 'Indução do parto', ARRAY['Bishop','inducao','maturacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais as fases do trabalho de parto?', 'Fases: latente (dilatação 0-6 cm, horas variáveis); ativa (6-10 cm, 1-1,2 cm/h nulípara); expulsão (descida e saída fetal); alumbramento (descolamento e saída de placenta). Monitorar progressão.', 'ginecologia_obstetricia', 'Parto', 'Fases do parto', ARRAY['fases','trabalho','progressao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar distócia de ombros?', 'Distócia de ombros: manobras (McRoberts, pressão suprapúbica, Woods). Episiotomia se necessário. Prevenir com avaliação pélvica pré-parto. Risco de lesão braquial ou fratura de clavícula.', 'ginecologia_obstetricia', 'Parto', 'Distócia', ARRAY['ombros','manobras','complicacoes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual analgesia para parto normal?', 'Analgesia: bloqueio paracervical ou peridural (lidocaína + bupivacaína). Não opioides sistêmicos por depressão fetal. Benefícios: alívio dor sem afetar contratilidade uterina.', 'ginecologia_obstetricia', 'Parto', 'Analgesia', ARRAY['analgesia','peridural','parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando indicar cesárea de emergência?', 'Indicações: sofrimento fetal agudo (CTG patológico), prolapso de cordão, descolamento prematuro de placenta, ruptura uterina. Preparar equipe e sala cirúrgica imediatamente.', 'ginecologia_obstetricia', 'Parto', 'Cesárea', ARRAY['cesarea','emergencia','indicacoes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é a via de parto em gestações múltiplas?', 'Gestações múltiplas: parto vaginal se dicoriônica, apresentação cefálica, sem CI. Cesárea se monocoriônica, apresentação não cefálica, >2 fetos. Monitorar discordância de crescimento.', 'ginecologia_obstetricia', 'Parto', 'Gestações múltiplas', ARRAY['multiplas','parto','dicorionica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como prevenir hemorragia pós-parto?', 'Profilaxia: ocitocina IV 10-40 UI após alumbramento, massagem uterina. Risco em atonia: PPH >500 mL vaginal. Tratar com misoprostol, ácido tranexâmico 1g IV nas primeiras 3h (WOMAN trial) — NÃO esperar refratariedade.', 'ginecologia_obstetricia', 'Parto', 'Hemorragia pós-parto', ARRAY['PPH','ocitocina','prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sintomas de endometriose?', 'Endometriose: dismenorreia intensa, dispareunia, infertilidade, dor pélvica crônica. Lesões ectópicas de endométrio. Diagnóstico por laparoscopia. Tratamento sintomático ou cirúrgico.', 'ginecologia_obstetricia', 'Ginecologia', 'Endometriose', ARRAY['endometriose','sintomas','dor']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como diagnosticar síndrome dos ovários policísticos?', 'SOP: hiperandrogenismo (hirsutismo, acne), oligo/anovulação, ovários policísticos no US. Critérios Rotterdam: 2/3 critérios. Excluir outras causas (TSH, prolactina). Risco de DM2 e endometrial.', 'ginecologia_obstetricia', 'Ginecologia', 'SOP', ARRAY['SOP','diagnostico','hiperandrogenismo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o tratamento para miomas uterinos sintomáticos?', 'Miomas: sintomáticos (sangramento, dor, infertilidade). Opções: AINEs, ACO, agonistas GnRH, embolização, histerectomia. Histeroscopia para submucosos. Monitorar crescimento pré-menopausa.', 'ginecologia_obstetricia', 'Ginecologia', 'Miomas', ARRAY['miomas','tratamento','histerectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é prolapso genital e graus?', 'Prolapso: descida de útero/vagina. Graus: I (mucosa acima do introito), II (atinge introito), III (protrui), IV (eversão completa). Fatores: paridade, menopausa. Tratamento pélvico ou cirúrgico.', 'ginecologia_obstetricia', 'Ginecologia', 'Prolapso', ARRAY['prolapso','graus','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar infecção vaginal por candidíase?', 'Candidíase: prurido, corrimento branco. Diagnóstico por exame microscópico (pseudohifas). Tratamento: fluconazol 150 mg VO dose única. Profilaxia em diabéticas ou imunossuprimidas.', 'ginecologia_obstetricia', 'Ginecologia', 'Infecções vaginais', ARRAY['candidiase','tratamento','corrimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os tipos de incontinência urinária feminina?', 'Incontinência: esforço (hipermobilidade uretral), urgência (detrusor hiperativo), mista. Avaliação: diário miccional, urodinâmica. Tratamento: Kegel, anticolinérgicos, sling.', 'ginecologia_obstetricia', 'Ginecologia', 'Incontinência', ARRAY['incontinencia','tipos','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o exame para rastreio de HPV?', 'Rastreio cervical: citologia oncótica (Papanicolaou) anual ou a cada 3 anos se normal. Teste HPV DNA para 30-65 anos. Colposcopia se ASCUS+ ou LSIL. Vacina HPV 9-valente.', 'ginecologia_obstetricia', 'Ginecologia', 'Câncer cervical', ARRAY['HPV','rastreio','citologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como tratar disfunção sexual feminina?', 'Disfunção: desejo hipoativo, anorgasmia, dispareunia. Causas: hormonal, psicológica, relacional. Tratamento: terapia sexual, lubrificantes, testosterona tópica, antidepressivos se necessário.', 'ginecologia_obstetricia', 'Ginecologia', 'Disfunção sexual', ARRAY['sexual','disfuncao','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é histerossalpingografia e indicação?', 'HSG: radiografia com contraste para avaliar cavidade uterina e permeabilidade tubária. Indicação: infertilidade, investigação de sangramento. Realizar na fase proliferativa. Risco de infecção.', 'ginecologia_obstetricia', 'Ginecologia', 'Infertilidade', ARRAY['HSG','infertilidade','tubas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais fatores de risco para câncer de ovário?', 'Câncer ovário: idade >50, nuliparidade, BRCA1/2, história familiar. Sintomas: distensão abdominal, saciedade precoce. Marcador CA-125, US transvaginal. Estadiamento cirúrgico.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Câncer de ovário', ARRAY['ovario','risco','CA125']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como é o estadiamento do câncer cervical?', 'Câncer cervical FIGO: IA (microinvasivo), IB1 (<2 cm), IB2 (2-4 cm), IB3 (≥4 cm) — FIGO 2018, II (parâmetros), III (vagina/pélvis), IV (bexiga/reto). Tratamento: cone, radioterapia, quimio (cisplatina). Rastreio previne.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Câncer cervical', ARRAY['cervical','FIGO','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o tratamento para carcinoma de endométrio?', 'Endométrio: histerectomia total + salpingo-ooforectomia bilateral. Adjuvante: radioterapia se alto risco (grau 3, invasão >50%). Hormonal (progestágenos) em jovens. Sem marcador específico.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Câncer endometrial', ARRAY['endometrio','histerectomia','radioterapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é displasia cervical e manejo?', 'Displasia: CIN1 (leve, observar), CIN2/3 (moderada/grave, excisão LEEP ou cone). HPV oncogênico causa. Follow-up com citologia/HPV a cada 6-12 meses pós-tratamento.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Displasia', ARRAY['CIN','displasia','LEEP']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sintomas de câncer vulvar?', 'Câncer vulvar: prurido, massa, ulceração, sangramento. Fatores: HPV, líquen escleroso, idade >65. Biópsia diagnóstica. Tratamento: cirurgia (vulvectomia) + linfonodectomia inguinal.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Câncer vulvar', ARRAY['vulvar','sintomas','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como rastrear câncer de mama em ginecologia?', 'Rastreio mama: mamografia anual >40 anos ou a partir de 50. US se densa. Autoexame mensal. Fatores risco: BRCA, nuliparidade. Biópsia se nódulo palpável ou suspeito.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Câncer de mama', ARRAY['mama','rastreio','mamografia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o papel da quimioterapia em gineco-oncologia?', 'Quimioterapia: adjuvante em ovário (carboplatina + paclitaxel), cervical avançado (cisplatina + RT). Neoadjuvante se irressecável. Efeitos: náusea, alopecia, neuropatia. Monitorar toxicidade.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Quimioterapia', ARRAY['quimio','tratamento','efeitos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é tumor de células da granulosa?', 'Granulosa: tumor ovariano de baixo grau maligno, estrogênio-produzente (sangramento). Inibina como marcador. Cirurgia: salpingo-ooforectomia + omentectomia. Recorrência tardia possível.', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Tumores ovarianos', ARRAY['granulosa','marcador','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar metástases em oncologia ginecológica?', 'Metástases: paliativo com RT, quimio, imunoterapia. Dor: opioides, RT local. Suporte nutricional, psicológico. Prognóstico depende do primário e disseminação (fígado, pulmão).', 'ginecologia_obstetricia', 'Oncologia ginecológica', 'Metástases', ARRAY['metastases','paliativo','suporte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual método contraceptivo de longa duração?', 'DIU: cobre (Tcu 380A, 10 anos) ou hormonal (Mirena, 8 anos — FDA 2023). Eficácia >99%. Inserir pós-menstrual. CI: infecção ativa, distorção uterina. Benefício: redução sangramento em Mirena.', 'ginecologia_obstetricia', 'Planejamento familiar', 'DIU', ARRAY['DIU','contracepcao','longa_duracao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como funciona a pílula anticoncepcional?', 'ACO: combinação estrogênio + progestágeno inibe ovulação, espessa muco cervical, afina endométrio. Iniciar dia 1 ciclo. Efeitos colaterais: náusea, cefaleia, TVP risco aumentado em fumantes >35.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Pílula', ARRAY['ACO','mecanismo','efeitos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais opções para contracepção de emergência?', 'Pós-coito: levonorgestrel 1,5 mg dose única até 72h, ou ulipristal 30 mg até 120h. Eficácia 85-95%. Não abortiva. Aconselhar método regular posterior.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Emergência', ARRAY['pos_coito','levonorgestrel','eficacia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é implante subdérmico contraceptivo?', 'Implante: etonogestrel 68 mg (Implanon, 3 anos). Liberação contínua de progestágeno. Eficácia 99,9%. Inserir braquial. Efeitos: amenorreia, spotting irregular. Remoção simples.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Implante', ARRAY['implante','etonogestrel','insercao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a DIU para nulíparas?', 'DIU em nulíparas: hormonal preferível (Mirena) por menor dor na inserção e menor risco PID. Eficácia similar. Orientar sobre cólicas iniciais e seguimento anual.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Nulíparas', ARRAY['nulipara','DIU','hormonal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como aconselhar métodos de barreira?', 'Barreiras: preservativo (dupla proteção IST/gravidez), diafragma, espermicida. Eficácia 82-88% com uso típico. Vantagem: sem hormônios. Desvantagem: falha usuário-dependente.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Barreiras', ARRAY['preservativo','barreira','IST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais contraindicações para ACO?', 'CI ACO: TVP/TEP história, enxaqueca com aura, hipertensão >160/100, tabagismo >35 anos, hepatopatia. Alternativas: progestágeno só ou DIU. Avaliar risco-benefício individual.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Contraindicações', ARRAY['ACO','CI','risco']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é esterilização tubária?', 'Laqueadura: método definitivo, pós-parto ou laparoscopia. Técnicas: Falope ring, clipes. Consentimento informado obrigatório. Reversão difícil. Taxa de falha <1%.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Esterilização', ARRAY['laqueadura','definitivo','consentimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o papel da consulta em planejamento familiar?', 'Consulta: avaliação de necessidades, história reprodutiva, exame físico se necessário. Orientar opções, eficácia, efeitos. Promover espaçamento gestacional e saúde sexual.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Consulta', ARRAY['planejamento','consulta','orientacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar sangramento breakthrough em ACO?', 'Sangramento irregular: avaliar adesão, interações (antibióticos). Se persistir, trocar formulação ou método (DIU hormonal). Excluir gravidez ou patologia endometrial.', 'ginecologia_obstetricia', 'Planejamento familiar', 'Efeitos colaterais', ARRAY['sangramento','ACO','manejo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é pré-eclâmpsia na gestação de alto risco?', 'A pré-eclâmpsia é uma complicação hipertensiva da gravidez após 20 semanas. Diagnóstico: PA ≥140/90 mmHg + proteinúria ≥300 mg/24h OU, na ausência de proteinúria, sinais de lesão orgânica (plaquetas <100.000, creatinina >1,1, transaminases 2× normal, edema pulmonar, sintomas cerebrais/visuais) — ACOG 2013/ISSHP 2018. Proteinúria NÃO é obrigatória. Tratamento: monitoramento materno-fetal, anti-hipertensivos (nifedipino, metildopa, hidralazina) e parto como cura definitiva.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'pré-eclâmpsia', ARRAY['hipertensao','proteinuria','gestacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais são os critérios diagnósticos de eclâmpsia?', 'A eclâmpsia é a evolução da pré-eclâmpsia com ocorrência de convulsões tônico-clônicas generalizadas. O diagnóstico é clínico, associado a hipertensão e proteinúria na gestação. O manejo inclui sulfato de magnésio para prevenção de recorrência de crises e parto imediato.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'eclâmpsia', ARRAY['convulsoes','sulfato_magnesio','parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que caracteriza a síndrome HELLP?', 'A síndrome HELLP envolve hemólise, elevação de enzimas hepáticas e plaquetopenia baixa, complicando a pré-eclâmpsia grave. Os critérios incluem LDH >600 UI/L, AST/ALT >70 UI/L e plaquetas <100.000/mm³. O tratamento é similar à pré-eclâmpsia, com indicação de parto em casos graves.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'HELLP', ARRAY['hemolise','hepatite','trombocitopenia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como diagnosticar diabetes gestacional?', 'O diabetes gestacional é diagnosticado por teste de tolerância à glicose oral entre 24-28 semanas, com valores alterados em jejum ≥92 mg/dL ou 2h ≥153 mg/dL. Fatores de risco incluem obesidade e história familiar. O manejo envolve dieta, exercício e insulina se necessário para controle glicêmico.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'diabetes gestacional', ARRAY['glicose','tolerancia','insulina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o manejo de placenta prévia?', 'A placenta prévia é implantação baixa cobrindo o orifício cervical, diagnosticada por ultrassonografia. Sintomas incluem sangramento vaginal indolor no terceiro trimestre. O tratamento é repouso, cesariana eletiva após 36 semanas e monitoramento fetal.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'placenta prévia', ARRAY['sangramento','ultrassom','cesariana']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é descolamento prematuro de placenta (DPP)?', 'O DPP é a separação prematura da placenta do útero, causando sangramento vaginal e dor abdominal. Fatores de risco incluem hipertensão e tabagismo. O manejo envolve estabilização materna, avaliação fetal e parto se houver sofrimento.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'DPP', ARRAY['descolamento','sangramento','hipertensao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Defina restrição de crescimento intrauterino (RCIU).', 'A RCIU é o crescimento fetal abaixo do percentil 10 para idade gestacional, diagnosticada por ultrassonografia. Causas incluem placentárias, maternas e fetais. O monitoramento usa Doppler e cardiotocografia, com indicação de parto precoce se necessário.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'RCIU', ARRAY['crescimento','ultrassom','doppler']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais complicações da gestação gemelar?', 'A gestação gemelar aumenta riscos como pré-eclâmpsia, parto prematuro e RCIU. Ocorrem em 1-2% das gestações, com maior vigilância necessária. O manejo inclui ultrassonografias seriadas e planejamento de parto por cesariana se monozigótica.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'gestação gemelar', ARRAY['geminelar','prematuro','vigilancia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é polidrâmnio na gravidez?', 'O polidrâmnio é excesso de líquido amniótico, índice >25 cm ao ultrassom, associado a diabetes gestacional ou anomalias fetais. Sintomas incluem dispneia materna. O tratamento é amnioredução em casos graves e correção da causa subjacente.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'polidrâmnio', ARRAY['liquido_amniotico','ultrassom','amnioreducao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Caracterize oligoâmnio em gestação de alto risco.', 'O oligoâmnio é redução de líquido amniótico, índice <5 cm, risco para compressão do cordão e RCIU. Diagnosticado por ultrassonografia. O manejo inclui hidratação materna e monitoramento fetal intensivo.', 'ginecologia_obstetricia', 'Gestação de alto risco', 'oligoâmnio', ARRAY['liquido_baixo','cordao','hidratacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais causas de hemorragia pós-parto?', 'A hemorragia pós-parto é perda >500 mL de sangue, causada por atonia uterina, lacerações ou retenção placentária. A atonia é a mais comum, tratada com massagem uterina e ocitocina. Prevenção inclui profilaxia com uterotônicos no parto.', 'ginecologia_obstetricia', 'Puerpério', 'hemorragia pós-parto', ARRAY['sangramento','atonia','ocitocina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sintomas e tratamento de infecção puerperal.', 'A infecção puerperal é endometrite pós-parto, com febre, dor abdominal e loquiação fétida. Causada por bactérias ascendentes. O tratamento é antibióticos intravenosos como clindamicina e gentamicina, com drenagem se abscesso.', 'ginecologia_obstetricia', 'Puerpério', 'infecção puerperal', ARRAY['endometrite','febre','antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é mastite no puerpério?', 'A mastite é infecção mamária lactacional, com dor, eritema e febre unilateral. Associada a fissuras mamilonares. O manejo inclui antibióticos como dicloxacilina, continuação da amamentação e analgésicos.', 'ginecologia_obstetricia', 'Puerpério', 'mastite', ARRAY['infecao_mamar','amamentacao','antibióticos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diagnóstico de depressão pós-parto.', 'A depressão pós-parto ocorre em 10-15% das puérperas, com sintomas como tristeza persistente, ansiedade e fadiga nas primeiras semanas. Diagnosticada por escalas como Edinburgh. O tratamento envolve psicoterapia e ISRS como sertralina.', 'ginecologia_obstetricia', 'Puerpério', 'depressão pós-parto', ARRAY['depressao','psicoterapia','antidepressivos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Riscos de trombose puerperal.', 'A trombose venosa profunda puerperal é risco aumentado por hipercoagulabilidade, com sintomas de edema e dor em membro. Profilaxia com heparina em alto risco. O tratamento é anticoagulação com enoxaparina por 3-6 meses.', 'ginecologia_obstetricia', 'Puerpério', 'trombose puerperal', ARRAY['trombose','heparina','anticoagulacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Processo de involução uterina no puerpério.', 'A involução uterina é redução do útero de 1000g para 50g em 6 semanas, com loquiação fisiológica. Monitorada por exame físico subinvolução se >12 cm acima sínfise. Complicações incluem infecção se anormal.', 'ginecologia_obstetricia', 'Puerpério', 'involução uterina', ARRAY['utero','loquiacao','exame']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Benefícios da amamentação no puerpério.', 'A amamentação exclusiva até 6 meses promove imunidade neonatal via IgA e reduz risco de câncer materno. Inicia nas primeiras horas pós-parto. Problemas comuns incluem ingurgitamento, resolvido com técnica correta.', 'ginecologia_obstetricia', 'Puerpério', 'amamentação', ARRAY['leite_materno','imunidade','tecnica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Opções de contracepção pós-parto.', 'A contracepção pós-parto inicia com métodos não hormonais como DIU de cobre imediatamente. Métodos hormonais após 21 dias se amamentando. Escolha baseada em desejo de fertilidade futura e aleitamento.', 'ginecologia_obstetricia', 'Puerpério', 'contracepção pós-parto', ARRAY['diu','hormonal','aleitamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é diástase reta no puerpério?', 'A diástase dos retos é separação >2 cm dos músculos retos abdominais, comum pós-parto. Diagnosticada por exame físico ou ultrassom. Tratamento conservador com exercícios pélvicos; cirurgia em casos graves.', 'ginecologia_obstetricia', 'Puerpério', 'diástase', ARRAY['abdominais','exercicios','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Cuidados com o recém-nascido (RN) no puerpério.', 'Os cuidados com RN incluem triagem neonatal, vacinação BCG/hepatite B e profilaxia oftálmica. Monitorar icterícia e ganho de peso. Orientar puérpera sobre sinais de alerta como febre ou recusa alimentar.', 'ginecologia_obstetricia', 'Puerpério', 'cuidados RN', ARRAY['triagem','vacinacao','orientacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Critérios diagnósticos da SOP.', 'A síndrome do ovário policístico (SOP) é diagnosticada por hiperandrogenismo, oligoamenorreia e ovários policísticos ao ultrassom (Rotterdam). Excluir outras causas como hiperprolactinemia. Acomete 5-10% das mulheres em idade reprodutiva.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'SOP', ARRAY['policistico','hiperandrogenismo','ultrassom']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Causas de amenorreia primária.', 'A amenorreia primária é ausência de menarca até 15 anos, causas incluem anomalias genitais ou hipogonadismo hipogonadotrófico. Avaliação com FSH, LH e cariótipo. Tratamento etiológico, como reposição hormonal se necessário.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'amenorreia', ARRAY['menarca','hormonios','cariotipo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Manifestações de hiperprolactinemia.', 'A hiperprolactinemia causa galactorreia, amenorreia e infertilidade por prolactinoma ou medicamentos. Dosagem sérica >25 ng/mL confirma. Tratamento com cabergolina, agonista dopaminérgico, reduzindo prolactina em 80% dos casos.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'hiperprolactinemia', ARRAY['prolactina','galactorreia','cabergolina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sintomas da menopausa e manejo.', 'A menopausa é cessação menstrual por 12 meses, com fogachos, secura vaginal e osteoporose. Confirmada por FSH >30 UI/L. Terapia de reposição hormonal (TRH) alivia sintomas, mas com riscos cardiovasculares.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'menopausa', ARRAY['fogacho','fsh','trh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Indicações para TRH na ginecologia.', 'A terapia de reposição hormonal (TRH) é indicada para sintomas vasomotores graves na perimenopausa. Combina estrogênio e progestagênio em útero intacto. Monitorar riscos de tromboembolismo e câncer endometrial.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'TRH', ARRAY['reposicao','estrogenio','riscos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sinais de hiperandrogenismo em mulheres.', 'O hiperandrogenismo manifesta hirsutismo, acne e alopecia, comum na SOP. Avaliar testosterona livre e DHEAS. Tratamento com anticoncepcionais orais para supressão ovariana.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'hiperandrogenismo', ARRAY['hirsutismo','testosterona','anticoncepcionais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Critérios para puberdade precoce.', 'A puberdade precoce é desenvolvimento mamário antes de 8 anos em meninas, por ativação prematura do eixo hipotálamo-hipofisário. Classificada central ou periférica. Tratamento com análogos de GnRH para supressão.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'puberdade precoce', ARRAY['desenvolvimento','gnrh','central']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é insuficiência ovariana primária?', 'A insuficiência ovariana primária (IOP) é falência ovariana antes dos 40 anos, com amenorreia e FSH >40 UI/L. Causas autoimunes ou genéticas. Tratamento com reposição estrogênica e aconselhamento reprodutivo.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'insuficiência ovariana', ARRAY['fsh_alta','amenorreia','reposicao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Manejo da dismenorreia primária.', 'A dismenorreia primária é dor pélvica cíclica sem patologia, por prostaglandinas. Aliviada por AINEs como ibuprofeno. Se refratária, considerar anticoncepcionais orais para supressão ovulatória.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'dismenorreia', ARRAY['dor_pelvica','aine','anticoncepcionais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sintomas e tratamento da TPM.', 'A tensão pré-menstrual (TPM) afeta humor, sono e apetite na fase lútea. Diagnosticada por diário menstrual. Tratamento inclui cálcio, SSRIs intermitentes e, em casos graves, supressão ovariana com GnRH.', 'ginecologia_obstetricia', 'Endocrinologia ginecológica', 'TPM', ARRAY['fase_lutia','ssri','gnrh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Estadiamento do câncer de mama.', 'O câncer de mama é estadiado pelo TNM, com mamografia e biópsia confirmatória. Estágio I é tumor <2 cm sem linfonodos. Prognóstico melhor em hormônio-positivo, tratado com tamoxifeno.', 'ginecologia_obstetricia', 'Mastologia', 'câncer de mama', ARRAY['estadiamento','biopsia','tamoxifeno']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diretrizes de rastreamento mamário.', 'O rastreamento de câncer de mama inicia mamografia anual aos 40 anos em mulheres de risco médio. Autoexame mensal complementa. Ultrassom em mamas densas para detecção precoce.', 'ginecologia_obstetricia', 'Mastologia', 'rastreamento', ARRAY['mamografia','autoexame','ultrassom']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que significa BIRADS 4 na mamografia?', 'O BIRADS 4 indica suspeita de malignidade, com biópsia recomendada, subdividido em 4A-C por risco. Probabilidade de câncer 2-95%. Seguimento imediato para confirmação histológica.', 'ginecologia_obstetricia', 'Mastologia', 'BIRADS', ARRAY['suspeita','biopsia','risco']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Características do fibroadenoma mamário.', 'O fibroadenoma é lesão benigna móvel, lisa, em mulheres jovens, diagnosticada por ultrassom. BIRADS 2-3, sem malignidade. Excisão se crescimento ou suspeita.', 'ginecologia_obstetricia', 'Mastologia', 'fibroadenoma', ARRAY['benigno','ultrassom','excisao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Causas de mastalgia cíclica.', 'A mastalgia cíclica é dor mamária pré-menstrual, hormonal, sem lesão. Tratada com suporte e AINEs. Avaliar com mamografia se persistente para excluir malignidade.', 'ginecologia_obstetricia', 'Mastologia', 'mastalgia', ARRAY['dor_ciclica','aine','mamografia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Avaliação de secreção papilar mamária.', 'A secreção papilar unilateral é suspeita de ducto intraductal papiloma ou carcinoma. Avaliar com mamografia e ductoscopia. Biópsia se citologia atípica.', 'ginecologia_obstetricia', 'Mastologia', 'secreção papilar', ARRAY['secrecao','biopsia','citologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Indicações para mastectomia.', 'A mastectomia é indicada em câncer de mama multicêntrico ou contraindicação a radioterapia. Tipos incluem simples ou radical modificada. Reconstrução imediata possível com implante.', 'ginecologia_obstetricia', 'Mastologia', 'mastectomia', ARRAY['cirurgia','radical','reconstrucao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Papel da quimioterapia neoadjuvante.', 'A quimioterapia neoadjuvante reduz tumor antes da cirurgia em câncer de mama localmente avançado. Esquemas incluem antraciclinas e taxanos. Avalia resposta patológica para prognóstico.', 'ginecologia_obstetricia', 'Mastologia', 'quimioterapia neoadjuvante', ARRAY['pre_cirurgia','antraciclinas','resposta']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Opções de reconstrução mamária pós-mastectomia.', 'A reconstrução mamária usa implantes ou retalhos autólogos como DIEP. Indicada após mastectomia para qualidade de vida. Timing imediato ou tardio, com simetria contralateral se necessário.', 'ginecologia_obstetricia', 'Mastologia', 'reconstrução mamária', ARRAY['implante','retalho','simetria']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Técnica do linfonodo sentinela em mama.', 'O linfonodo sentinela detecta metástase axilar precoce, injetando azul ou radiofármaco. Se negativo, evita dissecção axilar. Reduz linfedema em 70% dos casos.', 'ginecologia_obstetricia', 'Mastologia', 'linfonodo sentinela', ARRAY['sentinela','metastase','linfedema']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Tipos de incontinência urinária de esforço.', 'A incontinência urinária de esforço ocorre por perda uretral em tosse ou esforço, devido a hiperlaxidade. Diagnosticada por história e teste de esforço. Tratamento inicial com exercícios de Kegel.', 'ginecologia_obstetricia', 'Uroginecologia', 'incontinência urinária de esforço', ARRAY['esforco','uretral','kegel']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sintomas da bexiga hiperativa.', 'A bexiga hiperativa causa urgência miccional, noctúria e incontinência de urgência por detrusor instável. Avaliada por urodinâmica. Tratamento com anticolinérgicos como oxibutinina.', 'ginecologia_obstetricia', 'Uroginecologia', 'bexiga hiperativa', ARRAY['urgencia','urodinamica','anticolinergicos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Classificação do prolapso genital.', 'O prolapso genital é classificado por POP-Q, graus I-IV por descida vaginal. Causado por paridade e menopausa. Sintomas incluem sensação de peso pélvico.', 'ginecologia_obstetricia', 'Uroginecologia', 'prolapso genital', ARRAY['popq','descida','paridade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é cistocele e seu tratamento?', 'O cistocele é prolapso da bexiga anterior, grau I-III. Tratado com pessário ou colporrafia anterior. Prevenção por exercícios pélvicos pós-parto.', 'ginecologia_obstetricia', 'Uroginecologia', 'cistocele', ARRAY['bexiga','prolapso','colporrafia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Características do retocele.', 'O retocele é hérnia posterior vaginal com reto, causando dificuldade evacuatória. Diagnosticado por exame especular. Cirurgia com colporretoplastia em sintomáticas.', 'ginecologia_obstetricia', 'Uroginecologia', 'retocele', ARRAY['retal','evacuacao','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Causas de fístula vesicovaginal.', 'A fístula vesicovaginal é comunicação vesical-vaginal, por trauma obstétrico ou histerectomia. Sintomas de incontinência contínua. Tratamento cirúrgico com latzko ou flap.', 'ginecologia_obstetricia', 'Uroginecologia', 'fístula vesicovaginal', ARRAY['fistula','incontinencia','cirurgia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Indicações para estudo urodinâmico.', 'O estudo urodinâmico avalia função vesical em incontinência mista ou prolapso. Mede pressão de vazamento e capacidade. Essencial pré-operatório para sling.', 'ginecologia_obstetricia', 'Uroginecologia', 'urodinâmica', ARRAY['vesical','pressao','pre_op']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Técnica do sling na incontinência.', 'O sling suburetral suporta uretra média com malha sintética ou autólogo. Eficaz em 85% para esforço. Complicações incluem erosão e retenção urinária.', 'ginecologia_obstetricia', 'Uroginecologia', 'sling', ARRAY['suburetral','malha','complicacoes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Uso do pessário em uroginecologia.', 'O pessário é dispositivo intravaginal para suporte em prolapso leve, tipos ring ou Gellhorn. Ajustado por exame, com remoção semanal. Alternativa não cirúrgica para idosas.', 'ginecologia_obstetricia', 'Uroginecologia', 'pessário', ARRAY['suporte','intravaginal','idosas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Benefícios dos exercícios pélvicos.', 'Os exercícios de Kegel fortalecem assoalho pélvico, reduzindo incontinência em 50%. Indicados pós-parto e em prolapso inicial. Sessões diárias de 10 contrações por 10 segundos.', 'ginecologia_obstetricia', 'Uroginecologia', 'exercícios pélvicos', ARRAY['kegel','fortalecimento','post_parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é triagem por ultrassom na medicina fetal?', 'A triagem por ultrassom é um exame de imagem não invasivo usado para avaliar o desenvolvimento fetal, detectar anomalias estruturais e monitorar o crescimento. Realiza-se em diferentes trimestres da gestação, como o primeiro para translucência nucal e o segundo para morfologia fetal. É essencial para identificar riscos precocemente.', 'ginecologia_obstetricia', 'Medicina fetal', 'Ultrassom', ARRAY['ultrassom','triagem','fetal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a indicação principal do NIPT?', 'O NIPT (Non-Invasive Prenatal Testing) é um teste de triagem baseado em DNA fetal livre no sangue materno para detectar aneuploidias cromossômicas como trisomia 21, 18 e 13. É indicado para gestantes de alto risco, a partir da 10ª semana. Não é diagnóstico, mas tem alta sensibilidade e especificidade.', 'ginecologia_obstetricia', 'Medicina fetal', 'NIPT', ARRAY['NIPT','aneuploidia','triagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais anomalias fetais são detectadas por ultrassom?', 'Anomalias fetais como espinha bífida, cardiopatias congênitas e anomalias renais são detectadas por ultrassom morfológico. O exame avalia órgãos e estruturas fetais em detalhes. Em casos suspeitos, exames complementares como ecocardiografia fetal são recomendados.', 'ginecologia_obstetricia', 'Medicina fetal', 'Anomalias fetais', ARRAY['anomalias','ultrassom','fetal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é cordocentese e sua indicação?', 'Cordocentese é a punção do cordão umbilical para obter sangue fetal, usada para diagnóstico de anemias, infecções ou cariótipo. Realiza-se após a 18ª semana, guiada por ultrassom. Riscos incluem sangramento e infecção, mas é precisa para análises laboratoriais.', 'ginecologia_obstetricia', 'Medicina fetal', 'Cordocentese', ARRAY['cordocentese','sangue fetal','diagnóstico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando realizar amniocentese?', 'Amniocentese é indicada para diagnóstico invasivo de anomalias genéticas, infecções ou maturidade pulmonar fetal, entre 15-20 semanas. Envolve aspiração de líquido amniótico guiado por ultrassom. Risco de aborto é baixo, cerca de 0,5-1%.', 'ginecologia_obstetricia', 'Medicina fetal', 'Amniocentese', ARRAY['amniocentese','líquido amniótico','genética']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o papel da translucência nucal no primeiro trimestre?', 'A translucência nucal é medida por ultrassom no primeiro trimestre para avaliar risco de síndromes cromossômicas e defeitos cardíacos. Valores acima de 3mm indicam alto risco. Combina-se com dosagem de PAPP-A e beta-hCG livres para triagem.', 'ginecologia_obstetricia', 'Medicina fetal', 'Ultrassom', ARRAY['translucência','primeiro trimestre','triagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais limitações do NIPT?', 'O NIPT não detecta microdeleções ou anomalias estruturais não cromossômicas. Falsos positivos ocorrem em gestações gemelares ou placentárias anômalas. Sempre confirmar com exames invasivos como amniocentese em casos positivos.', 'ginecologia_obstetricia', 'Medicina fetal', 'NIPT', ARRAY['limitações','NIPT','falsos positivos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como classificar anomalias fetais?', 'Anomalias fetais classificam-se em estruturais (ex: agenesia renal), cromossômicas (ex: Down) e funcionais (ex: arritmias). Diagnóstico por ultrassom e testes genéticos. Aconselhamento multidisciplinar é essencial para manejo.', 'ginecologia_obstetricia', 'Medicina fetal', 'Anomalias fetais', ARRAY['classificação','anomalias','genética']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Riscos da cordocentese?', 'Riscos incluem perda fetal (1-2%), infecção e prematuridade. Indicada quando benefícios superam riscos, como em hidropsia fetal. Monitoramento pós-procedimento é obrigatório.', 'ginecologia_obstetricia', 'Medicina fetal', 'Cordocentese', ARRAY['riscos','cordocentese','fetal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diferença entre amniocentese e villocentese?', 'Amniocentese coleta líquido amniótico para análise citogenética; villocentese coleta vilosidades coriônicas para o mesmo fim, mais precoce (11-13 semanas). Amniocentese tem menor risco de mosaicismo confinado à placenta.', 'ginecologia_obstetricia', 'Medicina fetal', 'Amniocentese', ARRAY['amniocentese','vilocentese','invasivo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é vulvovaginite e suas causas?', 'Vulvovaginite é inflamação da vulva e vagina, causada por infecções como candidíase, vaginose bacteriana ou tricomoníase. Sintomas incluem prurido, corrimento e dispareunia. Diagnóstico por exame clínico e pH vaginal.', 'ginecologia_obstetricia', 'Infecções em GO', 'Vulvovaginite', ARRAY['vulvovaginite','infecção','corrimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o tratamento da PID?', 'A PID (Doença Inflamatória Pélvica) é tratada com antibióticos de amplo espectro, como ceftriaxona e doxiciclina, cobrindo gonococos, clamídia e anaeróbios. Hospitalização se gravidez ou abscesso. Acompanhamento para sequelas como infertilidade.', 'ginecologia_obstetricia', 'Infecções em GO', 'PID', ARRAY['PID','antibióticos','pélvica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'STIs na gravidez: riscos para o feto?', 'STIs como sífilis, HIV e herpes aumentam riscos de aborto, prematuridade e transmissão vertical. Triagem rotineira e tratamento precoce são cruciais. Profilaxia com cesárea em herpes ativa.', 'ginecologia_obstetricia', 'Infecções em GO', 'IST na gravidez', ARRAY['IST','gravidez','transmissão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é transmissão vertical?', 'Transmissão vertical é a passagem de infecções da mãe para o feto durante gestação, parto ou amamentação, como HIV, CMV e toxoplasmose. Prevenção por triagem, tratamento e cesárea quando indicado. Monitoramento fetal é essencial.', 'ginecologia_obstetricia', 'Infecções em GO', 'Transmissão vertical', ARRAY['transmissão vertical','infecção','feto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais infecções do grupo TORCH?', 'TORCH inclui Toxoplasmose, Outros (sífilis, varicela), Rubéola, CMV e Herpes. Causam malformações fetais como microcefalia e catarata. Diagnóstico sorológico e tratamento específico para reduzir riscos.', 'ginecologia_obstetricia', 'Infecções em GO', 'TORCH', ARRAY['TORCH','congênitas','infecções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diagnóstico de vulvovaginite candidiásica?', 'Diagnóstico por sintomas (prurido, corrimento coalhado) e exame microscópico mostrando pseudohifas de Candida. pH vaginal normal (<4,5). Tratamento com azóis tópicos ou orais.', 'ginecologia_obstetricia', 'Infecções em GO', 'Vulvovaginite', ARRAY['candidíase','diagnóstico','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Complicações da PID não tratada?', 'Complicações incluem infertilidade tubária, gravidez ectópica e dor pélvica crônica. Causada por ascensão de bactérias da vagina. Prevenção com tratamento precoce de ISTs.', 'ginecologia_obstetricia', 'Infecções em GO', 'PID', ARRAY['complicações','PID','infertilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Manejo de clamídia na gravidez?', 'Clamídia na gravidez trata-se com azitromicina 1g dose única, evitando tetraciclinas. Triagem no primeiro trimestre. Riscos incluem corioamnionite e baixo peso ao nascer.', 'ginecologia_obstetricia', 'Infecções em GO', 'IST na gravidez', ARRAY['clamídia','gravidez','azitromicina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Prevenção de transmissão vertical de HIV?', 'Prevenção com TARV materna, cesárea se carga viral alta e profilaxia neonatal com zidovudina. Evitar amamentação. Taxa de transmissão reduzida para <2%.', 'ginecologia_obstetricia', 'Infecções em GO', 'Transmissão vertical', ARRAY['HIV','prevenção','TARV']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Sintomas de infecção por CMV congênito?', 'CMV congênito causa microcefalia, surdez e hepatosplenomegalia. Diagnóstico por PCR em urina neonatal. Não há tratamento curativo, mas antivirais para casos graves.', 'ginecologia_obstetricia', 'Infecções em GO', 'TORCH', ARRAY['CMV','congênito','sintomas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Indicações para THS no climatério?', 'THS (Terapia Hormonal Substitutiva) é indicada para alívio de sintomas vasomotores graves e prevenção de osteoporose em mulheres sintomáticas abaixo de 60 anos. Avaliar riscos individualmente. Iniciar com estrogênio e progestágeno se útero intacto.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'THS', ARRAY['THS','climatério','indicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que são sintomas vasomotores?', 'Sintomas vasomotores incluem fogachos e sudorese noturna, causados por queda de estrogênio na menopausa. Afetam qualidade de vida. Tratamento com THS ou não hormonal como SSRI.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'Vasomotores', ARRAY['vasomotores','fogachos','menopausa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Manejo da osteoporose pós-menopausa?', 'Osteoporose trata-se com bifosfonatos, denosumabe ou teriparatida, além de cálcio e vitamina D. Avaliar por densitometria óssea. Exercícios e evitar tabagismo são preventivos.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'Osteoporose', ARRAY['osteoporose','tratamento','pós-menopausa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é síndrome geniturinária da menopausa?', 'Síndrome geniturinária da menopausa (SGM) envolve atrofia vaginal, secura e dispareunia devido a hipoestrogenismo. Tratamento com estrogênio tópico ou laser. Melhora sintomas e previne infecções.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'SGM', ARRAY['SGM','atrofia','vaginal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Contraindicações absolutas da THS?', 'Contraindicações incluem história de tromboembolismo, câncer de mama ou endométrio não tratado. Avaliar riscos cardiovasculares. Monitorar anualmente durante uso.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'THS', ARRAY['contraindicações','THS','riscos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Fatores de risco para sintomas vasomotores?', 'Fatores incluem tabagismo, baixo IMC e etnia caucasiana. Sintomas duram em média 7 anos. Avaliar impacto psicológico.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'Vasomotores', ARRAY['fatores risco','vasomotores','climatério']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Prevenção da osteoporose no climatério?', 'Prevenção com exercício de impacto, dieta rica em cálcio e screening por FRAX. THS pode ser opção em mulheres jovens. Evitar álcool excessivo.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'Osteoporose', ARRAY['prevenção','osteoporose','exercício']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diagnóstico da SGM?', 'Diagnóstico clínico por sintomas de secura vaginal e exame ginecológico mostrando palidez e friabilidade. pH vaginal >4,5. Biópsia se suspeita de lesão pré-maligna.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'SGM', ARRAY['diagnóstico','SGM','vaginal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Duração recomendada da THS?', 'Duração mínima para alívio sintomático, idealmente <5 anos. Reavaliar anualmente. Transição gradual ao descontinuar para minimizar rebote.', 'ginecologia_obstetricia', 'Climatério e menopausa', 'THS', ARRAY['duração','THS','reavaliação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual protocolo de atendimento à vítima de violência sexual?', 'Protocolo inclui acolhimento imediato, exame físico com consentimento, coleta de vestígios e profilaxia de IST/HIV. Notificar autoridades. Suporte multidisciplinar é essencial.', 'ginecologia_obstetricia', 'Violência sexual', 'Protocolo', ARRAY['protocolo','violência','atendimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é contracepção de emergência?', 'Contracepção de emergência usa levonorgestrel 1,5mg até 72h pós-coito ou ulipristal até 120h para prevenir gravidez indesejada. Eficácia de 85-95%. Não é abortiva.', 'ginecologia_obstetricia', 'Violência sexual', 'Contracepção emergência', ARRAY['emergência','levonorgestrel','pós-coito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Profilaxia de IST após violência sexual?', 'Profilaxia inclui ceftriaxona para gonorreia, azitromicina para clamídia e doxiciclina para sífilis. Vacina HPV e HAV se não imunizada. HIV com PEP se risco alto.', 'ginecologia_obstetricia', 'Violência sexual', 'Profilaxia IST', ARRAY['profilaxia','IST','violência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Aspectos legais na violência sexual no Brasil?', 'Lei 12.015/2009 tipifica estupro com pena de 6-10 anos. Notificação compulsória via SINAN. Preservar cadeia de custódia de evidências para processo judicial.', 'ginecologia_obstetricia', 'Violência sexual', 'Aspectos legais', ARRAY['legais','estupro','notificação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Suporte psicológico à vítima de violência sexual?', 'Suporte inclui escuta ativa, encaminhamento a psicoterapia e grupos de apoio. Avaliar risco de suicídio. Abordagem centrada na vítima para empoderamento.', 'ginecologia_obstetricia', 'Violência sexual', 'Suporte psicológico', ARRAY['psicológico','suporte','vítima']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Eficácia da contracepção de emergência com cobre DIU?', 'DIU de cobre como EC até 5 dias pós-coito tem eficácia >99%. Pode ser mantido como contraceptivo regular. Contraindicado em infecção ativa.', 'ginecologia_obstetricia', 'Violência sexual', 'Contracepção emergência', ARRAY['DIU','cobre','eficácia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando iniciar PEP para HIV?', 'PEP inicia-se em até 72h após exposição, com antirretrovirais por 28 dias. Avaliar risco e toxicidade. Acompanhamento sorológico em 6 semanas, 3 e 6 meses.', 'ginecologia_obstetricia', 'Violência sexual', 'Profilaxia IST', ARRAY['PEP','HIV','início']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Direitos da vítima de violência sexual?', 'Direitos incluem atendimento 24h em serviços de saúde, sigilo e assistência jurídica gratuita. Lei Maria da Penha aplica-se se conjugal. Encaminhamento a delegacia especializada.', 'ginecologia_obstetricia', 'Violência sexual', 'Aspectos legais', ARRAY['direitos','vítima','lei']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Transtornos pós-trauma em vítimas?', 'TEPT comum com flashbacks, evitação e hipervigilância. Depressão e ansiedade associadas. Terapia cognitivo-comportamental é eficaz.', 'ginecologia_obstetricia', 'Violência sexual', 'Suporte psicológico', ARRAY['TEPT','trauma','terapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é gravidez molar completa?', 'Gravidez molar completa é uma doença trofoblástica gestacional com ausência de feto, cistos grapelike e hCG elevado. Risco de coriocarcinoma. Tratamento é evacuação uterina e seguimento de hCG.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Mola completa', ARRAY['molar completa','trofoblástica','hCG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Seguimento de hCG em mola hidatiforme?', 'Seguimento semanal até hCG <5 mUI/mL, depois mensal por 6 meses. Contracepção obrigatória. Persistência indica doença trofoblástica gestacional persistente.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Seguimento hCG', ARRAY['hCG','seguimento','mola']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Indicações para quimioterapia em DTG?', 'Quimioterapia indicada em hCG persistente, metástases ou coriocarcinoma. Esquema com metotrexato para baixo risco ou EMA-CO para alto risco. Taxa de cura >90%.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Quimioterapia', ARRAY['quimioterapia','DTG','metotrexato']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é coriocarcinoma?', 'Coriocarcinoma é tumor maligno trofoblástico com metástases pulmonares comuns, originado de gestação molar ou aborto. hCG muito elevado. Tratamento quimioterápico agressivo com boa resposta.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Coriocarcinoma', ARRAY['coriocarcinoma','maligno','metástases']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diferença entre mola completa e parcial?', 'Mola completa: sem feto, diploide paterna; parcial: feto anormal, triploide. Ambas com risco de malignização. Diagnóstico por ultrassom e hCG.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Mola completa', ARRAY['mola parcial','completa','genética']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando suspeitar de recorrência de DTG?', 'Suspeita se hCG platô ou elevação após evacuação. Fatores de risco: mola prévia, idade >35 anos. Monitoramento vitalício em casos malignos.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Seguimento hCG', ARRAY['recorrência','DTG','hCG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Efeitos colaterais da quimioterapia em DTG?', 'Efeitos incluem mucosite, alopecia e mielossupressão. Monitorar função hepática e renal. Suporte com G-CSF se neutropenia.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Quimioterapia', ARRAY['efeitos','quimioterapia','suporte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Diagnóstico de coriocarcinoma?', 'Diagnóstico por biópsia mostrando citotrofoblasto e sinciciotrofoblasto, hCG sérica e imagem de metástases. História de gestação recente. Estadiamento FIGO para prognóstico.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Coriocarcinoma', ARRAY['diagnóstico','coriocarcinoma','biópsia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Riscos de mola hidatiforme?', 'Riscos incluem hipertiroidismo, pré-eclâmpsia precoce e malignização em 15-20% das completas. Evacuação imediata. Acompanhamento estrito.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Mola completa', ARRAY['riscos','mola','malignização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Gravidez após DTG?', 'Aguardar hCG normal por 6-12 meses antes de nova gestação. Contracepção essencial. Ultrassom precoce na próxima gestação para excluir recorrência.', 'ginecologia_obstetricia', 'Doenças trofoblásticas gestacionais', 'Seguimento hCG', ARRAY['gravidez','pós-DTG','contracepção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual é a definição de ovulação no contexto da reprodução humana?', 'A ovulação é o processo de liberação de um ovócito maduro do folículo ovariano dominante, ocorrendo geralmente no meio do ciclo menstrual sob influência do pico de LH. Esse evento é crucial para a fertilização, permitindo que o ovócito encontre espermatozoides nas trompas de Falópio. Alterações hormonais, como o aumento de estrogênio, desencadeiam esse mecanismo essencial para a concepção.', 'ginecologia_obstetricia', 'Reprodução humana', 'Ovulação', ARRAY['ovulacao','ciclo_menstrual','hormonios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais são as fases principais do ciclo menstrual?', 'O ciclo menstrual divide-se em fase folicular, ovulatória e lútea, reguladas por hormônios hipotalâmicos e gonadais. Na fase folicular, ocorre o desenvolvimento dos folículos ovarianos sob ação do FSH; a ovulatória marca a liberação do óvulo; e a lútea prepara o endométrio para implantação, com produção de progesterona pelo corpo lúteo. Desequilíbrios nessas fases podem levar a irregularidades menstruais ou infertilidade.', 'ginecologia_obstetricia', 'Reprodução humana', 'Ciclo menstrual', ARRAY['fases_ciclo','fase_folicular','fase_lutia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais hormônios estão envolvidos na regulação da reprodução feminina?', 'Os principais hormônios incluem GnRH do hipotálamo, FSH e LH da hipófise, estrogênio e progesterona dos ovários. O GnRH estimula a liberação de FSH e LH, que por sua vez promovem o crescimento folicular e a ovulação. Esses hormônios mantêm o equilíbrio do ciclo menstrual e preparam o útero para a gravidez, com desregulações podendo causar distúrbios como SOP.', 'ginecologia_obstetricia', 'Reprodução humana', 'Hormônios reprodutivos', ARRAY['gnrh','fsh','lh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva o processo de fertilização in vitro (FIV) para infertilidade?', 'A FIV envolve a estimulação ovariana com gonadotrofinas, coleta de óvulos via punção folicular e fertilização em laboratório com espermatozoides. Os embriões formados são cultivados por alguns dias e transferidos para o útero da paciente. Essa técnica é indicada para casais com fator tubário ou masculino, com taxas de sucesso variando de 30-50% por ciclo, dependendo da idade materna.', 'ginecologia_obstetricia', 'Reprodução humana', 'Fertilização in vitro', ARRAY['fiv','infertilidade','embrioes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é a implantação do embrião no útero?', 'A implantação ocorre cerca de 6-10 dias após a fertilização, quando o blastocisto adere ao endométrio uterino preparado pela progesterona. Esse processo envolve a invasão trófobástica e a formação da placenta inicial, essencial para o desenvolvimento gestacional. Falhas na implantação são uma causa comum de infertilidade recorrente, influenciadas por fatores imunológicos ou endometriais.', 'ginecologia_obstetricia', 'Reprodução humana', 'Implantação', ARRAY['blastocisto','endométrio','progesterona']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o papel da menstruação no ciclo reprodutivo?', 'A menstruação é a descamação do endométrio na ausência de gravidez, marcando o início de um novo ciclo sob queda de progesterona e estrogênio. Ela dura em média 3-7 dias e reflete o equilíbrio hormonal, com fluxos excessivos podendo indicar patologias como miomas. O monitoramento menstrual é fundamental para avaliar a fertilidade e saúde reprodutiva feminina.', 'ginecologia_obstetricia', 'Reprodução humana', 'Menstruação', ARRAY['ciclo','endométrio','hormonios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que caracteriza o climatério na mulher?', 'O climatério é a transição para a menopausa, com irregularidades menstruais devido à diminuição da reserva ovariana e níveis de estrogênio. Sintomas incluem ondas de calor, insônia e alterações de humor, durando em média 4-5 anos antes da cessação completa da menstruação. Avaliação hormonal e suporte sintomático são essenciais para gerenciar impactos na qualidade de vida.', 'ginecologia_obstetricia', 'Reprodução humana', 'Climatério', ARRAY['menopausa','estrogênio','sintomas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais são as causas principais de infertilidade feminina?', 'Causas incluem obstrução tubária, anovulação como na SOP e endometriose, afetando cerca de 10-15% dos casais. Fatores como idade avançada reduzem a reserva ovariana, enquanto hábitos como tabagismo agravam o quadro. Investigação inicial envolve histórico, exame físico e testes como dosagem de FSH e ultrassonografia.', 'ginecologia_obstetricia', 'Reprodução humana', 'Infertilidade', ARRAY['anovulacao','sop','tubas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva os métodos contraceptivos hormonais?', 'Métodos hormonais incluem pílulas combinadas de estrogênio e progesterona, DIU hormonal e implantes subcutâneos, inibindo a ovulação e alterando o muco cervical. Eles oferecem eficácia acima de 99% com uso correto, mas contraindicados em fumantes acima de 35 anos devido ao risco tromboembólico. A escolha depende do perfil da paciente, com monitoramento anual recomendado.', 'ginecologia_obstetricia', 'Reprodução humana', 'Contracepção', ARRAY['pilula','diu','hormonal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a importância do espermograma na reprodução?', 'O espermograma avalia contagem, motilidade e morfologia espermática, essencial para diagnosticar infertilidade masculina em 40% dos casos. Valores normais incluem mais de 15 milhões/mL e motilidade progressiva acima de 32%, conforme OMS. Alterações podem ser tratadas com antioxidantes ou assistência reprodutiva como ICSI.', 'ginecologia_obstetricia', 'Reprodução humana', 'Espermograma', ARRAY['infertilidade_masculina','motilidade','oms']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sinais clássicos de pré-eclâmpsia em emergências obstétricas?', 'A pré-eclâmpsia caracteriza-se por hipertensão após 20 semanas, proteinúria e edema, podendo evoluir para eclâmpsia com convulsões. Monitoramento de PA e proteinúria é crucial, com parto como tratamento definitivo após 37 semanas. Fatores de risco incluem nuliparidade e obesidade, exigindo vigilância em pré-natal.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Pré-eclâmpsia', ARRAY['hipertensao','proteinuria','convulsao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como manejar o descolamento prematuro de placenta?', 'O descolamento prematuro é uma hemorragia retroplacental antes do parto, causando dor abdominal e contrações, diagnosticado por USG. Manejo inclui estabilização materna com fluidos e transfusão se necessário, seguido de cesárea em casos graves. Complicações como coagulação intravascular disseminada demandam avaliação imediata em UTI obstétrica.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Descolamento placenta', ARRAY['hemorragia','usg','cesarea']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é rotura uterina e seus riscos?', 'A rotura uterina é a ruptura da parede uterina durante o trabalho de parto, mais comum em cicatrizes cesarianas prévias, levando a hemorragia e sofrimento fetal. Sinais incluem dor intensa, cessação de contrações e taquicardia fetal, exigindo laparotomia de emergência. Prevenção envolve monitoramento em gestantes de alto risco e evitar indução com ocitocina excessiva.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Rotura uterina', ARRAY['trabalho_parto','hemorragia','cesarea']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais as causas de hemorragia pós-parto?', 'A hemorragia pós-parto ocorre por atonia uterina (70%), lacerações, retenção placentária ou coagulopatias, definida como perda >500mL. Manejo inicial inclui massagem uterina, ocitocina e revisão cavitária, com histerectomia em casos refratários. Fatores de risco como multiparidade requerem profilaxia com uterotônicos no parto.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Hemorragia pós-parto', ARRAY['atonia','ocitocina','histerectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva o manejo da eclâmpsia?', 'A eclâmpsia é convulsão em paciente com pré-eclâmpsia, tratada com sulfato de magnésio para prevenção de recorrência e parto imediato. Monitoramento neurológico e pressão arterial com hidralazina ou labetalol é essencial, com suporte em UTI. Prognóstico melhora com intervenção rápida, reduzindo mortalidade materna para <1%.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Eclâmpsia', ARRAY['convulsao','sulfato_magnesio','uti']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é prolapso de cordão umbilical?', 'O prolapso de cordão ocorre quando o cordão sai antes do feto, comprimindo-se e causando bradicardia fetal, uma emergência obstétrica. Diagnóstico por exame vaginal ou USG, com cesárea imediata como tratamento. Prevenção inclui evitar amniotomia precoce em apresentações anômalas.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Prolapso cordão', ARRAY['bradicardia_fetal','cesarea','usg']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como tratar distócia de ombro no parto?', 'A distócia de ombro é impactação do ombro anterior após expulsão da cabeça, tratada com manobras como McRoberts e pressão suprapúbica. Se falhar, Woods ou Rubin, evitando tração excessiva para prevenir lesão braquial. Equipe multidisciplinar e episiotomia são auxiliares, com cesárea profilática em gestações diabéticas.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Distócia ombro', ARRAY['manobras','lesao_nervo','diabetes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sinais de choque hemorrágico em obstetrícia?', 'O choque hemorrágico apresenta taquicardia, hipotensão e oligúria devido a perda sanguínea aguda, como em aborto ou placenta prévia. Resposta inicial inclui reposição volêmica com cristaloides e transfusão, estabilizando antes de intervenção cirúrgica. Monitoramento de lactato e coagulograma guiam a terapia intensiva.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Choque hemorrágico', ARRAY['hipotensao','transfusao','coagulacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é embolia amniótica e seu manejo?', 'A embolia amniótica é entrada de líquido amniótico na circulação materna, causando colapso respiratório e coagulopatia, rara mas fatal. Sinais incluem dispneia súbita e hemorragia, tratadas com suporte ventilatório, inotrópicos e transfusão. Diagnóstico é clínico, com alta mortalidade apesar de reanimação agressiva.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Embolia amniótica', ARRAY['dispneia','coagulopatia','suporte_vital']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como prevenir trabalho de parto prematuro em emergências?', 'O trabalho de parto prematuro é contraído antes de 37 semanas, prevenido com progesterona em risco ou cerclagem cervical. Tocolíticos como nifedipino inibem contrações, enquanto corticoides aceleram maturação pulmonar fetal. Fatores como infecção ou tabagismo demandam aconselhamento pré-natal rigoroso.', 'ginecologia_obstetricia', 'Emergências obstétricas', 'Trabalho prematuro', ARRAY['tocoliticos','progesterona','corticoides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a definição de endometriose para o ENAMED?', 'A endometriose é a presença de tecido endometrial ectópico, geralmente na pelve, causando inflamação crônica e aderências. Afeta 10% das mulheres em idade reprodutiva, associada a infertilidade em 30-50% dos casos. Diagnóstico definitivo por laparoscopia, com sintomas como dismenorreia e dispareunia.', 'ginecologia_obstetricia', 'Endometriose', 'Definição', ARRAY['tecido_ectopico','infertilidade','laparoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais os sintomas principais da endometriose?', 'Sintomas incluem dor pélvica crônica, dismenorreia intensa e infertilidade, variando com localização das lesões. Dispareunia profunda e dor ao defecar durante menstruação são comuns em acometimento retovaginal. Avaliação inicial usa escore de dor e exame ginecológico, com impacto significativo na qualidade de vida.', 'ginecologia_obstetricia', 'Endometriose', 'Sintomas', ARRAY['dor_pelvica','dismenorreia','dispareunia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como é feito o diagnóstico de endometriose?', 'O diagnóstico inicia com anamnese e exame físico, seguido de USG transvaginal para lesões ovarianas como endometriomas. Ressonância magnética avalia extensões profundas, mas laparoscopia com biópsia confirma histologicamente. Marcadores como CA-125 podem auxiliar, embora não específicos.', 'ginecologia_obstetricia', 'Endometriose', 'Diagnóstico', ARRAY['usg','rm','laparoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais opções de tratamento para endometriose?', 'Tratamento médico inclui AINEs para dor e hormonais como progestágenos ou agonistas de GnRH para suprimir ovulação. Cirurgia laparoscópica remove implantes, preservando fertilidade em casos de infertilidade. Em dor refratária, histerectomia com ooforectomia é considerada, com recidiva em até 50%.', 'ginecologia_obstetricia', 'Endometriose', 'Tratamento', ARRAY['hormonal','cirurgia','gnrh']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como a endometriose afeta a fertilidade?', 'A endometriose causa infertilidade por distorção anatômica, inflamação peritoneal e toxicidade ovocitária, reduzindo taxas de concepção espontânea. Mecanismos incluem adesões tubárias e comprometimento ovariano, com FIV oferecendo melhores resultados. Tratamento cirúrgico moderado melhora chances, mas excisão excessiva pode danificar reserva ovariana.', 'ginecologia_obstetricia', 'Endometriose', 'Fertilidade', ARRAY['infertilidade','adesoes','fiv']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais as localizações comuns de endometriose?', 'Localizações incluem ovários (endometriomas), peritônio pélvico, ligamentos uterossacros e reto, formando nódulos profundos. Aderências podem levar a imunodeficiência local e cicatrizes. Avaliação por imagem identifica massas, guiando planejamento cirúrgico.', 'ginecologia_obstetricia', 'Endometriose', 'Localizações', ARRAY['ovarios','peritonio','aderencias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva a dor pélvica associada à endometriose?', 'A dor pélvica é cíclica, agravando-se na menstruação por prostaglandinas e inflamação ectópica. Pode irradiar para região lombar, com intensidade variando de leve a incapacitante. Terapias como acupuntura ou psicoterapia complementam o manejo farmacológico.', 'ginecologia_obstetricia', 'Endometriose', 'Dor pélvica', ARRAY['ciclica','prostaglandinas','inflamacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual o papel da laparoscopia no diagnóstico e tratamento?', 'A laparoscopia permite visualização direta de lesões e biópsia, classificando estágios por ASRM. Excisão ou ablação durante o procedimento alivia sintomas em 70% dos casos. É gold standard, mas requer centro especializado para minimizar riscos.', 'ginecologia_obstetricia', 'Endometriose', 'Laparoscopia', ARRAY['classificacao','excisao','asrm']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais terapias hormonais para endometriose?', 'Terapias incluem DIU com levonorgestrel, pílulas contínuas e inibidores de aromatase para reduzir estrogênio. Agonistas de GnRH induzem pseudo-menopausa, aliviando dor mas com efeitos colaterais como osteoporose. Escolha baseia-se em desejo gestacional e tolerância.', 'ginecologia_obstetricia', 'Endometriose', 'Terapia hormonal', ARRAY['diu','gnrh','estrogênio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que são miomas uterinos e sua prevalência?', 'Miomas são tumores benignos de músculo liso uterino, afetando 20-40% das mulheres em idade reprodutiva, mais comuns em negras. Crescem sob influência estrogênica, regressando pós-menopausa. Assintomáticos em 50%, mas podem causar sangramento e dor.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Definição', ARRAY['tumores_benignos','estrogênio','prevalencia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais sintomas dos miomas uterinos?', 'Sintomas incluem menorragia levando a anemia, dor pélvica e sensação de peso, dependendo de tamanho e localização. Miomas submucosos causam infertilidade por distorção cavitária, enquanto pediculados podem torcer. Avaliação sintomática guia indicação terapêutica.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Sintomas', ARRAY['menorragia','dor','anemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como diagnosticar miomas por ultrassonografia?', 'A USG transvaginal identifica miomas como massas hipoecogênicas bem delimitadas, avaliando número, tamanho e localização. Doppler avalia vascularização, diferenciando de ovários. É método inicial acessível, com histeroscopia para submucosos.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Diagnóstico USG', ARRAY['usg_transvaginal','hipoecogeno','doppler']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais as classificações dos miomas uterinos?', 'Classificação por FIGO inclui submucoso (0-2), intramural (3-5), subseroso (6-8) e outros como cervical. Localização influencia sintomas e tratamento, com intramurais mais comuns. RMN detalha em casos complexos.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Classificação', ARRAY['figo','submucoso','intramural']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva tratamento conservador para miomas?', 'Tratamento conservador usa AINEs para dor, antifibrinolíticos como ácido tranexâmico para sangramento e ulipristal para redução de tamanho. Em mulheres férteis, preserva útero, monitorando com USG seriado. Eficaz em sintomas leves, evitando cirurgia.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Tratamento conservador', ARRAY['ulipristal','aie','sangramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quando indicar histerectomia para miomas?', 'Histerectomia é indicada em sangramento refratário, dor intensa ou miomas >10cm, especialmente pós-filhos. Pode ser abdominal, vaginal ou laparoscópica, removendo útero com ou sem ovários. Riscos incluem infecção e menopausa cirúrgica se ooforectomia.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Histerectomia', ARRAY['cirurgia','sangramento','menopausa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais complicações de miomas na gravidez?', 'Miomas aumentam risco de aborto, parto prematuro e distócia por obstrução, com degeneração vermelha causando dor aguda. Monitoramento USG seriado é essencial, com miomectomia pré-concepção em sintomáticos. Maioria permanece assintomática na gestação.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Gravidez', ARRAY['degeneracao','aborto','miomectomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que é miomectomia e indicações?', 'Miomectomia remove miomas preservando útero, indicada em mulheres desejando fertilidade com sintomas moderados. Pode ser histeroscópica para submucosos ou laparoscópica para intramurais. Riscos incluem adesões e recorrência em 20-30%.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Miomectomia', ARRAY['preservacao_uterina','laparoscopica','recorrencia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como miomas causam anemia?', 'Miomas submucosos provocam menorragia crônica, levando a deficiência de ferro por perda sanguínea excessiva. Sintomas incluem fadiga e palidez, tratados com suplementação e controle hemorrágico. Avaliação hematimétrica é rotineira em sintomáticas.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Anemia', ARRAY['menorragia','ferro','fadiga']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais tipos de degeneração ocorrem em miomas?', 'Degeneração inclui hialina (comum, assintomática), vermelha (na gravidez, dolorosa) e sarcomatosa (rara, maligna). USG diferencia tipos, com biópsia em suspeita de sarcoma. Fatores como crescimento rápido alertam para malignidade.', 'ginecologia_obstetricia', 'Miomas uterinos', 'Degeneração', ARRAY['hialina','vermelha','sarcoma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Qual a definição de incontinência urinária?', 'Incontinência urinária é perda involuntária de urina, afetando qualidade de vida em 25% das mulheres adultas. Tipos incluem esforço, urgência e mista, com causas multifatoriais como parto e menopausa. Avaliação inicial usa diário miccional e exame físico.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Definição', ARRAY['perda_urina','tipos','qualidade_vida']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva incontinência de esforço?', 'Incontinência de esforço ocorre por aumento de pressão intra-abdominal, como tosse, devido a fraqueza do assoalho pélvico. Comum pós-parto vaginal, diagnosticada por teste de esforço. Tratamento inicial com exercícios de Kegel fortalece músculos.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Esforço', ARRAY['tosse','assoalho_pelvico','kegel']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que caracteriza incontinência de urgência?', 'Incontinência de urgência envolve desejo súbito e irresistível de urinar, associado a bexiga hiperativa por detrusor instável. Causas incluem infecções ou neurológicas, avaliadas por urodinâmica. Antimuscarínicos como oxibutinina controlam sintomas em 60%.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Urgência', ARRAY['bexiga_hiperativa','urodinamica','antimuscarinicos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais causas principais de incontinência urinária?', 'Causas incluem parto vaginal lesando esfíncter, prolapso genital e hipoestrogenismo menopausal. Obesidade e tabagismo agravam, com iatrogênicas como pós-histerectomia. História clínica identifica fatores modificáveis para terapia.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Causas', ARRAY['parto','menopausa','obesidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como avaliar paciente com incontinência?', 'Avaliação inicia com anamnese, exame ginecológico e teste de Marshall para esforço. Urodinâmica diferencia tipos, enquanto cistoscopia exclui lesões vesicais. Escala ICIQ quantifica gravidade para seguimento.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Avaliação', ARRAY['urodinamica','marshall','iciq']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais tratamentos conservadores para incontinência?', 'Tratamentos incluem fisioterapia pélvica com biofeedback, perda de peso e treinamento vesical. Pessários apoiam em prolapso associado, eficazes em 50-70% dos casos leves. Evidências suportam adesão para evitar progressão.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Conservador', ARRAY['fisioterapia','pessario','treinamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Descreva opções cirúrgicas para incontinência?', 'Cirurgias incluem colossuspensão de Burch para esforço ou sling mid-uretral, com sucesso >85%. Injeções de bulking para esfíncter intrínseco em idosos. Riscos como retenção pós-operatória demandam seleção cuidadosa.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Cirurgia', ARRAY['burch','sling','bulking']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Quais fatores de risco para incontinência urinária?', 'Fatores incluem multiparidade, obesidade, histerectomia e tabagismo, aumentando pressão intra-abdominal. Menopausa reduz colágeno pélvico, enquanto diabetes afeta inervação. Prevenção com exercícios pré-parto é recomendada.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Riscos', ARRAY['paridade','obesidade','menopausa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'Como a incontinência impacta a qualidade de vida?', 'Incontinência causa isolamento social, depressão e baixa autoestima, limitando atividades diárias. Mulheres evitam relações sexuais por medo de episódios, com custo emocional alto. Suporte psicológico integra o manejo multidisciplinar.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Qualidade vida', ARRAY['depressao','social','psicologico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000003', 'O que são exercícios de Kegel na incontinência?', 'Exercícios de Kegel contraem músculos pubococcígeos por 10 segundos, repetidos 3x/dia, fortalecendo suporte uretral. Eficazes em incontinência leve, com adesão melhorada por apps. Avaliação prévia exclui contra-indicações como prolapso avançado.', 'ginecologia_obstetricia', 'Incontinência urinária', 'Kegel', ARRAY['musculos_pelvicos','fortalecimento','adesao']);


-- ============================================================
-- FLASHCARDS: Pediatria — 200 cards
-- Deck: a0000001-0000-0000-0000-000000000004
-- ============================================================

INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Neonatologia, Crescimento e desenvolvimento, Infectologia pediátrica, Emergências pediátricas, Imunização
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual é o peso médio ao nascer de um recém-nascido a termo em g?', 'O peso médio ao nascer de um recém-nascido a termo é de aproximadamente 3.000 a 3.500 gramas. Valores abaixo de 2.500 g indicam baixo peso ao nascer, associado a maior risco de morbimortalidade neonatal. Fatores como tabagismo materno e prematuridade influenciam.', 'pediatria', 'Neonatologia', 'Peso ao nascer', ARRAY['recem_nascido','peso','neonatologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a síndrome de aspiração meconial no recém-nascido?', 'A síndrome de aspiração meconial ocorre quando o feto elimina mecônio no líquido amniótico e aspira durante o parto, causando obstrução das vias aéreas e pneumonia. Tratamento inclui suporte ventilatório e antibióticos se infecção.', 'pediatria', 'Neonatologia', 'Aspiração meconial', ARRAY['meconio','aspiracao','sindrome']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como avaliar a vitalidade do recém-nascido na sala de parto?', 'A avaliação usa a escala de Apgar aos 1 e 5 minutos de vida, pontuando cor, frequência cardíaca, reflexos, tônus muscular e respiração. Pontuação <7 indica necessidade de reanimação.', 'pediatria', 'Neonatologia', 'Escala de Apgar', ARRAY['Apgar','vitalidade','reanimacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais são os sinais de hipoglicemia no neonato?', 'Sinais incluem tremores, irritabilidade, sudorese, taquipneia e convulsões. Causas comuns: prematuridade, PIG ou diabetes materno. Monitorar glicemia capilar nas primeiras horas.', 'pediatria', 'Neonatologia', 'Hipoglicemia neonatal', ARRAY['hipoglicemia','neonato','sinais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é icterícia fisiológica no recém-nascido?', 'Icterícia fisiológica surge no 2º-3º dia de vida devido à imaturidade hepática, com bilirrubina <12 mg/dL. Resolve em 7-10 dias. Monitorar para diferenciar de patológica.', 'pediatria', 'Neonatologia', 'Icterícia', ARRAY['ictericia','fisiologica','bilirrubina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o manejo inicial de um prematuro de 32 semanas?', 'Manter em incubadora para termorregulação, suporte nutricional parenteral, profilaxia de infecção e monitorização cardiorrespiratória. Evitar hipotermia.', 'pediatria', 'Neonatologia', 'Prematuridade', ARRAY['prematuro','manejo','incubadora']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é persistência do canal arterial em neonatos?', 'Persistência do canal arterial (PCA) é falha no fechamento pós-natal, causando shunt esquerdo-direito e insuficiência cardíaca. Diagnóstico por ecocardiograma; tratamento com indometacina.', 'pediatria', 'Neonatologia', 'Canal arterial', ARRAY['PCA','ducto','cardiaco']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como prevenir infecções nosocomiais em UTI neonatal?', 'Medidas incluem higiene das mãos, isolamento de pacientes, uso de EPIs e protocolos de esterilização. Vigilância epidemiológica é essencial para controle.', 'pediatria', 'Neonatologia', 'Infecções nosocomiais', ARRAY['infeccao','UTI','prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais vacinas são indicadas no primeiro mês de vida?', 'No Brasil, BCG ao nascer, hepatite B na maternidade e primeira dose de hexavalente aos 2 meses. Rotina conforme calendário nacional do PNI.', 'pediatria', 'Neonatologia', 'Vacinação neonatal', ARRAY['vacinas','neonato','PNI']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é retinopatia da prematuridade?', 'Retinopatia da prematuridade (ROP) é proliferação vascular retinal em prematuros <32 semanas ou <1.500g, devido a oxigênio. Exame oftalmológico obrigatório aos 4-6 semanas.', 'pediatria', 'Neonatologia', 'Retinopatia', ARRAY['ROP','prematuro','retina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o marco motor grosso aos 3 meses de idade?', 'Aos 3 meses, o lactente sustenta a cabeça em posição prona, rola de decúbito dorsal para ventral e começa a alcançar objetos. Avaliar desenvolvimento neuromotor.', 'pediatria', 'Crescimento e desenvolvimento', 'Marcos motores', ARRAY['motor','3_meses','desenvolvimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como calcular o percentil de peso para idade em crianças?', 'Usar curvas de crescimento da OMS: plotar peso atual vs idade cronológica no gráfico. Percentil 50 é mediana; <5 indica desnutrição.', 'pediatria', 'Crescimento e desenvolvimento', 'Curvas de crescimento', ARRAY['percentil','peso','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é atraso no desenvolvimento cognitivo?', 'Atraso cognitivo é falha em atingir marcos como reconhecimento de objetos ou linguagem esperada para idade. Causas: genéticas, ambientais ou infecciosas. Intervenção precoce é crucial.', 'pediatria', 'Crescimento e desenvolvimento', 'Desenvolvimento cognitivo', ARRAY['cognitivo','atraso','marcos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual a altura média aos 12 meses em meninos?', 'Aos 12 meses, meninos medem em média 74-76 cm (percentil 50). Monitorar ganho linear para detectar falha de crescimento.', 'pediatria', 'Crescimento e desenvolvimento', 'Altura infantil', ARRAY['altura','12_meses','crescimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de puberdade precoce em meninas?', 'Puberdade precoce: desenvolvimento mamário antes dos 8 anos, telarca. Investigar com USG pélvica e dosagem hormonal. Tratamento com análogos de GnRH se idiopática.', 'pediatria', 'Crescimento e desenvolvimento', 'Puberdade', ARRAY['puberdade','precoce','meninas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é o teste de Denver para desenvolvimento?', 'O Denver II avalia marcos motores, linguagem, finos e pessoais-sociais até 6 anos. Identifica atrasos para intervenção. Aplicar em consultas de rotina.', 'pediatria', 'Crescimento e desenvolvimento', 'Avaliação desenvolvimento', ARRAY['Denver','teste','marcos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como manejar obesidade infantil inicial?', 'Abordagem: orientação nutricional, atividade física diária (60 min) e screening de comorbidades como dislipidemia. Envolver família no plano.', 'pediatria', 'Crescimento e desenvolvimento', 'Obesidade', ARRAY['obesidade','infantil','manejo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual marco de linguagem aos 18 meses?', 'Aos 18 meses, criança diz 10-20 palavras e aponta para partes do corpo. Atraso pode indicar transtorno do espectro autista ou surdez.', 'pediatria', 'Crescimento e desenvolvimento', 'Desenvolvimento linguagem', ARRAY['linguagem','18_meses','marcos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Fatores que influenciam o crescimento estatural?', 'Genética (80%), nutrição, hormônios (GH, tiroxina) e sono. Doenças crônicas como celíaca inibem. Monitorar velocidade de crescimento anual.', 'pediatria', 'Crescimento e desenvolvimento', 'Fatores crescimento', ARRAY['estatura','fatores','hormonios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é nanismo psicossocial?', 'Nanismo psicossocial resulta de privação emocional grave, com estatura <3 percentil. Melhora com remoção do estressor e suporte nutricional/emocional.', 'pediatria', 'Crescimento e desenvolvimento', 'Nanismo', ARRAY['nanismo','psicossocial','privacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o agente mais comum de meningite bacteriana em <3 meses?', 'Em lactentes <3 meses, Escherichia coli e Streptococcus grupo B são comuns. Vacinação meningocócica e pneumocócica previne em maiores. Cefalosporina 3ª gen + ampicilina.', 'pediatria', 'Infectologia pediátrica', 'Meningite', ARRAY['meningite','bacteriana','lactente']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de infecção por varicela em crianças?', 'Varicela: febre baixa, rash pruriginoso vesicular em estágios (mácula, pápula, vesícula, crosta). Contagiosa 1-2 dias antes até crostas secas. Aciclovir em imunossuprimidos.', 'pediatria', 'Infectologia pediátrica', 'Varicela', ARRAY['varicela','rash','contagiosa']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Tratamento de otite média aguda em <2 anos?', 'Amoxicilina 80-90 mg/kg/dia por 10 dias. Se alergia, azitromicina. Analgesia com ibuprofeno. Acompanhar perfuração timpânica.', 'pediatria', 'Infectologia pediátrica', 'Otite média', ARRAY['otite','tratamento','amoxicilina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é síndrome mão-pé-boca?', 'Causada por Coxsackie A16, rash vesicular em mãos, pés e boca, febre. Autolimitada em 7-10 dias. Hidratação e higiene para prevenir disseminação.', 'pediatria', 'Infectologia pediátrica', 'Síndromes virais', ARRAY['mao_pe_boca','Coxsackie','rash']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Profilaxia para tuberculose em contato domiciliar?', 'Isoniazida (INH) 10 mg/kg/dia por 6 meses em crianças <5 anos expostas. Teste tuberculínico e radiografia para diagnóstico ativo.', 'pediatria', 'Infectologia pediátrica', 'Tuberculose', ARRAY['TB','profilaxia','INH']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de dengue em crianças?', 'Dengue: febre alta, cefaleia, mialgia, rash. Formas graves: hemorragia, choque. Hidratação oral/IV essencial; evitar AINEs para prevenir sangramento.', 'pediatria', 'Infectologia pediátrica', 'Dengue', ARRAY['dengue','febre','hidratacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual vacina previne hepatite A?', 'Vacina contra hepatite A, dose única após 12 meses. Transmissão fecal-oral; higiene e saneamento reduzem risco em crianças.', 'pediatria', 'Infectologia pediátrica', 'Hepatite A', ARRAY['hepatite','vacina','transmissao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo de gastroenterite viral aguda?', 'Reidratação oral com SRO (solução de reidratação oral). Antieméticos se vômitos persistentes. Não rotineiro antibióticos; rotavírus vacinado previne.', 'pediatria', 'Infectologia pediátrica', 'Gastroenterite', ARRAY['gastroenterite','SRO','rotavirus']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é infecção por Streptococcus pneumoniae?', 'Pneumococo causa pneumonia, meningite, otite. Vacina conjugada 10-valente no calendário. Penicilina G para sensíveis; ceftriaxona para resistentes.', 'pediatria', 'Infectologia pediátrica', 'Pneumococo', ARRAY['pneumococo','infeccao','vacina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o sinal vital de alerta em criança com febre?', 'Taquicardia >160 bpm em lactentes ou letargia indica sepse. Avaliar perfusão capilar (normal <2s; prolongado >3s indica choque), FC, FR. Oxigênio se hipoxemia; internação se instável.', 'pediatria', 'Emergências pediátricas', 'Febre e sepse', ARRAY['sepse','febre','sinais_vitais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo inicial de convulsão febril simples?', 'Posicionar lateral, oxigênio se cianose, diazepam retal 0,5 mg/kg se >5 min (máx 10 mg). Antitérmicos após. EEG se atípica. Antipiréticos profiláticos (paracetamol ou ibuprofeno) NÃO previnem recorrência de convulsão febril (evidência robusta — AAP 2011, reafirmado).', 'pediatria', 'Emergências pediátricas', 'Convulsão febril', ARRAY['convulsao','febril','diazepam']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como tratar choque hipovolêmico em pediatria?', 'Bolus IV cristaloides 20 mL/kg em 20 min, repetir se necessário. Monitorar diurese >1 mL/kg/h. Identificar causa (desidratação, hemorragia).', 'pediatria', 'Emergências pediátricas', 'Choque', ARRAY['choque','hipovolemico','fluidos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de asma aguda grave em criança?', 'Asma grave: taquipneia >50 irpm, uso de musculatura acessória, sat O2 <92%. Oxigênio, salbutamol neb + ipratrópio, corticoides sistêmicos.', 'pediatria', 'Emergências pediátricas', 'Crise asma', ARRAY['asma','grave','salbutamol']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que fazer em trauma craniano leve?', 'Observar por 24h: vômitos, sonolência, pupilas. TC crânio se GCS <15 ou fatores de risco. Analgesia; repouso. Não usar profilático anticonvulsivante.', 'pediatria', 'Emergências pediátricas', 'Trauma craniano', ARRAY['trauma','cranio','TC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo de corpo estranho em vias aéreas?', 'Heimlich se consciente; RCP se inconsciente. Broncoscopia se retido. Prevenir com supervisão em brincadeiras.', 'pediatria', 'Emergências pediátricas', 'Corpo estranho', ARRAY['aspiracao','Heimlich','broncoscopia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual escala para dor em neonatos?', 'Escala NIPS (Neonatal Infant Pain Scale): choro, expressão facial, tônus, respiração, braços/pernas. Analgesia com sacarose + sucção não-nutritiva.', 'pediatria', 'Emergências pediátricas', 'Avaliação dor', ARRAY['dor','neonato','NIPS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Tratamento de queimadura superficial em criança?', 'Resfriar com água fria 20 min, curativo com sulfadiazina prata. Analgesia IV se >10% SC. Vacina tétano; profilaxia antibiótica se infectada.', 'pediatria', 'Emergências pediátricas', 'Queimaduras', ARRAY['queimadura','curativo','analgesia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de intoxicação por paracetamol?', 'Intoxicação paracetamol: náusea inicial, depois hepatotoxicidade >24h. N-acetilcisteína antídoto se <8h. Medir níveis séricos 4h pós-dose.', 'pediatria', 'Emergências pediátricas', 'Intoxicação', ARRAY['paracetamol','NAC','hepatotoxicidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de desidratação grave em lactente?', 'Desidratação grave: letargia, olhos fundos, fontanela deprimida, turgor cutâneo ruim. Bolus 20 mL/kg RL, depois manutenção SRO.', 'pediatria', 'Emergências pediátricas', 'Desidratação', ARRAY['desidratacao','grave','bolus']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o calendário de vacinação para hepatite B?', 'Hepatite B: dose 0 ao nascer (monovalente), reforço aos 2, 4 e 6 meses (pentavalente — DTP+HB+Hib no PNI brasileiro). Sorologia se exposição; Ig + vacina em pós-exposição.', 'pediatria', 'Imunização', 'Hepatite B', ARRAY['hepatite_B','vacina','calendario']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Contraindicações para vacina tríplice viral?', 'Tríplice viral (sarampo, caxumba, rubéola): contraindicada em imunodeficiência grave, gravidez, alergia a gelatina/neomicina. Febre leve não impede.', 'pediatria', 'Imunização', 'Tríplice viral', ARRAY['MMR','contraindicacoes','alergia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é esquema de reforço para DTP?', 'No PNI brasileiro utiliza-se DTPw (célula inteira) na pentavalente aos 2, 4 e 6 meses, com reforços DTP aos 15 meses e 4 anos. DTPa (acelular) é reservada para situações especiais (CRIE). A acelular tem menor reatogenicidade mas menor imunogenicidade.', 'pediatria', 'Imunização', 'DTP', ARRAY['DTPa','reforco','pertussis']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Vacina contra rotavírus: quando administrar?', 'Rotavírus (VORH/Rotarix no PNI): 2 doses orais aos 2 e 4 meses. Primeira dose: máximo 3 meses e 15 dias (14 semanas e 6 dias). Segunda dose: máximo 7 meses e 29 dias. Não administrar fora dessas janelas — risco de intussuscepção.', 'pediatria', 'Imunização', 'Rotavírus', ARRAY['rotavirus','oral','diarreia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Indicações para vacina meningocócica C?', 'Meningocócica C conjugada: aos 3 e 5 meses, reforço 12 meses. Risco maior em <2 anos; sorogrupo C prevalente no Brasil.', 'pediatria', 'Imunização', 'Meningococo', ARRAY['meningococo','C','reforco']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é HPV vacinal em adolescentes?', 'Vacina HPV quadrivalente: meninas 9-14 anos (2 doses), meninos 11-14 (2 doses). Previne verrugas e câncer cervical/anais. Esquema 0-6 meses.', 'pediatria', 'Imunização', 'HPV', ARRAY['HPV','adolescente','quadrivalente']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Calendário para pneumocócica 10-valente?', 'Pneumocócica 10V: 2,4,6 meses; reforço 12 meses. Reduz pneumonia e meningite invasiva em <5 anos.', 'pediatria', 'Imunização', 'Pneumocócica', ARRAY['pneumococica','10V','invasiva']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Vacina BCG: dose e indicações?', 'BCG: dose única intradérmica ao nascer, obrigatória no Brasil. Protege formas graves de TB em crianças; revacinação não rotineira.', 'pediatria', 'Imunização', 'BCG', ARRAY['BCG','tuberculose','intradermica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo de reações pós-vacinação comuns?', 'Reações locais (dor, eritema): compressa fria, paracetamol. Febre: hidratação, antitérmico. Anafilaxia rara: epinefrina IM imediata.', 'pediatria', 'Imunização', 'Reações vacinais', ARRAY['reacoes','vacina','anafilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual vacina para influenza em crianças?', 'Influenza: anual a partir de 6 meses. 2 doses no primeiro ano, 1 anual após. Inativada preferida; evita hospitalizações por complicações.', 'pediatria', 'Imunização', 'Influenza', ARRAY['gripe','vacina','anual']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Esquema para hepatite A vacinal?', 'Hepatite A: dose única aos 15 meses. Imunidade vitalícia; indicada para viajantes ou surtos. Não em <12 meses rotineiramente.', 'pediatria', 'Imunização', 'Hepatite A', ARRAY['hepatite_A','dose_unica','15_meses']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual é a técnica correta de amamentação?', 'A técnica correta de amamentação envolve posicionar o bebê de forma que sua boca cubra grande parte da aréola mamária, com o queixo encostado no seio e o nariz livre para respirar. Isso garante uma pega adequada, prevenindo lesões nos mamilos e promovendo uma sucção eficiente. A mãe deve estar confortável, com apoio lombar e o bebê em posição semi-sentada.', 'pediatria', 'Aleitamento materno', 'técnica de amamentação', ARRAY['amamentação', 'técnica', 'pega']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a pega correta no aleitamento materno?', 'A pega correta ocorre quando o bebê abocanha a aréola e não apenas o mamilo, com a boca bem aberta em ângulo de 140 graus e lábios evertidos. Isso estimula a ejeção do leite e evita fissuras mamilares. Sinais de pega incorreta incluem dor intensa e ruídos de sucção.', 'pediatria', 'Aleitamento materno', 'pega correta', ARRAY['pega', 'aleitamento', 'sucção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que significa amamentar em livre demanda?', 'Amamentar em livre demanda significa oferecer o seio ao bebê sempre que ele demonstrar sinais de fome, sem horários fixos, dia e noite. Isso favorece a produção de leite pela demanda-oferta e promove o vínculo mãe-bebê. Geralmente, resulta em 8 a 12 mamadas por dia nos primeiros meses.', 'pediatria', 'Aleitamento materno', 'livre demanda', ARRAY['demanda', 'fome', 'vínculo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais são as contraindicações ao aleitamento materno?', 'Contraindicações incluem HIV materno (contraindicação absoluta no Brasil, independente de carga viral — protocolo MS), uso de quimioterápicos ou radioativos, e galactemia no bebê. Infecções ativas como herpes mamário também contraindicam temporariamente. Nesses casos, deve-se optar por leite pasteurizado de banco ou fórmulas.', 'pediatria', 'Aleitamento materno', 'contraindicações', ARRAY['contraindicação', 'HIV', 'infecção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como tratar fissuras mamilares na amamentação?', 'Fissuras mamilares são tratadas expondo os mamilos ao ar após as mamadas, aplicando lanolina pura e corrigindo a pega incorreta. Uso de conchas de proteção é evitado para não interferir na sucção. Em casos graves, antibióticos tópicos podem ser necessários se houver infecção.', 'pediatria', 'Aleitamento materno', 'fissuras mamilares', ARRAY['fissura', 'mamilo', 'tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é ingurgitamento mamário e como manejá-lo?', 'Ingurgitamento mamário ocorre pelo acúmulo excessivo de leite, causando dor e endurecimento dos seios nos primeiros dias pós-parto. Manejo inclui massagem suave, compressas mornas antes da mamada e frias após, e ordenha manual se necessário. Analgésicos como ibuprofeno ajudam no alívio da dor.', 'pediatria', 'Aleitamento materno', 'ingurgitamento', ARRAY['ingurgitamento', 'seio', 'massagem']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o papel do banco de leite humano?', 'O banco de leite humano coleta, processa e distribui leite doado para bebês prematuros ou em risco, pasteurizado para segurança. Ele promove a nutrição ideal quando a amamentação direta não é possível. No Brasil, segue normas da Anvisa para qualidade e rastreabilidade.', 'pediatria', 'Aleitamento materno', 'banco de leite', ARRAY['banco', 'leite', 'doação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é relactação e quando é indicada?', 'Relactação é a retomada da produção de leite após interrupção, estimulada por sucção frequente do bebê e ordenha. É indicada em separações mãe-bebê ou falhas iniciais na lactação. Pode levar dias a semanas, com suporte nutricional à mãe.', 'pediatria', 'Aleitamento materno', 'relactação', ARRAY['relactação', 'produção', 'sucção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais são as fórmulas infantis recomendadas?', 'Fórmulas infantis baseadas em leite de vaca modificado são usadas quando o aleitamento não é possível, seguindo normas da Anvisa para nutrientes essenciais. Devem ser preparadas com água fervida e em quantidades exatas para evitar desidratação ou superalimentação. Monitorar ganho de peso e tolerância digestiva é essencial.', 'pediatria', 'Aleitamento materno', 'fórmulas infantis', ARRAY['fórmula', 'leite', 'nutrição']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quando suplementar o aleitamento materno?', 'Suplementação é indicada em hipoglicemia neonatal, perda de peso excessiva >10% ou icterícia grave. Usa-se leite materno extraído ou fórmula via copinho ou sonda para preservar a sucção. Avaliar causas subjacentes como pega inadequada antes de suplementar.', 'pediatria', 'Aleitamento materno', 'suplementação', ARRAY['suplementação', 'hipoglicemia', 'icterícia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é comunicação interventricular (CIV)?', 'A CIV é a cardiopatia congênita mais comum, caracterizada por um defeito na parede entre os ventrículos, causando shunt esquerdo-direito e sobrecarga pulmonar. Sintomas incluem sopro sistólico e insuficiência cardíaca em neonatos. Diagnóstico por ecocardiograma, com fechamento espontâneo em até 75% dos casos pequenos.', 'pediatria', 'Cardiopatias congênitas', 'CIV', ARRAY['CIV', 'shunt', 'ecocardiograma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais as características da comunicação interatrial (CIA)?', 'A CIA permite passagem de sangue entre átrios, geralmente assintomática na infância, com shunt esquerdo-direito levando a dilatação atrial direita. Tipos incluem ostium secundum e primum. Diagnóstico ecocardiográfico; fechamento cirúrgico se >10mm ou complicações.', 'pediatria', 'Cardiopatias congênitas', 'CIA', ARRAY['CIA', 'átrio', 'dilatação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é persistência do canal arterial (PCA)?', 'O PCA é falha no fechamento do ducto arterioso pós-natal, causando roubo pulmonar e hipertensão ventricular esquerda. Comum em prematuros; sintomas incluem sopro contínuo e taquipneia. Tratamento com indometacina ou cirurgia se persistir.', 'pediatria', 'Cardiopatias congênitas', 'PCA', ARRAY['PCA', 'ducto', 'prematuridade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Descreva a tetralogia de Fallot.', 'A tetralogia de Fallot inclui estenose pulmonar, CIV, dextroposição da aorta e hipertrofia ventricular direita, causando cianose e crises hipoxêmicas. Diagnóstico por ecocardiograma e RX tórax com silhueta em "botas". Correção cirúrgica precoce é essencial para prevenir complicações.', 'pediatria', 'Cardiopatias congênitas', 'tetralogia de Fallot', ARRAY['Fallot', 'cianose', 'estenose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os sinais da coarctação de aorta?', 'A coarctação de aorta é estreitamento da aorta descendente, levando a hipertensão nos membros superiores e pulso fraco nas pernas. Sinais incluem sopro sistólico e discrepância pressórica. Diagnóstico por ecocardiograma; tratamento cirúrgico ou angioplastia.', 'pediatria', 'Cardiopatias congênitas', 'coarctação de aorta', ARRAY['coarctação', 'hipertensão', 'pulso']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é transposição das grandes artérias (TGA)?', 'Na TGA, a aorta surge do ventrículo direito e a pulmonar do esquerdo, causando cianose grave neonatal sem shunt. Diagnóstico por ecocardiograma; tratamento urgente com prostaglandina e correção arterial switch. Prognóstico bom se intervenido cedo.', 'pediatria', 'Cardiopatias congênitas', 'TGA', ARRAY['TGA', 'cianose', 'switch']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar estenose pulmonar congênita?', 'Estenose pulmonar é obstrução valvular pulmonar, causando sopro ejetivo e hipertrofia ventricular direita. Graus variam de leve a crítica; ecocardiograma avalia gradiente de pressão. Valvuloplastia por balão é tratamento de escolha em casos moderados.', 'pediatria', 'Cardiopatias congênitas', 'estenose pulmonar', ARRAY['estenose', 'válvula', 'valvuloplastia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais as cardiopatias congênitas cianóticas?', 'Cardiopatias cianóticas incluem tetralogia de Fallot, TGA e truncus arteriosus, com mistura de sangue venoso no sistêmico causando hipoxemia. Apresentam cianose precoce e poliglobulia. Diagnóstico por ecocardiograma e oximetria de pulso neonatal.', 'pediatria', 'Cardiopatias congênitas', 'cardiopatia cianótica', ARRAY['cianótica', 'hipoxemia', 'mistura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como avaliar sopros cardíacos em crianças?', 'Sopros inocentes são comuns, sistólicos e sem irradiação, em crianças saudáveis. Sopros patológicos são contínuos, diastólicos ou associados a sintomas. Exame físico detalhado e ecocardiograma confirmam etiologia.', 'pediatria', 'Cardiopatias congênitas', 'sopros', ARRAY['sopro', 'inocente', 'patológico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual a importância do ecocardiograma fetal?', 'O ecocardiograma fetal detecta cardiopatias congênitas a partir de 18 semanas, avaliando anatomia cardíaca e fluxos. Indicado em mães diabéticas ou com exposição a teratogênios. Permite planejamento perinatal e intervenções precoces.', 'pediatria', 'Cardiopatias congênitas', 'ecocardiograma fetal', ARRAY['fetal', 'ecocardiograma', 'detecção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os sintomas da bronquiolite em lactentes?', 'Bronquiolite é infecção viral (VSR) das vias aéreas inferiores, com sibilos, taquipneia e retrações em bebês <2 anos. Diagnóstico clínico; suporte inclui oxigênio e hidratação. Hospitalização se saturação <92% ou desidratação.', 'pediatria', 'Doenças respiratórias', 'bronquiolite', ARRAY['bronquiolite', 'VSR', 'sibilos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como tratar asma infantil?', 'Asma infantil é tratada com corticoides inalatórios para controle crônico e beta-agonistas para crises agudas. Educar sobre gatilhos como alérgenos e infecções. Plano de ação escrito e seguimento com espirometria em maiores de 5 anos.', 'pediatria', 'Doenças respiratórias', 'asma infantil', ARRAY['asma', 'inalatório', 'crise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os achados na pneumonia pediátrica?', 'Pneumonia é suspeitada por febre, tosse e crepitações; RX tórax mostra infiltrados. Causas bacterianas (pneumococo) em <5 anos; tratamento com amoxicilina. Vacinação conjugada previne casos graves.', 'pediatria', 'Doenças respiratórias', 'pneumonia', ARRAY['pneumonia', 'RX', 'amoxicilina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é crupe e seu manejo?', 'Crupe é laringotraqueobronquite viral, com estridor inspiratório e rouquidão em 6m-3a. Manejo inclui nebulização racêmica e dexametasona oral. Intubação rara em casos graves.', 'pediatria', 'Doenças respiratórias', 'crupe', ARRAY['crupe', 'estridor', 'dexametasona']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais as fases da coqueluche?', 'Coqueluche tem fase catarral (tosse), paroxística (acessos com apneia) e convalescente. Causada por Bordetella; vacinada com DTP. Azitromicina em fase inicial; suporte em <6m.', 'pediatria', 'Doenças respiratórias', 'coqueluche', ARRAY['coqueluche', 'paroxística', 'vacina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar fibrose cística?', 'Fibrose cística é diagnosticada por teste do suor >60mmol/L Cl-, mutação CFTR e clínica (insuficiência pancreática, infecções pulmonares). Triagem neonatal por tripsinogênio. Tratamento multidisciplinar com enzimas e fisioterapia.', 'pediatria', 'Doenças respiratórias', 'fibrose cística', ARRAY['fibrose', 'suor', 'CFTR']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que fazer em aspiração de corpo estranho?', 'Aspiração de corpo estranho causa tosse súbita e assimetria respiratória; RX pode mostrar obstrução. Broncoscopia rígida remove o objeto. Prevenir com supervisão alimentar em <4a.', 'pediatria', 'Doenças respiratórias', 'aspiração corpo estranho', ARRAY['aspiração', 'broncoscopia', 'tosse']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais causas de apneia em neonatos?', 'Apneia neonatal é pausa >20s em respiração, comum em prematuros por imaturidade; causas incluem infecção ou refluxo. Monitorar com cardiorrespirador; tratamento com cafeína. Investigar com polissonografia se persistir.', 'pediatria', 'Doenças respiratórias', 'apneia', ARRAY['apneia', 'prematuro', 'cafeína']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é laringomalácia?', 'Laringomalácia é imaturidade da epiglote causando estridor inspiratório em neonatos, geralmente benigna e autolimitada até 18m. Diagnóstico por laringoscopia flexível. Cirurgia rara em casos graves com apneia.', 'pediatria', 'Doenças respiratórias', 'laringomalácia', ARRAY['laringomalácia', 'estridor', 'epiglote']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como manejar tuberculose infantil?', 'Tuberculose infantil é suspeitada por contato, tosse crônica e RX com infiltrados; teste tuberculínico positivo. Tratamento com RIPE por 6m; BCG previne formas graves. Isolamento inicial e seguimento.', 'pediatria', 'Doenças respiratórias', 'tuberculose infantil', ARRAY['tuberculose', 'RIPE', 'BCG']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é DRGE em crianças?', 'DRGE é refluxo gastroesofágico patológico, com vômitos recorrentes e irritabilidade em lactentes. Diagnóstico por história e pHmetria; tratamento com inibidores de bomba de prótons e espessantes. Cirurgia (Nissen) em falhas.', 'pediatria', 'Gastroenterologia pediátrica', 'DRGE', ARRAY['DRGE', 'refluxo', 'vômito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sintomas da doença celíaca?', 'Doença celíaca é intolerância ao glúten, com diarreia crônica, distensão abdominal e falha no crescimento. Diagnóstico por anticorpos anti-transglutaminase e biópsia duodenal. Dieta sem glúten vitalícia cura sintomas.', 'pediatria', 'Gastroenterologia pediátrica', 'doença celíaca', ARRAY['celíaca', 'glúten', 'biópsia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como identificar alergia alimentar?', 'Alergia alimentar causa urticária, vômitos ou anafilaxia após ingestão (leite, ovo comuns). Testes cutâneos e IgE específica confirmam; eliminação dietética testa. Educar sobre epinefrina em anafilaxia.', 'pediatria', 'Gastroenterologia pediátrica', 'alergia alimentar', ARRAY['alergia', 'IgE', 'anafilaxia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que causa constipação funcional em crianças?', 'Constipação funcional é retenção voluntária, com fezes duras e incontinência fecal. Tratamento com laxantes (lactulose) e desimpactação; educação para rotina de evacuação. Prevenir com fibras e hidratação.', 'pediatria', 'Gastroenterologia pediátrica', 'constipação', ARRAY['constipação', 'laxante', 'retenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo da diarreia aguda pediátrica?', 'Diarreia aguda é desidratação por rotavírus ou bacteriana; avaliar grau com escala de desidratação. Rehidratação oral com SRO; antibióticos só em disenteria. Vacina rotavírus previne.', 'pediatria', 'Gastroenterologia pediátrica', 'diarreia aguda', ARRAY['diarreia', 'SRO', 'rotavírus']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sinais de intussuscepção?', 'Intussuscepção é invaginação intestinal, com dor cólica, massa abdominal e "gelatina" nas fezes em 6m-2a. USG confirma; redução por enema ou cirurgia. Comum pós-infecção viral.', 'pediatria', 'Gastroenterologia pediátrica', 'intussuscepção', ARRAY['intussuscepção', 'enema', 'cólica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é estenose pilórica hipertrófica?', 'Estenose pilórica causa vômitos projetis em 3-6 semanas, com massa olive no epigástrio. USG mostra piloro alongado >15mm. Piloromiotomia é curativa pós-correção eletrolítica.', 'pediatria', 'Gastroenterologia pediátrica', 'estenose pilórica', ARRAY['estenose', 'pilórica', 'vômito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Características da atresia esofágica?', 'Atresia esofágica apresenta polidrâmnio fetal e saliva excessiva neonatal; tipos com ou sem fístula traqueoesofágica. Diagnóstico por RX com sonda; correção cirúrgica precoce. Complicações incluem GERD.', 'pediatria', 'Gastroenterologia pediátrica', 'atresia esofágica', ARRAY['atresia', 'esofágica', 'fístula']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar hepatite neonatal?', 'Hepatite neonatal causa icterícia prolongada >14 dias, com bilirrubina direta elevada. Causas infecciosas (TORCH) ou metabólicas; USG e biópsia hepática. Ursodiol e fenobarbital em colestase.', 'pediatria', 'Gastroenterologia pediátrica', 'hepatite neonatal', ARRAY['hepatite', 'icterícia', 'TORCH']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é megacólon chagásico em crianças?', 'Megacólon chagásico resulta de denervação colônica por Trypanosoma cruzi, com constipação grave e abdome distendido. Diagnóstico por história endêmica e RX com fecaloma. Tratamento cirúrgico ressectivo.', 'pediatria', 'Gastroenterologia pediátrica', 'megacólon', ARRAY['megacólon', 'chagásico', 'constipação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é convulsão febril simples?', 'Convulsão febril simples ocorre em 6m-5a com febre >38°C, duração <15min, sem recorrência em 24h. Benigna, sem risco de epilepsia; antipiréticos (paracetamol, ibuprofeno) NÃO previnem recorrência (AAP 2011). EEG só se atípica.', 'pediatria', 'Neurologia pediátrica', 'convulsão febril', ARRAY['febril', 'convulsão', 'benigna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais tipos de epilepsia infantil?', 'Epilepsia infantil inclui síndromes como Lennox-Gastaut (crises múltiplas, retardo) e ausência (piscadelas). EEG confirma; tratamento com valproato ou etossuximida. Monitorar efeitos colaterais e desenvolvimento.', 'pediatria', 'Neurologia pediátrica', 'epilepsia infantil', ARRAY['epilepsia', 'EEG', 'síndrome']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Características da paralisia cerebral?', 'Paralisia cerebral é lesão estática encefálica perinatal, com espasticidade, distonia ou atetose afetando movimento. Classificação por tipo (diplegia) e topografia (hemiplegia). Reabilitação com fisioterapia e órteses.', 'pediatria', 'Neurologia pediátrica', 'paralisia cerebral', ARRAY['paralisia', 'espástica', 'reabilitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Sinais de meningite bacteriana em crianças?', 'Meningite bacteriana (meningococo) causa febre, rigidez nucal e petéquias; LCR mostra neutrófilos e glicose baixa. Ceftriaxona IV urgente; dexametasona adjuvante. Vacinação meningocócica previne.', 'pediatria', 'Neurologia pediátrica', 'meningite', ARRAY['meningite', 'LCR', 'ceftriaxona']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é hidrocefalia e seu tratamento?', 'Hidrocefalia é acúmulo de LCR dilatando ventrículos, com macrocefalia e fontanela abaulada. Causas obstrutivas ou comunicantes; TC/RM confirma. Derivação ventrículo-peritoneal alivia pressão.', 'pediatria', 'Neurologia pediátrica', 'hidrocefalia', ARRAY['hidrocefalia', 'LCR', 'derivação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Causas de microcefalia congênita?', 'Microcefalia congênita (PC <2DP) resulta de infecções (Zika), toxinas ou genética. Avaliar com RM e testes infecciosos. Suporte multidisciplinar para déficits neurológicos.', 'pediatria', 'Neurologia pediátrica', 'microcefalia', ARRAY['microcefalia', 'Zika', 'genética']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Critérios diagnósticos de TDAH?', 'TDAH é diagnosticado por DSM-5 com inatenção/hiperatividade persistente >6m em 2 contextos, iniciando <12a. Escalas como SNAP-IV auxiliam; tratamento com metilfenidato e terapia comportamental. Comorbidades como ODD comuns.', 'pediatria', 'Neurologia pediátrica', 'TDAH', ARRAY['TDAH', 'DSM-5', 'metilfenidato']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza o Transtorno do Espectro Autista (TEA)?', 'TEA envolve déficits em comunicação social e comportamentos repetitivos, variando de leve a grave. Diagnóstico por ADOS e história; intervenção precoce com ABA melhora outcomes. Triagem aos 18-24m.', 'pediatria', 'Neurologia pediátrica', 'TEA', ARRAY['TEA', 'autismo', 'comunicação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais distrofias musculares pediátricas?', 'Distrofias musculares como Duchenne causam fraqueza progressiva por mutação distrofina, com elevação CK. Diagnóstico genético; suporte com ventilação e esteroides. Aconselhamento familiar essencial.', 'pediatria', 'Neurologia pediátrica', 'distrofias musculares', ARRAY['distrofia', 'Duchenne', 'CK']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Manejo do mielomeningocele?', 'Mielomeningocele é defeito neural com protrusão sacra, causando hidrocefalia e incontinência. Fechamento cirúrgico neonatal; shunt VP para hidro. Reabilitação e urologia seguem.', 'pediatria', 'Neurologia pediátrica', 'mielomeningocele', ARRAY['mielomeningocele', 'neural', 'shunt']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é infecção do trato urinário (ITU) em crianças?', 'A ITU em crianças é uma infecção bacteriana do trato urinário, mais comum em meninas e lactentes. Sintomas incluem febre, irritabilidade e disúria. O diagnóstico é por urocultura, e o tratamento envolve antibióticos como cefalosporinas.', 'pediatria', 'Nefrologia pediátrica', 'ITU', ARRAY['nefrologia','itu','crianças']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais as complicações de ITU não tratada em crianças?', 'ITU não tratada pode levar a pielonefrite, sepse ou dano renal permanente, especialmente em menores de 2 anos. Profilaxia com antibióticos é indicada em casos recorrentes. Exames de imagem como USG renal são recomendados para avaliar anomalias.', 'pediatria', 'Nefrologia pediátrica', 'ITU', ARRAY['nefrologia','complicações','itu']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza a síndrome nefrótica em crianças?', 'A síndrome nefrótica é definida por proteinúria maciça (>40 mg/m²/h), hipoalbuminemia (<2,5 g/dL), edema e hiperlipidemia. Causa comum é glomerulonefrite mínima em crianças. O tratamento inicial é com corticoides como prednisona.', 'pediatria', 'Nefrologia pediátrica', 'Síndrome nefrótica', ARRAY['nefrologia','síndrome nefrótica','edema']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os achados da síndrome nefrítica em crianças?', 'A síndrome nefrítica apresenta hematúria, oligúria, hipertensão e edema leve, frequentemente pós-estreptocócica. Proteinúria é moderada, e hipocomplementemia é comum. O tratamento é suporte com anti-hipertensivos e diálise se necessário.', 'pediatria', 'Nefrologia pediátrica', 'Síndrome nefrítica', ARRAY['nefrologia','síndrome nefrítica','hematúria']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é refluxo vésico-ureteral (RVU) em crianças?', 'RVU é o refluxo retrógrado de urina da bexiga para ureteres e rim, predispondo a ITU. Graus I a V classificam a severidade por cistouretrografia miccional. Tratamento conservador com antibióticos profiláticos ou cirurgia em casos graves.', 'pediatria', 'Nefrologia pediátrica', 'RVU', ARRAY['nefrologia','rvu','refluxo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diferenciar síndrome nefrótica de nefrítica?', 'Nefrítica tem hematúria e hipertensão proeminentes, enquanto nefrótica foca em proteinúria e edema. Complemento baixo sugere nefrítica pós-infecciosa. Ambas requerem avaliação renal urgente em crianças.', 'pediatria', 'Nefrologia pediátrica', 'Síndromes renais', ARRAY['nefrologia','diferencial','síndromes renais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o manejo da síndrome nefrótica em pediatria?', 'Manejo inclui diuréticos para edema, inibidores da ECA para proteinúria e imunossupressores em casos resistentes. Monitorar infecções e trombose devido à perda de antitrombina. Biópsia renal é reservada para falhas terapêuticas.', 'pediatria', 'Nefrologia pediátrica', 'Síndrome nefrótica', ARRAY['nefrologia','manejo','imunossupressores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quando indicar cirurgia para RVU pediátrico?', 'Cirurgia é indicada em RVU grau III-V com ITU recorrente apesar de profilaxia, ou deterioração renal. Endoscopia ou ureterocistostomia são opções. Monitoramento com USG e cistografia é essencial.', 'pediatria', 'Nefrologia pediátrica', 'RVU', ARRAY['nefrologia','cirurgia','rvu']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é enurese noturna em crianças?', 'Enurese noturna é micção involuntária durante o sono após 5 anos de idade, afetando 15-20% das crianças. Primária é sem sintomas diurnos; secundária pode indicar infecção ou estresse. Tratamento inclui alarme, dessmopressina ou terapia comportamental.', 'pediatria', 'Nefrologia pediátrica', 'Enurese', ARRAY['nefrologia','enurese','noturna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais causas orgânicas de enurese em pediatria?', 'Causas incluem ITU, diabetes insípido, apneia obstrutiva do sono ou constipação. Avaliar com exame físico, urocultura e diário miccional. Evitar punições e focar em suporte familiar.', 'pediatria', 'Nefrologia pediátrica', 'Enurese', ARRAY['nefrologia','causas','enurese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é anemia ferropriva em crianças?', 'Anemia ferropriva é a deficiência de ferro levando a Hb <11 g/dL, comum por dieta pobre ou perda sanguínea. Sintomas incluem fadiga, palidez e pica. Tratamento com sulfato ferroso oral por 3 meses, com dieta rica em ferro.', 'pediatria', 'Hematologia pediátrica', 'Anemia ferropriva', ARRAY['hematologia','anemia ferropriva','ferro']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é anemia falciforme em crianças?', 'Anemia falciforme é doença hereditária com mutação na beta-globina, causando crises vaso-oclusivas e hemólise crônica. Prevalente em descendentes africanos. Profilaxia com penicilina e hidroxiureia reduz complicações.', 'pediatria', 'Hematologia pediátrica', 'Anemia falciforme', ARRAY['hematologia','falciforme','crises']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é púrpura trombocitopênica idiopática (PTI) em crianças?', 'PTI é trombocitopenia autoimune (<100.000/mm³) sem esplenomegalia, comum pós-viral. Sangramentos mucocutâneos são típicos. A maioria resolve espontaneamente; corticoides ou IVIG em casos graves.', 'pediatria', 'Hematologia pediátrica', 'PTI', ARRAY['hematologia','pti','trombocitopenia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sinais iniciais de leucemia em crianças?', 'Sinais incluem anemia, febre persistente, hematomas e linfadenopatia. Leucocitose ou pancitopenia no hemograma sugere. Diagnóstico por mielograma e imunofenotipagem.', 'pediatria', 'Hematologia pediátrica', 'Leucemia', ARRAY['hematologia','leucemia','sinais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é hemofilia em crianças?', 'Hemofilia é coagulopatia hereditária ligada ao X por deficiência de fator VIII (A) ou IX (B). Manifesta por hemartroses e hematomas. Tratamento com fatores de coagulação profiláticos ou sob demanda.', 'pediatria', 'Hematologia pediátrica', 'Hemofilia', ARRAY['hematologia','hemofilia','coagulação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar anemia ferropriva pediátrica?', 'Diagnóstico por hemograma com microcitose, ferritina baixa (<12 ng/mL) e saturação de transferrina <16%. Excluir talassemia por eletroforese de Hb. Suplementação IV em casos graves ou má absorção.', 'pediatria', 'Hematologia pediátrica', 'Anemia ferropriva', ARRAY['hematologia','diagnóstico','anemia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais complicações agudas da anemia falciforme?', 'Complicações incluem síndrome torácica aguda, AVC e priapismo. Tratamento de crises com hidratação, analgésicos e oxigênio. Vacinação contra pneumococo e influenza é crucial.', 'pediatria', 'Hematologia pediátrica', 'Anemia falciforme', ARRAY['hematologia','complicações','falciforme']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quando tratar PTI aguda em pediatria?', 'Tratar se plaquetas <20.000/mm³ com sangramento ou <10.000 sem. Esplenectomia é rara em crianças. Monitorar com hemograma seriado.', 'pediatria', 'Hematologia pediátrica', 'PTI', ARRAY['hematologia','tratamento','pti']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o tratamento para leucemia linfoblástica aguda (LLA)?', 'LLA é tratada com quimioterapia em fases de indução, consolidação e manutenção por 2-3 anos. Taxa de cura >80% em crianças. Transplante em recidivas.', 'pediatria', 'Hematologia pediátrica', 'Leucemia', ARRAY['hematologia','lla','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como prevenir complicações na hemofilia pediátrica?', 'Profilaxia com infusões regulares previne sangramentos articulares. Evitar esportes de contato e educar sobre sinais de sangramento. Testes genéticos para portadoras.', 'pediatria', 'Hematologia pediátrica', 'Hemofilia', ARRAY['hematologia','prevenção','hemofilia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é hipotireoidismo congênito em recém-nascidos?', 'Hipotireoidismo congênito é deficiência de T4 e TSH elevado, causando atraso no desenvolvimento se não tratado. Triagem neonatal por teste do pezinho detecta. Tratamento com levotiroxina desde os primeiros dias.', 'pediatria', 'Endocrinologia pediátrica', 'Hipotireoidismo congênito', ARRAY['endocrinologia','hipotireoidismo','congênito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é puberdade precoce em meninas?', 'Puberdade precoce é desenvolvimento mamário antes dos 8 anos ou menarca antes dos 10, central por ativação precoce do eixo hipotálamo-hipofisário. Avaliar com GnRH teste e RM hipofisária. Tratamento com análogos de GnRH.', 'pediatria', 'Endocrinologia pediátrica', 'Puberdade precoce', ARRAY['endocrinologia','puberdade','precocidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais critérios para diabetes tipo 1 em crianças?', 'Diabetes tipo 1 é autoimune com hiperglicemia (glicemia >200 mg/dL aleatória), polidipsia e perda de peso. Autoanticorpos como anti-GAD confirmam. Insulina é tratamento vitalício.', 'pediatria', 'Endocrinologia pediátrica', 'Diabetes tipo 1', ARRAY['endocrinologia','diabetes tipo 1','insulina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é deficiência de hormônio do crescimento (DHC)?', 'DHC causa baixa estatura com velocidade de crescimento <4 cm/ano e IGF-1 baixo. Diagnóstico por teste de estímulo com clonidina ou insulina. Tratamento com GH recombinante diário.', 'pediatria', 'Endocrinologia pediátrica', 'DHC', ARRAY['endocrinologia','dhc','crescimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é hiperplasia adrenal congênita (HAC)?', 'HAC é defeito enzimático na síntese de cortisol, mais comum 21-hidroxilase, causando virilização em meninas. Diagnóstico por 17-hidroxiprogesterona elevada. Tratamento com glicocorticoides e mineralocorticoides.', 'pediatria', 'Endocrinologia pediátrica', 'HAC', ARRAY['endocrinologia','hac','adrenal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sequelas do hipotireoidismo congênito não tratado?', 'Sequelas incluem deficiência intelectual, hipotonia e constipação. Monitorar TSH e T4 livres anualmente. Dose inicial de L-tiroxina é 10-15 mcg/kg/dia.', 'pediatria', 'Endocrinologia pediátrica', 'Hipotireoidismo congênito', ARRAY['endocrinologia','sequelas','tratamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diferenciar puberdade precoce central de periférica?', 'Central tem elevação de LH após GnRH; periférica tem gonadotrofinas suprimidas. Periférica inclui tumores ovarianos ou CAH. Supressão puberal preserva altura final.', 'pediatria', 'Endocrinologia pediátrica', 'Puberdade precoce', ARRAY['endocrinologia','diferencial','puberdade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como manejar cetoacidose diabética pediátrica?', 'Cetoacidose é tratada com reidratação IV (solução salina), insulina regular e potássio. Monitorar glicemia e eletrólitos a cada hora. Prevenir recorrências com educação diabética.', 'pediatria', 'Endocrinologia pediátrica', 'Diabetes tipo 1', ARRAY['endocrinologia','cetoacidose','manejo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais indicações para terapia com GH em crianças?', 'Indicações incluem DHC idiopática, síndrome de Turner ou insuficiência renal crônica. Monitorar altura e densidade óssea. Efeitos colaterais raros incluem cefaleia.', 'pediatria', 'Endocrinologia pediátrica', 'DHC', ARRAY['endocrinologia','terapia gh','indicações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como monitorar HAC clássica em pediatria?', 'Monitorar com 17-OHP, renina e eletrólitos. Ajustar doses para evitar androgênio excessivo ou insuficiência adrenal. Cirurgia corretiva para genitália ambígua.', 'pediatria', 'Endocrinologia pediátrica', 'HAC', ARRAY['endocrinologia','monitoramento','hac']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais as etapas de Tanner na puberdade masculina?', 'Estágios de Tanner: I pré-púbere; II pilos pubiano e aumento testicular; III crescimento peniano; IV pilos axilar; V adulto. Avaliar para atraso ou precocidade. Puberdade normal inicia 9-14 anos em meninos.', 'pediatria', 'Medicina do adolescente', 'Tanner', ARRAY['adolescência','tanner','puberdade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é anorexia nervosa em adolescentes?', 'Anorexia nervosa é restrição calórica intencional com IMC <17, medo de ganhar peso e distorção imagem corporal. Complicações incluem amenorreia e osteoporose. Tratamento multidisciplinar com reabilitação nutricional e terapia cognitivo-comportamental.', 'pediatria', 'Medicina do adolescente', 'Distúrbios alimentares', ARRAY['adolescência','anorexia','distúrbios alimentares']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais riscos do abuso de substâncias em adolescentes?', 'Abuso inclui álcool, maconha e cocaína, levando a dependência, acidentes e problemas psiquiátricos. Sintomas: mudanças comportamentais e falha escolar. Intervenção precoce com aconselhamento e detox se necessário.', 'pediatria', 'Medicina do adolescente', 'Abuso de substâncias', ARRAY['adolescência','drogas','abuso']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais métodos contraceptivos para adolescentes?', 'Métodos incluem DIU, pílula oral e preservativo, priorizando eficácia e proteção IST. Aconselhamento confidencial é essencial. DIU de levonorgestrel é seguro pós-parto.', 'pediatria', 'Medicina do adolescente', 'Contracepção', ARRAY['adolescência','contracepção','métodos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sinais de depressão em adolescentes?', 'Depressão apresenta humor triste persistente, anedonia e ideação suicida por >2 semanas. Risco maior em meninas e com histórico familiar. Tratar com ISRS e psicoterapia.', 'pediatria', 'Medicina do adolescente', 'Saúde mental', ARRAY['adolescência','depressão','saúde mental']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como avaliar puberdade em meninas pelo Tanner?', 'Em meninas: I sem mamas; II broto mamário; III desenvolvimento mamário; IV areola proeminente; V mamas maduras. Menarca ocorre no estágio III-IV. Variantes incluem telarca isolada.', 'pediatria', 'Medicina do adolescente', 'Tanner', ARRAY['adolescência','tanner','meninas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais sinais de bulimia em jovens?', 'Bulimia envolve compulsão alimentar e purgação, com erosão dentária e calos nas mãos. Eletrólitos alterados como hipocalemia. Abordagem inclui suporte psicológico e monitoramento médico.', 'pediatria', 'Medicina do adolescente', 'Distúrbios alimentares', ARRAY['adolescência','bulimia','purgação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como abordar tabagismo em adolescentes?', 'Tabagismo inicia por curiosidade, com nicotina viciante. Cessação com terapia de reposição nicotina e suporte grupal. Prevenção por educação escolar sobre riscos pulmonares e cardiovasculares.', 'pediatria', 'Medicina do adolescente', 'Abuso de substâncias', ARRAY['adolescência','tabagismo','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quando indicar contraceptivo hormonal em meninas?', 'Indicar para regulação menstrual ou acne, iniciando após avaliação de contraindicações como trombose. Monitorar adesão e efeitos colaterais. Educação sobre uso correto previne falhas.', 'pediatria', 'Medicina do adolescente', 'Contracepção', ARRAY['adolescência','hormonal','contraceptivo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como manejar ansiedade em jovens?', 'Ansiedade inclui TAG ou pânico, com taquicardia e evitação. Terapia cognitivo-comportamental é primeira linha; benzodiazepínicos só em crise. Rastreio em consultas rotineiras.', 'pediatria', 'Medicina do adolescente', 'Saúde mental', ARRAY['adolescência','ansiedade','terapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é alimentação complementar em lactentes?', 'Alimentação complementar inicia aos 6 meses com papas de frutas e vegetais, introduzindo texturas graduais. Evitar mel e sal excessivo. Promove diversidade nutricional e desenvolvimento oral.', 'pediatria', 'Nutrição infantil', 'Alimentação complementar', ARRAY['nutrição','complementar','lactentes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o cronograma de introdução de alimentos sólidos?', 'Aos 6 meses: frutas e vegetais; 7-8: carnes e grãos; 9-12: laticínios e ovos. Introduzir um por vez para alergias. Aleitamento materno até 2 anos.', 'pediatria', 'Nutrição infantil', 'Introdução alimentar', ARRAY['nutrição','cronograma','alimentos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais vitaminas suplementar em crianças?', 'Suplementar vitamina D (400 UI/dia) em aleitadas e A em regiões de risco. Ferro a partir de 6 meses se necessário. Vitamina C com frutas cítricas.', 'pediatria', 'Nutrição infantil', 'Suplementação vitamínica', ARRAY['nutrição','vitaminas','suplementação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é insuficiência para crescer (IPTC)?', 'IPTC é peso < percentil 5 ou queda >2 percentis em curva de crescimento, por causas orgânicas ou psicossociais. Avaliar com histórico e exames laboratoriais. Intervenção nutricional precoce reverte em muitos casos.', 'pediatria', 'Nutrição infantil', 'IPTC', ARRAY['nutrição','iptc','crescimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza obesidade infantil?', 'Obesidade é IMC > percentil 95 para idade, associada a sedentarismo e dieta calórica. Riscos: diabetes tipo 2 e esteatose. Prevenção com atividade física >60 min/dia e porções controladas.', 'pediatria', 'Nutrição infantil', 'Obesidade', ARRAY['nutrição','obesidade','infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais erros comuns na introdução alimentar?', 'Erros incluem introdução precoce <4 meses ou alimentos alergênicos sem orientação. Risco de obesidade ou desnutrição. Orientar por pirâmide alimentar adaptada à idade.', 'pediatria', 'Nutrição infantil', 'Alimentação complementar', ARRAY['nutrição','erros','introdução']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como prevenir engasgos na introdução alimentar?', 'Cortar alimentos em pedaços pequenos, evitar nozes e uvas inteiras. Treinar mastigação supervisionada. Postura ereta durante refeições.', 'pediatria', 'Nutrição infantil', 'Introdução alimentar', ARRAY['nutrição','segurança','engasgo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quando indicar suplemento de ferro na infância?', 'Indicar se anemia ou dieta vegetariana, dose 3-6 mg/kg/dia. Monitorar com hemograma. Fontes alimentares: carnes vermelhas e feijão.', 'pediatria', 'Nutrição infantil', 'Suplementação vitamínica', ARRAY['nutrição','ferro','crianças']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais causas orgânicas de IPTC em bebês?', 'Causas incluem doença celíaca, infecções crônicas ou cardiopatias. Exames: TSH, calprotectina fecal e eco abdominal. Abordagem multidisciplinar com pediatra e nutricionista.', 'pediatria', 'Nutrição infantil', 'IPTC', ARRAY['nutrição','causas','iptc']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como tratar obesidade em crianças?', 'Tratamento envolve mudanças familiares: reduzir telas, aumentar frutas e monitorar peso. Medicamentos raros <12 anos. Cirurgia bariátrica em adolescentes selecionados.', 'pediatria', 'Nutrição infantil', 'Obesidade', ARRAY['nutrição','tratamento','obesidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual é o principal critério diagnóstico para artrite idiopática juvenil (AIJ) em crianças?', 'A artrite idiopática juvenil é diagnosticada pela presença de artrite persistente em pelo menos uma articulação por seis semanas em crianças menores de 16 anos, sem causa identificável. Subtipos incluem oligoarticular, poliarticular e sistêmica, com uveíte como complicação comum na oligoarticular. O tratamento inicial envolve AINEs e metotrexato para controle da inflamação.', 'pediatria', 'Reumatologia pediátrica', 'Artrite Idiopática Juvenil', ARRAY['AIJ', 'artrite', 'criança']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como se manifesta a artrite reumatoide juvenil sistêmica?', 'A artrite reumatoide juvenil sistêmica, também chamada de doença de Still, apresenta febre diária alta, rash evanescente salmonado e artrite. Pode evoluir para amiloidose ou pericardite como complicações graves. O diagnóstico usa critérios de Yamaguchi, e o tratamento inclui corticosteroides e inibidores de IL-1 como anakinra.', 'pediatria', 'Reumatologia pediátrica', 'Doença de Still', ARRAY['febre', 'rash', 'sistêmica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais são os achados laboratoriais típicos no lúpus eritematoso sistêmico (LES) pediátrico?', 'No LES pediátrico, os achados incluem ANA positivo em alta titulação, anti-dsDNA positivo e hipocomplementemia. Anemia hemolítica, trombocitopenia e proteinúria são comuns. O tratamento varia de hidroxicloroquina para leve a imunossupressores como ciclofosfamida para casos graves.', 'pediatria', 'Reumatologia pediátrica', 'Lúpus Eritematoso Sistêmico', ARRAY['ANA', 'autoimune', 'nefrite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o manejo inicial da dermatomiosite juvenil?', 'A dermatomiosite juvenil é caracterizada por rash heliotrópico, fraqueza muscular proximal e elevação de CPK. O diagnóstico usa critérios de Bohan e Peter, incluindo biópsia muscular. O tratamento inicial é com prednisona em altas doses, associada a metotrexato para poupar esteroides.', 'pediatria', 'Reumatologia pediátrica', 'Dermatomiosite Juvenil', ARRAY['rash', 'muscular', 'inflamatória']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que diferencia a espondilite anquilosante juvenil da idiopática?', 'A espondilite anquilosante juvenil afeta principalmente a coluna e sacroilíacas, com HLA-B27 positivo em 90% dos casos. Diferencia-se da AIJ pela ausência de artrite periférica precoce e presença de uveíte anterior. O tratamento inclui AINEs e anti-TNF como etanercepte.', 'pediatria', 'Reumatologia pediátrica', 'Espondilite Anquilosante', ARRAY['HLA-B27', 'coluna', 'uveíte']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais complicações oculares ocorrem na AIJ oligoarticular?', 'Na AIJ oligoarticular, a uveíte anterior crônica assintomática é comum, afetando até 20% das crianças. Requer rastreio oftalmológico a cada 3-4 meses com lâmpada de fenda. O tratamento usa colírios de corticosteroides e imunossupressores sistêmicos.', 'pediatria', 'Reumatologia pediátrica', 'Uveíte na AIJ', ARRAY['uveíte', 'olho', 'rastreio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar febre reumática aguda em pediatria?', 'O diagnóstico de febre reumática aguda usa critérios de Jones modificados, incluindo cardite, poliartrite migratória, coreia e evidência de infecção prévia por estreptococo. ASLO elevado e ECG com intervalo PR prolongado são achados típicos. A prevenção secundária usa penicilina benzatina mensal por anos.', 'pediatria', 'Reumatologia pediátrica', 'Febre Reumática', ARRAY['Jones', 'estreptococo', 'cardite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o papel do fator reumatoide na AIJ?', 'O fator reumatoide é positivo na AIJ poliarticular RF-positivo, que tem pior prognóstico com deformidades e nódulos. É mais comum em meninas adolescentes. O tratamento é agressivo com biológicos como tocilizumabe.', 'pediatria', 'Reumatologia pediátrica', 'Fator Reumatoide', ARRAY['RF', 'poliarticular', 'prognóstico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a vasculite de Henoch-Schönlein em crianças?', 'A vasculite de Henoch-Schönlein é uma vasculite de pequenos vasos com IgA, manifestando púrpura palpável, artrite e nefrite. Afeta crianças de 2-11 anos, frequentemente pós-infecção. O tratamento é suporte, com esteroides para nefrite grave.', 'pediatria', 'Reumatologia pediátrica', 'Vasculite Henoch-Schönlein', ARRAY['púrpura', 'IgA', 'nefrite']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais são os critérios para polimiosite juvenil?', 'A polimiosite juvenil envolve fraqueza muscular simétrica proximal sem rash cutâneo, com elevação de enzimas musculares e biópsia inflamatória. Diferencia-se da dermatomiosite pela ausência de lesões de pele. O manejo usa imunossupressores como azatioprina.', 'pediatria', 'Reumatologia pediátrica', 'Polimiosite Juvenil', ARRAY['muscular', 'inflamação', 'biópsia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o tratamento de primeira linha para dermatite atópica em lactentes?', 'A dermatite atópica em lactentes é tratada com emolientes diários e banhos mornos curtos para hidratação da pele. Corticosteroides tópicos de baixa potência como hidrocortisona são usados em crises agudas. Evitar irritantes e identificar alérgenos alimentares se necessário.', 'pediatria', 'Dermatologia pediátrica', 'Dermatite Atópica', ARRAY['eczema', 'hidratação', 'corticoides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como se apresenta o impetigo em crianças?', 'O impetigo é uma infecção bacteriana superficial por Staphylococcus ou Streptococcus, com lesões vesiculares que rompem formando crostas melicéricas douradas. Comum em áreas expostas como face. Tratamento com mupirocina tópica ou antibióticos orais como cefalexina se extenso.', 'pediatria', 'Dermatologia pediátrica', 'Impetigo', ARRAY['bacteriana', 'crostas', 'antibiótico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os sinais de escabiose em pediatria?', 'A escabiose causa prurido intenso noturno e lesões escavadas lineares entre dedos, punhos e axilas. Em lactentes, pode afetar face e couro cabeludo. Tratamento com permetrina 5% tópica e lavagem de roupas em água quente.', 'pediatria', 'Dermatologia pediátrica', 'Escabiose', ARRAY['sarna', 'prurido', 'permetrina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza a urticária em crianças?', 'A urticária pediátrica apresenta pápulas eritematosas pruriginosas transitórias, frequentemente alérgica a alimentos ou medicamentos. Angioedema pode acompanhar. Antihistamínicos como loratadina são primeira linha; investigar causas subjacentes se crônica.', 'pediatria', 'Dermatologia pediátrica', 'Urticária', ARRAY['alérgica', 'prurido', 'antihistamínico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o manejo da psoríase guttata em adolescentes?', 'A psoríase guttata surge após infecção estreptocócica, com lesões pequenas escamosas disseminadas no tronco. Responde bem a fototerapia UVB. Corticosteroides tópicos e evitar gatilhos infecciosos são essenciais.', 'pediatria', 'Dermatologia pediátrica', 'Psoríase Guttata', ARRAY['estreptococo', 'escamas', 'fototerapia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar hemangioma infantil?', 'O hemangioma infantil é um tumor vascular proliferativo que cresce rapidamente nos primeiros meses de vida, comum em cabeça e pescoço. Diagnóstico clínico; biópsia rara. A maioria involui espontaneamente, mas propranolol é usado para ulceração ou obstrução.', 'pediatria', 'Dermatologia pediátrica', 'Hemangioma', ARRAY['vascular', 'propranolol', 'involução']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais lesões cutâneas no sífilis congênita?', 'No sífilis congênita, lesões cutâneas incluem rash maculopapular nas palmas e plantas, e rinite snuffles. Diagnóstico por VDRL positivo no soro e IgM específica. Tratamento com penicilina G intravenosa por 10 dias.', 'pediatria', 'Dermatologia pediátrica', 'Sífilis Congênita', ARRAY['rash', 'VDRL', 'penicilina']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a alopecia areata em crianças?', 'A alopecia areata causa perda de cabelo em placas redondas não cicatriciais, autoimune contra folículos. Pode ser total ou universal. Tratamento com corticosteroides tópicos ou minoxidil; boa resposta em casos localizados.', 'pediatria', 'Dermatologia pediátrica', 'Alopecia Areata', ARRAY['autoimune', 'cabelo', 'corticoides']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o tratamento da candidíase cutânea em neonatos?', 'A candidíase cutânea em neonatos apresenta placas eritematosas satélites em dobras, tratada com nistatina tópica. Manter pele seca e evitar fraldas úmidas. Em prematuros, considerar fluconazol sistêmico se disseminada.', 'pediatria', 'Dermatologia pediátrica', 'Candidíase', ARRAY['fungo', 'nistatina', 'neonato']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como se manifesta a pitiríase versicolor em adolescentes?', 'A pitiríase versicolor causa máculas hipocrômicas ou hipercromáticas escamosas no tronco, por Malassezia. Diagnóstico com lâmpada de Wood fluorescente amarelada. Tratamento com xampu de cetoconazol ou itraconazol oral.', 'pediatria', 'Dermatologia pediátrica', 'Pitiríase Versicolor', ARRAY['Malassezia', 'escamas', 'cetoconazol']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o tratamento conservador do pé torto congênito?', 'O pé torto congênito é tratado inicialmente com método de Ponseti, envolvendo gesso seriado e tenotomia percutânea do tendão de Aquiles. Órteses noturnas são usadas por 3-4 anos para manutenção. Cirurgia é reservada para casos resistentes.', 'pediatria', 'Ortopedia pediátrica', 'Pé Torto Congênito', ARRAY['Ponseti', 'gesso', 'órtese']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a displasia de quadril em recém-nascidos?', 'A displasia de quadril é detectada por exame de Ortolani e Barlow, com instabilidade da anca. Ultrassonografia confirma em lactentes <6 meses. Tratamento com pavlik harness por 6-12 semanas para redução fechada.', 'pediatria', 'Ortopedia pediátrica', 'Displasia de Quadril', ARRAY['Ortolani', 'ultrassom', 'pavlik']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como manejar a fratura de Salter-Harris tipo II no punho?', 'A fratura de Salter-Harris tipo II no punho é tratada com redução fechada e imobilização gessada por 4-6 semanas se deslocamento <2mm. Monitorar crescimento para evitar deformidade. Tipos III e IV requerem redução aberta.', 'pediatria', 'Ortopedia pediátrica', 'Fraturas Salter-Harris', ARRAY['fratura', 'redução', 'crescimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais os sinais de escoliose idiopática em adolescentes?', 'A escoliose idiopática progride durante o estirão puberal, com rotação vertebral e gibosidade costal em Adams test positivo. Radiografia mostra ângulo de Cobb >10 graus. Colete ortopédico para curvas 20-40 graus; cirurgia para >50 graus.', 'pediatria', 'Ortopedia pediátrica', 'Escoliose', ARRAY['Cobb', 'Adams', 'colete']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza a doença de Osgood-Schlatter?', 'A doença de Osgood-Schlatter é apofisite da tuberosidade tibial em adolescentes ativos, com dor e inchaço anterior ao joelho. Tratamento conservador com repouso, gelo e alongamentos. Resolve com fechamento da fise.', 'pediatria', 'Ortopedia pediátrica', 'Osgood-Schlatter', ARRAY['apofisite', 'joelho', 'repouso']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o exame para luxação congênita de joelho?', 'A luxação congênita de joelho apresenta hiperextensão fetal, diagnosticada por exame físico e radiografia. Tratamento imediato com redução fechada e gesso. Prognóstico bom se tratado precocemente.', 'pediatria', 'Ortopedia pediátrica', 'Luxação de Joelho', ARRAY['congênita', 'redução', 'gesso']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como tratar a epifisólise femoral capital?', 'A epifisólise femoral capital é comum em obesos adolescentes, classificada como estável ou instável. Tratamento cirúrgico com fixação percutânea in situ. Evitar mobilização precoce para prevenir necrose avascular.', 'pediatria', 'Ortopedia pediátrica', 'Epifisólise Femoral', ARRAY['quadril', 'cirurgia', 'necrose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a osteogênese imperfeita em pediatria?', 'A osteogênese imperfeita é uma doença genética do colágeno tipo I, causando fraturas recorrentes e deformidades ósseas. Tipos variam de leve a letal perinatal. Tratamento com bifosfonatos como pamidronato para densidade óssea.', 'pediatria', 'Ortopedia pediátrica', 'Osteogênese Imperfeita', ARRAY['fraturas', 'colágeno', 'bifosfonatos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais intervenções para marcha em varo em crianças?', 'A marcha em varo fisiológica resolve até 2 anos; patológica requer investigação de raquitismo ou infecção. Órteses noturnas podem ser usadas. Cirurgia corretiva se progressiva.', 'pediatria', 'Ortopedia pediátrica', 'Marcha em Varo', ARRAY['varo', 'raquitismo', 'órteses']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual a importância da triagem auditiva na escola?', 'A triagem auditiva escolar detecta perda auditiva precoce, essencial para linguagem e aprendizado. Realizada com odoaudiometria em crianças >3 anos. Encaminhar para otorrino se falha, prevenindo atrasos no desenvolvimento.', 'pediatria', 'Saúde escolar', 'Triagem Auditiva', ARRAY['audição', 'escola', 'desenvolvimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como promover higiene bucal em alunos?', 'A promoção de higiene bucal inclui escovação supervisionada com flúor e aplicação de selantes em molares. Educar sobre dieta baixa em açúcares para prevenir cárie. Programas escolares reduzem prevalência em 40%.', 'pediatria', 'Saúde escolar', 'Higiene Bucal', ARRAY['cárie', 'flúor', 'educação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais vacinas são obrigatórias no calendário escolar?', 'Vacinas obrigatórias incluem DTP, VIP (série primária; VOP apenas nos reforços), tríplice viral e hepatite B, com verificação na matrícula escolar. Cobertura >95% previne surtos como sarampo. Reforço de tétano-difteria aos 11-12 anos.', 'pediatria', 'Saúde escolar', 'Vacinação Escolar', ARRAY['calendário', 'vacinais', 'surtos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que avaliar na triagem visual escolar?', 'A triagem visual usa tabela de Snellen para acuidade >20/40 em cada olho. Detecta ambliopia ou erros refrativos comuns em 20% das crianças. Óculos corrigem miopia ou hipermetropia, melhorando desempenho acadêmico.', 'pediatria', 'Saúde escolar', 'Triagem Visual', ARRAY['Snellen', 'ambliopia', 'óculos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como lidar com obesidade na escola?', 'O combate à obesidade inclui educação nutricional, recreação física diária e restrição de lanches calóricos. Monitorar IMC anualmente. Programas reduzem ganho de peso em 10-15%.', 'pediatria', 'Saúde escolar', 'Obesidade', ARRAY['IMC', 'nutrição', 'atividade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o papel do pediatra na saúde mental escolar?', 'O pediatra identifica ansiedade ou depressão por queixas somáticas e desempenho escolar ruim. Encaminha para psicólogo se necessário. Prevenção com promoção de resiliência e suporte familiar.', 'pediatria', 'Saúde escolar', 'Saúde Mental', ARRAY['ansiedade', 'depressão', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como prevenir acidentes no ambiente escolar?', 'Prevenção de acidentes envolve inspeção de playgrounds, educação sobre segurança e treinamento de primeiros socorros. Quedas são comuns; superfícies macias reduzem lesões. Relatar incidentes para correção.', 'pediatria', 'Saúde escolar', 'Prevenção Acidentes', ARRAY['segurança', 'playground', 'primeiros socorros']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é o programa de promoção de atividade física?', 'O programa incentiva 60 minutos diários de atividade moderada, integrando educação física curricular. Reduz risco de doenças crônicas. Monitorar adesão para benefícios cardiovasculares.', 'pediatria', 'Saúde escolar', 'Atividade Física', ARRAY['exercício', 'currículo', 'cardiovascular']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais medidas para controle de parasitoses?', 'Controle de parasitoses inclui vermifugação semestral com albendazol e higiene de mãos. Educa sobre saneamento para prevenir enterobíase. Prevalência cai com intervenções escolares.', 'pediatria', 'Saúde escolar', 'Parasitoses', ARRAY['vermífugo', 'higiene', 'albendazol']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais características da síndrome de Down?', 'A síndrome de Down é causada por trissomia 21, com dismorfias como prega epicântica, ponte nasal achatada, macroglossia e hipotonia. Risco de cardiopatias congênitas em 50% e leucemia. Manejo multidisciplinar inclui rastreio de tireoide e visão.', 'pediatria', 'Genética e síndromes', 'Síndrome de Down', ARRAY['trissomia 21', 'dismorfias', 'cardiopatia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a síndrome de Turner em meninas?', 'A síndrome de Turner é monossomia X (45,X), com baixa estatura, pescoço alado e amenorreia primária. Rastrear coarctação aórtica e insuficiência ovariana. Terapia com GH para crescimento e estrogênio para puberdade.', 'pediatria', 'Genética e síndromes', 'Síndrome de Turner', ARRAY['monossomia X', 'baixa estatura', 'GH']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar fibrose cística?', 'A fibrose cística é causada por mutação no gene CFTR, diagnosticada por teste de suor com Cl >60 mEq/L. Sintomas incluem insuficiência pancreática e infecções pulmonares recorrentes. Tratamento com enzimas pancreáticas e antibióticos.', 'pediatria', 'Genética e síndromes', 'Fibrose Cística', ARRAY['CFTR', 'teste suor', 'pulmonar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais achados na síndrome de Marfan?', 'A síndrome de Marfan é por mutação em fibrilina-1, com estatura alta, aracnodactilia e ectopia lentis. Risco de dilatação aórtica levando a dissecção. Ecocardiograma anual e beta-bloqueadores para manejo.', 'pediatria', 'Genética e síndromes', 'Síndrome de Marfan', ARRAY['fibrilina', 'aorta', 'ecocardiograma']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que caracteriza a fenilcetonúria?', 'A fenilcetonúria é deficiência de fenilalanina hidroxilase, detectada no teste do pezinho com níveis elevados de Phe. Sem dieta restrita, causa deficiência intelectual e microcefalia. Monitorar níveis plasmáticos e suplementar tirosina.', 'pediatria', 'Genética e síndromes', 'Fenilcetonúria', ARRAY['teste pezinho', 'dieta', 'retardo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Qual o manejo da síndrome de Klinefelter?', 'A síndrome de Klinefelter (47,XXY) causa hipogonadismo, ginecomastia e infertilidade em meninos. Diagnóstico por cariótipo. Terapia com testosterona a partir da puberdade para desenvolvimento secundário.', 'pediatria', 'Genética e síndromes', 'Síndrome de Klinefelter', ARRAY['XXY', 'testosterona', 'puberdade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como se apresenta a síndrome de Noonan?', 'A síndrome de Noonan tem mutação em PTPN11, com face característica, cardiopatia pulmonar valvular e baixa estatura. Semelhante a Turner mas em ambos sexos. Rastreio ecocardiográfico e GH se necessário.', 'pediatria', 'Genética e síndromes', 'Síndrome de Noonan', ARRAY['PTPN11', 'cardiopatia', 'face']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a ataxia-telangiectasia?', 'A ataxia-telangiectasia é doença autossômica recessiva com ataxia progressiva, telangiectasias conjuntivais e imunodeficiência. Risco aumentado de linfoma. Suporte sintomático e evitar radiação.', 'pediatria', 'Genética e síndromes', 'Ataxia-Telangiectasia', ARRAY['ataxia', 'imunodeficiência', 'câncer']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Quais riscos na neurofibromatose tipo 1?', 'A neurofibromatose tipo 1 tem manchas café com leite, neurofibromas e Lisch nodules. Risco de gliomas ópticos e feocromocitoma. Monitoramento anual com oftalmologia e ressonância.', 'pediatria', 'Genética e síndromes', 'Neurofibromatose', ARRAY['café com leite', 'glioma', 'monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'Como diagnosticar galactosemia?', 'A galactosemia é deficiência de galactose-1-fosfato uridiltransferase, detectada no teste neonatal com elevação de galactitol. Sintomas incluem icterícia, sepse e catarata. Dieta sem lactose vitalícia.', 'pediatria', 'Genética e síndromes', 'Galactosemia', ARRAY['teste neonatal', 'dieta', 'catarata']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000004', 'O que é a síndrome de Prader-Willi?', 'A síndrome de Prader-Willi é por deleção 15q11-13 paterna, com hipotonia neonatal, hiperfagia e obesidade. Retardo cognitivo moderado. Controle calórico e GH para composição corporal.', 'pediatria', 'Genética e síndromes', 'Síndrome de Prader-Willi', ARRAY['deleção', 'hiperfagia', 'GH']);


-- ============================================================
-- FLASHCARDS: Saúde Coletiva — 200 cards
-- Deck: a0000001-0000-0000-0000-000000000005
-- ============================================================

INSERT INTO flashcards (id, deck_id, front, back, area, subspecialty, topic, tags) VALUES
-- Round 1: Epidemiologia, Vigilância em saúde, SUS e políticas, Atenção primária, Saúde do trabalhador
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a diferença entre incidência e prevalência em epidemiologia?', 'A incidência mede o número de novos casos de uma doença em uma população durante um período específico, enquanto a prevalência representa o total de casos existentes (novos e antigos) em um momento dado. A incidência é útil para estudar etiologia, e a prevalência para planejamento de serviços de saúde. No Brasil, o SINAN utiliza essas medidas para doenças notificáveis.', 'saude_coletiva', 'Epidemiologia', 'Medidas epidemiológicas básicas', ARRAY['incidencia','prevalencia','epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é um estudo de coorte em epidemiologia?', 'Estudo de coorte é observacional prospectivo ou retrospectivo onde grupos expostos e não expostos a um fator de risco são seguidos para observar a ocorrência de desfechos. Vantagens: permite calcular risco relativo. Desvantagens: caro e demorado. Exemplo: estudo de Framingham para doenças cardiovasculares.', 'saude_coletiva', 'Epidemiologia', 'Tipos de estudos', ARRAY['coorte','observacional','risco_relativo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina odds ratio e como é calculado.', 'Odds ratio (OR) é uma medida de associação em estudos caso-controle, calculada como (odds de exposição nos casos) / (odds de exposição nos controles). OR >1 indica associação positiva. Não é risco relativo, mas aproxima-se em doenças raras. Usado em logística para modelagem.', 'saude_coletiva', 'Epidemiologia', 'Medidas de associação', ARRAY['odds_ratio','caso_controle','associacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da epidemiologia descritiva?', 'Epidemiologia descritiva descreve a distribuição de doenças por pessoa, lugar e tempo, usando variáveis demográficas. Não testa hipóteses causais, mas gera-as. Ferramentas: taxas, proporções. No Brasil, usada em boletins epidemiológicos do Ministério da Saúde.', 'saude_coletiva', 'Epidemiologia', 'Epidemiologia descritiva', ARRAY['distribuicao','pessoa_lugar_tempo','taxas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o conceito de causalidade em epidemiologia.', 'Causalidade envolve associação, temporalidade, força da associação, consistência, especificidade, plausibilidade biológica, dose-resposta, reversibilidade. Critérios de Hill auxiliam julgamento. Nem toda associação é causal; confusores devem ser controlados.', 'saude_coletiva', 'Epidemiologia', 'Causalidade', ARRAY['Hill','associacao','confusores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são indicadores epidemiológicos?', 'Indicadores são medidas quantitativas da saúde populacional, como mortalidade infantil, cobertura vacinal. Classificados em de saúde, morbidade, mortalidade. No SUS, monitorados pelo DATASUS para avaliação de políticas públicas.', 'saude_coletiva', 'Epidemiologia', 'Indicadores', ARRAY['mortalidade','morbidade','DATASUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina viés em estudos epidemiológicos.', 'Viés é erro sistemático que distorce a estimativa da associação verdadeira. Tipos: seleção (amostragem), informação (recall), confusão. Prevenção: randomização, cegamento, matching. Impacta validade interna dos estudos.', 'saude_coletiva', 'Epidemiologia', 'Viés e validade', ARRAY['vies','selecao','confusao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da curva epidêmica?', 'Curva epidêmica plota casos novos ao longo do tempo, ajudando a identificar padrões (pico, platô), surtos, endemias. Útil em vigilância para intervenções. No Brasil, usada em arboviroses como dengue.', 'saude_coletiva', 'Epidemiologia', 'Análise temporal', ARRAY['curva_epidemica','surtos','arboviroses']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a taxa de ataque em surtos?', 'Taxa de ataque é a proporção de casos entre os expostos em um surto. Calculada como (casos / expostos) x 100. Ajuda a identificar fonte de infecção. Exemplo: em surto alimentar, calcula-se por alimento consumido.', 'saude_coletiva', 'Epidemiologia', 'Investigação de surtos', ARRAY['taxa_de_ataque','surtos','expostos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o modelo de agente-hospedeiro-ambiente.', 'Modelo epidemiológico clássico: agente (causador), hospedeiro (susceptível), ambiente (facilitador). Interações determinam ocorrência de doenças. Aplicado em controle de vetores, imunização, saneamento no contexto brasileiro.', 'saude_coletiva', 'Epidemiologia', 'Modelos epidemiológicos', ARRAY['agente','hospedeiro','ambiente']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema de Informação de Agravos de Notificação (SINAN)?', 'SINAN é o sistema nacional para notificação compulsória de doenças e agravos. Registra dados epidemiológicos para vigilância. Mantido pelo Ministério da Saúde, integra dados de municípios para análise de tendências e intervenções.', 'saude_coletiva', 'Vigilância em saúde', 'Sistemas de vigilância', ARRAY['SINAN','notificacao','agravos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina vigilância epidemiológica ativa e passiva.', 'Vigilância ativa: busca proativa de casos por equipes de saúde. Passiva: notificação espontânea por profissionais. Ativa é mais completa mas custosa; passiva, mais ampla. No Brasil, combinadas em doenças como tuberculose.', 'saude_coletiva', 'Vigilância em saúde', 'Tipos de vigilância', ARRAY['ativa','passiva','epidemiologica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são as etapas da investigação de surto?', 'Etapas: confirmação do surto, descrição (quem, o quê, onde, quando), formulação de hipótese, testes (laboratorial, ambiental), controle (intervenções), comunicação. Baseado em manual do MS para resposta rápida.', 'saude_coletiva', 'Vigilância em saúde', 'Investigação de surtos', ARRAY['surto','hipotese','controle']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Lista Nacional de Notificação Compulsória?', 'Lista A (imediata) e B (até 24h ou semanal) de agravos como meningite, dengue, violência. Obrigatória para profissionais de saúde. Atualizada pela Portaria MS nº 204/2016, visa detecção precoce e controle.', 'saude_coletiva', 'Vigilância em saúde', 'Notificação compulsória', ARRAY['lista_A','lista_B','Portaria_204']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o papel da vigilância sanitária.', 'Vigilância sanitária monitora riscos à saúde pública em alimentos, medicamentos, serviços. Fiscaliza ANVISA e secretarias estaduais. Inclui inspeções, análises laboratoriais para prevenir contaminações e fraudes.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância sanitária', ARRAY['ANVISA','alimentos','medicamentos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema Nacional de Vigilância em Saúde (SNVS)?', 'SNVS integra vigilância epidemiológica, sanitária e ambiental. Coordena ações federais, estaduais, municipais via Ministério da Saúde. Facilita intercâmbio de dados para decisões em saúde pública.', 'saude_coletiva', 'Vigilância em saúde', 'Sistemas integrados', ARRAY['SNVS','epidemiologica','sanitaria']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina sentinela em vigilância.', 'Unidades sentinela são locais selecionados para monitoramento contínuo de agravos, como síndrome gripal em UPAs. Fornecem dados representativos com menor custo. Usado em influenza e COVID-19 no Brasil.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância sentinela', ARRAY['sentinela','sindrome_gripal','monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da vigilância ambiental?', 'Monitora fatores ambientais como qualidade da água, ar, solo para prevenir doenças. Integra SVS com agências como IBAMA. No Brasil, crucial para endemias como leptospirose em áreas urbanas.', 'saude_coletiva', 'Vigilância em saúde', 'Vigilância ambiental', ARRAY['agua','ar','leptospirose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são eventos de importância à saúde pública (EISP)?', 'EISP são situações incomuns ou graves como surtos, desastres, que demandam notificação imediata ao CIEVS. Portaria MS nº 2.041/2018 regula para resposta rápida nacional e internacional (OMS).', 'saude_coletiva', 'Vigilância em saúde', 'Eventos EISP', ARRAY['EISP','CIEVS','Portaria_2041']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o Centro de Informações Estratégicas em Vigilância em Saúde (CIEVS).', 'CIEVS são centros regionais para análise de riscos, investigação e articulação em vigilância. Recebem notificações de EISP e coordenam respostas. Implantados em capitais para agilidade em emergências.', 'saude_coletiva', 'Vigilância em saúde', 'CIEVS', ARRAY['CIEVS','riscos','emergencias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os princípios do SUS?', 'Universalidade (todos têm direito), equidade (atendimento conforme necessidade), integralidade (promoção, prevenção, tratamento, reabilitação), descentralização, participação social via conselhos. Base da Constituição 1988 e Lei 8.080/90.', 'saude_coletiva', 'SUS e políticas de saúde', 'Princípios do SUS', ARRAY['universalidade','equidade','integralidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Lei Orgânica da Saúde (Lei 8.080/90)?', 'Lei 8.080/90 cria o SUS, define organização, competências federativas, participação comunitária. Complementada pela 8.142/90 (conselhos). Fundamento legal para políticas públicas de saúde no Brasil.', 'saude_coletiva', 'SUS e políticas de saúde', 'Legislação SUS', ARRAY['Lei_8080','organizacao','conselhos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o financiamento do SUS.', 'Financiamento tripartite: União (mínimo 15% receita corrente líquida desde EC 86/2015), estados (12%), municípios (15%). Fontes: impostos, contribuições. Desafios: subfinanciamento crônico afeta expansão de serviços.', 'saude_coletiva', 'SUS e políticas de saúde', 'Financiamento', ARRAY['EC_86','tripartite','subfinanciamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são as Conferências de Saúde?', 'Conferências são instâncias deliberativas bienais (nacional, estadual, municipal) para debater políticas. Participação popular elege prioridades. Ex: 16ª CNS em 2019 discutiu desafios do SUS.', 'saude_coletiva', 'SUS e políticas de saúde', 'Participação social', ARRAY['conferencias','CNS','prioridades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina Conselho Nacional de Saúde (CNS).', 'CNS é instância máxima de controle social no SUS, paritário (governo/sociedade). Fiscaliza políticas, aprova diretrizes. Criado pela Lei 8.142/90, representa 50% não governamentais para democratização.', 'saude_coletiva', 'SUS e políticas de saúde', 'Controle social', ARRAY['CNS','paritario','fiscalizacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o Pacto pela Saúde?', 'Pacto pela Saúde (2006) reorganiza SUS com bases: Pacto pela Vida (qualidade), Gestão (eficiência), Atenção à Saúde (acesso). Melhora coordenação federativa para equidade e integralidade.', 'saude_coletiva', 'SUS e políticas de saúde', 'Pactos e reformas', ARRAY['Pacto_pela_Saude','gestao','atencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Política Nacional de Atenção Básica (PNAB)?', 'PNAB (2017) define atenção primária como porta de entrada do SUS, com equipes multidisciplinares. Expande ESF, NASF. Objetivo: 80% resolutividade em APS para promoção e prevenção.', 'saude_coletiva', 'SUS e políticas de saúde', 'Políticas nacionais', ARRAY['PNAB','ESF','resolutividade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Emenda Constitucional 29/2000.', 'EC 29/2000 estabelece percentuais mínimos de aplicação em saúde: estados 12% e municípios 15% de receitas próprias. Para a União, a EC 29 vinculou o gasto ao valor do ano anterior corrigido pela variação nominal do PIB — o piso de 15% da receita corrente líquida para a União veio somente com a EC 86/2015 (consolidado pela EC 95/2016). Visa garantir recursos estáveis, mas enfrenta judicializações por descumprimento.', 'saude_coletiva', 'SUS e políticas de saúde', 'Emendas constitucionais', ARRAY['EC_29','percentuais','recursos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Programa Saúde da Família (PSF)?', 'PSF, agora ESF, é estratégia de APS com equipes (médico, enfermeiro, agentes comunitários) vinculadas a territórios. Cobertura >70% municípios. Foco em família, comunidade para redução de desigualdades.', 'saude_coletiva', 'SUS e políticas de saúde', 'Estratégias SUS', ARRAY['PSF','ESF','agentes_comunitarios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da Reforma Sanitária no SUS?', 'Reforma Sanitária (década 80) articulou 8ª Conferência e Constituição 88, criando SUS universal. Movimentos sociais e acadêmicos pressionaram por equidade. Legado: sistema público apesar de resistências neoliberais.', 'saude_coletiva', 'SUS e políticas de saúde', 'Reforma Sanitária', ARRAY['8a_Conferencia','Constituicao_88','equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Estratégia Saúde da Família (ESF)?', 'ESF é modelo de APS com equipes multiprofissionais atendendo famílias cadastradas. Aborda determinantes sociais, promove saúde. No Brasil, cobre ~76% da população (2024), reduzindo hospitalizações por condições sensíveis.', 'saude_coletiva', 'Atenção primária', 'Estratégia Saúde da Família', ARRAY['ESF','APS','cadastramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina atenção primária à saúde (APS).', 'APS é primeiro nível de contato, coordenando cuidados integrais, resolutivos e longitudinais. Baseada em princípios de equidade, participação. No SUS, via UBS e ESF, resolve 80-90% demandas comuns.', 'saude_coletiva', 'Atenção primária', 'Conceitos básicos', ARRAY['APS','UBS','integralidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os atributos da APS segundo Starfield?', 'Acessibilidade, continuidade, coordenação de cuidados, integralidade, orientação familiar/comunitária, gatekeeping. Essenciais para eficiência do sistema. Aplicados na PNAB para fortalecimento do SUS.', 'saude_coletiva', 'Atenção primária', 'Atributos APS', ARRAY['Starfield','acessibilidade','gatekeeping']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique o Núcleo Ampliado de Saúde da Família (NASF).', 'NASF (2008) integrava profissionais (nutricionista, psicólogo, etc.) às equipes ESF para apoio matricial. Em 2020, renomeado NASF-AB; a partir de 2023/2024, transição para programa eMulti (Equipes Multiprofissionais). Mantém lógica de apoio matricial e amplia resolutividade em APS.', 'saude_coletiva', 'Atenção primária', 'NASF', ARRAY['NASF','matricial','multiprofissional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Atenção Básica no SUS?', 'Atenção Básica é ordenadora da rede, incluindo promoção, prevenção, diagnóstico, tratamento de alta frequência. Via UBS, ESF, programas como Hiperdia. Responsável por 80% ações em saúde pública brasileira.', 'saude_coletiva', 'Atenção primária', 'Atenção Básica', ARRAY['UBS','Hiperdia','prevencao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos Agentes Comunitários de Saúde (ACS)?', 'ACS visitam domicílios, educam famílias, identificam riscos, mobilizam comunidade. Formação técnica, vínculo territorial. Essenciais na ESF para equidade, cobrindo áreas vulneráveis no SUS.', 'saude_coletiva', 'Atenção primária', 'ACS', ARRAY['ACS','visitas_domiciliares','comunidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Política Nacional de Promoção da Saúde (PNPS).', 'PNPS (2006) foca em estilos de vida saudáveis, redução de riscos (tabagismo, sedentarismo). Ações intersetoriais via Pacto pela Vida. Integra APS para empoderamento comunitário e prevenção de NCDs.', 'saude_coletiva', 'Atenção primária', 'Promoção da saúde', ARRAY['PNPS','estilos_de_vida','NCDs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são condições sensíveis à atenção primária?', 'Condições como hipertensão, diabetes, pneumonia que poderiam ser prevenidas/tratadas em APS, evitando hospitalizações. Indicador ICSAP usado no SUS para avaliar efetividade da atenção básica.', 'saude_coletiva', 'Atenção primária', 'Avaliação APS', ARRAY['ICSAP','hospitalizacoes','hipertensao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina abordagem familiar e comunitária na APS.', 'Abordagem familiar considera dinâmica familiar como unidade de cuidado; comunitária, contexto social e determinantes. Ferramentas: cadastramento, visitas. Promove integralidade e resolutividade no SUS.', 'saude_coletiva', 'Atenção primária', 'Abordagens', ARRAY['familiar','comunitaria','determinantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é vigilância em saúde do trabalhador (Visat)?', 'Visat monitora riscos ocupacionais, agravos relacionados ao trabalho. Integra SES, CEREST. Notificação via SINAN para doenças ocupacionais. Baseada na NR-4 e Lei 8.213/91 para prevenção e reabilitação.', 'saude_coletiva', 'Saúde do trabalhador', 'Vigilância Visat', ARRAY['Visat','riscos','SINAN']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as Normas Regulamentadoras (NRs) principais?', 'NRs são 38 normas do MTE para segurança e saúde no trabalho. Ex: NR-1 (disposições gerais + PGR), NR-7 (PCMSO), NR-15 (atividades insalubres). Nota: o antigo PPRA (NR-9) foi substituído pelo PGR (NR-1) desde jan/2022. Obrigatórias para empresas, fiscalizadas por auditores.', 'saude_coletiva', 'Saúde do trabalhador', 'Normas Regulamentadoras', ARRAY['NRs','MTE','PPRA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina Programa de Gerenciamento de Riscos Ocupacionais (PGR).', 'PGR substitui PPRA e PCMSO desde 2022 (NR-1). Identifica, avalia, controla riscos físicos, químicos, biológicos, ergonômicos, acidentes. Obrigatório para todas empresas, integra gestão SST.', 'saude_coletiva', 'Saúde do trabalhador', 'PGR e PCMSO', ARRAY['PGR','riscos','SST']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são os Centros de Referência em Saúde do Trabalhador (CEREST)?', 'CEREST são unidades especializadas no SUS para diagnóstico, tratamento, reabilitação de agravos laborais. Oferecem Visat, formação. Regionalizados para articulação com APS e vigilância.', 'saude_coletiva', 'Saúde do trabalhador', 'CEREST', ARRAY['CEREST','agravos','reabilitacao']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Lista de Doenças Relacionadas ao Trabalho (LDT).', 'LDT (Portaria MS 1.339/1999, atualizada) classifica doenças ocupacionais para nexo causal em benefícios previdenciários. Inclui LER/DORT, asbestose, silicose. Auxilia notificação e indenizações.', 'saude_coletiva', 'Saúde do trabalhador', 'Doenças ocupacionais', ARRAY['LDT','LER_DORT','nexo_causal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da CIPA?', 'CIPA (Comissão Interna de Prevenção de Acidentes) representa trabalhadores e empregadores para identificar riscos, promover segurança. Obrigatória em empresas >20 funcionários (NR-5). Realiza SIPAT.', 'saude_coletiva', 'Saúde do trabalhador', 'CIPA e SESMT', ARRAY['CIPA','SIPAT','NR_5']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Programa de Controle Médico de Saúde Ocupacional (PCMSO)?', 'PCMSO (NR-7) monitora saúde trabalhadores via exames admissionais, periódicos, demissionais. Detecta precocemente agravos. Integra PGR, realizado por médicos do trabalho em empresas.', 'saude_coletiva', 'Saúde do trabalhador', 'PCMSO', ARRAY['PCMSO','exames','NR_7']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Defina insalubridade e periculosidade.', 'Insalubridade: exposição a agentes nocivos (ruído, químicos) com adicional de 10-40% salário (NR-15). Periculosidade: risco acentuado de vida (explosivos, eletricidade) com 30% adicional (NR-16). Fiscalizado MTE.', 'saude_coletiva', 'Saúde do trabalhador', 'Adicionais', ARRAY['insalubridade','periculosidade','NR_15']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Explique a Política Nacional de Saúde do Trabalhador (PNSST).', 'PNSST (1999, atualizada) integra saúde do trabalhador no SUS, com ênfase em prevenção, promoção, vigilância. Baseada em direitos humanos, participação. Enfrenta informalidade e precarização laboral.', 'saude_coletiva', 'Saúde do trabalhador', 'Políticas nacionais', ARRAY['PNSST','prevencao','informalidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é nexo causal em saúde do trabalho?', 'Nexo causal liga agravo à atividade laboral, presumido para LDT ou investigado por perito INSS. Essencial para aposentadoria especial, estabilidade gestante. Visat auxilia comprovação via notificações.', 'saude_coletiva', 'Saúde do trabalhador', 'Nexo causal', ARRAY['nexo_causal','INSS','LDT']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a média aritmética em bioestatística?', 'A média aritmética é calculada somando todos os valores de uma amostra e dividindo pelo número de observações, representando o valor central dos dados. É uma medida de tendência central apropriada para distribuições simétricas e intervalos de dados contínuos. No entanto, é sensível a valores atípicos, podendo distorcer os resultados em distribuições assimétricas.', 'saude_coletiva', 'Bioestatística', 'média', ARRAY['tendência central', 'estatística descritiva', 'dados contínuos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a definição de mediana em um conjunto de dados?', 'A mediana é o valor que divide o conjunto de dados ordenados ao meio, com 50% dos valores abaixo e 50% acima dela. É uma medida robusta de tendência central, ideal para dados assimétricos ou com outliers. Em bioestatística, é frequentemente usada em análises de renda ou tempos de sobrevivência.', 'saude_coletiva', 'Bioestatística', 'mediana', ARRAY['tendência central', 'dados ordenados', 'outliers']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que representa a moda em estatística?', 'A moda é o valor que ocorre com maior frequência em um conjunto de dados, podendo ser unimodal, bimodal ou multimodal. É útil para dados categóricos nominais, como tipos de doenças mais comuns em uma população. Diferente da média e mediana, não é afetada pela magnitude dos valores.', 'saude_coletiva', 'Bioestatística', 'moda', ARRAY['frequência', 'dados categóricos', 'distribuição']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como se calcula o desvio padrão?', 'O desvio padrão mede a dispersão dos dados em relação à média, calculado como a raiz quadrada da variância. Em bioestatística, indica a variabilidade em medidas como pressão arterial em uma população. Valores altos sugerem maior heterogeneidade nos dados.', 'saude_coletiva', 'Bioestatística', 'desvio padrão', ARRAY['dispersão', 'variância', 'medidas de variabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o intervalo de confiança em testes estatísticos?', 'O intervalo de confiança é uma faixa de valores que provavelmente contém o parâmetro populacional verdadeiro, com um nível de confiança como 95%. Em estudos epidemiológicos, estima a precisão de uma média amostral. Um intervalo estreito indica maior precisão na estimativa.', 'saude_coletiva', 'Bioestatística', 'intervalo de confiança', ARRAY['estimativa', 'nível de confiança', 'epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quando se usa o teste t de Student?', 'O teste t de Student compara médias de duas amostras independentes ou uma amostra com um valor conhecido, assumindo distribuição normal. É comum em bioestatística para avaliar diferenças em grupos de tratamento versus controle. Requer verificação de normalidade e homogeneidade de variâncias.', 'saude_coletiva', 'Bioestatística', 'teste t', ARRAY['comparação de médias', 'distribuição normal', 'amostras independentes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a aplicação do teste qui-quadrado?', 'O teste qui-quadrado avalia associações entre variáveis categóricas, como relação entre tabagismo e câncer de pulmão. É usado para testes de independência ou bondade de ajuste em tabelas de contingência. Assume amostras grandes e frequências esperadas mínimas de 5.', 'saude_coletiva', 'Bioestatística', 'qui-quadrado', ARRAY['variáveis categóricas', 'associação', 'tabelas de contingência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa o valor-p em uma análise estatística?', 'O valor-p é a probabilidade de obter resultados tão extremos quanto os observados, assumindo a nulidade verdadeira, indicando significância estatística. Um valor-p menor que 0,05 sugere rejeição da hipótese nula em convenção. Não mede o tamanho do efeito, apenas a compatibilidade com a nulidade.', 'saude_coletiva', 'Bioestatística', 'valor-p', ARRAY['significância', 'hipótese nula', 'probabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os tipos de amostragem em estudos epidemiológicos?', 'A amostragem aleatória simples garante igual chance a cada elemento, reduzindo viés, enquanto estratificada divide a população em subgrupos. Em bioestatística, a amostragem por conglomerados é útil para populações dispersas como municípios. O tamanho da amostra deve considerar margem de erro e confiança.', 'saude_coletiva', 'Bioestatística', 'amostragem', ARRAY['aleatória', 'estratificada', 'viés de seleção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é viés em pesquisa estatística?', 'Viés é um erro sistemático que distorce a estimativa do parâmetro populacional, como viés de seleção ao excluir grupos. Em estudos de saúde, o viés de informação ocorre na coleta de dados enviesada. Medidas preventivas incluem randomização e cegamento para minimizar impactos.', 'saude_coletiva', 'Bioestatística', 'viés', ARRAY['erro sistemático', 'seleção', 'informação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que foi a Reforma Psiquiátrica no Brasil?', 'A Reforma Psiquiátrica, iniciada na década de 1980, visou substituir manicômios por serviços comunitários, promovendo direitos humanos. Baseada na Lei 10.216/2001, enfatiza o tratamento em liberdade e reinserção social. Influenciada pelo movimento antimanicomial, integrou a saúde mental ao SUS.', 'saude_coletiva', 'Saúde mental coletiva', 'reforma psiquiátrica', ARRAY['antimanicomial', 'direitos humanos', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos CAPS na rede de saúde mental?', 'Os Centros de Atenção Psicossocial (CAPS) oferecem atendimento integral em saúde mental, com modalidades como CAPS I, II, III e AD para álcool e drogas. Funcionam como portas de entrada para casos graves, evitando hospitalizações desnecessárias. Integram a RAPS, promovendo reabilitação psicossocial.', 'saude_coletiva', 'Saúde mental coletiva', 'CAPS', ARRAY['atenção psicossocial', 'RAPS', 'reabilitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Rede de Atenção Psicossocial (RAPS)?', 'A RAPS é o conjunto de serviços e dispositivos para assistência em saúde mental no SUS, incluindo CAPS, leitos em hospitais gerais e serviços residenciais. Objetiva o cuidado integral e territorializado, com articulação entre atenção primária e especializada. Regulamentada pela Portaria GM/MS nº 3.088/2011.', 'saude_coletiva', 'Saúde mental coletiva', 'RAPS', ARRAY['rede de atenção', 'SUS', 'cuidado integral']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a saúde mental é integrada à atenção primária?', 'Na atenção primária, a saúde mental é abordada pela Estratégia Saúde da Família, com matriciamento para suporte a equipes. Identifica transtornos comuns como depressão e ansiedade precocemente. Promove acolhimento e referência para serviços especializados quando necessário.', 'saude_coletiva', 'Saúde mental coletiva', 'saúde mental na atenção primária', ARRAY['ESF', 'matriciamento', 'transtornos comuns']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os transtornos mentais comuns na população?', 'Transtornos comuns incluem depressão, ansiedade e estresse, afetando cerca de 20% da população adulta brasileira. São gerenciáveis na atenção básica com intervenções psicossociais e farmacológicas. Fatores de risco envolvem violência, pobreza e eventos estressantes.', 'saude_coletiva', 'Saúde mental coletiva', 'transtornos comuns', ARRAY['depressão', 'ansiedade', 'atenção básica']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a abordagem ao uso de substâncias na saúde coletiva?', 'O uso de substâncias como álcool e drogas é tratado como questão de saúde pública, com ênfase em redução de danos e CAPS AD. Políticas incluem prevenção escolar e tratamento comunitário, evitando criminalização. Integra a RAPS para reabilitação integral.', 'saude_coletiva', 'Saúde mental coletiva', 'uso de substâncias', ARRAY['redução de danos', 'CAPS AD', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é matriciamento em saúde mental?', 'Matriciamento é o processo de apoio e supervisão de equipes de atenção primária por especialistas em saúde mental, fortalecendo a resolução local. Promove discussões de casos e capacitação contínua. Essencial para a integração entre níveis de atenção no SUS.', 'saude_coletiva', 'Saúde mental coletiva', 'matriciamento', ARRAY['apoio matricial', 'capacitação', 'integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como manejar crises em saúde mental coletiva?', 'O manejo de crises envolve acolhimento imediato em UPAs ou CAPS, priorizando não farmacologização excessiva e avaliação de risco suicida. Usa estratégias de desescalada e referência para internação breve se necessário. Enfatiza direitos do usuário e rede de apoio familiar.', 'saude_coletiva', 'Saúde mental coletiva', 'crise', ARRAY['acolhimento', 'risco suicida', 'desescalada']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa desinstitucionalização na psiquiatria?', 'Desinstitucionalização é o processo de fechamento de manicômios e transferência de cuidados para serviços comunitários, promovendo autonomia. No Brasil, avançou com a Reforma Psiquiátrica, reduzindo leitos psiquiátricos em 70%. Desafios incluem moradias assistidas e inclusão social.', 'saude_coletiva', 'Saúde mental coletiva', 'desinstitucionalização', ARRAY['reforma psiquiátrica', 'comunitário', 'autonomia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os princípios da Lei 10.216/2001?', 'A Lei 10.216/2001 institui a proteção e os direitos das pessoas com transtornos mentais, garantindo tratamento humanizado e livre de constrangimentos. Proíbe práticas que desrespeitem a dignidade do paciente. A eletroconvulsoterapia (ECT) permanece indicação terapêutica legítima quando realizada com anestesia e consentimento. Integra a saúde mental ao SUS como direito universal.', 'saude_coletiva', 'Saúde mental coletiva', 'lei 10.216', ARRAY['direitos', 'tratamento humanizado', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os impactos da poluição do ar na saúde pública?', 'A poluição do ar, de fontes como veículos e indústrias, causa doenças respiratórias e cardiovasculares, com 7 milhões de mortes anuais globalmente. No Brasil, afeta populações urbanas, aumentando internações por asma e infarto. Medidas incluem monitoramento e padrões de qualidade pelo CONAMA (Resolução 491/2018).', 'saude_coletiva', 'Saúde ambiental', 'poluição do ar', ARRAY['doenças respiratórias', 'monitoramento', 'ANVISA']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como avaliar a qualidade da água para consumo humano?', 'A qualidade da água é avaliada por parâmetros físico-químicos, microbiológicos e sensoriais, conforme Portaria GM/MS nº 888/2021. Indicadores incluem coliformes totais e turbidez, visando prevenir diarreias e hepatites. Vigilância sanitária realiza análises periódicas em sistemas públicos.', 'saude_coletiva', 'Saúde ambiental', 'qualidade da água', ARRAY['parâmetros microbiológicos', 'vigilância sanitária', 'portaria 888']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância do saneamento básico no Brasil?', 'O saneamento básico, incluindo água, esgoto e lixo, reduz mortalidade por doenças infecciosas em até 90%. No SUS, é pilar para equidade em saúde, com metas do Plano Nacional de Saneamento. Cobertura incompleta afeta 35 milhões de brasileiros com água inadequada.', 'saude_coletiva', 'Saúde ambiental', 'saneamento', ARRAY['doenças infecciosas', 'equidade', 'plano nacional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como gerenciar resíduos sólidos em saúde coletiva?', 'A gestão de resíduos sólidos segue a PNRS, com coleta seletiva, reciclagem e tratamento para minimizar impactos ambientais. Em saúde, resíduos infectantes requerem incineração ou autoclave para prevenir infecções. Educação ambiental promove redução no fonte.', 'saude_coletiva', 'Saúde ambiental', 'resíduos sólidos', ARRAY['PNRS', 'reciclagem', 'resíduos infectantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os riscos dos agrotóxicos à saúde humana?', 'Agrotóxicos causam intoxicações agudas e crônicas, como câncer e distúrbios endócrinos, com 500 mil casos anuais no mundo. No Brasil, maior consumidor global, vigilância monitora resíduos em alimentos pela ANVISA. Políticas visam agricultura sustentável e redução de uso.', 'saude_coletiva', 'Saúde ambiental', 'agrotóxicos', ARRAY['intoxicação', 'ANVISA', 'sustentabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como as mudanças climáticas afetam a saúde pública?', 'Mudanças climáticas intensificam eventos extremos, vetores de doenças como dengue e desnutrição, afetando 250 mil mortes anuais até 2030. No Brasil, impacta populações vulneráveis em áreas costeiras e amazônicas. Estratégias incluem adaptação e mitigação via SUS.', 'saude_coletiva', 'Saúde ambiental', 'mudanças climáticas', ARRAY['vetores', 'adaptação', 'vulnerabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são zoonoses e exemplos no Brasil?', 'Zoonoses são doenças transmissíveis de animais a humanos, como raiva e leptospirose, com reservatórios silvestres ou domésticos. Vigilância epidemiológica monitora surtos em áreas urbanas periurbanas. Prevenção envolve vacinação animal e controle de roedores.', 'saude_coletiva', 'Saúde ambiental', 'zoonoses', ARRAY['transmissão', 'vigilância', 'prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais estratégias de controle de vetores?', 'Controle de vetores como Aedes aegypti usa eliminação de criadouros, inseticidas e armadilhas para arboviroses. No Brasil, o Programa Nacional de Controle integra educação comunitária. Resistência inseticida requer monitoramento contínuo.', 'saude_coletiva', 'Saúde ambiental', 'vetores', ARRAY['Aedes', 'criadouros', 'resistência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a vigilância ambiental em saúde?', 'Vigilância ambiental monitora fatores ambientais como qualidade do ar e água para prevenir riscos à saúde. Integra ações intersetoriais com meio ambiente e saneamento. No SUS, apoia decisões em políticas públicas preventivas.', 'saude_coletiva', 'Saúde ambiental', 'vigilância ambiental', ARRAY['fatores de risco', 'intersetorial', 'SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como responder a desastres ambientais na saúde?', 'Resposta a desastres envolve planos de contingência, evacuação e assistência médica imediata para lesões e infecções. No Brasil, o COBRADE coordena ações com SUS para enchentes e secas. Foco em populações vulneráveis e recuperação pós-desastre.', 'saude_coletiva', 'Saúde ambiental', 'desastres', ARRAY['contingência', 'COBRADE', 'recuperação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas estão no calendário vacinal infantil?', 'O calendário vacinal infantil inclui BCG, hepatite B, penta, VIP/VOP, rotavírus, pneumocócica e meningocócica, administradas em salas de vacinas do SUS. Protege contra 20 doenças graves nos primeiros anos de vida. Esquema é gratuito e universal para cobertura ampla.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'calendário vacinal', ARRAY['infantil', 'SUS', 'cobertura']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a cadeia de frio no PNI?', 'A cadeia de frio mantém vacinas em temperaturas de 2-8°C desde produção até aplicação, usando geladeiras e termômetros. Essencial para preservar potência de vacinas termolábeis como varicela. Monitoramento diário previne perdas e falhas imunológicas.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'cadeia de frio', ARRAY['temperatura', 'armazenamento', 'monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como calcular a cobertura vacinal?', 'Cobertura vacinal é o percentual de doses aplicadas na população-alvo, visando 95% para imunidade coletiva. Calculada via SI-PNI com dados de salas de vacinas. Baixa cobertura aumenta surtos, como sarampo em 2019 no Brasil.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'cobertura vacinal', ARRAY['percentual', 'imunidade coletiva', 'SI-PNI']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o EAPV no programa de imunizações?', 'O Evento Adverso Pós-Vacinação (EAPV) é qualquer ocorrência médica indesejada após vacinação, não necessariamente causal. A vigilância de EAPV envolve notificação compulsória, investigação e classificação de causalidade. O monitoramento contínuo pelo PNI garante a segurança do programa de imunizações.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'EAPV', ARRAY['avaliação', 'amostragem', 'IBGE']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as características das campanhas de vacinação?', 'Campanhas de vacinação são periódicas para reforço ou surtos, como multivacinação para inadimplentes. Mobilizam equipes de saúde e mídia para alta adesão. No PNI, exemplos incluem influenza anual e HPV para adolescentes.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'campanhas', ARRAY['reforço', 'adesão', 'multivacinação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que causa hesitação vacinal e como combatê-la?', 'Hesitação vacinal surge de desinformação, desconfiança e mitos, reduzindo coberturas como no caso do sarampo. Combate via educação em saúde, comunicação clara e envolvimento comunitário. PNI promove confiança com evidências científicas.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'hesitação vacinal', ARRAY['desinformação', 'educação', 'confiança']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o funcionamento da sala de vacinas?', 'A sala de vacinas é unidade básica equipada para imunização, com registro no SI-PNI e manutenção da cadeia de frio. Realiza aplicação, orientação e notificação de eventos adversos. Deve seguir normas de biossegurança e acessibilidade.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'sala de vacinas', ARRAY['imunização', 'SI-PNI', 'biossegurança']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que compõe a rede de frio no PNI?', 'A rede de frio inclui armazéns regionais, municipais e salas de vacinas com equipamentos refrigerados e veículos para transporte. Garante distribuição equitativa de imunobiológicos. Treinamento de profissionais é obrigatório para manutenção.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'rede de frio', ARRAY['distribuição', 'equipamentos', 'treinamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do SI-PNI nos imunizações?', 'O SI-PNI é o sistema informatizado que registra doses, calcula coberturas e gera relatórios para o PNI. Facilita rastreabilidade de lotes e alertas de estoque. Integra dados nacionais para monitoramento epidemiológico.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'SI-PNI', ARRAY['registro', 'cobertura', 'rastreabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as metas do PNI para coberturas vacinais?', 'Metas do PNI visam 95% de cobertura para vacinas básicas, como poliomielite e sarampo, para erradicação. Monitoradas anualmente, ajustam estratégias em áreas de risco. Contribui para redução de incidência de doenças preveníveis.', 'saude_coletiva', 'PNI (Programa Nacional de Imunizações)', 'metas', ARRAY['95%', 'erradicação', 'monitoramento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Sistema de Informações sobre Mortalidade (SIM)?', 'O SIM registra óbitos no Brasil via DATASUS, codificados pela CID-10 para análise de causas. Fornece indicadores como coeficiente de mortalidade para planejamento em saúde. Cobertura nacional desde 1975, com integração ao SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIM', ARRAY['óbitos', 'CID-10', 'DATASUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a função do SINASC?', 'O SINASC declara nascimentos vivos, coletando dados sobre peso, Apgar e assistência pré-natal para indicadores neonatais. Gerenciado pelo DATASUS, identifica riscos como prematuridade. Essencial para políticas de saúde materno-infantil no SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SINASC', ARRAY['nascimentos', 'indicadores neonatais', 'materno-infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que registra o SINAN?', 'O SINAN notifica agravos de notificação compulsória, como dengue e tuberculose, para vigilância epidemiológica. Permite análise temporal e espacial de surtos via DATASUS. Facilita resposta rápida e investigação de casos.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SINAN', ARRAY['notificação', 'agravos', 'vigilância']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona o SIH/SUS?', 'O SIH autoriza e registra internações hospitalares no SUS, gerando AIH para pagamento e análise de produção. Monitora leitos e custos, com dados no DATASUS. Contribui para equidade no acesso a procedimentos complexos.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIH', ARRAY['internações', 'AIH', 'equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o propósito do SIA?', 'O SIA registra procedimentos ambulatoriais no SUS, como consultas e exames, para controle de produção e financiamento. Integra dados do DATASUS para indicadores de acesso primário. Auxilia na alocação de recursos por região.', 'saude_coletiva', 'Sistemas de informação em saúde', 'SIA', ARRAY['procedimentos', 'ambulatorial', 'financiamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o e-SUS?', 'O e-SUS é o sistema eletrônico para atenção primária, registrando atendimentos na ESF com prontuário digital. Padroniza dados para integração nacional via DATASUS. Melhora qualidade e continuidade do cuidado em saúde básica.', 'saude_coletiva', 'Sistemas de informação em saúde', 'e-SUS', ARRAY['prontuário digital', 'ESF', 'integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais benefícios do prontuário eletrônico em saúde?', 'O prontuário eletrônico facilita acesso a histórico clínico, reduz erros e integra sistemas como e-SUS. No SUS, promove interoperabilidade entre níveis de atenção. Privacidade é garantida pela LGPD, com treinamento obrigatório.', 'saude_coletiva', 'Sistemas de informação em saúde', 'prontuário eletrônico', ARRAY['interoperabilidade', 'LGPD', 'continuidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são indicadores em sistemas de informação em saúde?', 'Indicadores são métricas como taxa de mortalidade infantil derivadas de SIM e SINASC para avaliação de desempenho. Calculados pelo DATASUS, orientam políticas e monitoramento. Devem ser sensíveis, específicos e atualizáveis.', 'saude_coletiva', 'Sistemas de informação em saúde', 'indicadores', ARRAY['métricas', 'avaliação', 'políticas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do DATASUS nos sistemas de saúde?', 'O DATASUS desenvolve e mantém sistemas como SIM e SINAN, processando dados para tabulação e análise. Fornece tabnet para consultas públicas e relatórios epidemiológicos. Essencial para evidências em planejamento do SUS.', 'saude_coletiva', 'Sistemas de informação em saúde', 'DATASUS', ARRAY['processamento', 'tabnet', 'evidências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a tabulação de dados em saúde?', 'Tabulação organiza dados brutos em tabelas para análise descritiva, como frequências por idade em SINAN. Usada no DATASUS para gerar boletins e estudos. Facilita identificação de padrões e tendências epidemiológicas.', 'saude_coletiva', 'Sistemas de informação em saúde', 'tabulação', ARRAY['análise descritiva', 'padrões', 'epidemiológicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o financiamento do SUS no Brasil?', 'O Sistema Único de Saúde (SUS) é financiado por recursos da União, estados e municípios, com percentuais mínimos constitucionais: 15% da receita corrente líquida para municípios e 12% para estados. Esses recursos são usados para custear ações e serviços de saúde pública. A coordenação ocorre via Fundo Nacional de Saúde.', 'saude_coletiva', 'Gestão em saúde', 'Financiamento SUS', ARRAY['SUS','financiamento','gestão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os princípios do planejamento em saúde no SUS?', 'O planejamento em saúde no SUS segue princípios como descentralização, participação social e integralidade. Envolve a elaboração de planos de saúde tripartite (União, estados, municípios) com metas baseadas em necessidades populacionais. Ferramentas incluem o Plano Nacional de Saúde e pactuações regionais.', 'saude_coletiva', 'Gestão em saúde', 'Planejamento em saúde', ARRAY['SUS','planejamento','princípios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o PNASS?', 'O Programa Nacional de Avaliação de Serviços de Saúde (PNASS) é um instrumento de planejamento do SUS que define diretrizes, objetivos e metas para o período de quatro anos. Ele orienta a programação e alocação de recursos em saúde. É elaborado pelo Ministério da Saúde com participação de entes federativos.', 'saude_coletiva', 'Gestão em saúde', 'PNASS', ARRAY['SUS','PNASS','planejamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a acreditação em serviços de saúde?', 'A acreditação é um processo voluntário de avaliação externa da qualidade de serviços de saúde, realizado por organizações certificadoras. No SUS, promove a melhoria contínua e a segurança do paciente. Certificados como ONA (Organização Nacional de Acreditação) atestam conformidade com padrões nacionais.', 'saude_coletiva', 'Gestão em saúde', 'Acreditação', ARRAY['SUS','acreditação','qualidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a gestão de contratos em saúde pública?', 'A gestão de contratos em saúde pública inclui a elaboração, fiscalização e avaliação de convênios e contratos com prestadores de serviços. No SUS, segue normas da Lei 14.133/2021 (Nova Lei de Licitações) e princípios de transparência e eficiência. Visa garantir a prestação adequada de serviços à população.', 'saude_coletiva', 'Gestão em saúde', 'Gestão de contratos', ARRAY['SUS','contratos','gestão']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel do Fundo Nacional de Saúde no SUS?', 'O Fundo Nacional de Saúde (FNS) é o instrumento de programação e controle dos recursos federais para o SUS. Gerencia transferências constitucionais e repasses a estados e municípios. Assegura a equidade na distribuição de recursos com base em critérios populacionais e epidemiológicos.', 'saude_coletiva', 'Gestão em saúde', 'Financiamento SUS', ARRAY['SUS','FNS','recursos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como é estruturado o planejamento municipal de saúde?', 'O planejamento municipal de saúde é realizado via Plano Municipal de Saúde, alinhado ao PMS e PNASS. Inclui diagnóstico situacional, definição de metas e alocação de recursos. Envolve participação do Conselho de Saúde e segue diretrizes de regionalização.', 'saude_coletiva', 'Gestão em saúde', 'Planejamento em saúde', ARRAY['SUS','municipal','planejamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais os objetivos principais do PNASS?', 'Os objetivos do PNASS incluem fortalecer a gestão do SUS, ampliar o acesso a serviços e promover a equidade em saúde. Define ações para prevenção, promoção e reabilitação. É revisado periodicamente para atender novas demandas sanitárias.', 'saude_coletiva', 'Gestão em saúde', 'PNASS', ARRAY['SUS','PNASS','objetivos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a certificação de qualidade em saúde?', 'A certificação de qualidade em saúde é um reconhecimento formal de que uma instituição atende padrões mínimos de segurança e eficiência. No contexto SUS, integra-se à acreditação e avalia processos assistenciais. Contribui para a humanização e resolutividade dos serviços.', 'saude_coletiva', 'Gestão em saúde', 'Acreditação', ARRAY['SUS','certificação','qualidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais normas regem os contratos no SUS?', 'Os contratos no SUS são regidos pela Lei de Licitações (8.666/93) e pela Lei Complementar 141/2012, que exige aplicação mínima de recursos em saúde. Incluem cláusulas de monitoramento e sanções por descumprimento. Promovem a accountability na gestão pública.', 'saude_coletiva', 'Gestão em saúde', 'Gestão de contratos', ARRAY['SUS','normas','contratos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Carta de Ottawa na promoção da saúde?', 'A Carta de Ottawa, de 1986, é um marco da promoção da saúde, definindo-a como processo de capacitação da comunidade para atuar em saúde. Propõe cinco áreas de ação: construir políticas saudáveis, criar ambientes favoráveis, fortalecer ação comunitária, desenvolver habilidades pessoais e reorientar serviços. Influencia políticas globais da OMS.', 'saude_coletiva', 'Promoção da saúde', 'Carta de Ottawa', ARRAY['promoção','Ottawa','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o PNPS no Brasil?', 'A Política Nacional de Promoção da Saúde (PNPS), instituída em 2006, visa ampliar a promoção da saúde no SUS. Integra ações intersetoriais para enfrentar determinantes sociais da saúde. Prioriza agendas como alimentação saudável, atividade física e redução de violência.', 'saude_coletiva', 'Promoção da saúde', 'PNPS', ARRAY['SUS','PNPS','promoção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são cidades saudáveis?', 'Cidades saudáveis são iniciativas baseadas na Carta de Ottawa para criar ambientes urbanos que promovam saúde. Envolvem planejamento urbanístico, mobilidade sustentável e participação comunitária. No Brasil, integram-se ao SUS via projetos locais de promoção.', 'saude_coletiva', 'Promoção da saúde', 'Cidades saudáveis', ARRAY['cidades','saudáveis','urbanismo']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é ação intersetorial na saúde?', 'A ação intersetorial na promoção da saúde envolve articulação entre setores como educação, transporte e meio ambiente para enfrentar determinantes sociais. Promove políticas integradas e compartilhamento de responsabilidades. É princípio da PNPS e Carta de Ottawa.', 'saude_coletiva', 'Promoção da saúde', 'Ação intersetorial', ARRAY['intersetorial','promoção','determinantes']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que significa empoderamento em saúde?', 'O empoderamento em saúde é o processo de capacitação de indivíduos e comunidades para tomar decisões que afetem sua saúde. Baseia-se na educação e participação ativa. É uma estratégia chave da promoção da saúde segundo a OMS.', 'saude_coletiva', 'Promoção da saúde', 'Empoderamento', ARRAY['empoderamento','comunidade','capacitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais as cinco ações da Carta de Ottawa?', 'As cinco ações são: construir políticas públicas saudáveis, criar ambientes suportivos, fortalecer comunidades, desenvolver habilidades pessoais e reorientar serviços de saúde. Elas guiam intervenções globais em promoção. Aplicam-se a contextos locais no SUS.', 'saude_coletiva', 'Promoção da saúde', 'Carta de Ottawa', ARRAY['Ottawa','ações','promoção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a PNPS se integra ao SUS?', 'A PNPS integra-se ao SUS por meio de pactuações em planos de saúde e financiamento de ações territoriais. Enfatiza a Atenção Básica como porta de entrada. Avalia impactos via indicadores de saúde populacional.', 'saude_coletiva', 'Promoção da saúde', 'PNPS', ARRAY['SUS','PNPS','integração']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais projetos exemplificam cidades saudáveis no Brasil?', 'Projetos como o Programa Cidades Saudáveis em municípios brasileiros promovem hortas comunitárias e ciclovias. Visam reduzir desigualdades urbanas em saúde. São apoiados pela OPAS e Ministério da Saúde.', 'saude_coletiva', 'Promoção da saúde', 'Cidades saudáveis', ARRAY['cidades','projetos','saudáveis']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que a ação intersetorial é essencial?', 'A ação intersetorial é essencial porque a saúde depende de fatores além do setor saúde, como saneamento e educação. Evita abordagens fragmentadas e potencializa resultados. Exemplos incluem políticas de segurança alimentar.', 'saude_coletiva', 'Promoção da saúde', 'Ação intersetorial', ARRAY['intersetorial','essencial','setores']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover o empoderamento comunitário?', 'Promove-se via educação em saúde, conselhos participativos e projetos locais. Encoraja a liderança comunitária em diagnósticos e soluções. Resulta em maior adesão a práticas saudáveis.', 'saude_coletiva', 'Promoção da saúde', 'Empoderamento', ARRAY['empoderamento','comunidade','educação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é educação popular em saúde?', 'A educação popular em saúde baseia-se na pedagogia de Paulo Freire, enfatizando o diálogo e a conscientização crítica. Visa transformar realidades sociais por meio da participação ativa. Aplicada em ações comunitárias do SUS.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['educação','popular','Freire']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a metodologia de Paulo Freire na educação em saúde?', 'A metodologia de Paulo Freire usa o método dialógico, partindo da realidade do educando para problematizar e agir. Em saúde, foca em temas geradores como saneamento e nutrição. Promove autonomia e mudança social.', 'saude_coletiva', 'Educação em saúde', 'Paulo Freire', ARRAY['Freire','metodologia','dialógico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é alfabetização em saúde?', 'Alfabetização em saúde é a capacidade de obter, processar e compreender informações básicas de saúde para tomar decisões. Reduz desigualdades no acesso a serviços. No SUS, é promovida via educação continuada.', 'saude_coletiva', 'Educação em saúde', 'Alfabetização em saúde', ARRAY['alfabetização','saúde','decisões']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos agentes comunitários de saúde?', 'Os agentes comunitários de saúde (ACS) atuam na Atenção Básica, visitando famílias para educação e prevenção. São formados em saúde familiar e participam de vigilância. São essenciais para a integralidade do cuidado no SUS.', 'saude_coletiva', 'Educação em saúde', 'Agentes comunitários', ARRAY['ACS','comunitários','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a educação popular difere da tradicional?', 'A educação popular é horizontal e libertadora, contrastando com a tradicional bancária que deposita conhecimento. Enfatiza a co-construção de saberes. Em saúde, empodera comunidades para ações coletivas.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['popular','tradicional','empoderamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais etapas da metodologia Freire em saúde?', 'Etapas incluem codificação (representação da realidade), decodificação (discussão crítica) e ação transformadora. Aplicadas em rodas de conversa sobre saúde. Levam à conscientização e intervenção comunitária.', 'saude_coletiva', 'Educação em saúde', 'Paulo Freire', ARRAY['Freire','etapas','conscientização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que a alfabetização em saúde é importante?', 'É importante para melhorar a adesão a tratamentos e prevenção de doenças. Reduz barreiras culturais e linguísticas no SUS. Medida por escalas como a de Toledo e Fernandes.', 'saude_coletiva', 'Educação em saúde', 'Alfabetização em saúde', ARRAY['alfabetização','adesão','prevenção']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais atribuições dos ACS na educação?', 'Os ACS educam sobre higiene, vacinação e direitos em saúde durante visitas domiciliares. Mobilizam comunidades para mutirões. Integram equipes multiprofissionais da Estratégia Saúde da Família.', 'saude_coletiva', 'Educação em saúde', 'Agentes comunitários', ARRAY['ACS','educação','visitas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como aplicar educação popular em campanhas?', 'Em campanhas, usa-se diálogo com a comunidade para identificar necessidades e co-criar mensagens. Exemplos incluem educação em DST/AIDS com grupos vulneráveis. Resulta em maior impacto e sustentabilidade.', 'saude_coletiva', 'Educação em saúde', 'Educação popular', ARRAY['campanhas','diálogo','comunidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é análise custo-efetividade em saúde?', 'A análise custo-efetividade avalia se uma intervenção em saúde gera benefícios proporcionais ao custo, usando indicadores como custo por ano de vida ganho. Ajuda na alocação eficiente de recursos escassos. É ferramenta da economia da saúde.', 'saude_coletiva', 'Economia da saúde', 'Custo-efetividade', ARRAY['custo','efetividade','análise']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são DALYs?', 'Os DALYs (Anos de Vida Ajustados por Incapacidade) medem o ônus de doença combinando mortalidade prematura e incapacidade. Usados pela OMS para priorizar intervenções. No Brasil, aplicados em avaliações do SUS.', 'saude_coletiva', 'Economia da saúde', 'DALY', ARRAY['DALY','ônus','doença']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é QALY?', 'O QALY (Ano de Vida Ajustado pela Qualidade) quantifica a qualidade e quantidade de vida gerada por uma intervenção. Varia de 0 (morte) a 1 (saúde perfeita). Útil em análises custo-utilidade para decisões em saúde.', 'saude_coletiva', 'Economia da saúde', 'QALY', ARRAY['QALY','qualidade','vida']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é avaliação de tecnologias em saúde?', 'A avaliação de tecnologias em saúde (ATS) examina eficácia, segurança e impacto econômico de medicamentos, procedimentos e equipamentos. No Brasil, coordenada pela CONITEC no SUS. Orienta incorporação ao sistema público.', 'saude_coletiva', 'Economia da saúde', 'ATS', ARRAY['ATS','tecnologias','CONITEC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é equidade no financiamento de saúde?', 'Equidade no financiamento de saúde aloca recursos priorizando populações vulneráveis e regiões com maiores necessidades. Contrasta com igualdade ao considerar desigualdades sociais. Princípio do SUS e ODS da ONU.', 'saude_coletiva', 'Economia da saúde', 'Equidade', ARRAY['equidade','financiamento','desigualdades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como calcular custo-efetividade?', 'Calcula-se dividindo o custo incremental pela efetividade incremental (ex: custo por DALY evitado). Compara opções para escolher a mais eficiente. Considera perspectiva societal ou pagadora.', 'saude_coletiva', 'Economia da saúde', 'Custo-efetividade', ARRAY['cálculo','incremental','eficiência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a diferença entre DALY e QALY?', 'DALY foca em perda de saúde (mortalidade + incapacidade), enquanto QALY em ganho de vida ajustada por qualidade. Ambos medem impacto, mas DALY é mais usado em ônus populacional. Aplicáveis em avaliações econômicas.', 'saude_coletiva', 'Economia da saúde', 'DALY', ARRAY['DALY','QALY','diferença']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o QALY é usado no SUS?', 'No SUS, QALY auxilia na priorização de tecnologias via CONITEC, avaliando custo por QALY ganho. Limiar de custo-utilidade define viabilidade. Promove alocação racional de recursos limitados.', 'saude_coletiva', 'Economia da saúde', 'QALY', ARRAY['SUS','QALY','priorização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais etapas da ATS no Brasil?', 'Etapas incluem protocolização, produção de evidências, consulta pública e decisão da CONITEC. Envolve stakeholders como sociedades médicas. Resulta em incorporação ou não ao SUS.', 'saude_coletiva', 'Economia da saúde', 'ATS', ARRAY['ATS','etapas','CONITEC']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover equidade no financiamento?', 'Promove-se via fórmulas de repasse que ponderam vulnerabilidade social e epidemiológica. Inclui incentivos para regiões pobres. Monitorado pela Lei Complementar 141/2012.', 'saude_coletiva', 'Economia da saúde', 'Equidade', ARRAY['equidade','repasse','vulnerabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o modelo de Dahlgren-Whitehead?', 'O modelo de Dahlgren-Whitehead representa determinantes sociais da saúde em camadas: individual, comportamental, redes sociais, condições de vida e econômicas, macros políticas. Ilustra influências amplas na saúde. Usado em políticas de equidade.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Dahlgren-Whitehead', ARRAY['determinantes','modelo','equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a CNDSS?', 'A Comissão Nacional sobre Determinantes Sociais da Saúde (CNDSS) foi criada no Brasil em 2008 para propor ações intersetoriais contra desigualdades. Inspirada na OMS, produziu relatórios e recomendações. Influenciou a PNS e políticas sociais.', 'saude_coletiva', 'Determinantes sociais da saúde', 'CNDSS', ARRAY['CNDSS','determinantes','desigualdades']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são desigualdades sociais em saúde?', 'Desigualdades sociais em saúde são diferenças evitáveis e injustas em indicadores de saúde entre grupos sociais. Causadas por pobreza, raça e gênero. No Brasil, evidenciadas por mortalidade infantil em regiões Norte/Nordeste.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Desigualdades sociais', ARRAY['desigualdades','saúde','injustas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais condições de vida afetam a saúde?', 'Condições de vida como moradia precária, saneamento inadequado e acesso a educação impactam diretamente a saúde. São determinantes sociais modificáveis por políticas públicas. Relacionam-se a morbimortalidade evitável.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Condições de vida', ARRAY['condições','vida','saneamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que são determinantes sociais da saúde?', 'Determinantes sociais da saúde são fatores econômicos, sociais e ambientais que influenciam a saúde além dos biológicos. Incluem renda, educação e suporte social. Abordados pela OMS para reduzir desigualdades.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Determinantes sociais', ARRAY['determinantes','sociais','OMS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o modelo Dahlgren-Whitehead é aplicado?', 'Aplicado em análises de políticas para identificar intervenções em camadas externas como emprego e ambiente. No SUS, orienta ações intersetoriais. Ajuda a prevenir doenças crônicas ligadas a desigualdades.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Dahlgren-Whitehead', ARRAY['modelo','aplicação','intersetorial']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais recomendações da CNDSS?', 'Recomendações incluem fortalecer intersetorialidade, investir em Atenção Básica e monitorar indicadores sociais. Enfatiza redução de desigualdades raciais e regionais. Base para a Estratégia Nacional de Saúde e Desenvolvimento.', 'saude_coletiva', 'Determinantes sociais da saúde', 'CNDSS', ARRAY['CNDSS','recomendações','intersetorialidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como medir desigualdades em saúde?', 'Medem-se por índices como razão de mortalidade entre grupos ou índice de Gini adaptado para saúde. Estudos epidemiológicos revelam padrões. Políticas visam reduzir gradientes sociais.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Desigualdades sociais', ARRAY['medir','índices','epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais intervenções melhoram condições de vida?', 'Intervenções incluem programas de saneamento, habitação digna e acesso a água potável. No Brasil, via PAC e SUS. Reduzem incidência de doenças infecciosas e melhoram qualidade de vida.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Condições de vida', ARRAY['intervenções','saneamento','habitação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Por que os determinantes sociais são prioritários?', 'São prioritários porque explicam 80% das variações em saúde populacional, segundo a OMS. Exigem abordagens além do setor saúde. No Brasil, integram a agenda da CNDSS e PNS.', 'saude_coletiva', 'Determinantes sociais da saúde', 'Determinantes sociais', ARRAY['prioritários','OMS','PNS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual é o principal desafio na saúde indígena no Brasil, segundo o SUS?', 'O principal desafio na saúde indígena é a garantia de acesso equitativo aos serviços de saúde, considerando a diversidade cultural e geográfica dos povos. O Subsistema de Atenção à Saúde Indígena (SasiSUS) foi criado para coordenar ações específicas, como atendimento diferenciado e respeito às práticas tradicionais. Epidemias como malária e tuberculose ainda afetam comunidades isoladas devido à falta de infraestrutura.', 'saude_coletiva', 'Saúde indígena', 'Acesso e políticas', ARRAY['SUS', 'SasiSUS', 'Epidemias']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a Política Nacional de Atenção à Saúde dos Povos Indígenas?', 'A Política Nacional de Atenção à Saúde dos Povos Indígenas, instituída em 2002, visa promover a integralidade do cuidado, respeitando a autonomia cultural e os direitos ancestrais. Ela enfatiza a participação comunitária na gestão de saúde e a formação de profissionais sensíveis à etnicidade. Ações incluem vigilância epidemiológica e promoção de saúde em terras indígenas.', 'saude_coletiva', 'Saúde indígena', 'Políticas nacionais', ARRAY['Política Nacional', 'Autonomia cultural', 'Vigilância']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS atende emergências em áreas indígenas remotas?', 'O SUS atende emergências em áreas indígenas remotas por meio de equipes multidisciplinares, incluindo Casas de Saúde Indígena e unidades móveis. A articulação com a Força Aérea Brasileira facilita o transporte aeromédico em casos graves. Prioriza-se o atendimento culturalmente adequado para reduzir morbimortalidade por doenças infecciosas.', 'saude_coletiva', 'Saúde indígena', 'Atendimento emergencial', ARRAY['SUS', 'Casas de Saúde', 'Aeromédico']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os principais agravos de saúde em povos indígenas?', 'Os principais agravos incluem doenças infecciosas como malária, tuberculose e diarreias, além de desnutrição e problemas respiratórios crônicos. Fatores como pobreza, saneamento precário e perda de territórios contribuem para alta vulnerabilidade. Programas de imunização e educação em saúde visam mitigar esses riscos.', 'saude_coletiva', 'Saúde indígena', 'Agravos epidemiológicos', ARRAY['Doenças infecciosas', 'Desnutrição', 'Imunização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel dos Distritos Sanitários Especiais Indígenas (DSEI)?', 'Os DSEI são responsáveis pela execução de ações de saúde em terras indígenas, coordenando equipes de campo e unidades fixas. Eles promovem a integração entre medicina convencional e tradicional, garantindo suprimentos e vigilância. Cobrem regiões específicas, como o DSEI Yanomami, para atender populações isoladas.', 'saude_coletiva', 'Saúde indígena', 'DSEI', ARRAY['DSEI', 'Equipes de campo', 'Medicina tradicional']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a saúde mental é abordada em comunidades indígenas?', 'A saúde mental em comunidades indígenas considera o impacto do contato com a sociedade não indígena, como perda cultural e violência. Abordagens incluem terapia comunitária e valorização de saberes ancestrais para tratar transtornos como depressão e suicídio. O SUS integra psicólogos e agentes indígenas para intervenções culturalmente sensíveis.', 'saude_coletiva', 'Saúde indígena', 'Saúde mental', ARRAY['Saúde mental', 'Terapia comunitária', 'Agentes indígenas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Atenção Diferenciada à Saúde Indígena?', 'A Atenção Diferenciada à Saúde Indígena é um princípio do SUS que adapta serviços às necessidades específicas, como mobilidade e práticas culturais. Inclui formação de recursos humanos e articulação intersetorial para proteção territorial. Visa reduzir desigualdades em indicadores de saúde como mortalidade infantil.', 'saude_coletiva', 'Saúde indígena', 'Atenção diferenciada', ARRAY['SUS', 'Formação profissional', 'Mortalidade infantil']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são prioritárias para povos indígenas?', 'Vacinas prioritárias incluem BCG, hepatite B, poliomielite e contra influenza, devido à alta suscetibilidade a surtos. Campanhas anuais no SUS visam cobertura vacinal acima de 95% em terras indígenas. A logística desafiadora exige armazenamento em cadeia de frio adaptada.', 'saude_coletiva', 'Saúde indígena', 'Imunização', ARRAY['Vacinas', 'Cobertura vacinal', 'Cadeia de frio']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o tabagismo afeta a saúde indígena?', 'O tabagismo introduzido por contato cultural aumenta riscos de doenças respiratórias e cardiovasculares em indígenas. Programas de cessação usam abordagens comunitárias, integrando xamãs e educação preventiva. O SUS monitora prevalência para políticas antitabaco específicas.', 'saude_coletiva', 'Saúde indígena', 'Tabagismo', ARRAY['Tabagismo', 'Doenças respiratórias', 'Cessação']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da participação indígena na gestão de saúde?', 'A participação indígena na gestão de saúde fortalece a democracia sanitária, com conselhos e conferências específicas. Permite priorizar demandas locais, como controle de endemias. A Lei 8.142/1990 garante representação em instâncias deliberativas.', 'saude_coletiva', 'Saúde indígena', 'Participação comunitária', ARRAY['Gestão participativa', 'Conselhos de saúde', 'Lei 8142']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o pré-natal de baixo risco na saúde da mulher?', 'O pré-natal de baixo risco na saúde da mulher envolve consultas regulares para monitorar gestação sem complicações, iniciando no primeiro trimestre. Inclui exames como ultrassom e testes laboratoriais para detectar anemias ou infecções precocemente. O SUS recomenda pelo menos seis consultas para reduzir mortalidade materna e fetal.', 'saude_coletiva', 'Saúde da mulher', 'Pré-natal', ARRAY['Gestação', 'SUS', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Atenção Básica para mulheres?', 'A Política Nacional de Atenção Básica para mulheres promove equidade de gênero, com ênfase em prevenção de câncer de mama e colo do útero. Inclui rastreamento por mamografia e Papanicolau em unidades de saúde da família. Visa integralidade do cuidado, abordando violência e saúde reprodutiva.', 'saude_coletiva', 'Saúde da mulher', 'Políticas nacionais', ARRAY['Equidade de gênero', 'Rastreamento', 'Saúde reprodutiva']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS aborda a violência contra a mulher?', 'O SUS aborda a violência contra a mulher por meio da Rede de Atenção às Vítimas de Violência, com serviços de acolhimento e notificação compulsória. A Lei Maria da Penha integra ações de saúde e justiça para proteção. Equipes multidisciplinares oferecem suporte psicológico e médico em UBS e hospitais.', 'saude_coletiva', 'Saúde da mulher', 'Violência de gênero', ARRAY['Lei Maria da Penha', 'Notificação', 'Acolhimento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza a menopausa na atenção à saúde da mulher?', 'A menopausa na atenção à saúde da mulher é um período de transição hormonal que pode causar sintomas como fogachos e osteoporose. O SUS oferece acompanhamento com reposição hormonal seletiva e promoção de atividade física. Rastreio de riscos cardiovasculares é essencial para qualidade de vida.', 'saude_coletiva', 'Saúde da mulher', 'Menopausa', ARRAY['Hormônios', 'Osteoporose', 'Riscos cardiovasculares']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais são os direitos reprodutivos da mulher no SUS?', 'Os direitos reprodutivos da mulher no SUS incluem planejamento familiar, contracepção gratuita e interrupção legal da gravidez em casos de risco à vida. A Lei 9.263/1996 regula o procedimento de esterilização voluntária após 25 anos. Educação sexual integral visa empoderamento e prevenção de DSTs.', 'saude_coletiva', 'Saúde da mulher', 'Direitos reprodutivos', ARRAY['Planejamento familiar', 'Lei 9263', 'Educação sexual']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir o câncer de colo do útero no Brasil?', 'A prevenção do câncer de colo do útero no Brasil envolve vacinação contra HPV para meninas de 9 a 14 anos e rastreamento anual por citologia oncótica a partir dos 25 anos. O SUS distribui vacinas e realiza exames em UBS para detecção precoce. Fatores de risco como tabagismo e múltiplos parceiros sexuais são abordados em educação em saúde.', 'saude_coletiva', 'Saúde da mulher', 'Câncer de colo', ARRAY['HPV', 'Citologia', 'Rastreamento']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Rede Cegonha no SUS?', 'A Rede Cegonha no SUS é uma estratégia para qualificar o cuidado materno-infantil, desde a pré-natal até o puerpério, com humanização no parto. Inclui regulação de vagas e transporte sanitário para regiões remotas. Reduziu a mortalidade materna em 20% desde 2011 por meio de articulação intersetorial.', 'saude_coletiva', 'Saúde da mulher', 'Rede Cegonha', ARRAY['Cuidado materno', 'Humanização', 'Mortalidade materna']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da anemia na gestação?', 'A anemia na gestação impacta o desenvolvimento fetal, aumentando riscos de parto prematuro e baixo peso ao nascer. O SUS suplementa ferro e ácido fólico em pré-natais para prevenção, monitorando hemoglobina em consultas. Mulheres com anemia grave recebem transfusões em hospitais de referência.', 'saude_coletiva', 'Saúde da mulher', 'Anemia gestacional', ARRAY['Suplementação', 'Ferro', 'Pré-natal']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS promove a saúde bucal da mulher?', 'O SUS promove a saúde bucal da mulher por meio do Brasil Sorridente, com atenção integrada à gestação para prevenir cáries e gengivites hormonais. Consultas odontológicas gratuitas em UBS incluem profilaxia e restaurações. Educação sobre higiene oral reduz desigualdades em acesso a serviços.', 'saude_coletiva', 'Saúde da mulher', 'Saúde bucal', ARRAY['Brasil Sorridente', 'Profilaxia', 'Higiene oral']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve o cuidado no puerpério?', 'O cuidado no puerpério envolve monitoramento de sangramento, infecções e saúde mental pós-parto, com visitas domiciliares pela equipe da ESF. O SUS incentiva amamentação exclusiva até seis meses e vacinação do recém-nascido. Acompanhamento psicológico previne depressão pós-parto em mulheres vulneráveis.', 'saude_coletiva', 'Saúde da mulher', 'Puerpério', ARRAY['Amamentação', 'ESF', 'Depressão pós-parto']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a Política Nacional de Saúde do Idoso?', 'A Política Nacional de Saúde do Idoso, de 1998, garante atendimento integral no SUS, com ênfase em prevenção de quedas e doenças crônicas. Inclui a Caderneta de Saúde da Pessoa Idosa para rastreio de hipertensão e diabetes. Promove envelhecimento ativo por meio de grupos de atividade física em UBS.', 'saude_coletiva', 'Saúde do idoso', 'Políticas nacionais', ARRAY['Envelhecimento ativo', 'SUS', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como prevenir quedas em idosos?', 'A prevenção de quedas em idosos envolve avaliação de risco em consultas geriátricas, com adaptações ambientais como corrimãos e remoção de tapetes. O SUS oferece órteses e fisioterapia para melhorar equilíbrio e força muscular. Exercícios regulares reduzem em 25% o risco de fraturas por osteoporose.', 'saude_coletiva', 'Saúde do idoso', 'Prevenção de quedas', ARRAY['Avaliação geriátrica', 'Fisioterapia', 'Osteoporose']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a atenção à saúde mental do idoso?', 'A atenção à saúde mental do idoso aborda demências como Alzheimer, com diagnóstico precoce e suporte familiar no SUS. Grupos de convivência em CRAS previnem isolamento social e depressão. Medicamentos como inibidores de colinesterase são fornecidos gratuitamente em farmácias de alto custo.', 'saude_coletiva', 'Saúde do idoso', 'Saúde mental', ARRAY['Alzheimer', 'CRAS', 'Demências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Quais vacinas são recomendadas para idosos?', 'Vacinas recomendadas para idosos incluem influenza anualmente, pneumocócica e herpes zoster para prevenir infecções graves. O SUS realiza campanhas nacionais com cobertura em UBS para reduzir hospitalizações. Idosos com comorbidades recebem prioridade em doses reforçadas.', 'saude_coletiva', 'Saúde do idoso', 'Imunização', ARRAY['Influenza', 'Pneumocócica', 'Campanhas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS cuida da incontinência urinária em idosos?', 'O SUS cuida da incontinência urinária em idosos com avaliação uroginecológica e treinamento de assoalho pélvico em fisioterapia. Medicamentos anticolinérgicos são prescritos para casos de urgência. Acompanhamento multiprofissional previne complicações como infecções recorrentes.', 'saude_coletiva', 'Saúde do idoso', 'Incontinência', ARRAY['Fisioterapia', 'Uroginecologia', 'Infecções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que caracteriza o envelhecimento populacional no Brasil?', 'O envelhecimento populacional no Brasil resulta em 14% de idosos acima de 60 anos, aumentando demanda por serviços crônicos no SUS. Políticas como o Estatuto do Idoso garantem prioridade em atendimentos. Projeções indicam 30% da população idosa até 2050, exigindo planejamento sanitário.', 'saude_coletiva', 'Saúde do idoso', 'Envelhecimento demográfico', ARRAY['Estatuto do Idoso', 'SUS', 'Projeções']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da ESF na saúde do idoso?', 'A ESF na saúde do idoso realiza visitas domiciliares para monitorar adesão medicamentosa e sinais vitais. Promove educação em saúde para autocuidado em hipertensos e diabéticos. A estratégia reduziu em 15% as internações por descompensação crônica.', 'saude_coletiva', 'Saúde do idoso', 'Estratégia Saúde da Família', ARRAY['Visitas domiciliares', 'Autocuidado', 'Internações']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como tratar a osteoporose em idosos?', 'O tratamento da osteoporose em idosos inclui bifosfonatos e cálcio/vitamina D prescritos no SUS, com densitometria óssea para diagnóstico. Exercícios de impacto controlado fortalecem ossos e previnem fraturas. Rastreio em mulheres pós-menopausa é anual a partir dos 65 anos.', 'saude_coletiva', 'Saúde do idoso', 'Osteoporose', ARRAY['Bifosfonatos', 'Densitometria', 'Exercícios']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a malnutrição em idosos?', 'A malnutrição em idosos é comum por perda de apetite e problemas dentários, levando a sarcopenia e imunossupressão. O SUS oferece suplementação nutricional em UBS e avaliação antropométrica regular. Programas de alimentação coletiva em ILPIs combatem desnutrição em institucionalizados.', 'saude_coletiva', 'Saúde do idoso', 'Nutrição', ARRAY['Sarcopenia', 'Suplementação', 'ILPIs']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a importância da reabilitação geriátrica?', 'A reabilitação geriátrica restaura funcionalidade após AVC ou fraturas, com fisioterapia e terapia ocupacional no SUS. Equipes interdisciplinares em CAPS e hospitais melhoram independência e qualidade de vida. Reduz custos com internações prolongadas em 30%.', 'saude_coletiva', 'Saúde do idoso', 'Reabilitação', ARRAY['Fisioterapia', 'AVC', 'CAPS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Relação Nacional de Medicamentos Essenciais (Rename)?', 'A Rename lista medicamentos essenciais para o SUS, priorizando custo-efetividade e evidências científicas para doenças prevalentes. Atualizada periodicamente, garante acesso gratuito em farmácias de unidades básicas. Inclui analgésicos, antibióticos e antirretrovirais para integralidade do cuidado.', 'saude_coletiva', 'Políticas de medicamentos', 'Rename', ARRAY['SUS', 'Custo-efetividade', 'Acesso gratuito']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a Assistência Terapêutica Integral (ATI)?', 'A ATI fornece medicamentos de alto custo para doenças crônicas como HIV e câncer via Componente Especializado da Assistência Farmacêutica. Protocolos clínicos definem critérios de dispensação em farmácias especializadas. O SUS monitora adesão para otimizar recursos e resultados terapêuticos.', 'saude_coletiva', 'Políticas de medicamentos', 'ATI', ARRAY['Medicamentos alto custo', 'HIV', 'Protocolos']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Farmácia Popular?', 'A Farmácia Popular subsidia medicamentos para hipertensão, diabetes e asma em farmácias credenciadas, com descontos de até 90%. Amplia acesso em áreas sem UBS, reduzindo desassistência. O programa atende 20 milhões de brasileiros anualmente com genéricos.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmácia Popular', ARRAY['Subsídios', 'Genéricos', 'Doenças crônicas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que define a dispensação racional de medicamentos?', 'A dispensação racional de medicamentos envolve orientação farmacêutica para adesão e prevenção de interações no SUS. Farmacêuticos em UBS verificam prescrições e promovem uso adequado. Reduz resistência antimicrobiana e iatrogenia em populações vulneráveis.', 'saude_coletiva', 'Políticas de medicamentos', 'Dispensação', ARRAY['Orientação farmacêutica', 'Adesão', 'Resistência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como o SUS gerencia medicamentos biológicos?', 'O SUS gerencia medicamentos biológicos para imunossupressão em transplantes e autoimunes via judicialização ou protocolos. A Conitec avalia incorporação com evidências de eficácia e segurança. Armazenamento em cadeia de frio é crucial para biossimilares distribuídos.', 'saude_coletiva', 'Políticas de medicamentos', 'Biológicos', ARRAY['Conitec', 'Protocolos', 'Judicialização']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a política de controle de preços de medicamentos?', 'A política de controle de preços de medicamentos, pela CMED, limita reajustes anuais para genéricos e de referência, garantindo acessibilidade. Multas por descumprimento protegem o consumidor. Integra a Assistência Farmacêutica do SUS para equidade.', 'saude_coletiva', 'Políticas de medicamentos', 'Controle de preços', ARRAY['CMED', 'Genéricos', 'Acessibilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Farmacovigilância no Brasil?', 'A Farmacovigilância no Brasil monitora reações adversas a medicamentos via Notivisa, reportando à Anvisa para alertas e recalls. Profissionais de saúde notificam em UBS para melhorar segurança. Contribui para atualizações na Rename e protocolos SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Farmacovigilância', ARRAY['Notivisa', 'Anvisa', 'Reações adversas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como promover o uso de genéricos?', 'A promoção de genéricos envolve intercambialidade comprovada pela Anvisa, com preços até 35% menores que referência. O SUS prioriza em licitações para economia de recursos. Campanhas educativas aumentam confiança e adesão em tratamentos crônicos.', 'saude_coletiva', 'Políticas de medicamentos', 'Genéricos', ARRAY['Anvisa', 'Intercambialidade', 'Economia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da judicialização de medicamentos?', 'A judicialização de medicamentos sobrecarrega orçamentos do SUS com demandas por fármacos off-label, sem evidências robustas. A Conitec media incorporações para racionalidade. Representa 1,5% do PIB em saúde, exigindo políticas de mediação.', 'saude_coletiva', 'Políticas de medicamentos', 'Judicialização', ARRAY['Conitec', 'Off-label', 'Orçamento SUS']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que regula a seleção de medicamentos essenciais?', 'A seleção de medicamentos essenciais segue critérios da OMS, adaptados à epidemiologia brasileira pela Conitec. Prioriza necessidades de saúde pública como vacinas e antibióticos. Atualizações bienais incorporam inovações para efetividade no SUS.', 'saude_coletiva', 'Políticas de medicamentos', 'Seleção essencial', ARRAY['OMS', 'Conitec', 'Epidemiologia']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a Agência Nacional de Vigilância Sanitária (Anvisa)?', 'A Anvisa regula serviços de saúde, inspecionando estabelecimentos para padrões de qualidade e biossegurança no SUS. Fiscaliza licenças de funcionamento de hospitais e clínicas. Colabora com regulação de dispositivos médicos e medicamentos para proteção ao usuário.', 'saude_coletiva', 'Regulação em saúde', 'Anvisa', ARRAY['Fiscalização', 'Biossegurança', 'Licenças']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o papel da Comissão Intergestores Tripartite (CIT)?', 'A CIT coordena políticas de saúde entre União, estados e municípios, aprovando pactuações de recursos e responsabilidades no SUS. Discute regulação de leitos e SAMU. Garante equidade na alocação de verbas federais para ações coletivas.', 'saude_coletiva', 'Regulação em saúde', 'CIT', ARRAY['Pactuação', 'SUS', 'Equidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como funciona a regulação de leitos hospitalares?', 'A regulação de leitos hospitalares usa sistemas como o SISREG para alocação centralizada, priorizando urgências via UPAs e SAMU. Evita superlotação e otimiza uso de recursos no SUS. Estados coordenam com municípios para transferências seguras.', 'saude_coletiva', 'Regulação em saúde', 'Leitos', ARRAY['SISREG', 'SAMU', 'Urgências']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é o Complexo Regulador em saúde?', 'O Complexo Regulador integra regulação assistencial, logística e judicial no SUS, com centrais de regulação para agendamentos especializados. Facilita acesso a exames e consultas em redes regionais. Melhora resolutividade em 40% das demandas ambulatoriais.', 'saude_coletiva', 'Regulação em saúde', 'Complexo Regulador', ARRAY['Assistencial', 'Agendamentos', 'Redes regionais']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual a função da Ouvidoria do SUS?', 'A Ouvidoria do SUS recebe denúncias e sugestões para melhoria de serviços, garantindo participação cidadã na gestão. Notifica irregularidades como filas excessivas para auditorias. Integra o controle social com conselhos de saúde locais.', 'saude_coletiva', 'Regulação em saúde', 'Ouvidoria', ARRAY['Participação cidadã', 'Denúncias', 'Controle social']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como a Anvisa regula transplantes?', 'A Anvisa regula transplantes por meio de habilitação de serviços e controle de tecidos via CNT, assegurando doadores e receptores. Fiscaliza boas práticas em bancos de órgãos. Reduziu transmissão de doenças em 95% com protocolos rigorosos.', 'saude_coletiva', 'Regulação em saúde', 'Transplantes', ARRAY['CNT', 'Habilitação', 'Boas práticas']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que envolve a regulação do SAMU?', 'A regulação do SAMU centraliza chamadas 192 para despacho de ambulâncias, priorizando códigos de Manchester para gravidade. Treina reguladores para triagem telefônica eficiente. Integra com UPAs para continuidade do cuidado pré-hospitalar.', 'saude_coletiva', 'Regulação em saúde', 'SAMU', ARRAY['Triagem', 'Códigos Manchester', 'Pré-hospitalar']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Qual o impacto da auditoria em saúde?', 'A auditoria em saúde verifica conformidade de procedimentos no SUS, controlando glosas e fraudes em faturamentos. Equipes do TCU e conselhos analisam contas hospitalares. Melhora transparência e alocação de recursos para prioridades epidemiológicas.', 'saude_coletiva', 'Regulação em saúde', 'Auditoria', ARRAY['TCU', 'Fraudes', 'Transparência']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'Como regular planos de saúde privados?', 'A regulação de planos de saúde privados pela ANS define coberturas mínimas e prazos de atendimento, protegendo direitos do consumidor. Multa operadoras por negativas indevidas. Integra com SUS para portabilidade e continuidade assistencial.', 'saude_coletiva', 'Regulação em saúde', 'Planos privados', ARRAY['ANS', 'Coberturas', 'Portabilidade']),
(gen_random_uuid(), 'a0000001-0000-0000-0000-000000000005', 'O que é a pactuação interfederativa?', 'A pactuação interfederativa define responsabilidades entre entes federados no SUS, alocando recursos para ações como vigilância sanitária. Conferências tripartites aprovam termos anuais. Garante regionalização de serviços para equidade.', 'saude_coletiva', 'Regulação em saúde', 'Pactuação', ARRAY['Interfederativa', 'Regionalização', 'Vigilância']);
