  (gen(), (SELECT id FROM question_banks WHERE code = 'harvested' LIMIT 1), 'ENARE-2025-Objetiva | R1 O escore BISAP tem sido amplamente utilizado na avaliação prognóstica da pancreatite aguda. Dentre os critérios abaixo, o único que NÃO faz parte desse escore é:', '[{"letter":"A","text":"presença de derrame pleural;"},{"letter":"B","text":"glicemia maior que 200 mg/dL;"},{"letter":"C","text":"ureia nitrogenada sérica maior que 25 mg/dL;"},{"letter":"D","text":"idade maior que 60 anos;"},{"letter":"E","text":"alteração do estado mental."}]', NULL, NULL, 'clinica_medica', 'Gastroenterologia', '["Pancreatite aguda","Escore BISAP"]', 'easy', 1, -1, 0.25, 'enare', 2026, '{"icd10Codes":["K85"],"atcCodes":[],"confidence":0.95,"parsedAt":"2026-02-02T15:38:56.758Z","sourceFile":"ENARE-2025.pdf"}'),
