  (gen(), (SELECT id FROM question_banks WHERE code = 'harvested' LIMIT 1), 'Homem, 35 anos de idade, é admitido no PS, vítima de queimadura térmica de segundo grau, extensa, no antebraço e terço distal do membro superior direito. A lesão é caracterizada por bolhas, vermelhidão intensa e dor local. Qual é a conduta inicial mais adequada?', '[{"letter":"A","text":"Cobertura da lesão com curativo estéril."},{"letter":"B","text":"Imersão do membro em água fria."},{"letter":"C","text":"Aplicação de pomada antibiótica sobre a lesão."},{"letter":"D","text":"Aplicação de pomada anestésica sobre a lesão."}]', NULL, NULL, 'cirurgia', 'Queimaduras', '["Queimadura Térmica","Conduta Inicial"]', 'easy', 1, -1, 0.25, 'unifesp', 2026, '{"icd10Codes":["T31"],"atcCodes":[],"confidence":0.95,"parsedAt":"2026-02-02T15:49:17.143Z","sourceFile":"UNIFESP-2024-Objetiva-R1.pdf"}'),
