  (gen(), (SELECT id FROM question_banks WHERE code = 'harvested' LIMIT 1), 'Uma paciente de 18 anos chega à emergência do hospital referindo ter sido vítima de violência sexual há 2 horas em uma festa. Relata que, no ato, houve penetração. Ela desconhece o agressor, o qual não usou preservativo. A abordagem inicial deve incluir:', '[{"letter":"A","text":"atendimento inicial exclusivo por médico;"},{"letter":"B","text":"encaminhamento da paciente primeiramente ao instituto médico legal;"},{"letter":"C","text":"encaminhamento da paciente para realizar boletim de ocorrência antes do atendimento médico;"},{"letter":"D","text":"início da profilaxia para as doenças sexualmente transmissíveis e prevenção de gravidez em até 5 dias;"},{"letter":"E","text":"avaliação do estado geral de saúde, orientação e proteção contra doenças de transmissão sexual, prevenção de gravidez e coleta de materiais biológicos ou outros indícios materiais que permitam a identificação do agressor."}]', NULL, NULL, 'clinica_medica', 'Medicina Legal', '["Violência Sexual","Profilaxia"]', 'medium', 1.2, 0, 0.25, 'enare', 2026, '{"icd10Codes":["T74.2"],"atcCodes":[],"confidence":0.98,"parsedAt":"2026-02-01T02:00:15.558Z","sourceFile":"ENARE-2025.pdf"}'),
