  (gen(), (SELECT id FROM question_banks WHERE code = 'harvested' LIMIT 1), 'Homem, 50 anos de idade, apresenta fraqueza e espasmo muscular progressivo nos membros inferiores. Exame neurológico com Babinski,lexia e eletroneuromiografia sugere acometimento de segundo neurônio motor. Qual é o diagnóstico mais provável?', '[{"letter":"A","text":"Síndrome de Guillain-Barré."},{"letter":"B","text":"Esclerose múltipla."},{"letter":"C","text":"Esclerose lateral amiotrófica (ELA)."},{"letter":"D","text":"Doença de Parkinson."}]', NULL, NULL, 'clinica_medica', 'Neurologia', '["Doenças do Neurônio Motor"]', 'medium', 1.2, 0, 0.25, 'unifesp', 2026, '{"icd10Codes":["G12.2"],"atcCodes":[],"confidence":0.98,"parsedAt":"2026-02-01T02:08:27.379Z","sourceFile":"UNIFESP-2024-Objetiva-R1.pdf"}'),
