  (gen(), (SELECT id FROM question_banks WHERE code = 'harvested' LIMIT 1), 'Um pesquisador brasileiro, visando calcular o risco de morte por encefalite pelo vírus do sarampo, reúne informações provenientes de alguns sistemas de informação em saúde e produziu uma fórmula para o cálculo do coeficiente de mortalidade específica por essa complicação dessa infecção viral. Assinale a alternativa que contém corretamente a fórmula de cálculo elaborada pelo pesquisador:', '[{"letter":"A","text":"Coeficiente de mortalidade específica por encefalite pelo vírus do sarampo = casos novos de encefalite pelo vírus do sarampo x 1.000 ÷ população total da localidade."},{"letter":"B","text":"Coeficiente de mortalidade específica por encefalite pelo vírus do sarampo = mortes por encefalite pelo vírus do sarampo x 100.000 ÷ total casos de sarampo na localidade."},{"letter":"C","text":"Coeficiente de mortalidade específica por encefalite pelo vírus do sarampo = mortes por encefalite pelo vírus do sarampo x 100 ÷ total casos de sarampo na localidade."},{"letter":"D","text":"Coeficiente de mortalidade específica por encefalite pelo vírus do sarampo = mortes por encefalite pelo vírus do sarampo x 100 ÷ total de óbitos na população da localidade."},{"letter":"E","text":"Coeficiente de mortalidade específica por encefalite pelo vírus do sarampo = mortes por encefalite pelo vírus do sarampo x 100.000 ÷ população total da localidade."}]', NULL, NULL, 'clinica_medica', 'Epidemiologia', '["Coeficiente de Mortalidade","Encefalite","Vírus do Sarampo"]', 'medium', 1.2, 0, 0.25, 'enare', 2026, '{"icd10Codes":["B05.0"],"atcCodes":[],"confidence":0.95,"parsedAt":"2026-02-01T02:56:57.548Z","sourceFile":"simulados-12_07_2024_100_DIAS_PARA_O_ENARE_-_SIMULADO_INEDITO_FGV_Caderno_de_Questoes_Atualizado.pdf"}'),
